INSERT INTO `tkb_aq_desease` (`id`, `cat_id`, `group_id`, `name`, `en_name`, `description`, `agent`, `zone`, `symptom`, `treatment`, `keyword`, `author`, `add_time`, `edit_time`, `reference`, `user_name`, `hit`, `focus`) VALUES
(1, 3, 0, 'Bệnh hoại tử thần kinh', 'Viral nervous necrosis', '', '<p>\r\n	G&acirc;y bệnh l&agrave; vi r&uacute;t <em>Betanodavirus </em>h&igrave;nh cầu, đường k&iacute;nh l&agrave; 26-32nm. Acid nh&acirc;n l&agrave; ARN. Virus k&yacute; sinh trong tế b&agrave;o chất của tế b&agrave;o thần kinh trong n&atilde;o v&agrave; trong v&otilde;ng mạc mắt.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Betaodavirus nuôi cấy từ não cá chẽm bệnh" src="/upload/images/desease/hoai_tu_than_kinh.jpg" style="width: 359px; height: 285px;" /></p>\r\n<p style="text-align: center;">\r\n	<em>Betaodavirus </em>nu&ocirc;i cấy từ n&atilde;o c&aacute; song bệnh (nhuộm &acirc;m, ảnh KHVĐT, thước đo 60nm)</p>\r\n', '<p>\r\n	Bệnh đ&atilde; ph&aacute;t hiện &iacute;t nhất l&agrave; 30 lo&agrave;i c&aacute; biển v&agrave; đặc biệt thường gặp ở c&aacute; nu&ocirc;i lồng như c&aacute; song điểm đai (<em>Epinephelus malabaricus</em>)- Th&aacute;i Lan; c&aacute; song mỡ (<em>E. tauvina</em>)- Singapore; c&aacute; song v&acirc;n m&acirc;y (<em>E.moara</em>) v&agrave; c&aacute; song chấm đỏ (<em>E. akaara</em>)- Nhật Bản; c&aacute; song bảy sọc (<em>E. septemfasciatus</em>)- H&agrave;n Quốc, Nhật Bản; c&aacute; song lưng g&ugrave; (<em>Cromileptes altivelis</em>)- Indonesia. Tỷ lệ chết 70-100% ở c&aacute; hương 2,5-4,0cm, khi c&aacute; lớn (15cm) tỷ lệ chết giảm c&ograve;n 20%. ở Việt Nam c&aacute;c lo&agrave;i c&aacute; song (<em>Epinephelus </em>spp) nu&ocirc;i lồng tr&ecirc;n vịnh Hạ Long thường gặp bệnh hoại tử thần kinh, kết quả điều tra ở huyện V&acirc;n Đồn, Quảng Ninh (2002) c&oacute; số lồng bị bệnh. Bệnh ph&aacute;t từ th&aacute;ng 5-10, đặc biệt khi mưa nhiều. Nhiệt độ th&iacute;ch hợp bệnh ph&aacute;t triển 25-30<sup>0</sup>C.</p>\r\n', '<p>\r\n	Bệnh VNN l&agrave; bệnh cấp t&iacute;nh xuất hiện từ trại ương giống. Ấu tr&ugrave;ng (từ 10-25ng&agrave;y tuổi) hoặc c&aacute; giống bỏ ăn, c&aacute; chết rải r&aacute;c, bơi lờ đờ tr&ecirc;n tầng mặt do b&oacute;ng hơi trương phồng. C&oacute; sự xung huyết trong n&atilde;o m&agrave; c&oacute; thể nh&igrave;n thấy được. C&aacute; nhiễm bệnh bơi kh&ocirc;ng b&igrave;nh thường, bơi lội mạnh kh&ocirc;ng định hướng, đầu ch&uacute;c xuống dưới.</p>\r\n<p style="text-align: center;">\r\n	<img alt=" ca benh hoai tu than kinh" src="/upload/images/desease/hoai_tu_than_kinh2.jpg" style="width: 405px; height: 228px;" /></p>\r\n<p style="text-align: center;">\r\n	<img alt="ca giong benh hoai tu than kinh" src="/upload/images/desease/hoai_tu_than_kinh3.jpg" style="width: 428px; height: 278px;" /></p>\r\n<p style="text-align: center;">\r\n	Nh&igrave;n b&ecirc;n tr&ecirc;n b&oacute;ng hơi trương phồng v&agrave; n&atilde;o xuất huyết r&otilde; r&agrave;ng nh&igrave;n thấy bằng mắt thường qua da của c&aacute; hương nhỏ</p>\r\n<p>\r\n	Bệnh cấp t&iacute;nh hoặc thứ cấp t&iacute;nh trong c&aacute; m&uacute; hương v&agrave; c&aacute; mới nu&ocirc;i lồng c&oacute; dấu hiệu giống nhau. C&aacute; chết v&agrave; hấp hối hầu hết b&oacute;ng hơi trương phồng. C&aacute; bơi lội hỗn loạn kh&ocirc;ng định hướng, h&agrave;m dưới c&oacute; vết hoại tử do ch&agrave; s&aacute;t với lưới. Nhiều c&aacute; c&oacute; m&agrave;u đen v&agrave; thường bơi chậm chạp. Triệu tăng dần khi quần đ&agrave;n đ&atilde; nhiễm bệnh.</p>\r\n<p style="text-align: center;">\r\n	<img alt="ca song benh" src="/upload/images/desease/hoai_tu_than_kinh4.jpg" style="width: 504px; height: 397px;" /></p>\r\n<p style="text-align: center;">\r\n	<img alt="Ca mú benh" src="/upload/images/desease/hoai_tu_than_kinh5.jpg" style="width: 409px; height: 348px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute; song bệnh bơi quay tr&ograve;n</p>\r\n<p>\r\n	Trong lồng c&aacute; lớn (&gt;150g) bị bệnh VNN c&oacute; &iacute;t triệu chứng hơn v&agrave; tỷ lệ chết giảm. c&aacute; thường chuyển m&agrave;u đen (tối) v&agrave; bơi chậm chạp với b&oacute;ng hơi trương phồng v&agrave; c&oacute; thể hoặc kh&ocirc;ng c&oacute; vết bệnh ở đầu. Giải phẫu cơ quan nội tạng b&igrave;nh thường v&agrave; ruột kh&ocirc;ng c&oacute; thức ăn.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cá song bệnh bóng hơi phồng ra" src="/upload/images/desease/hoai_tu_than_kinh6.jpg" style="width: 421px; height: 286px;" /></p>\r\n<p>\r\n	C&aacute; dưới 20 ng&agrave;y tuổi bệnh kh&ocirc;ng c&oacute; dấu hiệu r&otilde;. C&aacute; sau 20-45 ng&agrave;y tuổi dấu hiệu bệnh c&aacute; yếu bơi gần tầng mặt. C&aacute; từ 45 ng&agrave;y đến 4 th&aacute;ng tuổi khi bị bệnh bơi kh&ocirc;ng định hướng (bơi quay tr&ograve;n hoặc xo&aacute;y ch&ocirc;n ốc), k&eacute;m ăn hoặc bỏ ăn, th&acirc;n đen x&aacute;m, đặc biệt đu&ocirc;i v&agrave; c&aacute;c v&acirc;y chuyển m&agrave;u đen, mắt đục (h&igrave;nh 48) hoặc b&oacute;ng hơi phồng ra. C&aacute; bệnh hoạt động yếu đầu treo tr&ecirc;n mặt nước hoặc nằm dưới đ&aacute;y bể hoặc đ&aacute;y lồng. C&aacute; chết sau 3-5 ng&agrave;y c&oacute; dấu hiệu bệnh. M&ocirc; bệnh học: tổ chức n&atilde;o v&agrave; mắt xuất hiện nhiều kh&ocirc;ng b&agrave;o m&agrave;u trắng v&agrave; x&aacute;m, đường k&iacute;nh 5-10&mu;m.</p>\r\n', '<p>\r\n	- &Aacute;p dụng biện ph&aacute;p ph&ograve;ng bệnh tổng hợp, kh&ocirc;ng để cho c&aacute; sốc v&igrave; c&aacute;c yếu tố m&ocirc;i trường trong qu&aacute; tr&igrave;nh nu&ocirc;i.</p>\r\n<p>\r\n	- Thả giống c&oacute; địa chỉ đảm bảo kh&ocirc;ng nhiễm bệnh vi r&uacute;t.</p>\r\n<p>\r\n	- Cho c&aacute; ăn thức ăn dinh dưỡng tốt, kh&ocirc;ng cho thức ăn tươi sống cần nấu ch&iacute;n. M&ugrave;a ph&aacute;t bệnh cho ăn th&ecirc;m vitamin C liều lượng 20-30mg/kg c&aacute;/ng&agrave;y, mỗi th&aacute;ng cho ăn một đợt từ 7-10 ng&agrave;y.</p>\r\n', 'Ca song, ca mu, benh hoai tu than kinh, Betanodavirus,Viral nervous necrosis', 'Bùi Quang Tề', 0, 1329459373, '', '', 3413, 0),
(2, 3, 2, 'Bệnh do herpesvirus cá chép', 'Koi herpesvirus Disease', '', '<p>\r\n	Virus c&oacute; nh&acirc;n axit nucleic l&agrave; AND thuộc họ <em>Herpesviridae</em>, như giống <em>Herpesvirus </em>(Ronen <em>et al</em>., 2003), (Waltzek <em>et al.</em>, 2004). Những lo&agrave;i c&aacute; thuộc họ c&aacute; ch&eacute;p như c&aacute; v&agrave;ng (<em>Carassius auratus</em>) v&agrave; c&aacute; trắm cỏ (<em>Ctenopharyngodon idella</em>) dường như kh&ocirc;ng bị ảnh hưởng bởi bệnh KHV (OATA, 2001).</p>\r\n', '<p>\r\n	Bệnh Koi herpesvirus Disease l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y chết từ 80-100% trong quần đ&agrave;n c&aacute;, ở nhiệt độ 22-27<sup>0</sup>C (OATA, 2001). Bệnh Koi herpesvirus Disease nhiễm kh&aacute;c nhau với tuổi của c&aacute;, nhưng khi nhốt chung cho thấy c&aacute; hương nhiễm mạnh hơn c&aacute; trưởng th&agrave;nh.</p>\r\n<p>\r\n	G&acirc;y nhiễm bằng cho tiếp x&uacute;c trực tiếp với c&aacute; bệnh, với dịch từ c&aacute; bệnh v&agrave; nước từ hệ thống c&aacute; bệnh, bệnh c&oacute; thể nhiễm v&agrave; g&acirc;y chết c&aacute; phụ thuộc nhiệt độ nước tăng (OATA, 2001). C&aacute; v&agrave;ng v&agrave; những lo&agrave;i c&aacute; kh&aacute;c trong họ c&aacute; ch&eacute;p kh&ocirc;ng nhiễm bệnh Koi herpesvirus Disease</p>\r\n<p>\r\n	Virus xuất hiện sau khi nhiễm 14 ng&agrave;y cho c&aacute;, tuy nhi&ecirc;n bệnh c&ograve;n phụ thuộc v&agrave;o nhiệt độ l&agrave; yếu tố thứ hai cần thiết cho bệnh b&ugrave;ng ph&aacute;t. Tỷ lệ chết li&ecirc;n quan đến bệnh Koi herpesvirus Disease xảy ra ở nhiệt độ 18-27<sup>0</sup>C, hầu hết tỷ chết kh&ocirc;ng xảy ra khi nhiệt độ &lt; 18<sup>0</sup>C v&agrave; &gt; 30<sup>0</sup>C</p>\r\n', '<p>\r\n	Đầu ti&ecirc;n c&oacute; thể l&agrave; một v&agrave;i tổn thường tr&ecirc;n mang v&agrave; tỷ lệ chết cao. Một số trường hợp vi khuẩn v&agrave; KST l&agrave; t&aacute;c nh&acirc;n thứ hai c&oacute; thể l&agrave;m cho virus nhiễm đầu ti&ecirc;n nguy hiểm hơn.</p>\r\n<p>\r\n	Trạng th&aacute;i c&aacute; nhiễm bệnh thường gần tầng mặt, bơi lờ đờ v&agrave; c&oacute; thể bị sốc do ngạt thở v&agrave; bơi kh&ocirc;ng định hướng.</p>\r\n<p>\r\n	Dấu hiệu bệnh ngo&agrave;i của bệnh Koi herpesvirus Disease c&oacute; thể thấy mang c&oacute; vết chấm lốm đốm m&agrave;u đỏ v&agrave; m&agrave;u trắng (h&igrave;nh 15, 16) (giống như bệnh vi khuẩn dạng sợi), mang chảy m&aacute;u, mắt trũng, da c&oacute; đ&aacute;m bạc m&agrave;u hoặc phồng rộp. Lấy nhớt mang kiểm tra dưới k&iacute;nh hiển vi thường gặp số lượng lớn vi khuẩn v&agrave; KST kh&aacute;c nhau (Hedrick <em>et al.</em>, 2000; OATA, 2001; Goodwin, 2003).</p>\r\n<p>\r\n	Dấu hiệu b&ecirc;n trong của bệnh Koi herpesvirus Disease kh&ocirc;ng c&oacute; g&igrave; đặc biệt, nhưng ch&uacute;ng c&oacute; thể l&agrave; c&aacute;c cơ quan bắm chặt v&agrave;o xoang cơ thể v&agrave; xuất hiện c&aacute;c chấm lốm đốm (Hedrick <em>et al.</em>, 2000; Goodwin, 2003).</p>\r\n<p>\r\n	Tỷ lệ chết m&atilde;nh liệt xảy ra rất nhanh trong quần đ&agrave;n nhiễm bệnh, c&aacute; chết bắt đầu trong v&ograve;ng 24-48 giờ sau khi xuất hiện dấu hiệu bệnh. Th&iacute; nghiệm nhiễm bệnh bằng virus ở nhiệt độ 22<sup>0</sup>C, 82% c&aacute; chết trong v&ograve;ng 15 ng&agrave;y (Ronen <em>et al</em>., 2003).</p>\r\n<p style="text-align: center;">\r\n	<img alt="Mang ca benh Koi herpesvirus Disease" src="/upload/images/desease/Koi_herpesvirus_Disease1.jpg" style="width: 600px; height: 480px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute; ch&eacute;p bị bệnh KHV thường thấy mang c&oacute; vết chấm lốm đốm m&agrave;u đỏ v&agrave; m&agrave;u trắng. Những vết chấm n&agrave;y xuất như dạng bệnh vi khuẩn h&igrave;nh sợi tr&ecirc;n mang c&aacute;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Mang ca benh Koi herpesvirus Disease" src="/upload/images/desease/Koi_herpesvirus_Disease2.jpg" style="width: 200px; height: 150px;" /></p>\r\n', '<p>\r\n	&Aacute;p dụng biện ph&aacute;p ph&ograve;ng bệnh tổng hợp</p>\r\n', 'Koi herpesvirus Disease,Ca chep ben', 'Bùi Quang Tề', 0, 1329459481, '', '', 3117, 0),
(13, 3, 1, 'Bênh đầu vàng ở tôm sú', 'Yellow Head Disease-YHD', '', '<p>\r\n	Tác nhân gây bệnhh đầu vàng&nbsp; tôm sĩ là virus hình que kích thước 44±6x173±13nm. Nhân của virus có đừong kính gần bằng 15 nm, chiệu d́ài có thể tới 800 nm. Cấu trúc acid nhân là ARN có đặc điểm gần giống họ <em>Rhabdoviridae </em>hoặc nhóm virus dạng sợi của họ <em>Paramyxoviridae</em>. Một số nghiên cứu gần đây đã cho virus bệnh đầu vàng gần giống họ <em>Coronaviridae </em>(theo V. Alday de Graindorge &amp; T.W. Flegel, 1999)</p>\r\n', '<p>\r\n	Boonyaratpalin và CTV, 1992 lần đầu tiên mô tả bệnh đầu vàng gây chết tôm sú nuôi ở miền Trung và miền nam Thái lan, đặc biệt nguy hiểm cho các vùng nuôi thâm canh qua 1 số năm. Virus đầu vàng có thể liên quan đến đợt dịch bệnh của tôm sú nuôi ở Đài loan năm 1987-1988. Những nơi khác thuộc Đông Nam ¸: Indonesia, Malaysia, Trung quốc, Philippine gặp ít nhưng nguy hiểm cho tôm sú nuôi (Lightner, 1996). Bệnh thường xảy ra ở các ao nuôi có điều kiện môi trường xấu và những vùng có mật độ trại cao. Bệnh có thể xuất hiện sau khi thả giống 20 ngày thường gặp nhất 50-70 ngày ở các ao nuôi tôm sú thâm canh. Ngoài ra bệnh còn gặp ở một số loài tôm tự nhiên khác: tôm thẻ, tôm bạc (lớt), tôm rảo…</p>\r\n<p>\r\n	Ở Việt nam các vùng nuôi tôm sú của các tỉnh phía Bắc, miền Trung và Nam Bộ đã có tôm bị bệnh đầu vàng gây tôm chết (Theo Bùi Quang Tề, 1994-2001 và Đỗ Thị Hoà, 1995).</p>\r\n<p>\r\n	Bệnh đầu vàng lây truyền theo đường nằm ngang, virus trừ tôm nhiễm bệnh bài tiết ra môi trường hoặc một số tôm tự nhiên cũng nhiễm bệnh đầu vàng sẽ lây truyền cho các tôm trong ao nuôi. Có thể một số loài chim nước đã ăn tôm bị bệnh đầu vùng từ ao khác và bay đến ao nuôi đã mang theo các mẩu thừa rơi vào ao nuôi.</p>\r\n', '<p>\r\n	Biểu hiện đầu tiên tôm phát triển rất nhanh và ăn nhiều hơn mức bình thường. Đột ngột tôm dừng ăn, sau một hai ngày tôm dạt vào gần bờ và chết.</p>\r\n<p>\r\n	- Mang và gan tuỵ có màu vàng nhạt, toàn thân có màu nhợt nhạt.</p>\r\n<p>\r\n	- Bệnh có thể gây ra tỷ lệ chết nghiêm trọng đến 100% trong vòng 3-5 ngày.</p>\r\n<p>\r\n	- Khi tôm nhiễm bệnh đầu vàng kiểm tra tiêu bản máu thấy có dấu hiệu bất thường: Nhân tế bào hồng cầu thoái hoá kết đặc lại hoặc bị phá huỷ phân mảnh.</p>\r\n<p>\r\n	- Kiểm tra mô bệnh học tế bào có hiện tượng hoại tử ở nhiều cơ quan và xuất hiện các thể vùi trong tế bào chất, nhân thoái hoá kết đặc và phân mảnh của nhiều tế bào khác nhau: hệ bạch huyết (Lymphoid), tế bào mang, tế bào kẽ gan tuỵ, tế bào biểu bì ruột.</p>\r\n<p>\r\n	<img alt="" src="/upload/images/tom_su_benh_dau_vang.jpg" style="width: 600px; height: 420px;" /></p>\r\n<p>\r\n	Hình: tôm sú bị bệnh đầu vàng (2 con ở phía trên)</p>\r\n', '<p>\r\n	Áp dụng theo phương pháp phòng bệnh tổng hợp. Tránh vận chuyển tôm tơ nơi că bệnh đến nơi chưa phát bệnh để hạn chế sự lây lan vùng lân cận. Những tôm chết vớt ra khỏi ao, tốt nhất là chôn trong vôi nung hoặc đốt. Nứớc tơ ao tôm bệnh không thải ra ngói xử lý bằng vôi nung hoặc bằng clorua vôi (theo phương pháp tẩy ao). Xem xét tôm thường xuyên, nếu phát hiện có dấu hiệu bệnh, tốt nhất là thu hoạch ngay. nêu tôm quá nhỏ không đáng thu hoạch thì cần xử lý nước ao trước khi tháo bỏ.</p>\r\n', 'benh dau vang, tom su, YHD, Yellow Head Disease, tom benh dau vang', 'Bùi Quang Tề', 1329840920, 1330685598, '', '', 2741, 1),
(7, 3, 3, 'Bệnh virus cá trê sông', 'Channel catfish virus disease', '', '<p>\r\n	Giống <em>Herpesvirus</em>, h&igrave;nh cầu, đường k&iacute;nh virion c&oacute; vỏ bao 175-200nm. Nhu&ocirc;m &acirc;m thấy r&otilde; capsid đường k&iacute;nh 100nm</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/upload/images/desease/benh_cat_song.JPG" style="width: 480px; height: 344px;" /></p>\r\n<p style="text-align: center;">\r\n	Tiểu phần virus Channel catfish virus disease nhuộm &acirc;m (X90.000 h&igrave;nh KHVĐT) theo Wolf v&agrave; Darlington, 1971.</p>\r\n', '<p>\r\n	C&aacute; tr&ecirc; s&ocirc;ng (Ictalurus punctatus) của Mỹ thường nhiễm bệnh Channel catfish virus disease. Bệnh xuất hiện ở c&aacute; dưới 1 tuổi v&agrave; thường &iacute;t hơn 4 th&aacute;ng tuổi. Nhiệt độ nước tr&ecirc;n 27<sup>0</sup>C tỷ lệ chết cao hơn, nhưng hiếm khi ph&aacute;t bệnh nhiệt độ &lt; 18<sup>0</sup>C.</p>\r\n', '<p>\r\n	- Đ&acirc;y l&agrave; bệnh xuất huyết v&agrave; thận biến đổi. Bụng sưng to v&agrave; c&oacute; trường hợp bụng căng phồng ra. C&aacute;c v&acirc;y đều xuất huyết. Mắt c&aacute; lồi ra hoặc hơ lồi, c&oacute; một số vết bệnh m&agrave;u v&agrave;ng do một số vi khuẩn (<em>Flexibacter columnaris </em>hoặc <em>Aeromonas hydrophila</em>) l&agrave; nguy&ecirc;n nh&acirc;n thứ hai g&acirc;y ra.</p>\r\n<p>\r\n	- Mang thường chuyển m&agrave;u nhạt, nhưng thường một số c&aacute; bệnh thấy r&otilde; tơ mang xuất huyết. Mang của những con c&aacute; hương hấp hối, nẩm thủy sinh ph&aacute;t triển mạnh</p>\r\n<p>\r\n	- Trong xoang cơ thể c&oacute; dịch m&agrave;u v&agrave;ng, một số m&agrave;u đỏ. Ruột c&oacute; chứa chất dịch nh&agrave;y m&agrave;u v&agrave;ng. L&aacute; l&aacute;ch sưng lớn c&oacute; m&agrave;u đen, thận v&agrave; gan c&oacute; c&aacute;c đốm xuất huyết.</p>\r\n<p>\r\n	- M&ocirc; bệnh học: thận c&aacute; nhiễm bệnh thường ph&ugrave; v&agrave; hoại tử (h&igrave;nh 32), xuất huyết trong cơ xương. Gan thay đổi ph&ugrave; v&agrave; hoại tử.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Cá giống trê sông nhiễm bệnh CCVD; con phía trên bụng chướng to; lỗ liệu sinh" src="/upload/images/desease/benh_tre_song_1.jpg" style="width: 520px; height: 178px;" /></p>\r\n<p style="text-align: center;">\r\n	<img alt="Cá giống trê sông nhiễm bệnh CCVD; con phía trên bụng chướng to; lỗ liệu sinh" src="/upload/images/desease/benh_tre_song2.jpg" style="width: 411px; height: 154px;" /></p>\r\n<p>\r\n	C&aacute; giống tr&ecirc; s&ocirc;ng nhiễm bệnh CCVD; con ph&iacute;a tr&ecirc;n bụng chướng to; lỗ liệu sinh dục, v&acirc;y xuất huyết. Con ph&iacute;a dưới nh&igrave;n mặt lưng thấy bụng chướng to, mắt lồi.</p>\r\n', '<p>\r\n	&Aacute;p dụng biện ph&aacute;p ph&ograve;ng bệnh tổng hợp</p>\r\n', 'Channel catfish virus disease, benh ca tre song,Ictalurus punctatus', 'Bùi Quang Tề', 1322207376, 1329459330, '', '', 1688, 0),
(5, 3, 2, 'Bệnh xuất huyết do virus ở cá trắm cỏ', 'Grass carp Reovirus-GCRV', '', '<p>\r\n	Virus gây bệnh là dạng <em>Reovirus </em>có cấu trúc acid Nucleic nhân là ARN không có vỏ, hình khối 20 mặt đối xứng theo tỷ lệ 5:3:2, có 92 capsomer (Chen và Jiang, 1984; Chen và CTV 1985; Hong và CTV 1985), đừờng kính khoảng 60-70nm. Trương Thiết Phu (1984), Mạo Thụ Kiên (1988-1990) đã xác định <em>Picornavirus </em>nhỏ hơn, đừờng kính 25-35 nm. Viện nghiên cứu nuôi trồng thuỷ sản 1, đã kiểm tra dưới kính hiển vi điện tử gặp thể virus ở mạng lưới nội chất của tế b́ào gan, thận của cá trắm cỏ bị bệnh.</p>\r\n', '<p>\r\n	Bệnh xuất huyết do virus ở cá trắm cỏ xuất hiện năm 1972 ở phía Nam Trung Quốc đã gây thiệt hại lớn cho nghề nuôi cá trắm cỏ nhất là cá trắm cỏ giống, tỷ lệ sống của cá trắm cỏ giống nuôi thành cá thịt chỉ đạt 30 % (theo Jiang Yulin,1995). ở Việt Nam chúng ta đã và đang nghiên cứu bệnh này, bệnh đã xuất hiện nhiều từ năm 1994 đến nay, đặc biệt từ những năm gần đây bệnh đã xuất hiện hầu hết các ao lồng nuôi cá trắm cỏ gây thiệt hại lớn cho nghề nuôi cá. Hiện nay chỉ gặp ở cá trắm cỏ và trắm đen bị bệnh xuất huyết, các loài cá khác chưa phát hiện thấy.</p>\r\n<p>\r\n	<strong>Bệnh ở dạng cấp tính</strong>: phát triển rất nhanh và trầm trọng, cá bị bệnh sau 3-5 ngày có thể chết, tỷ lệ chết 60-80 %, nhiều ao, lồng chết 100 %. Bệnh xuất hiện chủ yếu ở cá giống cỡ 4-25 cm, đặc biệt cá giống cỡ 15-25 cm (0,3-0,4 Kg/con) mức độ nghiêm trọng nhất khi nuôi ở mật độ dầy như cá lồng và ương cá giống.</p>\r\n<p>\r\n	<strong>Bệnh ở dạng mãn tính</strong>: phát triển tương đối chậm, cá chết rải rác, bệnh xuất hiện trong suốt mùa phát bệnh, cá chết không có đỉnh cao rõ ràng. Bệnh mạn tính thường xuất hiện ở ao cá giống diện tích lớn nuôi thưa.</p>\r\n<p>\r\n	Mầm bệnh virus chủ yếu từ cá bệnh và cá mang virus. Cá bệnh sau khi chết, virus phát tán ở trong nước, các chất thải của cá mang virus và cá bệnh bao gồm phân, dịch bài tiết và nhớt ngoài thân đều có virus tồn tại, động vất thuỷ sinh khác nhiễm virus như: ốc trai, ếch và động vật phù du… đều có thể truyền virus qua dòng nước. Nguyên nhân bệnh lan rộng chính là nguồn nước nhiễm mầm bệnh virus không tiêu độc đã truyền từ thuỷ vực này sang thuỷ vực khác. Các thực vật thuỷ sinh mang virus trong ao bệnh như: bèo tấm, cỏ nước, rong… cho cá trắm cỏ khoẻ ăn, cũng có thể làm cho cá cảm nhiễm bệnh. Qua quan sát kính hiển vi điện tử, trứng của cá bố mẹ cũng có thể mang virus, như vậy đường truyền bệnh<br />\r\n	cũng sẽ khả năng truyền theo phương thẳng đứng.</p>\r\n<p>\r\n	Bệnh xuất huyết của cá trắm cỏ là bệnh của nước ấm (tính ôn). Thông thường phát bệnh khi nhiệt độ nước từ 25-320 C, khi thấp dưới 230C và cao hơn 350C bệnh rất ít phát sinh hoặc không phát bệnh. Theo các nhà nghiên cứu bệnh cá Trung Quốc (1999), khi nhiễm bệnh nhân tạo, ở 280C sau khi tiêm mầm bệnh từ 4-7 ngày cá sẽ phát bệnh, ngâm từ 7-9 ngày cá mới phát bệnh. Đem cá khoẻ thả trong bể cá bệnh, có thể làm lây lan được bệnh. Tổ chức mang, não, cơ, thận, gan, tỳ, ruột của cá bệnh đều có thể phân lập được virus. Qua thí nghiệm, đem cá bệnh cảm nhiễm đã xuất hiện triệu chứng nuôi từ nhiệt độ 280C giảm xuống 200C, triệu chứng cá bệnh mất dần không dẫn đến tử vong.</p>\r\n<p>\r\n	Mùa vụ xuất hiện thường vào cuối xuân đầu hè (tháng 3 đến tháng 5), mùa thu (từ tháng 8 đến tháng 10) khi nhiệt độ nước 25-300 C bệnh xuất hiện nhiều và gây cá chết hàng loạt.</p>\r\n', '<p>\r\n	<em>Dấu hiệu bên ngoài: </em>Da cá ḿàu tối xẫm, cá nổi lờ đờ trên tầng mặt. Khi có hiện tượng cá chết, mắt lồi và xuất huyết, mang nhợt nhạt, nắp mang, vây xuất huyết. Nhìn chung dấu hiệu bệnh bên ngói không thay đổi lớn. Cá giống thường xuất hiện dấu hiệu sớm nhất là vây đuôi chuyển ḿàu đen, bề ngói thân ḿàu tối đen, hai bên cơ lưng có thể xuất hiện hai giải sọc ḿu trắng. Cá bệnh nặng bề ngói thân tối v́ xuất huyết hơi đỏ. Cá giống trắm cỏ (4-6cm), nhìn dưới ánh sáng mạnh, có thể thấy cơ xung huyết. Xoang miệng, nắp mang, xung quanh mắt, gốc vây và phần bụng… đều biểu hiện xuất huyết. Nhãn cầu lồi ra, tơ mang ḿà đỏ tím hoặc xuất huyết, nếu cá bệnh xuất huyết nghiêm trọng thì tơ mang xuất huyết th́ành ḿà hơi trắng và dính bùn. Có một số cá bệnh hậu môn viêm đỏ. Cá trắm cỏ mắc bệnh hai tuổi trở lên, gặp nhiều ở phần gốc tia vây và phần bụng xuất huyết là chính, đồng thời thấy triệu chứng hậu môn viêm đỏ.</p>\r\n<p>\r\n	<em>Dấu hiệu bên trong: </em>Bóc da cá bệnh nhìn thấy <strong>các đốm hoặc đám cơ đỏ xuất huyết,</strong> <strong>bệnh nặng, cơ tón thân xuất huyết đỏ t</strong><strong>ư</strong><strong>ơi, </strong>đây là dấu hiệu đặc trưng thường thấy của bệnh. Cơ quan nội tạng: ruột xuất huyết tương đối rõ ŕàng, ruột cục bộ hoặc toàn bộ xuất huyết ḿu đỏ thẫm, <strong>th́ành ruột còn chắc chắn, không hoại tử</strong>. Trong ruột không có thức ăn. Gan xuất huyết có đốm ḿàu trắng. Xoang bụng xuất huyết. Cá trắm cỏ bị bệnh trên hai tuổi xuất huyết không rõ ŕàng, thường gặp xuất huyết đường ruột. Bệnh kết hợp với bệnh viêm ruột do vi khuẩn làm cho ruột hoại tử và chứa hơi.</p>\r\n<p>\r\n	<img alt="" src="/upload/images/benh-xuat-huyet-ca-tram-co.jpg" style="width: 393px; height: 233px;" /></p>\r\n<p>\r\n	<em>Cá trắm cỏ giống các gốc vây xuất huyết, các tia vây rách nát và cụt dần, vẩy rụng<br />\r\n	và khô ráp.</em></p>\r\n<p>\r\n	<img alt="" src="/upload/images/benh-xuat-huyet-ca-tram-co-2.jpg" style="width: 360px; height: 136px;" /></p>\r\n<p>\r\n	<em>Cá trắm cỏ bị bệnh, thận xuất huyết</em></p>\r\n<p>\r\n	<img alt="" src="/upload/images/benh-xuat-huyet-ca-tram-co-3.jpg" style="width: 390px; height: 220px;" /></p>\r\n<p>\r\n	<em>Cá trắm cỏ bị bệnh, mang và nội tạng xuất huyết.</em></p>\r\n<p>\r\n	<img alt="ca tram co xuat huyet toan than" src="/upload/images/benh-xuat-huyet-ca-tram-co-4.jpg" style="width: 393px; height: 110px;" /></p>\r\n<p>\r\n	<em>Cá trắm cỏ bị bệnh, xuất huyết toàn thân</em></p>\r\n<p>\r\n	Tóm lại <strong><em>hệ thống cơ d</em></strong><strong><em>ư</em></strong><strong><em>ới da xuất huyết </em></strong>và trong xoang cơ thể, gan, thận, lá lách xuất huyết. Ruột không có thức ăn, <strong><em>thành ruột xuất huyết nh</em></strong><strong><em>ư</em></strong><strong><em>ng không hoại tử </em></strong>(thành ruột còn tương đối chắc chắn, không thối nát). Máu biến đổi, khi cá nhiễm bệnh, hồng cầu, huyết tương và urê đều giảm sau 4-5 ngày, sau 8 ngày hồng cầu, huyết tương, hemoglobin giảm tới mức thấp nhất, nhưng glucose máu không thay đổi. Một số mẫu bệnh thu ở tự nhiên, máu cũng biến đổi và còn thêm K+ trong huyết thanh tăng, Ca++ giảm. Cá bệnh tỷ lệ tế b́o Lympho giảm, tỷ lệ tế b́o bạch cầu có hạt tăng nhanh.</p>\r\n', '<p>\r\n	Áp dụng đầy đủ biện pháp phòng bệnh tổng hợp.</p>\r\n<p>\r\n	Để giải quyết phòng trị bệnh xuất huyết cho cá trắm cỏ: đối với cá nuôi lồng áp dụng theo tiêu chuẩn 28TCN 111:1998 ; cá nuôi ao trước tiên cần phải cải tạo ao trước khi nuôi cá và thường xuyên cải thiện môi trường trong quá trình nuôi bằng vôi nung (CaO) liều lượng 2kg vôi/100m3 nước. Một tháng bón vôi 2 lần. Vôi hòa ra nước té đều khắp ao.</p>\r\n<p>\r\n	Mùa xuất hiện bệnh nên cho cá ăn thuốc KN-04-12. Mỗi đợt cho ăn 3 ngày liên tục. Liều lượng: cá giống 4 g/1 kg cá/1 ngày (400 g thuốc/100 kg cá /1nǵy), cá thịt 2g/1 kg cá/ 1 ngày (200 g thuốc/100 kg cá/ 1 nǵy). Hoặc có thể dùng Vitamin C cho cá ăn với liều lượng 30 mg/ 1kg cá/ngày (30g/ 100 kg cá /ngày) cho cá ăn liên tục trong mùa phát bệnh.</p>\r\n<p>\r\n	Dùng phương pháp miễn dịch có triển vọng, bằng phương pháp cho ăn hoặc tắm vacxin dễ&nbsp; thực hiện với sản xuất. Điều chế vacxin vô hoạt. Kết quả 80% cá được miễn dịch kéo d́i sau 14 tháng, sự bảo hộ xuất hiện sau ngày thứ 4 khi nhiệt độ &gt;200C nhưng sau 20 ngày khi nhiệt độ 150C và 30 ngày ở nhiệt độ 100C (theo Chen và CTV, 1985).</p>\r\n<p>\r\n	<strong><em>Ph</em></strong><strong><em>ư</em></strong><strong><em>ơng pháp điều chế vacxin vô hoạt (chết) </em></strong>(theo Chen và CTV, 1985): Lấy gan, thận, lá lách và các mô cơ của cá trắm cỏ nhiễm bệnh xuất&nbsp; huyết nghiền nhỏ và pha loãng 10-100 lần với nước muối sinh lý 0,85%. Ly tâm ở 3.000 vòng/phút thời gian 30 phút, lấy dung dịch phần trên mặt. Diệt vi khuẩn và virus (vô hoạt) bằng Penicillin (800 IU/ml), Streptomycin (800 ́m/ml) và 0,1 % Formalin. Để dung dịch hỗn hợp ở 320C trong 72 giờ. Vacxin được kiểm tra vô trùng và bảo quản ở nhiệt độ 40C.</p>\r\n', 'Ctenopharyngodon idella, benh xuat huyet, ca tram co, benh virus', '', 1322204954, 1332298998, '', '', 21163, 0),
(6, 3, 2, 'Bệnh xuất huyết virus', 'Viral haemorrhagic septicaemia', '', '<p>\r\n	Giống <em>Novirhabdovirus </em>thuộc họ <em>Rhabdoviridae, </em>h&igrave;nh que một đầu tr&ograve;n (vi&ecirc;n đạn), k&iacute;ch thước 60 x 177nm.</p>\r\n', '<p>\r\n	Bệnh nhiễm ở c&aacute; hồi v&acirc;n (<em>Oncorhynchus mykiss</em>), c&aacute; hồi n&acirc;u (<em>Salmo trutta</em>), c&aacute; grayling (<em>Thymallus thymallus</em>), c&aacute; trắng (<em>Coregonus </em>sp.), c&aacute; ch&oacute; (<em>Esox lucius</em>), c&aacute; hồi miệng lớn (<em>Micropteus salmoides</em>), c&aacute; bơn Nhật Bản (<em>Paralichthys olivaceus</em>) v&agrave; c&aacute; bơn (<em>Scophthalmus maximus</em>).</p>\r\n<p>\r\n	Nhiệt độ nước l&agrave; yếu tố m&ocirc;i trường quan trọng. Bệnh xuất hiện từ 4-140C. Nhiệt độ nước thấp (1-50C) thường bệnh ph&aacute;t rộng, tỷ lệ chết h&agrave;ng ng&agrave;y thấp, nhưng tỷ lệ chết dồn t&iacute;ch cao. Ở nhiệt độ nước cao (15-180C) bệnh ph&aacute;t ngắn với tỷ lệ chết dồn t&iacute;ch cao nhất. Bệnh VHS xuất hiện ở tất c&aacute;c m&ugrave;a, nhưng thường xuất hiện m&ugrave;a xu&acirc;n khi nhiệt độ nước th&iacute;ch hợp</p>\r\n', '<p>\r\n	C&aacute; bệnh th&acirc;n chuyển m&agrave;u tối v&agrave; c&oacute; thể lồi một mắt hoặc cả hai mắt. Mang chuyển m&agrave;u nhạt, nhưng c&oacute; thể c&oacute; c&aacute;c đốm xuất huyết. V&acirc;y v&agrave; ổ mắt xuất huyết.</p>\r\n<p>\r\n	C&aacute; bệnh trong xoang cơ thể c&oacute; nhiều dịch m&aacute;u, gan v&agrave; thận biến đổi r&otilde; r&agrave;ng. C&oacute; một &iacute;t đốm ph&acirc;n bố rải r&aacute;c tr&ecirc;n b&oacute;ng hơi. Gan chuyển m&agrave;u nhạt, thận m&agrave;u đỏ thẫm., l&aacute; l&aacute;ch chuyển m&agrave;u đỏ.</p>\r\n', '<p>\r\n	&Aacute;p dụng biện ph&aacute;p ph&ograve;ng bệnh tổng hợp</p>\r\n', '', '', 1322205624, 1322377618, '', '', 1098, 0),
(8, 3, 0, 'Bệnh “cá ngủ” do Iridovirus ở cá biển', 'Iridovirus', '', '<p>\r\n	Gây bệnh là <em>Iridovirus</em> hình cầu 20 mặt, đường kính nhân 140-160nm, vỏ bao quanh đường kính 220-240nm. Acid nhân là AND. Vi rút ký sinh ở thận, gan, lá lách của cá bệnh.</p>\r\n', '<p>\r\n	Gặp ở cá nuôi lồng: cá song điểm đai (<em>Epinephelus malabaricus</em>) vμ cá song chấm cam (<em>E.coioides</em>) - Thái Lan; cá song mỡ (<em>E. tauvina</em>)- Singapore; cá song (<em>Epinephelus </em>sp)- Đoài Loan. Bệnh “cá ngủ” gây bệnh ở cá giai đoạn cá giống và cá thịt, tỷ lệ chết 80-90%. ở Việt Nam bệnh xuất ở nhiều loài cá nuôi lồng ở vịnh Hạ Long. Mùa vụ phát bệnh từ tháng 3-8.</p>\r\n', '<p>\r\n	Cá bệnh kém ăn hoặc bỏ ăn, cơ thể chuyển mμu đen, đặc biệt ở phần cuối thân vμ vây đuôi. Xuất hiện các mụn phồng rộp màu trắng trên thân, vây của cá. Cá bệnh nặng nổi lên tầng mặt sau đó từ từ chìm xuống đáy và chết, nên gọi là bệnh “cá ngủ”.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Cá chẽm bệnh do Iridovirus" src="/upload/images/desease/benh_ca_ngu.jpg" style="width: 415px; height: 304px;" /></p>\r\n<p style="text-align: center;">\r\n	Cá chẽm bệnh do Iridovirus</p>\r\n<p style="text-align: center;">\r\n	<img alt="Cá mú bênh do Iridovirus" src="/upload/images/desease/Benh_ca_ngu2.jpg" style="width: 415px; height: 317px;" /></p>\r\n<p style="text-align: center;">\r\n	Cá mú bệnh do Iridovirus</p>\r\n', '<p>\r\n	- Áp dụng biện pháp phòng bệnh tổng hợp, không để cho cá sốc vì các yếu tố môi trường trong quá trình nuôi. Thả giống có địa chỉ đảm bảo không nhiễm bệnh virus.</p>\r\n<p>\r\n	- Cho cá ăn thức ăn dinh dưỡng tốt, không cho thức ăn tươi sống cần nấu chín. Mùa phát bệnh cho ăn thêm vitamin C liều lượng 20-30mg/kg cá/ngày, mỗi tháng cho ăn một đợt từ 7-10 ngày.</p>\r\n', 'Cá mú bệnh do Iridovirus, cá chẽm, bệnh cá ngủ,Iridovirus', 'Bùi Quang Tề', 1322207413, 1332400279, '', '', 2203, 0),
(9, 3, 2, 'Bệnh khối u tế bào Lympho', 'Lympho Iridovirus', '', '<p>\r\n	Virus g&acirc;y bệnh khối u tế b&agrave;o Lympho l&agrave; <em>Iridovirus </em>lớn nhất trong giống n&agrave;y: K&iacute;ch thước trung b&igrave;nh l&agrave; 200 &plusmn;} 50 nm, nhỏ nhất l&agrave; 130nm, lớn nhất l&agrave; 330 nm, k&iacute;ch thước kh&aacute;c nhau của virus phụ thuộc v&agrave;o k&yacute; chủ. <em>Iridovirus </em>c&oacute; acid nh&acirc;n l&agrave; ADN, virus c&oacute; l&otilde;i đặc, b&ecirc;n ngo&agrave;i c&oacute; 2 lớp vỏ cấu tạo bằng c&aacute;c Capsid, ngo&agrave;i c&ugrave;ng c&oacute; riềm l&ocirc;ng tơ. Nh&acirc;n của thể virus thấy r&otilde; c&aacute;c ống giống như v&ograve;ng nhẫn v&agrave; tren bề mặt của thể virus c&oacute; c&aacute;c Capcid cấu tr&uacute;c giống c&aacute;c mấu (Theo Madeley v&agrave; ctv, 1978). Berthiaume v&agrave; ctv, 1984 đ&atilde; m&ocirc; tả nh&acirc;n của thể virus như quả b&oacute;ng phức tạp c&oacute; c&aacute;c sợi acid osmic hoặc c&aacute;c hạt. Ph&iacute;a ngo&agrave;i thể virus cấu tr&uacute;c h&igrave;nh cầu 20 mặt đối xứng c&oacute; diềm l&ocirc;ng tơ (xem h&igrave;nh 34, 35). Th&agrave;nh phần ho&aacute; sinh của virus: 42% Protein, 17% Lipit, 1,6% ADN.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Mô phỏng cấu trúc của thể virus ở bệnh khối u tế bào Lympho không nhuộm màu" src="/upload/images/desease/khoi_u_te_bao_lympo1.jpg" style="width: 439px; height: 517px;" /></p>\r\n<p style="text-align: center;">\r\n	M&ocirc; phỏng cấu tr&uacute;c của thể virus ở bệnh khối u tế b&agrave;o Lympho kh&ocirc;ng nhuộm m&agrave;u</p>\r\n', '<p>\r\n	Bệnh khối u tế b&agrave;o Lympho xuất hiện ở 125 lo&agrave;i, 34 họ thuộc 9 bộ c&aacute; nước ngọt v&agrave; c&aacute; nước mặn, gặp nhiều nhất ở ba bộ: <em>Perciformes, Pleuronectiformes, Tetraodontiformes, </em>ngo&agrave;i ra c&ograve;n gặp ở s&aacute;u bộ c&aacute; kh&aacute;c như: <em>Clupeiformes, Salmoniformes, Ophidiiformes,</em> <em>Cyprinodontiformes... </em>C&aacute; sống tự nhi&ecirc;n mức độ cảm nhiễm bệnh thấp v&agrave; kh&ocirc;ng g&acirc;y nguy hiểm. Nhưng ở c&aacute;c lo&agrave;i c&aacute; nu&ocirc;i tăng sản như nu&ocirc;i lồng dễ bị nhiễm bệnh tế b&agrave;o Lympho v&agrave; g&acirc;y nguy hiểm cho c&aacute; nu&ocirc;i. V&iacute; dụ như c&aacute; chẽm nu&ocirc;i lồng dặc biệt l&agrave; c&aacute; giống từ 4 -7 cm, c&aacute; r&ocirc;phi Tilapia đ&atilde; nhiễm bệnh tế b&agrave;o Lympho l&agrave;m c&aacute; chết h&agrave;ng loạt.</p>\r\n', '<p>\r\n	Bệnh khối u tế b&agrave;o lympho l&agrave; dạng ảnh hưởng trong từng tế b&agrave;o v&agrave; tr&ecirc;n vật chủ &iacute;t ảnh hưởng. G&acirc;y bệnh l&agrave; virus ưa nhiệt, ch&uacute;ng h&igrave;nh th&agrave;nh c&aacute;c nốt sần (mụn cơm) mắt thường c&oacute; thể thấy được ở hầu hết hệ thống mạch ngoại bi&ecirc;n. Những dấu hiệu b&ecirc;n ngo&agrave;i của bệnh điển h&igrave;nh l&agrave; c&aacute;c nốt sần c&oacute; cấu tạo dạng vi&ecirc;n sỏi, k&iacute;ch thước to nhỏ kh&aacute;c nhau, m&agrave;u kem x&aacute;m đến m&agrave;u x&aacute;m, sắc tố biểu b&igrave; b&igrave;nh thường. Đ&ocirc;i khi hệ thống mạch ngoại bi&ecirc;n tụ th&agrave;nh đ&aacute;m lớn c&aacute;c tế b&agrave;o c&oacute; m&agrave;u đỏ. Xu hướng c&aacute;c tế b&agrave;o lympho xuất hiện trong c&aacute;c đ&aacute;m l&agrave; dạng sợi.</p>\r\n<p>\r\n	Những dấu hiệu b&ecirc;n trong: Xuất hiện một số tế b&agrave;o lympho nhiễm virus ở trong cơ, m&agrave;ng bụng (ph&uacute;c mạc) v&agrave; tr&ecirc;n bề mặt của c&aacute;c cơ quan nội tạng.</p>\r\n<p>\r\n	Những dấu hiệu m&ocirc; bệnh học: Duy nhất chỉ c&oacute; c&aacute;c tế b&agrave;o Lympho của c&aacute; trương to khổng lồ, k&iacute;ch thước tế b&agrave;o điển h&igrave;nh đa số l&agrave; 100 &agrave;m hoặc lớn nhất l&agrave; 1 mm v&agrave; ch&uacute;ng tăng từ 50.000 - 100.000 lần về thể t&iacute;ch. Điểm đặc biệt của tế b&agrave;o l&agrave; m&agrave;ng tế b&agrave;o mỏng trong suốt, ở trung t&acirc;m c&oacute; nh&acirc;n trương lớn thấy r&otilde; ADN (xem h&igrave;nh 37). Tế b&agrave;o h&igrave;nh ovan hoặc dạng amip. C&aacute;c thể v&ugrave;i tế b&agrave;o chất bắt m&agrave;u t&iacute;m l&agrave; nơi chứa c&aacute;c thể virus, c&oacute; hai dạng k&iacute;ch thước tuỳ theo k&yacute; chủ.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cá bị bệnh khối u tế bào lympho" src="/upload/images/desease/khoi_u_te_bao_lympo2.jpg" style="width: 428px; height: 405px;" /></p>\r\n<p style="text-align: center;">\r\n	<img alt="cá bị bệnh khối u tế bào lympho" src="/upload/images/desease/khoi_u_te_bao_lympo3.jpg" style="width: 468px; height: 352px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute; Vược</p>\r\n<p style="text-align: center;">\r\n	<img alt="cá bị bệnh khối u tế bào lympho" src="/upload/images/desease/khoi_u_te_bao_lympo4.jpg" style="width: 375px; height: 295px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute; v&acirc;y c&aacute;nh (<em>Holacanthus ciliaris)</em></p>\r\n', '<p>\r\n	Một số nước như Scotlan, Mỹ đ&atilde; nghi&ecirc;n c&oacute; hướng nghi&ecirc;n cứu đ&aacute;p ứng miễn dịch của bệnh khối u tế b&agrave;o Lympho nhưng chưa chế được vacxin th&iacute;ch hợp để ph&ograve;ng bệnh cho c&aacute;. Biện ph&aacute;p tốt nhất l&agrave; &aacute;p dụng ph&ograve;ng bệnh tổng hợp, ch&uacute; &yacute; khi nu&ocirc;i c&aacute; kh&ocirc;ng cho c&aacute; ăn thức ăn tươi sống l&agrave; c&aacute; nhiễm bệnh tế b&agrave;o Lympho, loại bỏ c&aacute;c c&aacute; đ&atilde; nhiễm bệnh ra khỏi v&ugrave;ng nu&ocirc;i.</p>\r\n<p>\r\n	Chưa nghi&ecirc;n cứu chữa bệnh cho c&aacute;.</p>\r\n', 'Lympho,benh khoi u, virus Iridovirus', 'Bùi Quang Tề', 1322377203, 1329459286, '', '', 2261, 1),
(10, 3, 2, 'Bệnh xuất huyết do virus ở cá chép', 'Spring virus disease', '', '<p>\r\n	Bệnh xuất huyết (đốm đỏ) c&oacute; t&aacute;c nh&acirc;n g&acirc;y bệnh l&agrave; virus v&agrave; điều tra sự biến đổi của m&ocirc;i trường v&agrave; sự kết hợp giữa 2 t&aacute;c nh&acirc;n virus v&agrave; vi khuẩn.</p>\r\n<p>\r\n	Từ 2 dạng bệnh xuất huyết cấp t&iacute;nh điển h&igrave;nh v&mu; dạng bệnh m&atilde;n t&iacute;nh lở lo&eacute;t. Fijan v&agrave; CTV, 1971 đ&atilde; ph&acirc;n lập được t&aacute;c nh&acirc;n g&acirc;y bệnh l&agrave; virus <em>Rhabdovirus carpio</em>. Tiếp theo l&agrave; h&agrave;ng loạt c&aacute;c nh&agrave; khoa học Ch&acirc;u &Acirc;u, Mỹ, Nhật v&agrave; đi s&acirc;u nghi&ecirc;n cứu t&aacute;c nh&acirc;n g&acirc;y bệnh xuất huyết (bệnh virus m&ugrave;a xu&acirc;n) ở c&aacute; ch&eacute;p v&agrave; nhiều lo&agrave;i c&aacute; trong họ c&aacute; ch&eacute;p; c&aacute; m&egrave; trắng.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Rhabdovirus carpio" src="/upload/images/desease/r_carpio.jpg" style="width: 265px; height: 300px;" /></p>\r\n<p>\r\n	<em>R. carpio</em>, cấu tr&uacute;c acid nh&acirc;n l&agrave; ARN v&agrave; lớp vỏ l&agrave; protein (Gupta v&mu; Roy, 1980, 1981; Kiuchi v&mu; Roy, 1984; Roy, 1981; Roy v&agrave; CTV, 1984) h&igrave;nh que một đầu tr&ograve;n như vi&ecirc;n đạn, chiều d&agrave;i 90-180nm, rộng 60-90nm, n&oacute; c&oacute; 450 lớp m&mu;ng, trạng th&aacute;i xốp k&iacute;ch thước 200nm nhưng thường giữ lớp m&agrave;ng 100nm. Việc chẩn đo&aacute;n bằng kh&aacute;ng huyết thanh đ&atilde; x&aacute;c định được <em>Rhabodovirus</em> ở nhiều c&aacute; kh&aacute;c nhau. Thei Hill v&agrave; CTV, 1975 đ&atilde; thừa nhận virus g&acirc;y bệnh vi&ecirc;m b&oacute;ng hơi c&aacute; ch&eacute;p đều l&agrave; virus g&acirc;y bệnh xuất huyết ở c&aacute; ch&eacute;p.</p>\r\n', '<p>\r\n	Bệnh chủ yếu gặp ở c&aacute; ch&eacute;p, ch&uacute;ng g&acirc;y bệnh từ c&aacute; giống đến c&aacute; thịt.</p>\r\n', '<p>\r\n	- Trạng th&aacute;i: Dấu hiệu đầu ti&ecirc;n c&aacute; ngạt thở, bơi ở tầng mặt, c&aacute; chết ch&igrave;m ở tầng đ&aacute;y, c&aacute; mất thăng bằng bơi kh&ocirc;ng định h&minus;ớng (bệnh vi&ecirc;m b&oacute;ng hơi)</p>\r\n<p>\r\n	- Dấu hiệu b&ecirc;n ngo&agrave;i: mang v&agrave; da xuất huyết c&oacute; thể ở cả mắt. Da c&oacute; m&agrave;u tối, những chỗ vi&ecirc;m c&oacute; nhiều chất nhầy, mắt lồi nhẹ, mang nhợt nhạt, c&aacute;c tơ mang d&iacute;nh kết lại. M&aacute;u lo&atilde;ng chảy ra từ hậu m&ocirc;n.</p>\r\n<p>\r\n	- Nội tạng; Bụng chướng to, trong xoang bụng xuất huyết c&oacute; dấu hiệu t&iacute;ch nước (ph&ugrave;), b&oacute;ng hơi xuất huyết v&agrave; teo dần một ngăn, l&aacute; l&aacute;ch sưng to, tim, gan, thận, ruột xuất huyết, xoang bụng c&oacute; chứa nhiều dịch nhờn.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Ca chep xuat huyet" src="/upload/images/desease/ca_chep_xuat_huyet.jpg" style="width: 633px; height: 218px;" /></p>\r\n<p>\r\n	Dựa v&agrave;o c&aacute;c dấu hiệu bệnh l&yacute; v&agrave; bệnh thường xuất hiện v&agrave;o m&ugrave;a xu&acirc;n v&agrave; m&ugrave;a thu. Bệnh ph&aacute;t rất nhanh (bệnh cấp t&iacute;nh), tỷ lệ chết cao. Khi ph&acirc;n lập kh&ocirc;ng c&oacute; vi khuẩn v&agrave; c&oacute; c&aacute;c dấu hiệu bệnh b&ecirc;n ngo&agrave;i th&igrave; x&aacute;c định dễ d&agrave;ng l&agrave; virus.</p>\r\n', '<p>\r\n	Một biện ph&aacute;p sinh học cơ bản để ph&ograve;ng bệnh l&agrave; nhiệt độ bằng c&aacute;ch nu&ocirc;i c&aacute; ở nhiệt độ cao hơn 20<sup>0</sup>C. V&igrave; những dấu hiệu thay đổi m&ugrave;a vụ l&agrave; đặc trưng giới hạn của bệnh ph&aacute;t triển (bệnh virus m&ugrave;a xu&acirc;n). Do đ&oacute; khi ph&ograve;ng bệnh cho c&aacute;, ch&uacute;ng ta c&oacute; thể nu&ocirc;i c&aacute; ở những v&ugrave;ng nước ấm v&igrave; t&aacute;c nh&acirc;n g&acirc;y bệnh &iacute;t xuất hiện.</p>\r\n<p>\r\n	Biện ph&aacute;p ph&ograve;ng bệnh bằng c&aacute;ch chọn giống những c&aacute; c&oacute; sức đề kh&aacute;ng với bệnh xuất huyết do virus c&oacute; thể &aacute;p dụng đựoc, nhưng thực hiện biện ph&aacute;p n&agrave;y kh&ocirc;ng đại tr&agrave; được. Từ năm 1962 Li&ecirc;n X&ocirc; cũ đ&atilde; c&oacute; chương tr&igrave;nh chọn giống c&aacute; ch&eacute;p c&oacute; sức đề kh&aacute;ng với bệnh xuất huyết do virus đ&atilde; x&aacute;c nhận rằng tỷ lệ sống khi nu&ocirc;i c&aacute; ch&eacute;p giữa c&aacute; c&oacute; sức đề kh&aacute;ng với bệnh v&agrave; c&aacute; dễ mắc bệnh ch&ecirc;nh nhau khoảng 30% (Kirpichnikov v&agrave; Faktorovich, 1972; Kirpichnikov v&agrave; CTV, 1972). ở V&minus;ơng quốc Anh đ&atilde; t&igrave;m đ&minus;ợc d&ograve;ng c&aacute; ch&eacute;p nu&ocirc;i nội địa c&oacute; sức đề kh&aacute;ng hơn c&aacute; ch&eacute;p hoang dại. Tỷ lệ chết của c&aacute; ch&eacute;p hoang dại l&agrave; 60-90% trong khi đ&oacute; c&aacute; ch&eacute;p nu&ocirc;i (nh&agrave;) tỷ lệ chết kh&ocirc;ng đ&aacute;ng kể (Hill, 1977).</p>\r\n<p>\r\n	Qua thực tế việc chữa v&agrave; ph&ograve;ng bệnh đối với bất kỳ một bệnh virus ở c&aacute; th&igrave; biện ph&aacute;p ph&ograve;ng bằng ho&aacute; chất kh&ocirc;ng cho kết quả cao (gần như kh&ocirc;ng c&oacute; t&aacute;c dụng). Biện ph&aacute;p ph&ograve;ng bệnh bằng miễn dịch c&oacute; thể cho kết quả tốt hơn v&agrave; đ&atilde; thực hiện cho bệnh xuất huyết do virus ở c&aacute; ch&eacute;p. Sự ph&aacute;t triển ng&agrave;y c&agrave;ng mạnh về biện ph&aacute;p ph&ograve;ng bệnh bằng miễn dịch cho người v&agrave; động vật nhưng đối với c&aacute; n&oacute; cũng bị giới hạn như khả năng miễn dịch theo tuổi của c&aacute; v&agrave; nhiệt độ (cao hơn 20<sup>0</sup>C) thuận lợi cho phản ứng miễn dịch của c&aacute;. Nhưng vacxin x&eacute;t về mặt kinh tế chưa đ&aacute;p ứng cho nghề nu&ocirc;i c&aacute; nh&minus; gi&aacute; th&agrave;nh cao. Trong ao nu&ocirc;i c&aacute; giống kh&oacute; thực hiện được ph&ograve;ng bệnh bằng vacxin.</p>\r\n<p>\r\n	&Aacute;p dụng theo phương ph&aacute;p ph&ograve;ng chung.</p>\r\n', 'ca chep, virus, Spring virus disease,Rhabdovirus carpio', 'Bùi Quang Tề', 1322380108, 1329459129, '', '', 4907, 0),
(11, 3, 1, 'Bệnh MBV ở tôm sú', 'Monodon Baculovirus', '', '<p>\r\n	Tác nhân gây bệnh MBV (Monodon <em>Baculovirus</em>) là virus type A <em>Baculovirus monodon</em>, cấu trúc nhân (acid nucleoic) là ds ADN, có lớp vỏ bao, dạng hình que. Theo J.Mari và CTV, 1993 thì chủng MBV của tôm sú từ ấn Độ Thái Bình Dương có kích thước nhân 42 ± 3 x 246 ± 15 nm, kích thước vỏ bao 75 ± 4 x 324 ± 33 nm. Chủng PMV của tôm <em>(P.plebejus, P. monodon, P. merguiensis</em>) từ úc có kích thước nhân 45-52 x 260-300 nm, kích thước vỏ bao 60 x 420 nm.</p>\r\n<p>\r\n	Virus ký sinh ở tế bào biểu mô hình ống gan tuỵ (Hepatopancreas) và tế bào biểu bì phía trước ruột giữa, virus tái sản xuất bên trong nhân tế bào vật nuôi, bao gồm các giai đoạn sau:</p>\r\n<p>\r\n	- Giai đoạn O (tiềm ẩn): Sau khi tế bào nhiễm MBV là giai đoạn sớm của tế bào chất biến đổi.</p>\r\n<p>\r\n	- Giai đoạn 1: Nhân tế bào sưng nhẹ, các nhiễm sắc thể tan ra và di chuyển ra sát màng nhân. Tế bào chất mất dần chức năng của chúng và hình thành giọt mỡ. Virus bắt đầu gây ảnh hưởng.</p>\r\n<p>\r\n	- Giai đoạn 2: Nhân sưng nhanh, số lượng virus tăng nhanh, xuất hiện thể ẩn (Occlusion bodies) trong nhân.</p>\r\n<p>\r\n	- Giai đoạn 3: tế bào bị bệnh, nhân tăng lên gấp 2 lần, đường kính bình thường và tăng 6 lần về thể tích. bên trong nhân có 1 đến nhiều thể ẩn, trong thể ẩn chứa đầy các virus. Các virus phá huỷ các tế bào ký chủ, tiếp tục di chuyển sang tế bào khác hoặc theo chất bài tiết ra ngoài môi trường, tạo thành virus tự do tồn tại trong bùn và nước.</p>\r\n', '<p>\r\n	Bệnh MBV được phát hiện đầu tiên năm 1980 ỏ đàn tôm sú (<em>Penaues monodon</em>) đưa từ Đài Loan đến nuôi ở Mehico (Lightner và CTV, 1981, 1983). Tiếp theo các nhà nghiên cứu đã phát hiện bệnh MBV có xuất phát từ Đài Loan, Philippines, Malaysia, Polynesia thuộc Pháp, Singapore, Indonesia, Thái Lan, Trung Quốc...ở Đài Loan bệnh MBV có liên quan đến thiệt hại nghiêm trọng cho nghề nuôi tôm sú năm 1987 và 1988 (Chen và CTV, 1989).</p>\r\n<p>\r\n	Cho đến nay người ta biết bệnh MBV phân bố rất rộng rãi: châu á, Thái Bình Dương, châu Phi, miền Nam châu Âu, châu Mỹ. Tôm sú (<em>P. monodon</em>) thường xuyên nhiễm bệnh MBV và một số tôm khác cũng nhiễm bệnh MBV: <em>P. merguiensis, P. semisulcatus, P. kerathurus,</em> <em>P. plebejus, P. indicus, P. penicillatus, P. esculentus, P. vannamei </em>(có khả năng)<em>. </em>Virus MBV nhiễm từ Post-larvae đến tôm trưởng thành.</p>\r\n<p>\r\n	Bệnh MBV lan truyền theo phương nằm ngang, không truyền bệnh theo phương thẳng đứng.ở Việt Nam tháng 10-11/1994 Bùi Quang Tề lần đầu tiên đã nghiên cứu về mức độ nhiễm bệnh MBV trên tôm sú nuôi các tỉnh ven biển phía nam: Tôm sú nuôi nhiễm virus MBV khá cao: Tôm thịt ở Minh hải: 50-85,7%, ở Sóc Trăng 92,8%; Tôm giống ở Bà Rịa-Vũng Tàu 5,5-31,6%, tôm giống Nha Trang 70-100%. Bệnh MBV là một&nbsp; trong những nguyên nhân gây chết tôm ở các Tỉnh phía nam năm 1993-1994. Tiếp theo Đỗ Thị Hoà từ tháng 11/1994-7/1995 cũng đã nghiên&nbsp; cứu bệnh MBV trên tôm sú nuôi ở các tỉnh Nam Trung Bộ, kết quả cho thấy: tỷ lệ nhiễm virus MBV ở ấu trùng tôm sú là 33,8%, tôm giống là&nbsp; 52,5%, tôm thịt là 66,5%. Năm 1995 sơ bộ điều tra bệnh tôm sú nuôi ở các tỉnh phía Bắc đã nhiễm mầm bệnh MBV ở các tỉnh: Nghệ An,&nbsp; Thanh Hoá, Hải Phòng. Vì những tỉnh này đều lấy tôm giống từ Nha Trang ra nuôi (Bùi Quang tề và CTV, 1997). Đến nay kiểm tra tôm post sản xuất từ miền Bắc ở Quảng Ninh đến các tỉnh phía Nam ở Cà Mau hầu hết chúng đều nhiễm mầm bệnh MBV, ở mức độ khác nhau. Bệnh MBV không làm tôm chết hàng loạt, nhưng làm tôm chậm lớn và chết rải rác. Khi thu hoạch tỷ lệ tôm sống rất thấp đây là vấn đề nan giải của nghề nuôi tôm biển ở các tỉnh ven biển.</p>\r\n', '<p>\r\n	Khi tôm mới nhiễm virus MBV, dấu hiệu bệnh không biểu hiện rõ rμng. Khi tôm nhiễm bệnh nặng và phát bệnh thường có biểu hiện một số dấu hiệu sau:</p>\r\n<p>\r\n	- Tôm có màu tối hoặc xanh tái, xanh xẫm. Tôm kém ăn, hoạt động yếu và sinh trưởng chậm (chậm lớn).</p>\r\n<p>\r\n	- Các phần phụ và vỏ kitin có hiện tượng hoại tử, có nhiều sinh vật bám (ký sinh trùng đơn bào, tảo bám và vi khuẩn dạng sợi).</p>\r\n<p>\r\n	- Gan tuỵ teo lại có mμu trắng hơi vùng, thối rất nhanh.</p>\r\n<p>\r\n	- Tỷ lệ chết dồn tích, cao tới 70% hoặc có thể tôm chết hầu hết trong ao.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Tom su benh MBV cham lon" src="/upload/images/desease/MBV_tom_su.jpg" style="width: 533px; height: 563px;" /></p>\r\n<p style="text-align: center;">\r\n	Tôm sú bệnh MBV chậm lớn màu xanh xẩm</p>\r\n', '<p>\r\n	Phòng bệnh là chính:</p>\r\n<p>\r\n	+ Không dùng tôm giống có nhiễm mầm bệnh MBV.</p>\r\n<p>\r\n	+ Tẩy dọn ao, bể nuôi như phương pháp phòng chung.</p>\r\n<p>\r\n	+ Nuôi tôm đúng mùa vụ, quản lý chăm sóc tốt, cung cấp đầy đủ thức ăn về chất và lượng. Không để tôm sốc trong quá trình nuôi.</p>\r\n<p>\r\n	+ Kiểm dịch đμn tôm bố mẹ trước khi cho đẻ.</p>\r\n<p>\r\n	+ Xử lý nước bằng tầng ôzôn vụ các chất sát trùng Bezalkon clorua trước khi ấp trứng thì có thể sản xuất được đàn tôm Postlarvae không nhiễm virus MBV.</p>\r\n', 'Bệnh MBV,Monodon Baculovirus, tom su, bệnh còi', 'Bùi Quang Tề', 1322386719, 1331134812, '', '', 4009, 1);
INSERT INTO `tkb_aq_desease` (`id`, `cat_id`, `group_id`, `name`, `en_name`, `description`, `agent`, `zone`, `symptom`, `treatment`, `keyword`, `author`, `add_time`, `edit_time`, `reference`, `user_name`, `hit`, `focus`) VALUES
(12, 3, 1, 'Bệnh virus đốm trắng', 'White spot syndrome virus', '', '<p>\r\n	Trước năm 2002, có 3 chủng Baculovirus gây bệnh đốm trắng hoặc còn gọi là virus Trung Quốc. Tùy từng nước nghiên cứu chúng có tên gọi và kích thước như sau:<br />\r\n	Tên virus Kích thước virus Kích thước nhân</p>\r\n<ul>\r\n	<li>\r\n		Virus Trung Quốc (HHNBV) 120 x 360 nm</li>\r\n	<li>\r\n		Virus tôm Nhật 1(RVPJ-1) 84 x 226 nm</li>\r\n	<li>\r\n		Virus tôm Nhật 2 (RV-PJ-2) 83 x 275 nm 54 x 216 nm</li>\r\n	<li>\r\n		Virus bệnh đốm trắng Thái lan (SEMBV) 121 x 276 nm 89 x 201 nm</li>\r\n	<li>\r\n		Virus bệnh đốm trắng (WSBV) 70-150x350-380nm 58-67x330-350nm</li>\r\n</ul>\r\n<p>\r\n	Hội nghị virus học quốc tế lần thứ 12 (Paris, 2002) các tác giả: Just M. Vlak, Jean-Robert Bonami, Tim W. Flegel, Guang-Hsiung Kou, Donald V. Lightner, Chu-Fang Lo, Philip C. Loh and Peter J. Walker đã phân loại virus gây hội chứng đốm trắng là một giống mới Whispovirus thuộc họ mới Nimaviridae<br />\r\n	- Virus dạng hình trứng, kích thước 120x275nm, có một đuôi phụ ở một đầu, kích thước 70x300nm.<br />\r\n	- Virus có ít nhất 5 lớp protein, trong lượng phân tử từ 15- 28 kilodalton. Vỏ bao có hai lớp protein VP28 và VP19; Nucleocapsid có 3 lớp VP26, VP24, VP15<br />\r\n	- Nhân cấu trúc dsADN: Không có thể ẩn (Occlusion body).<br />\r\n	- Khi tôm xuất hiện các đốm trắng, quan sát thấy rất nhiều các thể vuì (inclusion body). ở trong nhân của tế bào mang, biểu bì ruột, dạ dày và tế bào biểu bì dưới vỏ, cơ quan lympho,các nhân hoại tử và sưng to.<br />\r\n	-Khi môi trường nuôi tôm xấu bệnh dễ xuất hiện.</p>\r\n', '<p>\r\n	Bệnh đốm trắng được thông báo đầu tiên ở Trung Quốc trong các đầm nuôi tôm sú nuôi tỷ lệ chết rất cao (Chen, 1989). ở Thái lan các trại tôm ở vùng Samut Sakhorn năm 1989 đã có báo cáo bệnh đỏ thân ở tôm sú. Năm 1992 - 1993 ở Thái Lan, tôm nuôi đã bị bệnh đầu vàng và đốm trắng thiệt hại hơn 40 triệu đôla (Flegel T.W, 1996). Năm 1993 Nhật Bản nhập tôm của Trung Quốc về nuôi đã xuất hiện bệnh đốm trắng. Năm 1994 đã có các báo cáo từ ấn Độ, Trung Quốc, Indonesia, Nhật Bản và Thái Lan tìm ra nguyên nhân gây bệnh đốm trắng.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Nguyên nhân gây bệnh đốm trắng" src="/upload/images/desease/benh_dom_trang.JPG" style="width: 517px; height: 583px;" /></p>\r\n<p style="text-align: center;">\r\n	Sơ đồ nguyên nhân gây bệnh đốm trắng.</p>\r\n<p>\r\n	Hiện tượng tôm bệnh thường xảy ra ở tôm giống đến tôm trưởng thành, của các khu vực nuôi thâm canh và quảng canh. Khi môi trường nuôi tôm xấu bệnh dễ xuất hiện. Bệnh đốm trắng xuất hiện ở nhiều động vật giáp xác tự nhiên, như các loài tôm he, tôm nước ngọt, cua, tôm hùm, chân chèo vμ ấu trùng côn trùng (xem bảng 14) do đó bệnh lây lan rất nhanh chóng trong các đầm nuôi tôm.</p>\r\n<p>\r\n	Bệnh đốm trắng lây truyền qua đường nằm ngang là chính. Virus lây từ các giáp xác khác (tôm cua, chân chèo) nhiễm bệnh đốm trắng từ môi trường bên ngoài ao hoặc ngay trong ao nuôi tôm. Khi các loài tôm bị bệnh đốm trắng trong ao sức khoẻ chúng yếu hoặc chết các con tôm khoẻ đã ăn chúng dẫn đến bệnh lây lan càng nhanh hơn. Có thể một số loài chim nước đã ăn tôm bị bệnh đốm trắng từ ao khác và bay đến ao nuôi đã mang theo các mẩu thừa rơi vào ao nuôi. Bệnh đốm trắng không có khả năng lây truyền qua đường thẳng đứng vì các noãn bào (trứng) phát hiện chúng nhiễm virus đốm trắng thì chúng không chín (thành thục) được. Nhưng trong quá trình đẻ trứng của tôm mẹ có thể thải ra các virus đốm trắng từ trong buồng trứng của chúng, do đó ấu trùng tôm dễ dàng nhiễm virus ngay từ giai đoạn sớm.</p>\r\n<p>\r\n	Trong những năm gần đây bệnh đốm trắng thường xuyên xuất hiện trong các khu vực nuôi tôm ven biển ở Việt Nam, hầu hết các tỉnh khi bị nhiễm bệnh đốm trắng đã làm tôm chết hàng loạt và gây tổn thất lớn cho nghề nuôi tôm. Mùa xuất hiện bệnh là mùa xuân và đầu hè khi thời tiết biến đổi nhiều như biên độ nhiệt độ trong ngày biến thiên quá lớn (&gt; 50C) gây sốc cho tôm. Bệnh đốm trắng thường gây chết tôm rảo, tôm nương, cua, ghẹ, sau đó tôm sú nuôi khoảng 1-2 tháng bệnh đốm trắng xuất hiện và gây chết tôm. Năm 2001, Bùi Quang Tề và cộng sự đã điều tra 483 hộ nuôi tôm sú thuộc 23 huyện của 8 tỉnh ven biển phía Bắc (Quảng Ninh, Hải Phòng, Thái Bình, Nam Định, Ninh Bình, Thanh Hoá, Nghệ An, Hà Tĩnh) có 166 hộ (34,37%) đã mang mầm bệnh đốm trắng ở tôm nuôi và tôm cua tự nhiên và có 169 hộ (34,99%) bệnh đốm trắng đã gây tôm chết. Tôm sú nuôi sau 1-2 tháng bệnh đốm trắng xuất hiện và gây tôm chết hàng loạt.</p>\r\n', '<p>\r\n	Dấu hiệu đặc trừng của bệnh có những đốm trắng ở dừới vỏ. Những đốm trắng thừờng có đừờng kính từ 0,5-2,0 mm.</p>\r\n<p>\r\n	- Thừờng liên quan đến sự xuất hiện của bệnh đỏ thân.</p>\r\n<p>\r\n	- Những dấu hiệu khác: Đầu tiên thấy tôm ở tầng mặt và dạt vào bờ, bỏ ăn, hoạt động kém, các phần phụ bị tổn thừơng, nắp mang phồng lên và vỏ có nhiều sinh vật bám.</p>\r\n<p>\r\n	- Khi có dấu hiệu sức khoẻ tôm yếu, đồng thời các đốm trắng xuất hiện, tỷ lệ tôm phát bệnh trong vòng từ 3-10 ngày lên đến 100% và tôm chết hầu hết trong ao nuôi.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Tôm bệnh đốm trắng" src="/upload/images/desease/benh_dom_trang1.JPG" style="width: 489px; height: 287px;" /></p>\r\n<p style="text-align: center;">\r\n	<em>Tôm bệnh đốm trắng</em></p>\r\n<p style="text-align: center;">\r\n	<img alt="Dấu hiệu tôm bênh đốm trắng" src="/upload/images/desease/benh_dom_trang2.JPG" style="width: 480px; height: 354px;" /></p>\r\n<p style="text-align: center;">\r\n	<em>Đầu tôm bệnh đốm trắng</em></p>\r\n', '<p>\r\n	Chọn tôm bố mẹ có chất lượng tốt (chiều dài từ 26-30cm, đánh ở độ sâu 60-120m) không nhiễm WSSV.</p>\r\n<p>\r\n	- Không vận chuyển tôm giống mật độ cao.</p>\r\n<p>\r\n	- Thức ăn tươi sống không hư thối và dùng nhiệt nấu chín.</p>\r\n<p>\r\n	- Hàng tháng cho tôm ăn Vitamin C từ 1-2 đợt với liều 2-3 g/1 kg thức ăn cơ bản, mỗi đợt cho tôm ăn một tuần liên lục.</p>\r\n<p>\r\n	- Nguồn nước cấp cho ao nuôi tôm phải lắng lọc và khử trùng.</p>\r\n<p>\r\n	- Vớt tôm chết ra khỏi ao</p>\r\n<p>\r\n	- Ngăn chặn không cho tôm và giáp xác khác vào ao nuôi.</p>\r\n<p>\r\n	- Nước ao nuôi tôm bị bệnh đốm trắng phải xử lý bằng Chlorua vôi nồng độ cao (30-50g/m3), không được xả ra ngoài. Khi phát hiện bệnh, tốt nhất là thu hoạch ngay.</p>\r\n', 'White spot syndrome virus,tôm sú, tôm thẻ, bệnh đốm trắng', 'Bùi Quang Tề', 1322587618, 1330685541, '', '', 4216, 1),
(14, 3, 1, 'Bệnh liên quan đến mang của tôm', 'Gill Asociated Virus - GAV', '', '<p>\r\n	Giống <em>Okavirus </em>thuộc <em>Roniviridae </em>, bộ <em>Nidovirales </em>(theo Mayo, M. A. 2002 )<br />\r\n	- Nucleocapsid dạng ống xoắn và thể virus (virion) hình que có vỏ bao, hình dạng giống virus đầu vàng.<br />\r\n	- Kích thước nucleocapsid: 16-18 x 166-435nm<br />\r\n	- Axít nhân là ARN</p>\r\n', '<p>\r\n	Bệnh hiện nay chỉ mới thông báo nhiễm tự nhiên ở tôm sú của úc. Gây nhiễm thực nghiệm đã gây ở tôm <em>P. esculetus, P. merguiensis, P. japonicus</em></p>\r\n<p>\r\n	- Kết quả chẩn đoán bằng kỹ thuật RT-PCR tôm sú ở Việt Nam nhiễm YHD/GAV rất cao. Thực tế bệnh YHD/GAV ít xuất hiện ở các ao nuôi thương phẩm.</p>\r\n<p>\r\n	- Đã gặp một số trường trong ao nuôi tôm sú xuất hiện tôm đỏ thân, chân đỏ và gây chết hàng loạt, nhưng test WSSV âm tính</p>\r\n<p>\r\n	- Đã gặp trường hợp một số đàn tôm bố mẹ bắt cho đẻ, tôm xuất hiện đỏ thân, đỏ mang, khi kiểm tra dưới KHVĐT có virus hình que trong mang</p>\r\n<p>\r\n	- Tôm sú bố mẹ khi đánh bắt ở biển khơi hoặc trong các đầm phá có hiện tượng bị bệnh đỏ mang sau khi đánh bắt từ 3-4 ngày, tỷ lệ chết tới 80-100%, thời gian tôm bị bệnh chết nhiều vào tháng 3-4 (sau tết). Kiểm tra dưới kính hiển vi điện tử có xuất hiện các thể virus hình que và test RT-PCR dương tính với bệnh GAV.</p>\r\n<p>\r\n	- Bệnh lây truyền theo trục ngang và trục dọc từ mẹ sang con.</p>\r\n', '<p>\r\n	Virus GAV thường có mặt trên tôm khỏe</p>\r\n<p>\r\n	- Tôm nhiễm GAV mạn tính, thể virus nằm trong tế bào nhiễm của tổ chức Lympho (LO), gặp ở tôm sú tự nhiên và tôm nuôi, ít xuất hiện các dấu hiệu bệnh lý.</p>\r\n<p>\r\n	- Tôm nhiễm GAV cấp tính, virus thường gặp ở tôm tự nhiên và có thể xuất hiện ở tôm sú nuôi. Tôm hôn mê, kém ăn và bơi trên tầng mặt và gần bời ao. Cơ thể xuất hiện màu đỏ thẫm ở các phần phụ, mang tôm chuyển sang màu hồng và vàng.</p>\r\n<p>\r\n	<img alt="tom su giong benh ve mang" src="/upload/images/tom_su_benh_mang.jpg" style="width: 598px; height: 367px;" /></p>\r\n<p>\r\n	Hình: tôm sú nhiễm bênh về mang GAV</p>\r\n<p>\r\n	<img alt="Tom su bo me bi do mang" src="/upload/images/desease/tom_su_benh_mang2.jpg" style="width: 600px; height: 345px;" /></p>\r\n<p>\r\n	Hình: tôm sú bố mẹ bị đỏ mang - dương tính bênh GAV</p>\r\n<p>\r\n	<img alt="tom benh mang vang" src="/upload/images/desease/tom_su_benh_mang_4.jpg" style="width: 600px; height: 347px;" /></p>\r\n<p>\r\n	Hình: Tôm sú bệnh mang chuyển sang màu vàng</p>\r\n', '<p>\r\n	Áp dụng theo phương pháp phòng bệnh tổng hợp. Tránh vận chuyển tôm tơ nơi că bệnh đến nơi chưa phát bệnh để hạn chế sự lây lan vùng lân cận. Những tôm chết vớt ra khỏi ao, tốt nhất là chôn trong vôi nung hoặc đốt. Nứớc tơ ao tôm bệnh không thải ra ngói xử lý bằng vôi nung hoặc bằng clorua vôi (theo phương pháp tẩy ao). Xem xét tôm thường xuyên, nếu phát hiện có dấu hiệu bệnh, tốt nhất là thu hoạch ngay. nêu tôm quá nhỏ không đáng thu hoạch thì cần xử lý nước ao trước khi tháo bỏ.</p>\r\n', 'benh ve mang, tom su, benh lien quan mang, mang tom,Gill Asociated Virus, GAV', 'Bùi Quang Tề', 1329843237, 1330685663, '', '', 4073, 0),
(15, 3, 1, 'Bệnh nhiễm trung virus dưới da và hoại tử', 'Infectious hypodermal and haematopoietic necrosis virus- IHHNV', '', '<p>\r\n	T&aacute;c nh&acirc;n g&acirc;y bệnh nhiễm tr&ugrave;ng virus dưới da v&agrave; hoại tử l&mu; giống <em>Parvovirus, </em>cấu tr&uacute;c acid nh&acirc;n l&agrave; ADN, đường k&iacute;nh 22 nm. Virus k&yacute; sinh trong nh&acirc;n tế b&agrave;o tuyến anten, tế b&agrave;o hệ bạch huyết, tế b&agrave;o mang, tế b&agrave;o d&acirc;y thần kinh, kh&ocirc;ng c&oacute; thể ẩn (occlusion body) m&agrave; c&oacute; thể v&ugrave;i (inclusion body), ch&uacute;ng l&agrave;m hoại tử v&agrave; sưng to nh&acirc;n vật chủ</p>\r\n', '<p>\r\n	Bệnh IHNNV được ph&aacute;t hiện ở Mỹ trong đ&agrave;n t&ocirc;m ch&acirc;n trắng (<em>Penaeus vannamei</em>), c&ograve;n gọi l&agrave; hội chứng dị h&igrave;nh c&ograve;i cọc của t&ocirc;m ch&acirc;n trắng Nam Mỹ. Bệnh xuất hiện từ giai đoạn postlarvae đến t&ocirc;m trưởng th&agrave;nh. Tỷ lệ chết của t&ocirc;m <em>P. stylirostris </em>rất cao. Bệnh xuất hiện cả ở Singapore, Philippines, Th&aacute;i Lan, Indonesia v&agrave; Malaysia</p>\r\n<p>\r\n	Bệnh IHHNV lan truyền cả chiều đứng v&agrave; chiều ngang, virus c&oacute; thể truyền từ t&ocirc;m bố mẹ sang t&ocirc;m ấu tr&ugrave;ng hoặc l&acirc;y nhiễm ở giai đoạn sớm của ấu tr&ugrave;ng t&ocirc;m.</p>\r\n<p>\r\n	Ở Việt Nam qua ph&acirc;n t&iacute;ch m&ocirc; bệnh học gan tuỵ của t&ocirc;m s&uacute; <em>P.monodon </em>Minh Hải, S&oacute;c Trăng xuất hiện c&aacute;c thể v&ugrave;i ở nh&acirc;n tế b&agrave;o tuyến anten của t&ocirc;m s&uacute; (B&ugrave;i Quang Tề, 1994) nhưng tỷ lệ nhiễm virus thấp. T&ocirc;m s&uacute; v&agrave; t&ocirc;m ch&acirc;n trắng (<em>P. vannamei</em>) nu&ocirc;i ở Quảng Ninh chẩn đo&aacute;n bằng test PCR cho thấy t&ocirc;m đ&atilde; nhiễm bệnh IHHNV, t&ocirc;m nu&ocirc;i chậm lớn v&agrave; kh&ocirc;ng đều, tỷ lệ t&ocirc;m c&ograve;i 20-50% (B&ugrave;i Quang Tề, 2004).</p>\r\n', '<p>\r\n	- T&ocirc;m nhiễm bệnh IHHNV thường h&ocirc;n m&ecirc;, hoạt động yếu, chủy biến dạng. T&ocirc;m s&uacute; (<em>P.monodon</em>) bị bệnh l&uacute;c sắp chết thường chuyển m&agrave;u xanh, cơ phần bụng m&mu;u đục. T&ocirc;m ch&acirc;n trắng (<em>P. vannamei</em>) thể hiện hội chứng dị h&igrave;nh c&ograve;i cọc, t&ocirc;m giống (Juvenil) chủy biến dạng, sợi anten quăn queo, vỏ kitin x&ugrave; x&igrave; hoặc biến dạng.</p>\r\n<p>\r\n	Hệ số c&ograve;i cọc trong đ&agrave;n t&ocirc;m giống ch&acirc;n trắng bị bệnh IHHNV thường từ 10-30%, khi bị bệnh nặng hệ số c&ograve;i cọc lớn 30% c&oacute; khi tới 50%. T&ocirc;m <em>P. stylirostris </em>bị bệnh dạng cấp t&iacute;nh, tỷ lệ chết rất cao, virus bệnh l&acirc;y từ mẹ sang ấu tr&ugrave;ng (phương thẳng đứng) nhưng kh&ocirc;ng ph&aacute;t bệnh, thường đến postlarvae 35 dấu hiệu bệnh quan s&aacute;t l&agrave; tỷ lệ chết cao, virus l&acirc;y lan theo chiều ngang ở t&ocirc;m giống ảnh hưởng rất m&atilde;nh liệt, t&ocirc;m trưởng th&agrave;nh đ&ocirc;i khi c&oacute; dấu hiệu bệnh hoặc chết.</p>\r\n<p>\r\n	- Kiểm tra m&ocirc; bệnh học tế b&agrave;o tuyến anten, tế b&agrave;o d&acirc;y thần kinh v&agrave; tế b&agrave;o mang của t&ocirc;m nhiễm bệnh IHHNV, c&oacute; thể v&ugrave;i trong nh&acirc;n tế b&agrave;o. Thời kỳ đầu thường nhỏ nằm ở trung t&acirc;m của nh&acirc;n, sau lớn dần nằm gần k&iacute;n nh&acirc;n (bắt m&agrave;u Eosin m&agrave;u đỏ đến đỏ xẫm). Trong thể v&ugrave;i c&oacute; chứa nhiều virus.</p>\r\n<p>\r\n	<img alt="tom su benh nhiem trung virus duoi da" src="/upload/images/desease/tom_nhiem_trung_virus_duoi_da.jpg" style="width: 600px; height: 174px;" /></p>\r\n<p>\r\n	H&igrave;nh: t&ocirc;m ch&acirc;n trắng bị b&ecirc;nh chủy biến dạng, anten quăn queo</p>\r\n', '<p>\r\n	&Aacute;p dụng theo phương ph&aacute;p ph&ograve;ng bệnh tổng hợp. Tr&aacute;nh vận chuyển t&ocirc;m tơ nơi că bệnh đến nơi chưa ph&aacute;t bệnh để hạn chế sự l&acirc;y lan v&ugrave;ng l&acirc;n cận. Những t&ocirc;m chết vớt ra khỏi ao, tốt nhất l&agrave; ch&ocirc;n trong v&ocirc;i nung hoặc đốt. Nứớc tơ ao t&ocirc;m bệnh kh&ocirc;ng thải ra ngói xử l&yacute; bằng v&ocirc;i nung hoặc bằng clorua v&ocirc;i (theo phương ph&aacute;p tẩy ao). Xem x&eacute;t t&ocirc;m thường xuy&ecirc;n, nếu ph&aacute;t hiện c&oacute; dấu hiệu bệnh, tốt nhất l&agrave; thu hoạch ngay. n&ecirc;u t&ocirc;m qu&aacute; nhỏ kh&ocirc;ng đ&aacute;ng thu hoạch th&igrave; cần xử l&yacute; nước ao trước khi th&aacute;o bỏ.</p>\r\n', 'IHHNV, tom su, tom the, benh hoai tu, nhiem trung duoi da, Parvovirus', 'Bùi Quang Tề', 1329846501, 1329846576, '', '', 1530, 0),
(16, 4, 3, 'Bệnh gan thận mủ trên cá tra', 'Edwardsiella ictaluri', '', '<p>\r\n	&nbsp;Bệnh mủ gan (bệnh đốm trắng trên gan, thận) trên cá tra, cá basa do nhóm vi khuẩn Edwardsiella ictaluri gây ra (Crumlish và ctv, 2002). Vi khuẩn E. ictaluri thuộc họ Enterbacteriaceae là vi khuẩn gram âm, hình que, kích thước 1 x 2- 3μm, không sinh bào tử, là vi khuẩn yếm khí tùy tiện, phản ứng catalase dương tính, oxidase âm tính, không oxy hoá, lên men trong môi trường glucose. Có 1 - 3 Plasmid liên kết với E. Ictaluri (Speyerer và Boyle, 1987; Newton và ctv,1988) .</p>\r\n<p>\r\n	Những plasmid có thể đóng vai trò quan trọng trong việc đề kháng với kháng sinh. E. ictaluri là một trong những loài khó tính nhất của chủng Edwarsiella. Tăng trưởng chậm trên môi trường nuôi cấy, cần từ 36-48 giờ ở 28 - 30<sup>o</sup>C để phát triển mọc thành khuẩn lạc nhỏ trên thạch BHI (Brain heart infusion Agar) và vi khuẩn tăng trưởng chậm hoặc không tăng trưởng khi ủ ở 37<sup>o</sup>C (Valerie và ctv, 1994).</p>\r\n<p>\r\n	Vi khuẩn có thể phân lập từ mẫu cá bệnh (gan, thận, tỳ tạng) trên môi trường TSA (Trytone Soya Agar) hoặc EMB (Eosine Methylene blue lactase Agar) sau 48 giờ ở 28<sup>o</sup>C tạo thành khuẩn lạc này trắng đục.</p>\r\n', '<p>\r\n	&nbsp; Bệnh thường xảy ra nhiều vào mùa mưa lũ kéo dài đến mùa khô. Thời điểm phát triển bệnh và mức độ thiệt hại khác nhau theo từng năm.</p>\r\n<p>\r\n	E.ictaluri có thể nhiễm cho cá bằng hai đường khác nhau. Vi khuẩn trong nước có thể, qua đường mũi của cá xâm nhập vào cơ quan khứu giác và di chuyển vào dây thần kinh khứu giác, sau đó vào não (Miyazaki và Plumb 1985; Shotts và ctv,1986). Bệnh lan rộng từ màng não đến sọ và da. E. ictaluri cũng có thể xâm nhiễm qua đường tiêu hoá qua niêm mạc ruột vào máu gây nhiễm trùng máu (Shotts và ctv,1986). Bằng đường này thì vi khuẩn vào mao mạch trong biểu bì gây hoại tử và mất sắc tố của da. Cá da trơn còn nhiễm E.ictaluri qua đường miệng gây nhiễm khuẩn ruột. Bệnh tiến triển gây viêm ruột, viêm gan và viêm cầu thận trong vòng 2 tuần sau khi nhiễm bệnh ( Shotts và ctv, 1986).</p>\r\n<p>\r\n	Tóm lại, vi khuẩn E. ictaluri có thể xâm nhập vào cơ thể cá từ môi trường nước qua da, qua mang cá và qua miệng bằng đường thức ăn gây bệnh mủ gan cho cá.</p>\r\n', '<p>\r\n	* Mức độ nhẹ: Bên ngoài thân cá bình thường không biểu hiện xuất huyết, mắt hơi lồi nhưng khi mổ ra thì gan, thận, tỳ tạng có nhiều đốm trắng (như đốm mủ). Đó là biểu hiện bệnh lý đặc trưng nhất của bệnh mủ gan.</p>\r\n<p>\r\n	* Mức độ nặng: Cá bệnh bỏ ăn, bơi lờ đờ trên mặt nước, cá thường nhào lộn và xoay tròn. Khi bệnh nặng cá không phản ứng với tiếng động. Một số cá xuất huyết tất cả các vi hoặc xuất huyết toàn thân. Có khi cá xuất huyết trầm trọng, khi nhấc lên khỏi mặt nước máu sẽ chảy ra từ da và mang cá.</p>\r\n<p>\r\n	Một số cá bệnh còn biểu hiện màu sắc nhợt nhạt, có nhiều bệch lớn, nhỏ trên da. Số lượng cá chết hằng ngày khá cao và tỷ lệ tăng dần.</p>\r\n', '<p>\r\n	<strong>1. Phòng bệnh:</strong></p>\r\n<p>\r\n	Nên cho cá ăn thức ăn nấu chín hoặc thức ăn viên để phòng bệnh xâm nhập qua đường thức ăn.</p>\r\n<p>\r\n	Để chủ động phòng bệnh, ngăn chặn mầm bệnh xâm nhập và lây lan vào hệ thống nuôi cá tra trong vùng, các hộ nuôi cá cần có ao lắng lọc, sát trùng nước trước khi bơm nước vào ao nuôi. Định kỳ 10 - 15 ngày xử lý nước ao nuôi để diệt vi khuẩn, virus gây bệnh trong ao bằng cách dùng vôi nông nghiệp CaCO3 liều lượng 2 - 3kg/100m2 tạt quanh ao kết hợp các loại thuốc sát trùng nước ao nuôi hiệu quả cao như BKC, Vime-Protex, Vimekon.</p>\r\n<p>\r\n	Đối với những ao nuôi cá tra, basa lượng vật chất hữu cơ lơ lửng tồn tại trong ao rất cao, điều đó ảnh hưởng rất lớn đến tác dụng diệt khuẩn của các loại thuốc sát trùng như Chlorin, thuốc tím, H2O2 . Vì vậy nên dùng Vimekon để xử lý nước vì nó có ưu điểm là hiệu quả diệt khuẩn cao, không bị ảnh hưởng bởi môi trường có nhiều chất hữu cơ .</p>\r\n<p>\r\n	<strong>2. Trị bệnh</strong></p>\r\n<p>\r\n	Đặc trị gan thận mủ ở cá Theo các nghiên cứu gần đây vi khuẩn E. ictaluri gây bệnh mủ gan cá tra, basa, cá trê rất nhạy với Florphenicol. Đây là loại kháng sinh mới được phép sử dụng để điều trị bệnh cá của nhiều quốc gia trên thế giới kể cá Hoa Kỳ, thị trường xuất khẩu cá tra, basa lớn nhất của Việt Nam.</p>\r\n<p>\r\n	Florfenicol có hoạt tính chống lại sự phát triển của vi khuẩn bằng cách kết dính với tiểu đơn vị 50S của ribosom, ngăn chặn cầu nối peptid giữa các acid amin vì vậy ức chế sự tổng hợp protein làm cho vi khuẩn này không còn khả năng phát triển và tồn tại.</p>\r\n<p>\r\n	Đầu năm 2006, Khoa Thủy sản Trường Đại học Cần Thơ công bố kết quả nghiên cứu tìm kiếm các loại thuốc kháng sinh thay thế các loại thuốc cấm, đã công nhận Florfenicol là kháng sinh đặc trị bệnh này. Sử dụng thuốc từ 7 - 10 ngày sẽ cho hiệu quả tốt, cá sẽ hồi phục nhanh khi người nuôi thực hiện tốt khâu vệ sinh diệt mầm bệnh trong khu vực nuôi và trong môi trường nước.</p>\r\n<p>\r\n	Florfenicol có độ tồn dư thấp trong mô cơ. Dùng thuốc liều 10mg/kg thể trọng liên tục 12 ngày, khi ngưng sử dụng 7 ngày mức tồn dư trong cơ cá tra còn 0,222 - 0,109 ppm (mức cho phép của Việt Nam và Mỹ là 1ppm) (Schering Plough Animal Health Comporation, 2005)</p>\r\n<p>\r\n	Hiện nay, sản phẩm Vime - fenfish với hoạt chất chính Florfenicol và các chất dẫn xuất đặc biệt là sản phẩm đang được dùng để điều trị bệnh mủ gan mang lại hiệu quả rất cao ở khu vực Đồng Bằng Sông Cửu Long. Liệu trình điều trị như sau:</p>\r\n<p>\r\n	- Cách 1:<br />\r\n	* Sáng: Vime - Glucan 1kg / 10 - 13 tấn cá.<br />\r\n	* Chiều: Vime - fenfish 2000 1lít/ 15-20 tấn cá + Trimesul 1kg/4 tấn cá.</p>\r\n<p>\r\n	- Cách 2:<br />\r\n	* Sáng: Vime - Glucan 1kg / 10 - 13 tấn cá.<br />\r\n	* Chiều: Vimenro 200 1lít/ 20 tấn cá + Trimesul 1kg/ 4 tấn cá.</p>\r\n<p>\r\n	- Cách 3:<br />\r\n	* Sáng: Glusome 115 1kg/ 10 - 13 tấn cá.<br />\r\n	* Chiều: Vime - fenfish 2000 1 lít/ 15-20 tấn cá + Vime - Cicep 1kg/ 5 tấn cá.</p>\r\n<p>\r\n	- Cách 4:<br />\r\n	* Sáng: Glusome 115 1kg/ 10 - 13 tấn cá.<br />\r\n	* Chiều: Vime - fenfish 2000 1 lít/ 15-20 tấn cá + Doxery 1kg/ 5 tấn cá.</p>\r\n<p>\r\n	Chú ý:<br />\r\n	- Thuốc sử dụng được tính theo trọng lượng cá thực tế.<br />\r\n	- Cho cá ăn liên tục 7 - 10 ngày nhằm tránh tái nhiễm kết hợp xử lý nước:<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Đối với cá con (&lt;100g): dùng Vimekon 1kg/ 2.000m3 .<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Đối với cá lớn: dùng Fresh Water 1kg/ 1.500-2.000m3 hoặc Protectol 1lít/ 1.500 - 2.000m3 .<br />\r\n	- Khi trộn thuốc với thức ăn phải để khô ráo và nên áo bằng Vime - Lecithin nhằm tăng khả năng dung nạp của thuốc</p>\r\n<p>\r\n	Tuy nhiên, hiện nay khi hầu hết các loại kháng sinh đang được sử dụng điều trị bệnh gan thận mủ và xuất huyết trên cá tra và basa đã bị vi khuẩn đề kháng, hiệu quả điều trị không cao. Biện pháp tăng liều dùng và kéo dài thời gian điều trị dẫn đến tăng chi phí điều trị và gây tồn dư ảnh hưởng đến chất lượng thịt.</p>\r\n<p>\r\n	Sản phẩm "Forfish" được nghiên cứu và thử nghiệm nghiêm ngặt và liên tục trong vòng 3 năm với tiêu chí chuyên biệt đáp ứng cho xuất khẩu để tạo ra một sản phẩm mới, hiệu quả điều trị cao, dứt bệnh nhanh và đảm bảo bệnh không tái nhiễm đem lại lợi nhuận cao cho các nhà nuôi cá thâm canh quy mô lớn. Forfish được xem như là Giải pháp mới - Hiệu quả tuyệt vời đặc trị bệnh gan thận mủ, xuất huyết trên cá</p>\r\n', 'bệnh gan thận mủ, forfish,Edwardsiella ictaluri, cá tra, E. ictaluri , cá tra gan thận mủ', 'Long Ca', 1330435615, 1330686250, '', '', 4164, 1),
(19, 3, 1, 'Bệnh Parvovirus gan tuỵ tôm he', 'Hepatopancreatic Parvovirus - HPV', '', '<p>\r\n	Tác nhân gây bệnh gan tuỵ ở tôm he là nhóm Parvovirus, cấu trúc acid nhân là ADN, đường kính 22-24 nm. Virus ký sinh trong nhân tế bào gan tuỵ, biểu bì ruột trước, không có thể ẩn (occlusion body) mà có thể vùi (inclusion body), chúng làm hoại tử và sưng to nhân ký chủ.</p>\r\n', '<p>\r\n	Bệnh HPV lần đầu tiên được phát hiện ở Mỹ trong đàn tôm nhập nội. Tiếp theo đó là tôm nuôi ở Malaysia đã nhiễm virus HPV (Lightner và Redman, 1985 ). Bệnh HPV cùng với MBV gây tác hại trong đợt dịch tôm chết ở Đài Loan 1987-1988.</p>\r\n<p>\r\n	Những tôm thường hay nhiễm virus HPV là tôm <em>P. merguiensis, P. monodon</em>, <em>P. chinensis,</em> <em>P. japonicus, P. indicus</em>, <em>P. penicillatus, P. vanname, </em>và <em>Macrobrranchium rosenbergin</em></p>\r\n<p>\r\n	Bệnh phân bố rộng rãi ở các châu Á châu Úc, châu Phi và lan sang châu Mỹ. Bệnh HPV lan truyền theo phương nằm ngang, không truyền bệnh theo phương thẳng đứng.</p>\r\n<p>\r\n	Ở Việt Nam qua phân tích mô bệnh học gan tuỵ của tôm thẻ <em>P. merguiensis </em>Minh Hải, Sóc Trăng (Bùi Quang Tề, 1994), tôm sú nuôi rất chậm lớn trong 1 một số ao nuôi ở Nghệ An (2002), kiểm tra mô gan tụy đã xuất hiện các thể vùi ở nhân tế bào biểu bì mô hình ống. Tháng 7/2002 kiểm tra một lô tôm post 25-30 ở Quảng Ngãi, kết quả cho thấy tỷ lệ nhiễm 100% bệnh HPV, tôm có hiện tượng đen thân và chết nhiều (Bùi Quang Tề, 2002)</p>\r\n', '<p>\r\n	Tụm nhiễm virus HPV thường bỏ ăn, hoặc ít ăn, hoạt động yếu, dễ bị nhiễm các sinh vật bám trên mang, vỏ và các phần phụ. Gan tôm bị teo lại hoặc hoại tử, hệ cơ bụng đục mờ, hiện tượng chết thường xảy ra ở tôm ấu trùng, tỷ lệ chết từ 50-100%.<br />\r\n	- Tôm nhiễm virus HPV thường có liên quan đến tôm nuôi thương phẩm (từ tháng thứ 3-4) thải phân trắng, tôm bỏ ăn, hoạt động chậm chạp và chết rải rác. Vấn đề này đang tiếp tục nghiên cứu.<br />\r\n	- Kiểm tra mô bệnh học tế bào gan tuỵ của tôm nhiễm bệnh HPV, cú thể vùi nằm trong tế bào biểu bỡ mô hình ống gan tuỵ. Thời kỳ đầu thường nhỏ nằm ở trung tâm của nhân, sau lớn dần nằm gần kín nhân (bắt màu Eosin màu đỏ đến đỏ xẫm). Trong thể vùi có chứa nhiều virus.</p>\r\n', '', 'phân trắng, hpv, gan tụy, tôm su, tom thẻ, Parvovirus, virus', 'Bùi Quang Tề', 1330689841, 1331529576, '', '', 2157, 0),
(20, 4, 1, 'Bệnh hoại tử mắt của tôm', '', '', '<p>\r\n	Nguyên nhân gây bệnh là do các vi khuẩn <em>Vibrio </em>spp (<em>V. harveyi, V. vulnificus, V. alginolyticus, V.anguillarum, V. parahaemolyticus…</em>); virus hình que (giống như virus của cơ quan Lympho, virus ở mang và virus đầu vàng). Nhìn qua kính hiển vi điện tử, cho thấy tế bào thần kinh trong vùng hội tụ (gần màng đáy) chứa các túi tế bào chất (đường kính 1-3µm) có các hạt (đường kính nhân 15-26nm) và vỏ (Nucleocapsid) hình que. Virus hình que có chiều dài 130-260nm, đường kính 10-16nm .</p>\r\n', '<p>\r\n	Bệnh ở mắt thường gặp ở vùng nuôi tôm ở khu vực châu ¸ Thái Bình Dương và óc. Hiện nay mới có thông báo gặp ở tôm sú nuôi. Việt Nam chúng đã xuất hiện bệnh mắt (còn gọi là bệnh đui mắt) tôm sú nuôi từ tháng thứ hai. Bệnh xuất hiện nhiều vùng Trung bộ và Nam bộ là chính, ở miền Bắc gặp ở Quảng Ninh những ao tôm sú nuôi thâm canh (Bùi Quang Tề, 2004, 2006).</p>\r\n', '<p>\r\n	Tôm hôn mê, lờ đờ, kém ăn, ruột không có thức ăn, thân tôm chuyển màu đen, mang có màu nâu, cơ đuôi trắng, đuôi và vỏ kitin hoại tử. Tôm lúc sắp chết mắt có dấu hiệu nhiễm trùng viêm, hoại tử , u hạt và mềm nhũn, thấy rõ trong lát cắt mô học. Hoại tử của mắt là chứng phù và thâm nhiễm của tế bào máu ở những địa điểm áp xe.</p>\r\n<p>\r\n	<img alt="tom benh hoai tu mat" src="/upload/images/tom_benh_hoai_tu_mat.jpg" style="width: 564px; height: 389px;" /></p>\r\n<p>\r\n	Ví dụ thường ở lớp giữa hạch thần kinh mỏng (LG) và vùng hội tụ hẹp (20àm) có các tế bào máu tự do. Nếu mắt bị bệnh thì lớp giữa rộng 50-100àm có dịch màu hồng của tế bào bị viêm.<br />\r\n	Mạch máu và kẽ hở của hạch thần kinh mỏng đã mở rộng đáng kể và các tế bào máu tạo thành đường nhăn. Vùng khúc xạ và các vùng khác trong mắt tìm thấy các tế bào hoại tử và thoái hóa ở những chỗ áp xe.</p>\r\n<p>\r\n	Hoại tử mắt thường gặp ở các ao nuôi tôm từ 10- 50%. U hạt của mắt thường xuất trong các ao nuôi tôm khi mắt có hiện tượng nhiễm melanin trên tầng biểu bì. Tỷ lệ u hạt của mắt xuất hiện từ 2-5% ở những tôm sắp chết. Nó có đặc điểm là được thay thế bởi mắt con, u hạt và cấu trúc bên trong của mắt với những mô sợi bắt màu eosin chứa các hạt nhỏ nhiễm melanin của tế bào máu, tế bào thoái hóa, hoại tử, lớp nhiễm melanin của các tế bào máu bên dưới lớp biểu bì.</p>\r\n<p>\r\n	Mắt phồng (rộp) chiếm 1-2% ở tôm sắp chết có đặc điểm là hoại tử ở mô thần kinh, khoang nhỏ, mạch phát triển rộng trong hạch lõi. Nghiên cứu cẩn thận mô thần kinh hoại tử phát hiện thấy các tế bào đa nhân khổng lồ. Những dấu vết còn lại của tế bào cho thấy, chúng bao quanh và để lộ ra các hạt chất nhiễm sắc mà chúng là các đại thực bào chết của hệ thống thần kinh. Kiểm tra thần kinh mắt thấy rõ tế bào thần kinh đệm trương to. Rải rác khắp nơi ở cuối ngoại biên của thần kinh mắt có khoảng trống chứa các tế bào hình tròn nhân nhỏ, như sự thoái hóa của sợi thần kinh. Trong một số lát cắt mô mắt có một số vi khuẩn hình que bắt màu hồng, Vi khuẩn gram âm có trong huyết tương, kẽ lỏng và không bào, nhân tế bào trương to và kết đặc.</p>\r\n<p>\r\n	Kiểm tra mô học của mang, gan tụy có thấy u hạt, biểu bì nhiễm melanin và các vi khuẩn ở trong các kẽ hở của mang. Mức độ nhiễm vi khuẩn Vibrio spp ở trong các mô của tôm sắp chết tăng từ 10-100 lần so với tôm khỏe. Mang của tôm sắp chết mức độ nhiễm Vibrio spp cũng rất cao, kết quả chúng bám bên ngoài cũng gia tăng. Mức độ nhiễm trung bình 2,2. 105 khuẩn lạc/g đuôi tôm; 2,1.103 khuẩn lạc/ml huyết tương; 3,6. 103 khuẩn lạc/g mắt tôm. Phân lập ở trong mắt tôm sắp chết có các loài vi khuẩn: Vibrio harveyi chiếm 29,6% và các loài khác V. vulnificus 21,6%, V. alginolyticus 10,2%, V. anguillarum 10,2%, V. parahaemolyticus 4,2%... (theo Paul T. Smith, 2000).</p>\r\n', '<p>\r\n	Áp dụng biện pháp phòng chung</p>\r\n', 'bệnh đui mắt tôm, bệnh mù mắt tôm, hoại tử mắt tôm,Vibrio harveyi,V. vulnificus', 'Bùi Quang Tề', 1330922444, 1330922592, '', '', 2077, 0),
(21, 3, 1, 'Bệnh đuôi đỏ - Hội chứng virus Taura', 'Taura syndrom virus- TSV', '', '<p>\r\n	Gây bệnh là Picornavirus, thuộc họ Picornaviridae cấu trúc aixt nhân là ARN, virus hình cầu có 20 mặt, đường kính 30-32nm. Hệ thống gen (genome) là một mạch RNA, chiều dài 10,2kb, cấu trúc capsid có 3 phần (55, 40 và 24 kD) và một đoạn polypeptide phụ (58kD). Virus ký sinh tế bào biểu mô và dưới biểu mô đuôi.</p>\r\n', '<p>\r\n	Hôi chứng bệnh Taura là bệnh thường gặp ở tôm he chân trắng (L. vannamei = Penaeus vannamei) ở giai đoạn nuôi từ 14-40 ngày nuôi ở ao hoặc trong các bể ương. Bệnh TSV thường gặp ở tôm giống nhỏ cỡ 0,05-5,0g, tôm lơn hơn có thể xuất hiện nếu giai đoạn đầu bệnh chưa xuất hiện thì giai đoạn giống lớn hoặc tôm thương phẩm có thể xảy ra. Dịch bệnh TSV gây chết từ 40- 90% ở tôm nuôi từ post, tôm giống, tôm giống lớn. Bệnh TSV cũng có thể nhiễm ở tôm Tây bán cầu (P. stylirostris, P. setiferus và P. schmitti) thường bệnh gây chết ở giai đoạn post hoặc giai đoạn giống nhỏ. Ngoài ra một số tôm Tây bán cầu (P. aztecus và P. duorarum) và Đông bán cầu (P. chinensis, P. monodon và P. japonicus) có thể gây nhiễm bệnh TSV bằng thực nghiệm.</p>\r\n<p>\r\n	Năm 1992 bệnh đã xuất hiện ở tôm L. vannamei nuôi ở Ecuador (6/1992), bệnh TSV phát triển rất nhanh toàn bộ vùng nuôi tôm ở châu Mỹ bệnh nhiễm từ post đến tôm bố mẹ. Trong thời gian ngắn có các báo cáo bệnh TSV gặp ở các loài tôm he nuôi ở Tây bán cầu, châu Mỹ và Hawaii. Dịch bệnh TSV đã xuất hiện ở tôm nuôi của ven biển Thái Bình Dương châu Mỹ từ Peru đến Mexico và bệnh còn tìm thấy ở tôm he chân trắng (L. vannamei) tự nhiên. Bệnh TSV cũng đã báo cáo ở vùng nuôi tôm he từ Atlantic, Caribe và vịnh Mexico châu Mỹ. Đài Loan đã có báo cáo đầu tiên về tôm he chân trắng (L. vannamei) nhập từ Trung Mỹ đã bị bệnh TSV tới 90% (Chien Tu và CTV, 1999). Đến nay bệnh TSV đã lây sang tôm sú tự nhiên ở Trung Quốc và một số nước châu ¸ khác.</p>\r\n<p>\r\n	Bảng: các loài tôm nhiễm bệnh Taura (theo V.A.Graindorge &amp; T.W.Flegel,1999)</p>\r\n<table border="1" cellpadding="3" cellspacing="1" style="width: 500px; height: 99px;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				Tôm tự nhiên</td>\r\n			<td>\r\n				Tôm thực nghiệm</td>\r\n			<td>\r\n				Tôm khí bị nhiễm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<em>P. vannaimei</em></td>\r\n			<td>\r\n				<em>P. azticus</em></td>\r\n			<td>\r\n				<em>P. duodarum</em></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<em>P. setiferus</em></td>\r\n			<td>\r\n				<em>P. orientalis</em></td>\r\n			<td>\r\n				<em>P. azticus</em></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<em>P. stylirostris</em></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	Việt Nam chúng ta nhập tôm chân trắng vào từ năm 1999, như ông Tô Ngọc Tùng ở Quảng Điền, Quảng Hà- Quảng Ninh nhập tôm trắng (Nam Mỹ) của Trung Quốc từ năm 1999 đến nay nuôi 7 vụ, nhưng chưa thành công. Riêng vụ đầu năm 2001 thả tôm sau 45 ngày có hiện tượng tôm lao vào bờ chết.</p>\r\n<p>\r\n	Một số tôm chân trắng nhập từ Mỹ vào làm tôm bố mẹ hậu bị (7/2002), khi kiểm tra mô học đã thấy xuất hiện bệnh TSV. Khu vực nuôi tôm ở Hải Phòng, trong ao nuôi chân trắng xuất hiện bệnh “đỏ đuôi” vào tháng 11-12/2002 và tháng 5/2003, bệnh đã gây cho tôm chết. Khi thu mẫu phân tích mô học có biểu hiện mô bệnh học bệnh TSV. Phân tích RT- PCR kết quả dương tính bệnh TSV, như vậy bệnh TSV đã xuất hiện ở vùng nuôi tôm Hải Phòng, Nam Định, Quảng Ninh, Phú Yên, Long An, Bạc Liêu (theo Bùi Quang Tề, 2003, 2004).</p>\r\n', '<p>\r\n	Dấu hiệu bệnh lý tương tự như bệnh vi khuẩn. Bệnh dạng cấp tính đuôi tôm chuyển màu đỏ và bệnh mạn tính có nhiều đốm nhiễm melani do biều bì hoại tử. Tỷ lệ chết xuất hiện liên quan đến quá trình lột vỏ. Tuy nhiên nếu tôm sống lột vỏ được, chúng thường hồi phục sinh trưởng bình thường, mặc dù chúng có nhiễm liên tục virus.</p>\r\n<p>\r\n	Bệnh TSV có ba giai đoạn: cấp tính, chuyển tiếp và mạn tính được phân biệt rõ. Dấu hiệu lâm sàng thấy rõ nhất, khi tôm L. vannamei bị bệnh ở giai đoạn cấp tính và chuyển tiếp là yếu lờ đờ (hấp hối), đuôi phồng chuyển màu đỏ và hoại tử, nên ngư dân nuôi tôm ở Ecuador gọi là bệnh “đỏ đuôi” Khi quan sát kỹ ở biểu bì phần đuôi (telson, chân bơi, …) dưới kính hiển vi X10 thấy có dấu hiệu biểu bì hoại tử. Tôm ở giai đoạn cấp tính còn thấy dấu hiệu mềm vỏ, ruột không có thức ăn. Giai đoạn cấp tính ảnh hưởng đến sự lột vỏ của tôm. Nếu tôm lớn &gt; 1 g/con khi bị bệnh chim có thể nhìn thấy tôm hôn mê ở ven bờ hoặc trên tầng mặt ao. Do đó có hàng trăm con chim biển kiếm ăn ở những ao tôm bị bệnh.</p>\r\n<p>\r\n	Mặc dù bệnh chỉ xảy ra ít ngày, dấu hiệu bệnh của tôm ở giai đoạn chuyển tiếp có thể chẩn đoán được. Trong giai đoạn chuyển tiếp có các đốm đen trên biểu bì, tôm có thể có hoặc không có dấu hiệu phồng đuôi và chuyển màu đỏ.</p>\r\n<p>\r\n	Tiếp theo tôm chuyển sang giai đoạn mạn tính, virus ký sinh trong tổ chức lympho. Bệnh TSV có thể lan truyền bệnh theo chiều ngang hoặc có khả năng chuyền bệnh theo chiều đứng.</p>\r\n<p>\r\n	Bệnh TSV thường nhiễm ở các tổ chức ngoại bì và trung bì. Bệnh TSV nhiễm ở tôm L. vannamei và P. stylirostris có ba giai đoạn: cấp tính, chuyển tiếp và mạn tính. Biểu mô biểu bì hầu hết bị ảnh hưởng ở giai đoạn cấp tính, ở giai đoạn mạn tính của bệnh chỉ có tổ chức lympho nhiễm virus. Tôm L. vannamei ở giai đoạn cấp tính có tỷ lệ chết cao, hầu hết tôm P. stylirostris bị nhiễm bệnh nhưng chúng có khả năng chống không cho bệnh TSV phát triển.</p>\r\n<p>\r\n	<img alt="Tôm sú taura" src="/upload/images/tom_benh_taura.jpg" style="width: 450px; height: 128px;" /></p>\r\n<p>\r\n	<img alt="Tôm thẻ bệnh taura" src="/upload/images/tom_the_benh_taura.jpg" style="width: 450px; height: 408px;" /></p>\r\n<p>\r\n	<img alt="" src="/upload/images/tom_the_benh_do_duoi.png" style="width: 450px; height: 251px;" /></p>\r\n<p>\r\n	Hình: tôm thẻ chân trắng bệnh Taura thân màu trắng đục (tôm ở trên)</p>\r\n<p>\r\n	Mô biểu bì hoặc dưới biểu mô các tế bào nhiễm virus bị hoại tử, tế bào chất bắt màu hồng trong có chứa nhân kết đặc hoặc phân mảnh. Đặc điểm quan trọng là tế bào chất của biểu bì chuyển màu hồng hoặc màu xanh nhạt. Điều cần phải phân biệt với bệnh đầu vàng cũng có tế bào chất bắt màu hồng. Tuy nhiên phân biệt bệnh đầu vàng các mô ngoại bì và trung bì có thể vùi và luôn luôn có màu xanh đậm. Bệnh TSV ở những tôm bình phục hoặc bệnh mạn tính, vùng nhiễm melanin tìm thấy địa điểm bình phục và lành lại của tôm bệnh cấp tính.</p>\r\n', '<p>\r\n	Áp dụng biện pháp phòng bệnh chung.</p>\r\n', 'bênh đỏ đuôi, bênh tôm thẻ chân trắng,P. vannaimei, thẻ chân trắng', 'Bùi Quang Tề', 1331012806, 1331013124, '', '', 4928, 1),
(22, 3, 1, 'Bệnh Virus hoại tử tuyến ruột giữa của tôm he', 'Baculovirus Migut gland Necrosis - BMN', '', '<p>\r\n	Gây bệnh là Baculovirus type C nhân ADN, có thể vùi inlusion body), kích thước virus 72 x 310 nm, nhân virus 36 x 250 nm.</p>\r\n', '<p>\r\n	Bệnh Virus hoại tử tuyến ruột giưa của tôm he gặp đầu tiên ở tôm he Nhật bản (P.japonicus) nuôi ở Nhật bản và Hàn Quốc. Sau đó quan sát thấy ở tôm sú (P. monodon) P. chinesis, P. plebejus và Metapenaeus ensis.</p>\r\n<p>\r\n	Bệnh BMN gây tỷ lệ tử vong cao ở các trại sản xuất tôm giống và thường gây bệnh từ giaiđoạn Mysis 2 đến postlarvae và tôm giống. Có trường hợp postlarvae 9 - 10 đã nhiễm virus BMN tới 98% và gây chết hàng loạt ở postlarvae 20.</p>\r\n<p>\r\n	Ở việt nam chưa điều tra nghiên cứu virus BMN nhưng một số trường hợp các trại ương giống tôm chết hàng loạt chưa tìm rõ nguyên nhân và khi tôm chết có dấu hiệu bệnh lý như bệnh BMN, trong sản xuất giống tôm he Nhật bản cần chú ý đến bệnh này.</p>\r\n', '<p>\r\n	Dấu hiệu đầu tiên ấu trùng tôm hôn mê hoạt động chậm chạp, nổi trên tầng mặt, gan tuỵ của tôm màu trắng đục và ruột dọc theo phần bụng cũng có màu trắng đục. Thường bệnh xuất hiện ở postlarvae có chiều dài từ 6 - 9 mm. Tế bào biểu bì mô hình ống gan tụy nhiễm bệnh BMN, nhân trương to, hạch nhân bắt màu đỏ, các chất nhiễm sắc di chuyển ra mép màng nhân.</p>\r\n<p>\r\n	<img alt="tôm post chết do bệnh đục thân" src="/upload/images/desease/tom-benh-hoai-tu-tuyen-ruot.jpg" style="width: 500px; height: 187px;" /></p>\r\n<p>\r\n	<em>Tôm post chết do bệnh đục thân</em></p>\r\n<p>\r\n	<img alt="" src="/upload/images/desease/tom-benh-chet-do-duc-than.jpg" style="width: 500px; height: 318px;" /></p>\r\n<p>\r\n	<em>Tôm post đục giữa thân</em></p>\r\n<p>\r\n	-Dựa vào các dấu hiệu bệnh lý để chẩn đoán bệnh.</p>\r\n<p>\r\n	-Dựa vào các dấu hiệu mô bệnh học, thử bằng kháng thể huỳnh quang, soi kính hiển vi nền đen, soi kính hiển vi điện tử...để chẩn đoán bệnh.</p>\r\n<p>\r\n	Đặc điểm của mô bệnh học: Các tế bào biểu bì mô hình ống gan tuỵ bị hoại tử, nhân trương to bắt màu đỏ đến tím nhạt. Thể vùi không có hình dạng nhất định, nhiễm sắc thể giảm bớt và di chuyển ra màng nhân, không hình thành thể ẩn (occlusion body).</p>\r\n<p>\r\n	Kiểm tra bằng kính hiển vi nền đen: Chuẩn bị mẫu tươi gan tuỵ, quan sát nhân tế bào gan tuỵ trương to (không nhuộm màu hoặc nhuộm màu) cho thấy có màu trắng dưới nền đen, ở giữa có nhiều thể hình que, chiều dài gần 1àm và hầu hết chúng sắp xếp bên trong màng nhân.</p>\r\n', '<p>\r\n	- Áp dụng biện pháp phòng bệnh chung.</p>\r\n', 'tôm post bệnh đục thân, bệnh đục thân, virus hoại tử tuyến ruột tôm, postlarvae bệnh đục thân', 'Bùi Quang Tề', 1332830179, 1332830179, '', '', 2240, 0),
(23, 3, 1, 'Bệnh hoại tử cơ ở tôm thẻ chân trắng', 'infectious myonecrosis virus – IMNV', '', '<p>\r\n	Bệnh hoại tử cơ là một bệnh truyền nhiễm do tác nhân vi rút gây ra (infectious myonecrosis virus – IMNV). Đây là một trong những bệnh vi rút trên tôm được phát hiện trong thời gian gần đây nhất. Năm 2002, bệnh xảy ra lần đầu tiên ở các ao nuôi tôm thẻ chân trắng Penaeus vannamei miền Đông Bắc Braxin. Sau đó, bệnh lây lan sang các nước khác thuộc khu vực Châu Á như Indonesia, Thái Lan và tỉnh Hải Nam, Trung Quốc. Quá trình lây lan của IMNV qua các châu lục khác nhau được ghi nhận là do sự nhập chuyển của tôm bố mẹ P. vannamei.</p>\r\n<p>\r\n	IMNV là vi rút có vật chất di truyền là ARN mạch đôi, với kích thước 7.560bp, cấu trúc không có lớp màng bao. Phân tích phát sinh loài dựa vào gen RNA-dependent RNA polymerase (RdRp) đã phân loại IMNV vào họ Totiviridae, giống Giardiavirus.</p>\r\n', '<p>\r\n	Về phương thức lây nhiễm, IMNV được xác định có khả năng truyền bệnh nếu tôm khỏe ăn tôm bệnh nhiễm IMNV. Ngoài ra, IMNV còn được ghi nhận lây lan thông qua nguồn nước. Phương thức lây nhiễm theo chiều dọc từ bố mẹ truyền qua cho thế hệ con vẫn chưa được xác định là do sự nhiễm bên ngoài trứng hay bên trong buồng trứng. Vẫn chưa có số liệu về vật truyền bệnh IMN.</p>\r\n<p>\r\n	Tuy nhiên, do tác nhân gây bệnh hoại tử cơ có cấu trúc ARN mạch đôi, không có màng bao cho nên rất có khả năng là IMNV vẫn còn giữ khả năng lây nhiễm khi tồn tại trong ruột và chất thải của một số loài chim biển đã ăn những con tôm bệnh chết do IMNV.</p>\r\n', '<p>\r\n	Tôm thẻ chân trắng được ghi nhận là vật chủ chính của IMNV do khả năng gây tỉ lệ chết cao ở loài tôm này. IMNV thường gây tỉ lệ chết cho tôm thẻ chân trắng trong khoảng từ 40 cho đến 70% quần đàn. Tuy nhiên, trong các vùng nuôi tôm thẻ chân trắng có xảy ra dịch bệnh thì tỉ lệ chết do IMNV có thể lên đến 100%. Trong điều kiện phòng thí nghiệm đã cho thấy IMNV có khả năng cảm nhiễm với hai loài tôm, tôm xanh Nam Mỹ (Penaeusstylirostris) và tôm sú (Penaeus monodon).Nhiệt độ và nồng độ muối được xem là một trong những yếu tố môi trường có ảnh hưởng đến quá trình bộc phát của bệnh hoại tử cơ.</p>\r\n<p>\r\n	<img alt="benh hoai tu co tom tren the chan trang" src="/upload/images/desease/benh-hoai-tu-co-tom-the-chan-trang.jpg" style="width: 600px; height: 412px;" /></p>\r\n<p>\r\n	<em>Hình1. Bệnh hoại tử cơ do Infectious myonecrosis virus (IMNV). (a) Hình chụp dưới kính hiển vi điện tử, IMNV nhiễm tự nhiên trên tôm thẻ chân trắng ở Brazil; (b) (c) dấu hiệu bệnh hoại tử cơ trên tôm thẻ chân trắng; (d) kích thước của cơ quan bạch huyết của tôm nhiễm IMNV tăng gấp 2-4 lần so với kích thước thông thường (đánh dấu trong vòng tròn). Nguồn: Lightner, 2011.</em></p>\r\n<p>\r\n	Ở giai đoạn cấp tính, tôm bệnh hoại tử cơ thường có các dấu hiệu bệnh lý như phần cơ bụng và cơ đuôi trắng đục, do vậy có thể dẫn đến hiện tượng hoại tử và đỏ ở các vùng cơ này. Trong một số trường hợp, cơ quan lympho trương to lên gấp 2-4 lần kích thước bình thường (Hình 1). Ao tôm nhiễm IMNV mức độ nặng có thể chết đột ngột và kéo dài trong nhiều ngày. Bệnh hoại tử cơ với tỉ lệ chết cao đột ngột, thường xảy ra vào sau các thời điểm hay các hoạt động có thể gây sốc cho tôm ví dụ chài tôm, độ mặn hay nhiệt độ thay đổi một cách đột ngột... Một số tôm bệnh chết do IMNV vẫn ở trạng thái no với ruột đầy thức ăn, đó là do tôm vừa được cho ăn no ngay trước thời điểm xuất hiện của các nhân tố gây sốc kể trên.</p>\r\n<p>\r\n	Bệnh hoại tử cơ IMN trên tôm thẻ chân trắng có dấu hiệu lâm sàng và đặc điểm mô bệnh học tương tự với bệnh trắng đuôi trên tôm biển gây ra bởi một loài vi rút khác có tên là Penaeus vannamei novavirus – PvNV. Do vậy, cần lưu ý đặc điểm này trong quá trình chẩn đoán bệnh.</p>\r\n<p>\r\n	Tôm thẻ chân trắng ở giai đoạn ấu niên, tiền trưởng thành là thường nhạy cảm nhất với IMNV. Trong đó, cơ quan đích của IMNV được ghi nhận là cơ vân, mô liên kết, tế bào máu, và cơ quan bạch huyết. Do vậy, đây là một trong những cơ quan được sử dụng cho các qui trình chẩn đoán bệnh. Trường hợp tôm nhiễm IMNV mãn tính, cơ quan bạch huyết được ưu tiên dùng để phân lập IMNV. Chân bơi là cơ quan được khuyến cáo nên dùng cho quá trình phát hiện IMNV trên tôm bố mẹ.</p>\r\n', '<p>\r\n	Trong các trại sản xuất tôm giống, phương thức tiệt trùng trứng và ấu trùng được xem là một trong những phương pháp phòng bệnh hiệu quả. Sàng lọc và thả tôm giống không nhiễm IMNV được xem là giải pháp phòng bệnh trong các ao nuôi tôm thịt. Trường hợp ao nuôi thịt vừa xuất hiện vài con tôm chết với dấu hiệu của bệnh hoại tử cơ, nên thực hiện các bước xử lý: (i) ổn định môi trường ao nuôi, chú trọng đến nhiệt độ, nồng độ muối, pH; (ii) tăng cường sục khí; (iii) giảm lượng thức ăn hoặc ngừng cho tôm ăn. Trường hợp bệnh xảy ra với tỉ lệ chết cao, ao nuôi tôm thịt cần được xử lý với chorin 30ppm trong vài ngày.</p>\r\n', 'Penaeus vannamei', 'Ths. Trần Thị Tuyết Hoa (Nguồn: UV Việt Nam)', 1333246656, 1333246656, '', '', 4868, 0),
(24, 3, 1, 'Bệnh đốm trắng trên tôm thẻ chân trắng', 'White spot disease – WSSD', '', '<p>\r\n	Theo các nhà khoa học bệnh này do 1 loại virus có tên Baculovirus. Virus này có acid nucleic là DNA, kí sinh trong nhân. Virus có độc lực cực mạnh, tấn công nhiều mô tế bào khác nhau: thường trên tế bào biểu mô da. WSSV gây chết trên tất cả các giai đoạn phát triển của tôm từ ấu trùng đến tôm giống và tôm trưởng thành.</p>\r\n', '<p>\r\n	Tôm thẻ chân trắng có nguồn gốc từ vùng biển xích đạo Ðông Thái Bình Dương (biển phía Tây Mỹ La tinh), Châu Mỹ La tinh và Hawaii). Hiện nay, tôm thẻ được nuôi ở rất nhiều nước trên thế giới như: Đài Loan, Trung Quốc, Việt Nam... Trong quá trình nuôi, tôm thẻ cũng mắc 1 số bệnh truyền nhiễm như bệnh đốm trắng, bệnh Taura, bệnh còi, bệnh đầu vàng, … Đây là những bệnh nguy hiểm do virus gây ra nên khả năng điều trị rất khó.</p>\r\n<p>\r\n	Bệnh đốm trắng (tên tiếng anh là White spot disease – WSSD) là loại bệnh được xác định đầu tiên năm 1990-1991. Bệnh này xuất hiện ở Đông Bắc Á (Đài Loan, Hồng Kông, Trung Quốc, Hàn Quốc, Nhật Bản), từ đó lây sang các nước Châu Á Thái Bình Dương. Năm 1995, lần đầu tiên xuất hiện bệnh ở Tây bán cầu do Châu Mỹ nhập tôm sú Châu Á về nghiên cứu (những con tôm này có thể đã mang mầm bệnh).</p>\r\n<p>\r\n	Ở Việt Nam, bệnh bùng phát lần đầu vào các năm 1994-1995 tại các tỉnh miền Nam và một số tỉnh miền Trung.</p>\r\n<p>\r\n	Bệnh có thể lây truyền theo chiều dọc tức là virus đốm trắng (WSSV) từ bố mẹ truyền sang tôm con. Hoặc bệnh lây truyền theo chiều ngang tức là WSSV lây lan trực tiếp từ nước qua các vết thương tổn hay niêm mạc ống tiêu hóa. Tôm bố mẹ ăn giáp xác nhỏ có mang WSSV. Tôm có tập tính ăn đồng loại nên chúng có thể ăn các con tôm bệnh. Virus trong môi trường nước có thể lây trực tiếp cho tôm thẻ qua các tế bào biểu mô che phủ trên mang. Con bị nhiễm lây sang con chưa bị nhiễm khi nuôi chúng trong cùng ao.</p>\r\n', '<p>\r\n	bệnh xảy ra trong giai đoạn tôm thương phẩm sau 2 tháng trở lên. Tuy nhiên, bệnh có thể xuất hiện sớm hơn từ vài 3 tuần đến 1 tháng. Vì kích thước tôm nhỏ nên đôi khi không thấy đốm trắng mà chỉ thấy đỏ thân, hay đôi khi độc lực của virus rất mạnh, chưa thấy đốm trắng tôm đã chết.</p>\r\n<p>\r\n	Khi tôm bị bệnh, quan sát chúng ta thấy tôm giảm ăn đột ngột (đối với tôm thẻ chân trắng hay ăn nhiều hơn trước khi giảm ăn). Tôm lờ đờ, tấp vào bờ và chết, cơ thịt hơi đục. Đốm trắng xuất hiện ở giáp đầu ngực, đốt bụng cuối trước và lan khắp cơ thể. Đốm trắng nằm trong vỏ kitin. Bệnh thường xuất hiện lúc trời lạnh.</p>\r\n', '<p>\r\n	đây là bệnh do virus gây ra nên hiện nay vẫn chưa có phương pháp chữa trị, vì vậy, người nuôi cần phải tăng cường các biện pháp phòng bệnh sau:<br />\r\n	- Thả tôm giống sạch bệnh (có chứng nhận kiểm dịch của địa phương).<br />\r\n	- Hạn chế cấp nước trực tiếp vào ao nuôi, nên cấp qua ao lắng đã xử lý và nâng mực nước trong ao nuôi đạt 1,0 – 1,2m.<br />\r\n	- Kiểm tra thường xuyên màu sắc, khả năng bắt mồi, tình trạng sức khỏe của tôm để kịp thời phát hiện và xử lý.<br />\r\n	- Cách ly ao nuôi với các tác nhân có thể lan truyền bệnh (giáp xác).<br />\r\n	- Bổ sung vitamin C vào ao hoặc thức ăn cho tôm.<br />\r\n	- Đối với ao tôm bệnh, người nuôi nên vớt tôm chết ra khỏi ao. Sau đó dùng Chlorin với liều lượng 30 kg/1.000 m3; hoặc formol 200 lít/1.000m3 hòa nước tạt đều ao, ngâm 7 ngày rồi tiến hành xổ ra môi trường. Khi phát hiện bệnh tốt nhất là thu hoạch ngay để giảm thiệt hại.</p>\r\n', 'benh dom trang tren tom the chan trang, benh dom trang tren tom the, benh dom trang', 'Tố Nguyên – TT Thủy sản Long An', 1333418232, 1333418232, '', '', 7134, 1),
(25, 6, 1, 'Bệnh Rickettsia và Chlamydia ở tôm', '', '', '<p>\r\n	Hai giống Rickettsia và Chlamydia gây bệnh ở gan tuỵ ở tôm he và tôm càng xanh. Kích thước của chúng rất nhỏ (0,2-0,7 x 0,8-1,6 àm), hình cầu hoặc hình que ngắn, gram âm, ký sinh nội bào. Giống Rickettsia ký sinh gây bệnh ở gan tuỵ của tôm thẻ P. merguiensis và nội ký sinh ở tôm sú P. monodon.</p>\r\n', '<p>\r\n	Bệnh đã được phát hiện ở tôm thẻ P. merguiensis nuôi ở Singapore (Chong và Loh, 1984) và tôm sú nuôi ở các vùng thuộc Malaysia (Anderson và CTV, 1987). Giai đoạn biến thái IV-V của ấu trùng tôm càng xanh-Macrobrachium rosenbergii, Rickettsia là nguyên nhân gây chết nghiêm trọng do chúng ký sinh ở gan tuỵ (Cohen và Issar, 1989). Chlamydia ký sinh gây bệnh trong tế bào gan tuỵ của tôm chân trắng-P.vanmamei nuôi ở mỹ.</p>\r\n<p>\r\n	Ở Việt nam chưa đi sâu nghiên cứu bệnh Rickettsia và Chlamydia. Nhưng qua những đợt điều tra bệnh tôm từ năm 1993-1994. Quan sát những mẫu cắt mô tế bào gan tuỵ của tôm thẻ nuôi ở Minh hải, một số tế bào gan tuỵ có những khuẩn lạc nhỏ của Rickttssia trong tế bào chất. vấn đề này sẽ tiếp tục nghiên cứu tiếp theo.</p>\r\n', '<p>\r\n	Dấu hiệu đặc trưng là tôm kém ăn, yếu, thường dạt gần vào bờ ao, bơi không định hướng, sau hiện tượng tôm chết kéo dài 1-2 tuần. Bệnh có thể kết hợp với bệnh khác như bệnh virus, vi khuẩn.</p>\r\n', '<p>\r\n	Bệnh này còn ít báo cáo về kết quả phòng và trị bệnh. Nhưng một số nơi đã áp dụng biện pháp phòng chung: Bón vôi nung (CaO) liều 10-20 ppm hoặc trị bằng Terramycin có kết quả khả quan (Cohn và Issar, 1989).</p>\r\n', 'Terramycin, P. monodon, tôm sú, tôm càng xanh, tôm he, ký sinh gan tụy, ký sinh gan tụy tôm', 'Bùi Quang Tề', 1333641748, 1333641748, '', '', 1566, 0),
(26, 3, 1, 'Bệnh virus gây chết tôm bố mẹ khi lưu giữ', 'Spawnerisolated Mortality Viral Diseses- SMVD', '', '<p>\r\n	Virus hình sáu cạnh, đối xứng 20 mặt, kích thước 20nm. Axít nhân là AND. Virus gần giống với họ Parvoviridae.</p>\r\n', '<p>\r\n	Tôm sú: Penaeus monodon bố mẹ khi lưu giữ kéo dài thường nhiễm bệnh SMVD. Ngoài ra còn nhiễm bệnh tôm càng đỏ- Cherax quandricarinatus. Có thể gây nhiễm thành công ở một số loài tôm: P. esculentus; P. merguiensis; P. japonicus; tôm rảo Metapenaeus ensis; Một số nước Úc, Philippines, Srilanka đã thống báo có bệnh này. Việt Nam chúng ta chưa nghiên cứu bệnh này.</p>\r\n', '<p>\r\n	Không có dâu hiệu bệnh lý điển hình. Tôm kém ăn hoặc bỏ ăn, cơ thể chuyển màu nhạt, có nhiều sinh vật bám. Gây chết tôm bố mẹ khi lưu giữ.</p>\r\n', '<p>\r\n	Áp dụng phòng chung.</p>\r\n', 'tôm bố mẹ chết, gây chết tôm bố mẹ, virus trên tôm bố mẹ', 'Bùi Quang Tề', 1333642020, 1333642020, '', '', 1548, 0);
INSERT INTO `tkb_aq_desease` (`id`, `cat_id`, `group_id`, `name`, `en_name`, `description`, `agent`, `zone`, `symptom`, `treatment`, `keyword`, `author`, `add_time`, `edit_time`, `reference`, `user_name`, `hit`, `focus`) VALUES
(27, 3, 6, 'Bệnh lở loét, đỏ chân', '', '<p style="text-align: center; ">\r\n	Một số nguyên nhân chính gây bệnh ở ếch:</p>\r\n<p>\r\n	- Môi trường nuôi không tốt: Đây là yếu tố rất quan trọng dẫn đến việc nuôi ếch thành công hay thất bại. Một số yếu tố môi trường như chất đất, nước bị ô nhiễm nặng sẽ là những nguyên nhân dẫn đến nhiều loại bệnh ở ếch. Ao nuôi ếch bị nhiễm bẩn do nhiều nguyên nhân như: nguồn nước lấy vào không tốt, phân ếch thải ra, thức ăn thừa không được lấy đi hàng ngày, không có nguồn nước để bổ sung, thay thế khi ao quá ô nhiễm.</p>\r\n<p>\r\n	- Mật độ nuôi quá cao: Nuôi với mật độ quá dày làm cho môi trường nhanh bị ô nhiễm, ếch cạnh tranh nhau về thức ăn, chỗ ngủ theo hướng bất lợi cho cả bầy đàn.<br />\r\n	- Thức ăn kém chất lượng: Thức ăn chất lượng kém làm ếch còi cọc, tăng trưởng chậm, sức đề kháng kém. Thức ăn bị ôi thiu, ẩm mốc có thể gây ngộ độc cho ếch, đặc biệt là Aflatoxin B1.</p>\r\n<p>\r\n	- Con giống chất lượng kém: Nhiều người nuôi ếch không thành công là do mua con giống ở những cơ sở không có uy tín. Con giống kém cũng là nguyên nhân rất đến thất bại khi nuôi ếch.</p>\r\n', '<p>\r\n	Tác nhân gây bệnh là vi khuẩn Aeromonas hydrophila</p>\r\n', '<p>\r\n	Khi môi trường nước nuôi ếch bị ô nhiễm thì loài vi khuẩn này sẽ phát triển rất mạnh và gây bệnh cho ếch</p>\r\n', '<p>\r\n	Ếch bị bệnh sẽ xuất hiện những nốt chấm đỏ trên thân, gốc đùi có tụ huyết, chân bị sưng, ếch bỏ ăn, chậm di chuyển, lờ đờ, giải phẫu thấy có hiện tượng xuất huyết trong ổ bụng, trong xoang bụng thường thấy máu và dịch lỏng màu vàng. Khi dịch bệnh xảy ra, nếu không có biện pháp phòng trị kịp thời ếch sẽ chết hàng loạt.</p>\r\n', '<p>\r\n	Phòng bệnh này bằng cách thường xuyên kiểm tra môi trường nước nuôi, nếu thấy nước dơ cần thay ngay bằng nước sạch, nuôi mật độ vửa phải, không gây ồn ào khiến ếch bị sốc. Bổ sung N9.100, Vitamin C Antistress vào thức ăn của ếch để tăng sức đề kháng.</p>\r\n<p>\r\n	Trị bệnh: Phát hiện bệnh sớm, điều trị sẽ rất hiệu quả. Dùng kháng sinh Kamoxin F, Oxytetracycline (3-5 g/kg thức ăn) hoặc Doxery trộn vào thức ăn dùng liên tiếp 5 - 7 ngày. Ngâm ếch trong dung dịch Vime - Iodine 200 1 lít cho 500 - 700 m3/ 30 phút.</p>\r\n', '', 'Trương Đình Hoài. ĐH Nông nghiệp Hà Nội', 1335347678, 1335347678, '', '', 3393, 0),
(28, 4, 1, 'Bệnh đốm trắng do vi khuẩn ở tôm (đốm vôi)', 'Bacterial white spot syndrome - BWSS', '', '<p>\r\n	Bacillus subtilis thuộc giống Bacillus, họ Bacillaceae, bộ Bacillales, lớp Bacilli, ngành Firmicutes. Vi khuẩn có khả năng là nguyên nhân gây ra bệnh đốm trắng (Wang et al. 2000) ở tôm sú nuôi ở Malaysia. Vibrio cholerae cũng thường được nuôi cấy từ mẫu bệnh tôm nuôi (ở Thái Lan) ở các ao có pH và độ kiềm cao và vi khuẩn là nguyên nhân cơ hội (thứ hai). ở Việt Nam cũng đã nuôi cấy được Vibrio spp từ các mẫu ở tôm sú nuôi (Bùi Quang Tề và CTV, 2004).</p>\r\n<p>\r\n	<img alt="Bacillus subtilis gây bênh đốm trắng do vi khuẩn" src="/upload/images/desease/benh-dom-trang/dom-trang-Bacillus-subtilis.png" style="width: 488px; height: 256px;" /></p>\r\n<p>\r\n	<em>A- Bacillus subtilis trong đốm trắng của tôm (theo Wang et al. 2000- ảnh KHVĐT quét);<br />\r\n	B- Vibrio sp phân lập từ tôm sú bệnh đốm trắng (theo Bùi Quang Tề và CTV, 2004)</em></p>\r\n', '<p>\r\n	Bệnh đốm trắng do vi khuẩn được mô tả gặp ở tôm sú nuôi ở Malaysia (Wang et al. 2000). Các ao nuôi thâm canh thường xuất hiện bệnh đốm trắng, nhưng test PCR bệnh WSSV âm tính.</p>\r\n', '<p>\r\n	Tôm sinh trưởng bình thường không có hiện tượng tôm chết. Tôm bệnh có các đốm trắng mờ đục nhìn thấy trên vỏ khắp cơ thể, khi bóc vỏ ra nhìn rõ hơn. Đốm trắng hình tròn nhỏ hơn đốm trắng của bệnh virus (WSSV). Soi mẫu tươi dưới kính hiển vi đốm trắng có dạng lan tỏa hình địa y ở giữa rỗng (có hiện tượng ăn mòn) khác với đốm trắng do virus có đốm đen (melanin) ở giữa. Các đốm trắng thường chỉ ở phía ngoài lớp biểu bì và tổ chức liên kết, ít nguy hiểm với tổ chức phía trong. Các đốm trắng này có thể mất khi tôm lột vỏ (Wang et al. 2000).</p>\r\n<p>\r\n	<img alt="vo tom benh vi khuan dom trang" src="/upload/images/desease/benh-dom-trang/vo-tom-benh-vi-khuan-dom-trang.png" style="width: 500px; height: 200px;" /><br />\r\n	<em>Đốm trắng trên vỏ đầu ngực của tôm nhiễm bệnh BWSS (theo Wang et al. 2000).</em></p>\r\n<p>\r\n	<img alt="" src="/upload/images/desease/benh-dom-trang/dom-trang-do-vi-khuan.jpg" style="width: 500px; height: 337px;" /><br />\r\n	<img alt="" src="/upload/images/desease/benh-dom-trang/vo-tom-benh-vi-khuan-dom-trang-2.png" style="width: 500px; height: 342px;" /><br />\r\n	<em>Đốm trắng trên vỏ tôm bị bệnh thấy rõ hiện tượng ăn mòn (-&gt;) hoặc lỗ rỗng ở giữa (=&gt;); mẫu soi tươi - thu Hải Phòng 7/2004 (theo Bùi Quang Tề)</em></p>\r\n', '<p>\r\n	Kiểm soát mật độ vi khuẩn trong nước ao nuôi tôm. Thường xuyên thay nước ao nuôi. Xác định vi khuẩn Bacillus subtilis trong chế phẩm vi sinh hạn chế dùng cho ao nuôi tôm, ngăn chặn chúng có liên quan đến bệnh đốm trắng do vi khuẩn. Ao đã nhiễm bệnh đốm trắng do vi khuẩn dùng vôi nung (CaO) bón cho ao liều lượng 25ppm, để không làm tăng độ kiềm trong ao và tăng pH nhanh. Dùng một số khoáng vi lượng kích thích tôm lột vỏ sẽ giảm bớt các đốm trắng trên thân tôm.</p>\r\n', 'bệnh đốm trắng, đốm trắng, đốm do vi khuẩn, đốm vôi, bện đốm vôi', 'Theo Bùi Quang Tề', 1336613926, 1336614001, '', '', 6114, 0),
(29, 6, 2, 'Bệnh u nang biểu bì ở mang của cá', 'Epitheliocystic', '', '<p>\r\n	Qua hình dạng phóng đại của kính hiển vi điện tử một số tác giả đã cho rằng tác nhân gây bệnh u nang biểu bì ở mang cá là những loài của Rickettsia (Paperna và CTV, 1981) hoặc Chlamydia (Wolf, 1981). Trong một số ký chủ, hình thái cấu tạo của chúng giống với những loài của Clarmydia, tuy nhiên một số ít nó giống những loài khác. Trong 2 kết quả nghiên cứu (Turnbull, 1987 và Bradley và CTV, 1988) đã xác định được kháng nguyên của giống Chlamydia là Lipopolysaccharide. Do đó tác nhân gây bệnh này là một nhóm mới nằm trong bộ Chlamydiales. Sự ảnh hưởng trong ký chủ là các hình dạng của chúng ký sinh trên vật chủ, bao gồm có 5 dạng hình khác nhau của u nang biểu bì trên mang và da.</p>\r\n<p>\r\n	- Thể khởi đầu: là giai đoạn đầu (sớm) phát triển của Chlamydia, hình thành một số u nang. Các tế bào có thể dạng hình que không đều, tế bào chất trong suốt chứa nhiều Riboxome...kích thước của chúng đường kính 0,7-1,25 μm.</p>\r\n<p>\r\n	- Tế bào kéo dài: Dạng thứ 2 là các tế bào kéo dài có cấu trúc tương tự như dạng đầu, kích thước lớn nhất là chiều dài 7,5 μm, đường kính 0,3-0,6 μm.</p>\r\n<p>\r\n	- Tế bào hình ovan hoặc hình tròn: Những loài Chlamydia sp có tế bào thường xuyên hình tròn hoặc ovan, đường kính 0,3-1 μm.</p>\r\n<p>\r\n	- Tế bào nhỏ: Có đặc điểm hình thành không bào trong suốt trong tế bào chất, kích thước rất khác nhau giữa các loài cá khác nhau: Cá chép kích thước là 0,5-0,7 x 0,3-0,5 μm hoặc 0,9-1,3 x 0,5-0,7 μm.</p>\r\n<p>\r\n	- Tế bào đầu và đuôi: Chúng hình thành tất cả trên các đầu của biểu bì một u nang hình que (0,3 x 0,4 àm) có chứa nhân đậm đặc và đuôi biểu bì (từ 0,3 àm chiều dài) được phìng rộng hình tròn ở phía cuối (đường kính 0,125 μm).</p>\r\n<p>\r\n	- Chlamydia sp, gây bệnh u nang gram âm.</p>\r\n', '<p>\r\n	Bệnh u nang biểu bì xuất hiện rộng khắp thế giới: Bắc Mỹ, Đông Nam Châu á, Trung Đông, Châu Âu, Nam Phi. Bệnh xuất hiện ở tất cả các giai đoạn phát triển của cá từ cá giống đến cá trưởng thành: ở 11 họ cá: Centrarchidae (họ cá mặt trăng); Chaetodontidae; Cichlidae; Cyprinidae (họ cá chép); Hippoglossidae; Ictaluridae; Moronidae; Mullidae; Salmonidae; Sparidae và<br />\r\n	Zanclidae. Bệnh có thể xuất hiện khi điều kiện môi trường thay đổi lớn gây sốc cho cá.</p>\r\n', '<p>\r\n	Bệnh thường xuyên xuất hiện ở mang và cũng có gặp ở da những u nang của bệnh (Hoffman và CTV, 1969). Các u nang mới&nbsp; xuất hiện màu trắng hoặc màu vàng... Bệnh làm nguy hiểm cho mang, hình thành nhiều dịch nhờn trên mang ngăn cản sự hô hấp của cá</p>\r\n', '<p>\r\n	Chưa nghiên cứu đầy đủ phương pháp phòng trị bệnh này. Một số tác giả (Paperna và CTV, 1978, Hoffman và CTV, 1969) có sử dụng một số kháng sinh để trị bệnh.</p>\r\n', 'u nang biểu bì, bệnh mang cá, Chlamydia, Rickettsia', '', 1338281811, 1338281811, '', '', 1401, 0),
(30, 6, 1, 'Bệnh run chân do Rickettsia ở cua.', '', '', '<p>\r\n	Giống Rickettsia gây bệnh run chân ở cua. Hình cầu, đường kính 0,22-0,35 àm, ký sinh nội bào. Giống Rickettsia ký sinh trong cơ và mô liên kết và xâm nhập đến các mô bằng các tế bào máu của cua. Ngoài ra một số trường còn tìm thấy thể virus và vi bào tử.</p>\r\n', '<p>\r\n	Bệnh phân bố ở một số loài cua biển và cua nước ngọt. Tỷ lệ chết khá cao ở cua xanh (Paralithodes platypus) (Johnson, 1984), cua hoàng đế (Lithodes aequispina) (Meyers và CTV, 1990) và cua- Carcinus mediterraneus (Bonami &amp; Pappalardo, 1980). Cua nước ngọt (Eriocheir sinensis) ở một số tỉnh phía Nam Trung Quốc, tỷ lệ nhiễm bệnh trong các ao nuôi 34,3% và bệnh có thể gây chết từ 30-90% (theo Wen Wang, Zhifeng Gu, 2002). Bệnh xuất hiện vào mùa ấm, nhiệt độ từ 190- 280C.</p>\r\n<p>\r\n	Ở Việt nam chưa đi sâu nghiên cứu bệnh Rickettsia trong cua.</p>\r\n', '<p>\r\n	Rickettsia ký sinh trong các mô liên kết của tim, chân bò và ruột, huyết tương. Làm cho cua kém ăn, hoạt động yếu. Bệnh nặng chân bò run, nên còn gọi là “bệnh run chân”</p>\r\n', '<p>\r\n	Bệnh này còn ít báo cáo về kết quả phòng và trị bệnh, áp dụng biện pháp phòng tổng hợp.</p>\r\n', 'bênh run chân cua, bệnh cua, bệnh trên cua, Rickettsia, cua nước ngọt', '', 1338284024, 1339428006, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2006. <em>Bện học thủy sản</em>, Viện Nghiên Cứu Nuôi Trồng Thủy Sản 1.</li>\r\n</ol>\r\n', '', 1509, 0),
(31, 4, 6, 'Bệnh nhiễm trùng do vi khuẩn Aeromonas di động ở động vật thuỷ sản.', '', '', '<p>\r\n	Giống Aeromonas thuộc họ Aeromonadaceae, bộ Aeromonadales, lớp Gammaproteobacteria, ngành Proteobacteria. Trong giống Aeromonas có hai nhóm: Nhóm 1: Aeromonas không di động (A. salmonicida) thường gây bệnh ở nước lạnh.<br />\r\n	Nhóm 2: Là các loài Aeromonas di động, bao gồm A. hydrophyla, A. caviae, A. sobria. Đặc tính chung của ba loài vi khuẩn này là di động nhờ có 1 tiên mao. Vi khuẩn Gram âm dạng hình que ngắn, hai đầu tròn, kích thước 0,5 x 1,0-1,5 àm. Vi khuẩn yếm khí tuỳ tiện, Cytochrom oxidase dương tính, khử nitrate, không mẫn cảm với thuốc thử Vibriostat 0/129... Tỷ lệ Guanin + Cytozin trong ADN là 57 - 63 mol%.</p>\r\n<p>\r\n	Ba loài vi khuẩn Aeromonas di động có những đặc điểm khác nhau (xem bảng dưới). Sự hoại tử thử trên máu thỏ của hai loài vi khuẩn A. hydrophyla khác với A. sobria (Olivier và ctv, 1981). A. hydrophyla dung huyết trên thạch máu khi nuôi cấy ở nhiệt độ 10<sup>0</sup>C và 30<sup>0</sup>C nhưng A. sobria chỉ dung huyết ở 30<sup>0</sup> C . Các vi khuẩn aeromonas di động đều phân lập từ cá nước ngọt nhiễm bệnh, thường gặp nhất là loài A. hydrophyla. Ngoài ra có thể gặp vi khuẩn Gram âm Pseudomonas fluorescens hoặc Proteus rettgeri.</p>\r\n<p>\r\n	<img alt="vi khuan aeromonas hydrophila" src="/upload/images/desease/aeromonas/aeromonas-hydrophila.jpg" style="width: 460px; height: 243px;" /><br />\r\n	Vi khuẩn Aeromonas hydrophila có một tiên mao. Ảnh kính hiển vi điện tử (theo Bùi Quang Tề, 1998).</p>\r\n<p>\r\n	<strong>Một số đặc điểm của các loài vi khuẩn Aeromonas di động (Popoff, 1984).</strong></p>\r\n<table border="1" cellpadding="2" cellspacing="0" style="width: 500px;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">\r\n				Đặc điểm</td>\r\n			<td style="text-align: center;">\r\n				A. hydrophyla</td>\r\n			<td style="text-align: center;">\r\n				A. caviae</td>\r\n			<td style="text-align: center;">\r\n				A. sobria</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Di động</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Thuỷ phân ascculin</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Phát triển trong n-ớc KCN</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sử dụng: L. Histidine</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sử dụng: L. Arginine</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sử dụng: L. Arabinose</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Lên men Salixin</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Voges Proskaue</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sinh H<sub>2</sub>S từ Glucose</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sinh H<sub>2</sub>S từ Cysteine</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	- Bệnh nhiễm trùng do nhóm vi khuẩn Aeromonas spp di động thường gặp ở nhiều loài động vật thuỷ sản nước ngọt. ở Việt Nam các loài cá nuôi lồng, bè và nuôi ao nước ngọt thường gặp bệnh đốm đỏ như: trắm cỏ, cá trôi, cá chép, cá mè, cá ba sa, cá bống tượng, cá he nuôi bè, cá tai tượng, cá trê, cá nheo... Vi khuẩn có thể gây bệnh ở ba ba, cá sấu, bệnh đỏ chân ở ếch, đốm nâu ở tôm càng xanh. Tỷ lệ tử vong ở động vật thuỷ sản thường từ 30-70% riêng ở cá giống (ba ba, trê) có thể chết 100%.<br />\r\n	- Bệnh xuất hiện quanh năm nhưng thường tập trung vào mùa xuân và mùa thu ở miền Bắc, ở miền Nam bệnh phát nhiều vào mùa mưa.<br />\r\n	- Đông Nam á: Thái Lan gây bệnh ở cá trê, Indonesia-cá chép bị bệnh, cá trê bị bệnh.</p>\r\n', '<p>\r\n	Bệnh nhiễm trùng ở động vật thuỷ sản thường biểu hiện ở các dạng khác nhau:<br />\r\n	- Hoại tử da và cơ: Đốm đỏ xuất huyết.<br />\r\n	- Vây bị phá huỷ: Gốc vây xuất huyết, tia rách nát và cụt dần.<br />\r\n	- Vẩy dựng (rộp) và bong ra, da xuất huyết.<br />\r\n	- Xoang bụng sưng to, các cơ quan nội tạng bị xuất huyết và viêm nhũn (dịch hoá), ruột viêm và chứa đầy hơi.</p>\r\n<p>\r\n	Đối với từng loài động vật thuỷ sản có các dấu hiệu bệnh lý cụ thể như sau:</p>\r\n<p>\r\n	Dấu hiệu đầu tiên là cá kém ăn hoặc bỏ ăn, nổi lờ đờ trên tầng mặt. Da cá thường đổi màu tối không có ánh bạc, cá mất nhớt, khô ráp. Xuất hiện các đốm xuất huyết màu đỏ trên thân, các gốc vây, quanh miệng, râu xuất huyết hoặc bạc trắng. Xuất hiện các vết loét ăn sâu vào cơ, có mùi hôi thối, trên vết loét thường có nấm và ký sinh trùng ký sinh. Mắt lồi đục, hậu môn viêm xuất huyết, bụng có thể chướng to, các vây xơ rách, tia vây cụt dần.</p>\r\n<p>\r\n	Giải phẫu nội tạng: Xoang bụng xuất huyết, mô mỡ cá ba sa xuất huyết nặng. Gan tái nhợt, mật sưng to, thận sưng, ruột, dạ dày, tuyến sinh dục, bóng hơi đều xuất huyết. Có trường hợp cá ba sa 2 đoạn ruột lồng vào nhau. Xoang bụng có chứa nhiều dịch nhờn mùi hôi thối.</p>\r\n<p>\r\n	- Cá trê giống bị bệnh thường tách đàn và “treo râu” đầu hướng lên trên vuông góc với mặt nước. Cá bống tượng có hiện tượng da mất hết nhớt gọi bệnh “tuột nhớt”.</p>\r\n<p>\r\n	- Ở ba ba xuất hiện các vết loét xuất huyết, không có hình dạng nhất định ở xung quanh và trên mai lưng; phần bụng; các chân có thể cụt hết móng. Bệnh nặng cơ thể ba ba mềm nhũn hoạt động chậm chạp, khi lật ngửa ba ba không tự lật sấp lại được. Ba ba ít ăn hoặc bỏ ăn, sau 1-2 tuần chúng bò lên cạn và chết, tỷ lệ chết tới 30-40%. Giải phẫu phổi, gan, thận có màu đen.</p>\r\n<p>\r\n	<img alt="cá trám cỏ bênh aeromonas" src="/upload/images/desease/aeromonas/ca-tram-co-benh-aeromonas.jpg" style="width: 450px; height: 101px;" /></p>\r\n<p>\r\n	<img alt="cá tra bệnh nhiễm khuẩn aeromonas" src="/upload/images/desease/aeromonas/ca-tra-benh-aeromonas.jpg" style="width: 450px; height: 149px;" /></p>\r\n<p>\r\n	<img alt="thủy sản bệnh nhiễm khuẩn aeromonas" src="/upload/images/desease/aeromonas/ca-benh-aeromonas.jpg" style="width: 600px; height: 446px;" /></p>\r\n<p>\r\n	A- Cá trắm cỏ bị bệnh đốm đỏ do vi khuẩn Aeromonas hydrophyla có các đốmđỏ, vẩy rụng, gốc vây xuất huyết;<br />\r\n	B- cá tra bị bệnh xuất huyết trên vây;<br />\r\n	C- cá he bị bệnh xuất trên các vây;<br />\r\n	D- cá rô phi bệnh viêm ruột;<br />\r\n	E- Cá trắm cỏ giải phẫu mang xuất huyết dính bùn,cơ quan nội tạng xuất huyết;<br />\r\n	F- Cá ba sa bị bệnh xuất huyết do vi khuẩn các cơ quan nội tạng: gan, thận, ruột mô mỡ xuất huyết, thịt xuất huyết;<br />\r\n	G- Cá rô phi bị bệnh viêm ruột do vi khuẩn bụng trướng to, hậu môn sưng loét đỏ, ruột xuất huyết chứa đầy hơi;<br />\r\n	H- Cá nheo bị bệnh viêm ruột do vi khuẩn.</p>\r\n<p>\r\n	<img alt="cá bống tượng bệnh nhiễm khuẩn aeromonas" src="/upload/images/desease/aeromonas/ca-bong-tuong-benh-aeromonas.jpg" style="width: 500px; height: 274px;" /></p>\r\n<p>\r\n	<img alt="ba ba bệnh nhiễm khuẩn aeromonsa" src="/upload/images/desease/aeromonas/ba-ba-benh-aeromonas.jpg" style="width: 600px; height: 225px;" /></p>\r\n<p>\r\n	A- cá bống tượng bị bệnh tuột nhớt;<br />\r\n	B- Ba ba bị bệnh viêm loét do vi khuẩn, có vết loét trên mai và dưới bụng, cụt móng;<br />\r\n	C- ba ba bệnh có phổi đen, trên gan có đốm đen.</p>\r\n<p>\r\n	<img alt="tôm càng xanh bênh nhiễm khuẩn aeromonas" src="/upload/images/desease/aeromonas/tom-cang-xanh-benh-aeromonas.jpg" style="width: 500px; height: 360px;" /></p>\r\n<p>\r\n	Tôm càng xanh bị bệnh đốm nâu: tôm bị đen mang, đốm đen trên vỏ.</p>\r\n', '<p>\r\n	- Biện pháp phòng bệnh quan trọng nhất không để cho động vật nuôi thuỷ sản bị sốc do môi trường thay đổi xấu: nhiệt độ, oxy hoà tan, nhiễm bẩn của nước. Môi trường nước đảm bảo tốt cho đời sống của động vật thuỷ sản.<br />\r\n	Đối với bè nuôi cá thường xuyên treo túi vôi, mùa xuất hiện bệnh 2 tuần treo một lần, mùa khác một tháng treo 1 lần. Vôi có tác dụng khử trùng và kiềm hoá môi trường nước. Lượng vôi tính trung bình 2 kg vôi nung/10m3. Bè lớn treo nhiều túi và bè nhỏ treo ít túi tập trung ở chỗ cho ăn và phía đầu nguồn nước chảy. Đối với các ao nuôi áp dụng tẩy dọn ao như phương pháp phòng tổng hợp. Cũng định kỳ mùa bệnh 2 tuần rắc xuống ao 1 lần, mùa khác rắc 1 tháng 1 lần, liều lượng trung bình 2 kg vôi nung/100 m3 nước. Ngoài ra, có thể bổ sung thêm lượng vitamin C cho vào thức ăn trước mùa bệnh hoặc dùng thuốc phối chế KN - 04 -12 của Viện I cho cá ăn phòng bệnh, cách dùng: xem mục thuốc KN-04-12.</p>\r\n<p>\r\n	- Trị bệnh: Có thể dùng một số kháng sinh, thảo mộc có tác dụng diệt khuẩn điều trị bệnh nhiễm khuẩn máu như sau:<br />\r\n	+ Cá giống dùng phương pháp tắm thời gian 1 giờ. Oxytetracyline nồng độ 20-50 ppm. Streptomycin nồng độ 20-50 ppm.<br />\r\n	+ Cá thịt dùng phương pháp cho ăn kháng sinh trộn với thức ăn tinh. Sulfamid liều dùng 150-200 mg/1 kg cá/ngày. Thuốc phối chế KN-04-12: liều dùng 2-4 g/1 kg cá/ngày.</p>\r\n<p>\r\n	Cho cá ăn liên tục từ 5-7 ngày. Riêng với kháng sinh từ ngày thứ 2 trở đi liều lượng giảm đi 1/2 so vớí ngày ban đầu.</p>\r\n', 'Aeromonas hydrophila, Aeromonas, vi khuẩn, nhiễm trùng hoại tử da, động vật thủy sản, thủy sản', '', 1339434646, 1339436752, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2006. <em>Bện học thủy sản</em>, Viện Nghiên Cứu Nuôi Trồng Thủy Sản 1.</li>\r\n</ol>\r\n', 'phongt94', 11986, 1),
(32, 8, 3, 'Bệnh &quot;Gạo&quot; trên cá tra', '', '', '<p>\r\n	Theo kết quả điều tra từ hộ nuôi cá tra thâm canh ở một số tỉnh ĐBSCL ( Hậu Giang, Vĩnh Long, Cần Thơ...) và kết quả của Khoa Thủy sản - Đại học Cần Thơ thì bệnh "gạo" thường xuất hiện trong các ao nuôi cá tra thương phẩm từ giai đoạn cá giống đến cá thịt, bệnh "gạo" xuất hiện quanh năm, không theo mùa vụ như một số nhóm ký sinh trùng khác. Kết quả phân tích bằng phương pháp soi tươi, nghiên cức mô bệnh học và sử dụng kỹ thuật PCR đã xác định các nang "gạo" xuất hiện trong cơ cá là do thích bào tử trùng Myxosporea và vi bào tử trùng Microspora. Bào tử của hai nhóm trùng này có&nbsp; kính thước&nbsp; và&nbsp; hình dạng khác nhau tùy theo&nbsp; loài, nhưng vòng đời và quá trình xâm nhiễm trực tiếp ký sinh trong&nbsp; cá tương tự nhau. Bào tử ngoài môi trường nước có thể chui qua da, mang hoặc theo đường tiêu hóa vào ký sinh trong các cơ quan nội tạng của cá như mang, dạ dày, ruột, gan... đặc biệt là vùng&nbsp; cơ là vị trí&nbsp; ký sinh ưa thích của chúng. Sau một thời gian ký sinh bào tử bắt đầu sinh sản tăng nhanh số lượng và tổ chức xung quanh vùng nhiễm bệnh bị kích thích và thoái hóa sinh ra một lớp màng bao lấy các bào tử và hình thành&nbsp; bào nang. Bào tử thành thục có thể ký sinh trong cơ từ giai đoạn cá giống đến cá thịt hoặc có thể phá vỡ bào nang chui ra môi trường nước và tiếp tục xâm nhiễm vào cá khác để hình thành một chu kỳ sống mới. Ngoài ra, bào tử có thể phát tán ra môi trường nước từ cá nhiễm \\"gạo\\" chết nằm dưới đáy ao.</p>\r\n', '<p>\r\n	Cá tra nuôi thâm canh ở Đồng Bằng Sông Cửu Long.</p>\r\n', '<p>\r\n	Kết quả nghiên cứu cũng cho thấy cá bệnh "gạo" không có dấu hiệu bệnh lý rõ ràng, chỉ thấy cá chết rải rác hàng ngày. Ở những mẫu cá nhiễm "gạo" nặng thường có dấu hiệu bệnh do vi khuẩn (gan thận mủ, xuất huyết, vàng da...)&nbsp; và vùng da bụng bị thủng lỗ nhỏ li ti hoặc có một số lỗ rất lớn. Tuy nhiên các cơ quan nội tạng bình thường và ở một số ít cá bệnh có dịch mật nhạt màu. Kích thước bàng nang dao động từ 0.5-3 mm tùy theo kích cỡ cá, đôi khi tạo thành những hốc to hơn 3mm chứa đầy dịch màu trắng sữa( dễ vỡ) ở cá có trọng lượng lớn hơn 500gram, các bào nang có thể được tìm thấy bằng&nbsp; mắt thường,.</p>\r\n<p>\r\n	Sự phát triển của bào tử trùng bên trong cơ cá phụ thuộc nhiều vào&nbsp; yếu tố môi trường. Trong đó nhiệt độ có tác động lớn nhất. Các&nbsp; nghiên cứu thực nghiệm cho thấy sự phát triển của bào tử xảy ra chậm ở nhiệt&nbsp; nước 16oC. Các bào tử có thể sống lâu trong nước và bùn đáy ao, nên khả năng cá nuôi&nbsp; bị nhiễm bào tử trùng rất cao. mặt khác, bào tử có vỏ bọc bên ngoài bằng chất kitin khá chắc chắn và nằm trong bào nang nên thuốc và hóa chất khó tiêu diệt được chúng. Một số nghiên cứu cho thấy bào nang của vi bào tử trùng có thể kháng lại một số chất khử trùng như chlorine, các thuốc diệt giun sán như Menbendazole hay Ivermectin... cũng không có hiệu quả diệt các bào nang của vi bào tử trùng. Vì vậy, chúng ta cần phải có phương pháp phòng bệnh nghiêm ngặt cho các ao nuôi, đặc biệt là xử lý triệt để những cá đã nhiễm bệnh nhằm hạn chế lây nhiễm cho các ao và vùng nuôi khác.</p>\r\n<p>\r\n	<img alt="bệnh gạo trên cá tra" src="/upload/images/benh-gao-ca-tra.jpg" style="width: 500px; height: 304px;" /></p>\r\n', '<p>\r\n	Hiện nay chưa có một loại thuốc và hóa chất nào đặc trị triệt để bệnh "gạo". Tuy nhiên, có thể áp dụng các nguyên tắc sau để phòng bệnh đạt hiệu quả tốt.</p>\r\n<p>\r\n	<strong>1. </strong>Cải tạo ao trước khi thả nuôi: Dùng vôi sống CaO (10-15 kg/100m2) rải xuống đáy ao và phơi nắng từ 3-7 ngày để diệt bào tử trùng chìm dước đáy ao. Trường hợp không thể rút cạn nước trong ao nuôi thì xử lý bằng vôi CaO với liều cao từ 1,5-2,0 kg/m2.</p>\r\n<p>\r\n	<strong>2. </strong>Kiểm tra cá giống (ít nhất 30 mẫu cá) trước khi thả nuôi, nếu phát hiện đàn cá đã nhiễm "gạo"&nbsp; thì loại bỏ đàn cá.</p>\r\n<p>\r\n	<strong>3.</strong> Xử lý nước trong quá trình nuôi: Dùng 1-1,5 kg muối ăn và 2 kg vôi/100m3 nước, hoặc định kỳ xử lý nước ao trong thời gian 15-20 ngày/lần bằng các chất khử trùng như BKC, PMP và các chất khử trùng khác theo liều chỉ dẫn của nhà Sản xuất. Tốt nhất nên có hệ thống lắng,&nbsp; lọc và xử lý nước cấp để hạn chế mầm bệnh xâm nhiễm vào ao nuôi.</p>\r\n<p>\r\n	<strong>4.</strong> Xử lý đáy ao trong quá trình nuôi: Định kỳ hút bùn đáy ao 2 tháng /lần đối với cá nhỏ hơn 300g và 1 tháng / lần đối với cá lớn hơn 300g. Sau khi hút bùn xử lý đáy ao bằng muối với&nbsp; liều luợng 5-7kg/100m2 kết hợp với các&nbsp; loại hóa chất khử trùng.</p>\r\n<p>\r\n	<strong>5.</strong> Kiểm tra cá trong quá trình nuôi: Định kỳ mỗi tháng mổ khám 30 mẫu cá /lần, đối với cá&nbsp; lớn&nbsp; thì kiểm tra từ 10-15 mẫu/ lần. Nếu phát&nbsp; hiện có cá bị nhiễm "gạo" trong ao thì cần phải cách&nbsp; li đàn cá, khử trùng dụnh cụ nuôi, vớt hết cá bệnh ra khỏi ao và xử lý bằng cách nấu chín hay trộn với vôi sống và chôn dưới đất. Đặc biệt là tránh gây sốc cho cá trong giai đoạn nhiễm "gạo" và không vứt xác&nbsp; cá chết ra nguồn nước vì bào tử sẽ phóng thích và lây nhiễm ra các ao khác.</p>\r\n<p>\r\n	Việc trị bệnh "gạo" trên cá tra hiện nay chủ yếu được khuyến cáo từ các nhà&nbsp; sản xuất thuốc hoặc do kinh nghiệm của người nuôi. Tuy nhiên, các phương pháp trị bệnh này chưa có cơ sở khoa học vững chắc, cho đến nay các nhà khoa học chưa tìm được loại thuốc nào đặc trị bệnh "gạo" trên các loài cá tra nuôi.</p>\r\n', 'bệnh gạo, bệnh trên cá tra, Myxosporea, Microspora', 'Ths. Nguyễn Thị Thu Hằng - Khoa Thủy sản - Đại học Cần Thơ.', 1342840940, 1342841385, '<ol>\r\n	<li>\r\n		Tài liệu kỹ thuật UV-Việt Nam</li>\r\n</ol>\r\n', 'phongt94', 2732, 0),
(33, 4, 6, 'Bệnh do vi khuẩn vibrio ở động vật thủy sản', 'Vibrio spp', '', '<p>\r\n	Giống Vibrio thuộc họ Vibrionaceae, bộ Vibrionales, lớp Gammaproteobacteria, ngành Proteobacteria.. Đặc điểm chung các loài vi khuẩn thuộc giống Vibrio: Gram âm, hình que thẳng hoặc hơi uốn cong, kích thước 0,3-0,5 x 1,4-2,6 μm. Chúng không hình thành bào tử và chuyển động nhờ một tiên mao hoặc nhiều tiên mao mảnh.</p>\r\n<p>\r\n	Tất cả chúng đều yếm khí tuỳ tiện và hầu hết là oxy hoá và lên men trong môi trường O/F Glucose. Thiosulphate citrate bile salt agar TCBS là môi trường chọn lọc của Vibrio. Hầu hết các loài đều phát triển trong môi trường nước biển cơ bản, Na+ kích thích cho sự phát triển của tất cả các loài Vibrio và nhiều loài là nhu cầu tuyệt đối, chúng không phát triển trong môi trường không muối (NaCl), không sinh H2S. Chúng mẫn cảm với Vibriostat 2,4 diamino-6,7 diisopropyl pteridine phosphate (0/129). Cơ bản chúng đều sống trong môi trường nước, đặc biệt là nước biển và cửa sông, liên quan đến các động vật biển. một số loài là tác nhân gây bệnh cho người và động vật biển. Tương tự Aeromonas trong nước ngọt thì<br />\r\n	Vibrio ở trong nước biển. Tỷ lệ Guanin-G + Cytozin-C trong ADN là 38-51 mol%.</p>\r\n<p>\r\n	Những loài gây bệnh cho động vật thuỷ sản là: V. alginolyticus; V. anguillarum; V. ordalii; V. salmonicida, V. parahaemolyticus, V. harvey, V. vulnificus....</p>\r\n<p>\r\n	Đối với cá Vibrio spp gây bệnh nhiễm khuẩn máu là chủ yếu. Đối với tôm Vibrio spp gây bệnh phát sáng, đỏ dọc thân, ăn mòn vỏ kitin. V. anguillarum; V. vulnificus gây bệnh nhiễm khuẩn máu cá trình. V. anguillarum được Hofer 1904 mô tả lần đầu. V. salmonicida gây bệnh ở vùng nước lạnh. V. parahaemolyticus gây bệnh phát sáng ở ấu trùng tôm sú. V. alginolyticus gây bệnh đỏ dọc thân ấu trùng tôm sú. V. parahaemolyticus, V. harvey, V. vulnificus, V. anguillarum... gây bệnh đỏ thân ở tôm sú thịt, ăn mòn vỏ ở giáp xác, gây bệnh máu vón cục ở cua, gây bệnh ấu trùng nhuyễn thể.<br />\r\n	&nbsp;</p>\r\n', '<p>\r\n	<em>- Vibrio</em> spp thường gây bệnh ở động vật thuỷ sản nước mặn và nước ngọt: cá, giáp xác, nhuyễn thể... Những vi khuẩn này thường là tác nhân cơ hội, khi động vật thuỷ sản sốc do môi trường biến đổi xấu hoặc bị nhiễm các bệnh khác như virus, nấm, ký sinh trùng. Động vật thuỷ sản yếu không có sức đề kháng, các loài vi khuẩn <em>Vibrio</em> spp cơ hội gây bệnh nặng làm động vật thuỷ sản chết rải rác tới hàng loạt.</p>\r\n<p>\r\n	- Mùa vụ xuất hiện bệnh tuỳ theo loài và địa điểm nuôi. Theo nghiên cứu của các tác giả nước ngoài và Việt Nam <em>Vibrio</em> spp tìm thấy phổ biến ở trong nước biển và ven bờ, trong nước bể ương tảo, bể ương Artemia, trong bể ương ấu<br />\r\n	trùng.</p>\r\n<p>\r\n	- Trong bể ương lượng ấu trùng Vibrio tăng theo thời gian nuôi, tầng đáy cao hơn tầng mặt, do đó khi xi phông tầng đáy có tác dụng giảm mật độ Vibrio trong bể ương.</p>\r\n<p>\r\n	- Bệnh ở cá nuôi lồng như biển, đầm nước lợ, dấu hiệu bệnh lý giống bệnh nhiễm khuẩnmáu do vi khuẩn <em>Aeromonas</em> spp di động.</p>\r\n<p>\r\n	Một số bênh ở động vật thủy sản do Vibrio spp gây ra:</p>\r\n<table border="0" cellpadding="3" cellspacing="1" class="bang" style="width: 500px;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>STT</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Tên Bệnh</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Giai Đoạn</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Vibrio Gây Bệnh</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Tác Hại</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1</td>\r\n			<td style="text-align: center;">\r\n				Bệnh phát sáng</td>\r\n			<td style="text-align: center;">\r\n				Ấu trùng, giống</td>\r\n			<td style="text-align: center;">\r\n				V.parahaemolyticus<br />\r\n				V.harveyi</td>\r\n			<td style="text-align: center;">\r\n				gây chết hàng loạt</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2</td>\r\n			<td style="text-align: center;">\r\n				Bệnh đỏ dọc thân</td>\r\n			<td style="text-align: center;">\r\n				Ấu trùng, giống</td>\r\n			<td style="text-align: center;">\r\n				V.alginolyticus</td>\r\n			<td style="text-align: center;">\r\n				gây chết rải rác</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3</td>\r\n			<td style="text-align: center;">\r\n				Bệnh đỏ thân</td>\r\n			<td style="text-align: center;">\r\n				Tôm thịt</td>\r\n			<td style="text-align: center;">\r\n				Vibrio spp.</td>\r\n			<td style="text-align: center;">\r\n				gây chết rải rác</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4</td>\r\n			<td style="text-align: center;">\r\n				Bệnh vỏ hay ăn mòn kitin, đen mang</td>\r\n			<td style="text-align: center;">\r\n				Ở các giai đoạn tôm,cua</td>\r\n			<td style="text-align: center;">\r\n				Vibrio spp<br />\r\n				Pseudomonas spp.<br />\r\n				Proteus sp</td>\r\n			<td style="text-align: center;">\r\n				gây chết gải rác hàng loạt</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5</td>\r\n			<td style="text-align: center;">\r\n				Nhiễm khuẩn ở cá</td>\r\n			<td style="text-align: center;">\r\n				Cá nuôi đầm, lông</td>\r\n			<td style="text-align: center;">\r\n				Vibrio spp</td>\r\n			<td style="text-align: center;">\r\n				gây chế rải rác</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	- Tôm ở trạng thái không bình thường: Nổi lên mặt ao, dạt bờ, kéo đàn bơi lòng vòng.</p>\r\n<p>\r\n	- Tôm, cua trạng thái hôn mê, lờ đờ, kém ăn hoặc bỏ ăn.</p>\r\n<p>\r\n	- Tôm có sự biến đổi màu đỏ hay màu xanh. Tôm cua vỏ bị mềm và xuất hiện các vết thương hoại tử, ăn mòn trên vỏ và các phần phụ.</p>\r\n<p>\r\n	<img alt="" src="/upload/images/benh-vibrio-tom-su.jpg" style="width: 660px; height: 567px;" /></p>\r\n<p>\r\n	<img alt="tôm sú bệnh vibrio" src="/upload/images/benh-vibrio-tom-su-2.jpg" style="width: 510px; height: 366px;" /><br />\r\n	Tôm sú bị nhiễm khuẩn Vibrio spp: A- ấu trùng tôm bị bệnh đỏ dọc thân; B- Tôm sú bị bệnh đỏ thân; C- Tôm sú bị bệnh đỏ thân (con thứ 3,4); D- Tôm sú bị bệnh đỏ chân; E- đuôi tôm sú bị ăn mòn; F- đuôi tôm sú bị hoại tử; G- đuôi tôm sú bị đỏ; H- đuôi tôm sú bị phồng; I- tôm sú bị bệnh các phần phụ (râu, chân bò, chân bơi, đuôi) ăn mòn cụt dần.</p>\r\n<p>\r\n	- Ấu trùng tôm và tôm giống có hiện tượng phát sáng khi nhiễm V. parahaemolyticus và V. harveyi.</p>\r\n<p>\r\n	<img alt="tôm sú bệnh phát sáng" src="/upload/images/tom-su-benh-phat-sang.jpg" style="width: 336px; height: 304px;" /><br />\r\n	Tôm sú bị bệnh phát sáng</p>\r\n<p>\r\n	- Xuất hiện các điểm đỏ ở gốc râu, phần đầu ngực, thân, các phần phụ của ấu trùng giáp xác khi nhiễm V. alginolyticus.</p>\r\n<p>\r\n	- Ấu trùng bào ngư khi nhiễm Vibrio spp chuyển từ màu hồng sang màu đỏ.</p>\r\n<p>\r\n	- Cua nhiễm Vibrio spp sau 24 - 48 giờ trong máu có hiện tượng vón cục (kết tủa) gồm các tế bào máu và vi khuẩn.</p>\r\n<p>\r\n	- Bệnh ở cá nuôi lồng như biển, đầm nước lợ, dấu hiệu bệnh lý giống bệnh nhiễm khuẩn máu do vi khuẩn Aeromonas spp di động</p>\r\n<p>\r\n	<img alt="cá mú bệnh vibrio" src="/upload/images/ca-mu-benh-do-vibrio.jpg" style="width: 544px; height: 505px;" /><br />\r\n	A- Cá mú bị bệnh nhiễm trùng do vi khuẩn trên thân có các đốm xuất huyết, mắt mù; B- Cá song nhiễm bệnh vi khuẩn trên gan có các đốm trắng.</p>\r\n', '<p>\r\n	<strong>Phòng bệnh:</strong> Các trại sản xuất tôm cần thực hiện một số biện pháp sau:<br />\r\n	+ Lọc nước qua tầng lọc cát và xử lý tia cực tím.<br />\r\n	+ Xử lý tôm bố mẹ bằng Formalin 20-25 ppm thời gian 30-60 phút.<br />\r\n	+ Xử lý tảo bằng Oxytetracyline 30-50 ppm thời gian 1-2 phút.<br />\r\n	+ Xử lý Artemia bằng Chlorin 10-15 ppm trong 01 giờ ở nước ngọt, vớt ra rửa sạch rồi mới cho ấp.<br />\r\n	+ Có thể phun vào môi trường ương EDTA 2-5 ppm tác dụng kìm hãm phát triểncủa vi khuẩn.<br />\r\n	+ Thường xuyên xi phông đáy để giảm lượng vi khuẩn ở tầng đáy bể ương.<br />\r\n	+ Giai đoạn Zoea vμ Mysis phòng bệnh bằng Oxtetracyline 1-2 ppm.<br />\r\n	+ Trường hợp bị bệnh nặng phải huỷ đợt sản xuất và xử lý bằng Chlorin 200-250 ppm trong một giờ mới xả ra ngoài.</p>\r\n<p>\r\n	<strong>Trị bệnh:</strong> Dùng một số kháng sinh trị bệnh cho ấu trùng tôm.<br />\r\n	+ Oxytetracyline + Bacitracin (tỷ lệ 1:1) nồng độ 1-3 ppm.<br />\r\n	+ Erytromycin + Rifamycin (tỷ lệ 5:3) nồng độ 1-2 ppm.<br />\r\n	+ Erytromycin + Bacitracin (tỷ lệ 1:1) nồng độ 1-3 ppm.<br />\r\n	+ Thuốc phun trực tiếp trong bể sau 12 giờ thay nước, xử lý 3 ngày liên tục.<br />\r\n	* Dùng một số kháng sinh trộn với thức ăn tinh để trị bệnh tôm thịt.</p>\r\n', 'bệnh do vibrio, bệnh vibrio trên tôm, bệnh phát sáng, trị bệnh vibrio spp, nhiễm khuẩn vibrio spp', '', 1343466308, 1343466533, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2006. Bệnh học thủy sản. Viện nghiên cứu NTTS1</li>\r\n</ol>\r\n', 'phongt94', 14812, 0),
(34, 4, 2, 'Bệnh nổ mắt trên cá điêu hồng', 'Steptococcus', '', '<p>\r\n	Do vi khuẩn Steptococcus gây ra. Vi khuẩn này phát triển mạnh ở môi trường có nhiệt độ nước 20 – 30oC.</p>\r\n', '', '<p>\r\n	&nbsp;&nbsp;&nbsp; Cá có dấu hiệu hôn mê, mất phương hướng bơi lội. Vùng mắt bị thương tổn như viêm mắt, lồi mắt, chảy máu mắt</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Xuất hiện các vết lở loét xuất huyết không lành ở quanh mắt, các gốc vây hoặc những vùng da hơi đỏ xung quanh hậu môn, sinh dục của cá</p>\r\n<p>\r\n	<img alt="" src="/upload/images/Hinh%201%20-%20Benh%20loi%20mat.JPG" /><br />\r\n	&nbsp;&nbsp;&nbsp; Có dịch chất lỏng trong bụng cá chảy ra hậu môn (dấu hiệu của dịch bệnh ở thời kỳ cấp tính)</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Cá bỏ ăn, kiểm tra không thấy thức ăn trong dạ dày hoặc ruột của cá bị bệnh, quan sát thấy túi mật to</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Gan, thận, lá lách, tim, ống ruột bị xuất huyết. Lá lách và thận bị trương lên và sưng nhẹ</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Khi cá bị nhiễm bệnh nặng kiểm tra có sự dính nhau của các cơ quan nội tạng với màng trong khoang bụng của cá, quan sát thấy có các tơ huyết trong màng ở khoang bụng</p>\r\n', '<p>\r\n	&nbsp;&nbsp;&nbsp; Thực hiện tốt công việc chuẩn bị ao, lồng bè nuôi, đặc biệt là khâu xử lý đáy ao và xử lý nước. Dùng Virkon® A để xử lý nước với liều 0,7 kg/1.000 m3 nước ao hoặc cho vào các túi vải, treo xung quanh lồng bè nuôi để tiêu diệt vi khuẩn gây bệnh</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Trước khi thả cá nuôi nên tắm qua nước muối 2 – 3% trong thời gian 5 – 15 phút. Nên thả nuôi với mật độ vừa phải</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; Trong quá trình nuôi cần theo dõi thường xuyên các yếu tố môi trường nước, nếu được duy trì hàm lượng oxy hoà tan ở mức cao bằng máy quạt nước. Trộn cho ăn liên tục 5 g Aqua C® Fish + 3 g Grow Fish trong 1 kg thức ăn, định kỳ từ 7 – 10 ngày/tháng để tăng cường sức đề kháng cho cá khi nhiệt độ thay đổi</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; Khi dịch bệnh xảy ra nên cắt giảm một phần hoặc toàn bộ lượng thức ăn cho cá. Giảm mật độ nuôi sẽ giảm bớt căng thẳng và mức độ lây lan bệnh đến cá. Lập tức vớt bỏ số cá chết ra khỏi ao, lồng bè nuôi</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; Điều trị ngay bằng kháng sinh Osamet® Fish (hoặc Fortoca®) liều 5 – 10 gram + Aqua C® Fish liều 5 gram trong 1 kg thức ăn (hoặc cho 2 – 3 tấn cá nuôi), cho ăn liên tục trong 7 – 10 ngày</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp; Điều trị bệnh giai đọan sớm hiệu quả điều trị sẽ rất cao.</p>\r\n', 'bệnh nổ mắt, bệnh phù mắt, bệnh lồi mắt, bệnh cá điêu hồng', 'http://agriviet.com', 1351766778, 1351767304, '<p>\r\n	http://agriviet.com/nd/3097-benh-no-mat-va-thoi-mang-o-ca-dieu-hong/</p>\r\n', 'daotrunghieu87', 2306, 0),
(35, 4, 3, 'Bệnh đốm trắng (hoại tử cơ quan nội tạng) cá da trơn', 'Edwardsiellosi', '', '<p>\r\n	<em>Edwardsiella</em> thuộc họ <em>Enterobacteriaceae</em>, bộ <em>Enterobacteriales</em>, lớp<em> Gammaproteobacteria</em>, ngành <em>Proteobacteria</em>. Chúng có đặc điểm gram âm, hình que mảnh, kích thước 1 x 2-3 μm, không sinh bào tử, chuyển động nhờ vành tiêm mao. Yếm khí tuỳ tiện, catalase dương, Cytocrom oxidase âm oxy hoá âm và lên men trong môi trường O/F glucose. Thành phần Guanin và Cytozin trong ADN là 55-59 mol%. Thường gặp hai loài: <em>E. tarda</em> và <em>E. ictaluri.</em> (xem bảng).</p>\r\n<p>\r\n	<em>E. tarda</em> là tác nhân gây bệnh nhiễm khuẩn ở cá nước ấm, đặc biệt là cá không vẩy. <em>E. ictaluri</em> gây bệnh nhiễm khuẩn trong các cơ quan nôi tạng gan, tụy, thận của cá không vẩy. Loài <em>E. tarda</em> hầu hết không lên men các loại đường nhưng có một vài chủng lên men đường khá nhanh.</p>\r\n<p>\r\n	<strong>Bảng: Những đặc tính sinh lý và sinh hoá khác nhau giữa 2 loài <em>Edwardsiella tarda</em> và <em>E. ictaluri</em></strong> (theo Wyatt và ctv, 1979; Farmer và Mc Whorter, 1984; Waltman và ctv, 1986; Plumb và Vinitnantharat, 1989)</p>\r\n<table border="0" cellpadding="1" cellspacing="1" class="bang" style="width: 500px;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">\r\n				ĐẶC ĐIỂM</td>\r\n			<td style="text-align: center;">\r\n				<em>Edwardsiella tarda</em></td>\r\n			<td style="text-align: center;">\r\n				<em>Edwardsiella ictaluri</em></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Di động ở 25<sup>0</sup>C</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sinh Indole</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Methyl red</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Citrate simmons</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Citrate christensens</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sinh H<sub>2</sub>S trong triple suga iron</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sinh H2S trong pepton iron agar</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Giới hạn nồng độ muối 1,5%</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Giới hạn nồng độ muối 3%</td>\r\n			<td style="text-align: center;">\r\n				+</td>\r\n			<td style="text-align: center;">\r\n				-</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Tỷ lệ G - C của ADN mol%</td>\r\n			<td style="text-align: center;">\r\n				55-58</td>\r\n			<td style="text-align: center;">\r\n				53</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Vi khuẩn thường gây ở động vật máu lạnh: Rắn, cá sấu, ba ba, cá...và những động vật thủy sản khác. Việt Nam đã phân lập được <em>E. tarda </em>từ cá trê giống; <em>E. ictaluri</em> từ cá tra, cá ba sa, cá nheo giống và cá thịt. Bệnh gây thiệt hại trong các ao nuôi cá tra hương (cỡ từ 4-6cm) đến 5-6 tháng tuổi, tỷ lệ tử vong của cá từ 60-70%, có trường hợp tới 100% (theo Bùi Quang Tề, 2003). Bệnh xuất hiện nhiều nhất vào mùa xuân, mùa thu và trong ao nuôi mật độ cao, nuôi cá lồng bè.</p>\r\n<p>\r\n	<strong>Bảng: Các vi khuẩn <em>Edwardsiella</em> tarda và <em>E. ictaluri </em>gây bệnh ở các loài cá</strong></p>\r\n<table border="0" cellpadding="1" cellspacing="1" class="bang" height="203" width="580">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="2" rowspan="1" style="text-align: center;">\r\n				<strong>Loài cá</strong></td>\r\n			<td colspan="1" rowspan="2" style="text-align: center;">\r\n				<strong>Vi khuẩn</strong></td>\r\n			<td colspan="1" rowspan="2" style="text-align: center;">\r\n				<strong>Tác giả</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">\r\n				<strong>Tên khoa học</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Tên địa phương</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Ictalurus punctata</td>\r\n			<td>\r\n				Cá trê sông</td>\r\n			<td style="text-align: center;">\r\n				Edwardsiella tarda</td>\r\n			<td>\r\n				Meyer và Bullock, 1973</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Oncorhynchus ishawytscha</td>\r\n			<td>\r\n				Cá hồi</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Amandi và ctv, 1982</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Cyprinus carpio</td>\r\n			<td>\r\n				Cá chép</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Sae - Oui và ctv, 1982</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Evynnis japonicus</td>\r\n			<td>\r\n				Cá vền biển</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Kusuda và ctv, 1977</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Paralichthys olivaceus</td>\r\n			<td>\r\n				Cá bơn Nhật Bản</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Nakatsugawa, 1983</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Anguilla japonica</td>\r\n			<td>\r\n				Cá chình Nhật Bản</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Egusa, 1976</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Micropterus salmoides</td>\r\n			<td>\r\n				Cá chình Nhật Bản</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				White và ctv, 1973</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Mugil cephalus</td>\r\n			<td>\r\n				Cá đối mục</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Kusuda và ctv, 1976</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Chryophrys major</td>\r\n			<td>\r\n				Cá vền đỏ biển</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Yasunaga và ctv, 1982</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Morone saxatilis</td>\r\n			<td>\r\n				Cá vền đỏ biển</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Herman và Bullock, 1986</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Oreochromis niloticus</td>\r\n			<td>\r\n				Cá rô phi vằn</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Miyashito, 1984</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Serida gaingu eradiata</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Yasunaga và ctv, 1982</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Clarias juscus</td>\r\n			<td>\r\n				Cá trê đen</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Bùi Quang Tề và ctv, 1993</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				C. macrocephalus</td>\r\n			<td>\r\n				Cá trê vàng</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Bùi Quang Tề và ctv, 1993</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Pangasius hypophthalmus</td>\r\n			<td>\r\n				Cá tra</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Bùi Quang Tề, 2003</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Ictalurus punctata</td>\r\n			<td>\r\n				Cá trê sông</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Meyer và ctv, 1973</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				I. nebulosus</td>\r\n			<td>\r\n				Cá trê sông nâu</td>\r\n			<td style="text-align: center;">\r\n				E. ictaluri</td>\r\n			<td>\r\n				Hawke, 1976</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				I. furcatus</td>\r\n			<td>\r\n				Cá trê sông xanh</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Plumb và Sanchez, 1983</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Danio devario</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Waltman và ctv, 985</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Eigemannia virens</td>\r\n			<td>\r\n				Cá dao xanh</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Kent và Lyons, 1982</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Clarias batrachus</td>\r\n			<td>\r\n				Cá trê trắng</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Kasornchandra và ctv, 1987</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Ictalurus catus</td>\r\n			<td>\r\n				Cá trê sông trắng</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Plamb vμ Sanchez, 1983</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Pangasianodon<br />\r\n				hypophthalmus</td>\r\n			<td>\r\n				Cá tra</td>\r\n			<td style="text-align: center;">\r\n				nt</td>\r\n			<td>\r\n				Crumlish, vμ ctv, 2001<br />\r\n				Bùi Quang Tề, 2003</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Silurus asotus</td>\r\n			<td>\r\n				Cá nheo</td>\r\n			<td style="text-align: center;">\r\n				Edwardsiella sp</td>\r\n			<td>\r\n				Bùi Quang Tề, 2005</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Cá bị bệnh kém ăn hoặc bỏ ăn, gầy yếu, bụng thường chướng to, xung quanh miệng có các đám xuất huyết, gốc vây xuất huyết, mắt lồi.</p>\r\n<p>\r\n	Giải phẫu cơ quan nội tạng gan, lá lạch, thận bị hoại tử thành những đốm màu trắng đục đường kính 0,5-2,5mm, còn gọi là “bệnh đốm trắng”.</p>\r\n<p style="text-align: center;">\r\n	<img alt="bệnh đốm trắng nội tạng" src="/upload/images/dom-trang-ca-tra.png" style="width: 478px; height: 561px;" /><br />\r\n	<em><span style="color:#0000cd;">C- da trơn bị bệnh hoại tử cơ quan nội tạng: A- cá tra giống bụng chướng to; B- cá nheo bụng chướng to; C,E- trên gan cá tra giống có các đốm trắng; D- thận cá tra giống có nhiều đốm trắng.</span></em></p>\r\n', '<p>\r\n	&nbsp;- Biện pháp phòng bệnh quan trọng nhất không để cho động vật nuôi thuỷ sản bị sốc do môi trường thay đổi xấu: nhiệt độ, oxy hoà tan, nhiễm bẩn của nước. Môi trường nước đảm bảo tốt cho đời sống của động vật thuỷ sản.<br />\r\n	Đối với bè nuôi cá thường xuyên treo túi vôi, mùa xuất hiện bệnh 2 tuần treo một lần, mùa khác một tháng treo 1 lần. Vôi có tác dụng khử trùng và kiềm hoá môi trường nước. Lượng vôi tính trung bình 2 kg vôi nung/10m3. Bè lớn treo nhiều túi và bè nhỏ treo ít túi tập trung ở chỗ cho ăn và phía đầu nguồn nước chảy. Đối với các ao nuôi áp dụng tẩy dọn ao như phương pháp phòng tổng hợp. Cũng định kỳ mùa bệnh 2 tuần rắc xuống ao 1 lần, mùa khác rắc 1 tháng 1 lần, liều lượng trung bình 2 kg vôi nung/100 m3 nước. Ngoài ra, có thể bổ sung thêm lượng vitamin C cho vào thức ăn trước mùa bệnh hoặc dùng thuốc phối chế KN - 04 -12 của Viện I cho cá ăn phòng bệnh, cách dùng: xem mục thuốc KN-04-12.</p>\r\n<p>\r\n	- Trị bệnh: Có thể dùng một số kháng sinh, thảo mộc có tác dụng diệt khuẩn điều trị bệnh nhiễm khuẩn máu như sau:<br />\r\n	+ Cá giống dùng phương pháp tắm thời gian 1 giờ. Oxytetracyline nồng độ 20-50 ppm. Streptomycin nồng độ 20-50 ppm.<br />\r\n	+ Cá thịt dùng phương pháp cho ăn kháng sinh trộn với thức ăn tinh. Sulfamid liều dùng 150-200 mg/1 kg cá/ngày. Thuốc phối chế KN-04-12: liều dùng 2-4 g/1 kg cá/ngày.</p>\r\n<p>\r\n	Cho cá ăn liên tục từ 5-7 ngày. Riêng với kháng sinh từ ngày thứ 2 trở đi liều lượng giảm đi 1/2 so vớí ngày ban đầu.</p>\r\n', 'Edwardsiella, E. ictaluri, Edwardsiella tarda, đốm trắng nội tạng, gan thận mủ', '', 1362476999, 1362500373, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 3685, 0);
INSERT INTO `tkb_aq_desease` (`id`, `cat_id`, `group_id`, `name`, `en_name`, `description`, `agent`, `zone`, `symptom`, `treatment`, `keyword`, `author`, `add_time`, `edit_time`, `reference`, `user_name`, `hit`, `focus`) VALUES
(36, 5, 2, 'Bệnh nấm hạt', 'Dermocystidiosis', '', '<p>\r\n	Tác nhân gây bệnh là nấm hạt <em>Dermocystidium</em> spp. <em>Dermocystidium ko</em>i (ký sinh cá chép hình) bào tử hình cầu, đường kính 8-12 μm, bên trong có thể hình cầu sáng lệch về một bên.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Dermocystidium koi ky sinh ca chep" src="/upload/images/Dermocystidium-koi-ks-ca-chep.jpg" style="width: 600px; height: 259px;" /><br />\r\n	<span style="color:#0000cd;"><em>Dermocystidium koi </em>(ký sinh cá chép)</span><br />\r\n	A - Các bào tử thấy rõ thể hồng cầu sáng, nhân và không bảo (mẫu tươi);<br />\r\n	B - Bào tử của nấm D. koi thấy rõ không bào và nhân lệch tâm. x1000 (nhuộm H&amp;E).</p>\r\n<p>\r\n	<em>Dermocystidium kwangtungensis</em> (ký sinh cá quả- <em>Ophiocephalus maculates</em>- hình) bào nang dạng hình sợi mảnh rất dài cuộn không đều, kích thước thay đổi chiều dài từ 6,5-84,0mm, nhưng chiều rộng hẹp (0,1-0,2mm). Cắt ngang bào nang hình tròn, thành bào nang mỏng, chiều dầy 1,2-1,5μm. Bào tử hình cầu, đường kính 8,5 μm (6,5-10,3μm), bên trong có thể hình cầu sáng lệch về một bên đường kính 5,8 μm (2,9-7,4μm).</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/upload/images/Dermocystidium-kwangtungensis-ks-ca-loc.jpg" style="width: 335px; height: 252px;" /><br />\r\n	<span style="color:#0000cd;"><em>Mẫu mô cắt ngang sợi nấm chứa các bào tử. Các tế bào máu (hồng cầu) ở giữa sợi nấm. x400 (nhuộm H&amp;E)</em></span></p>\r\n<p>\r\n	<em>Dermocystidium sinensis</em> (ký sinh ở cá trắm cỏ- hình) thể dinh dưỡng (trưởng thành) hình cầu, đường kính 9-17μm, trong tế bào chất có nhiều hạt nhỏ. Bào tử hình cầu, đường kính 13,8 μm (11,6-16,2μm), bên trong có thể hình cầu sáng lệch về một bên, đường kính 9,5 μm (8,0-11,0 μm).</p>\r\n<p style="text-align: center;">\r\n	<img alt="Dermocystidium sinensi ký sinh cá trẳm cỏ" src="/upload/images/Dermocystidium-sinensi.jpg" style="width: 400px; height: 447px;" /><br />\r\n	<span style="color:#0000cd;"><em>Dermocystidium sinensis</em> Xiao Chongxue and Chen Chih-Leu, 1993 (1-3- thể dinh dưỡng; 4-5- bào tử mẫu tươi; 6- bào<br />\r\n	tử nhuộm H&amp;E; 7- bào tử nhuộm giemsa)</span></p>\r\n', '<p>\r\n	Nấm hạt Dermocystidium spp. Ký sinh ở nhiều loài động vật thủy sản nước ngọt và nước mặn (xem bảng). Bệnh không gây cho động vật thủy sản chết hàng loạt. Nhưng khi bị nhiễm nấm hạt sẽ tạo điều kiện cho các tác nhân gây bệnh khác dễ xâm nhập. Bệnh thường xuất hiện vào mùa xuân. Việt Nam ít quan tâm nghiên cứu bệnh này.</p>\r\n<p>\r\n	<strong>Bảng: Một số loài nấm hạt <em>Dermocystidium</em> spp. Ký sinh ở các động vật thủy sản</strong></p>\r\n<table border="0" cellpadding="1" cellspacing="1" class="bang" style="width: 550px;">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="1" rowspan="2">\r\n				&nbsp;Tên nấm</td>\r\n			<td colspan="2" rowspan="1">\r\n				Loài thủy sản</td>\r\n			<td colspan="1" rowspan="2">\r\n				Tác giả</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Tên Latin</td>\r\n			<td>\r\n				Tên Tiếng Việt</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium pusula</td>\r\n			<td>\r\n				Triturus marmoratus</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				PÐrez, 1907</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium banchialis</td>\r\n			<td>\r\n				Trutta faris</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				LÐger, 1914</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium ranae</td>\r\n			<td>\r\n				Rana temperasia</td>\r\n			<td>\r\n				Ếch</td>\r\n			<td>\r\n				Guyenot-<br />\r\n				Naville, 1922</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium vejdovskyi</td>\r\n			<td>\r\n				Esox lucius</td>\r\n			<td>\r\n				Cá chó</td>\r\n			<td>\r\n				Jirovec, 1930</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium salmonis</td>\r\n			<td>\r\n				Oncorhynchus<br />\r\n				tshawytscha</td>\r\n			<td>\r\n				Cá hồi</td>\r\n			<td>\r\n				Davis, 1947</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium koi</td>\r\n			<td>\r\n				Cyprinus carpio</td>\r\n			<td>\r\n				Cá chép</td>\r\n			<td>\r\n				Hoshina-<br />\r\n				Sahara,1950</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium daphinae</td>\r\n			<td>\r\n				Daphina magna</td>\r\n			<td>\r\n				Chân chèo</td>\r\n			<td>\r\n				Ruhberg, 1933</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium marinum</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Hâu</td>\r\n			<td>\r\n				Mackin, et al,<br />\r\n				1950</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium guyenoi</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Nhóm cá vược</td>\r\n			<td>\r\n				Thélin, 1955</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium percae</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Nhóm cá vược</td>\r\n			<td>\r\n				Richenbach-<br />\r\n				Klinke, 1950</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium percae</td>\r\n			<td>\r\n				Mylopharyngodon<br />\r\n				idellus</td>\r\n			<td>\r\n				Cá trắm đen</td>\r\n			<td>\r\n				Chen Chih-Leu,<br />\r\n				1956</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium<br />\r\n				kwangtunggensis</td>\r\n			<td>\r\n				Ophiocephalus<br />\r\n				maculatus</td>\r\n			<td>\r\n				Cá chuối hoa</td>\r\n			<td>\r\n				Chen Chih-Leu,<br />\r\n				et al, 1960</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Dermocystidium sinensis</td>\r\n			<td>\r\n				Ctenopharyngodon<br />\r\n				idellus</td>\r\n			<td>\r\n				Cá trắm cỏ</td>\r\n			<td>\r\n				Ctenopharyngodon<br />\r\n				idellus</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Nấm hạt Dermocystidium spp. Thường ký sinh trên vây, cơ thể, mang cá, những chỗ bị bệnh sưng tấy màu hồng, hình dạng khác nhau (tròn, ôvan hoặc hình dài), kích thước khác nhau từ 1-2cm có khi lớn tới 10cm. Xung quanh chỗ sưng tấy có các đốm viêm nhỏ, chứa các bào tử.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Dermocystidiosis trên cá chép" src="/upload/images/Dermocystidiosis-ca-chep.jpg" style="width: 352px; height: 288px;" /><br />\r\n	<span style="color:#0000cd;"><em>Các cục u của nấm hạt (Dermocystidium) đục vẩn chuẩn bị vỡ trên thân cá chép</em></span></p>\r\n', '<p>\r\n	Phòng bệnh:</p>\r\n<p>\r\n	Dùng thuốc tím (KMnO4) hoặc Formalin tắm cho cá giống phòng bệnh trước khi nuôi.</p>\r\n<p>\r\n	Trị bệnh:</p>\r\n<p>\r\n	- Tắm cho ĐVTS bằng Formalin nồng độ 200-300 ppm thời gian 30-60 phút hoặc<br />\r\n	phun xuống ao, bể nuôi 2 lần/ tuần thuốc Formalin nồng độ 10-20 ppm. Hoặc thay thế Formalin bằng thuốc tím (KMnO4).<br />\r\n	- Dùng Bronopol tắm cho cá 30ppm (30mg/l) thời gian 15 phút. Dùng 50ppm Bronopol để<br />\r\n	xử lý trứng cá trong thời gian 30 phút.</p>\r\n', 'bệnh cá chép, Dermocystidium, bào tử nấm', '', 1362503442, 1362503442, '<ol>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', '', 2545, 0),
(37, 8, 0, 'Bệnh trùng roi trong máu cá', 'Trypanosomosis', '', '<p>\r\n	Bộ Trypanosomidea Grasse, 1952.<br />\r\n	Họ Trypanosomidae Doflein,191<br />\r\n	GiốngTrypanosoma Gruby, 1841</p>\r\n<p style="text-align: center;">\r\n	<img alt="Trypanosoma ctenopharyngodoni Chen et Hsieh" src="/upload/images/trung-roi-tren-ca.jpg" style="width: 336px; height: 435px;" /><br />\r\n	<em>Trypanosoma ctenopharyngodoni</em> Chen et Hsieh</p>\r\n<p>\r\n	Cơ thể Trypanosoma nhỏ, dài khoảng 38-54 μ, chiều rộng 1,2 - 4,6 μ , kích thước thay đổitheo loài. ở giữa cơ thể lớn, 2 đầu nhỏ, có 1 roi ở phía trước, mỗi khi vận động cơ thể rất hoạt bát nhưng ít thay đổi vị trí. Hạch của tế bào hình bầu dục ở chính giữa cơ thể. Chiều dài của hạch lớn gần bằng chiều ngang cơ thể. Hạch nhỏ hình tròn ở gần điểm gốc của roi. Phần sau cơ thể có hạt gốc roi sinh ra roi chạy dài theo bề mặt cơ thể hướng về phía trước tạo thành màng mỏng sóng. Màng rung động làm cho cơ thể chuyển động được. Trùng trưởng thành màng sóng có 5 - 6 nếp gặp không đều nhau, phần vượt ra ngoài cơ thể, ở phía trước là roi trước, phần cuối của roi nhọn, sắc để cắm vào tổ chức của ký chủ. Chiều dài của roi khoảng 7 - 17 μm . Trypanosoma dinh dưỡng bằng thẩm thấu qua toàn bộ bề mặt cơ thể.</p>\r\n<p>\r\n	Trypanosoma sinh sản bằng phương pháp phân đôi cơ thể. Quá trình sinh sản qua ký chủ là đỉa cá: Piscicola geometa, Hemiclepsis magrinata, đỉa hút máu cá có nhiễm Trypanosoma, trùng theo máu vào ruột đỉa. ở đây Trypanosoma mất roi và màng sóng, cơ thể co ngắn lại thành hình tròn, sau một thời gian không lâu, cơ thể phân chia thành 2,4,8 tế bào. Mỗi tế bào hình thành cơ thể mới hình tròn, có hạch lớn, có hạch nhỏ. Sau đó cơ thể có xu hướng kéo dài mọc roi nhưng chưa có màng sóng, khoảng vài giờ sau chúng bắt đầu vận động, lúc này cơ thể và roi đều kéo dài tạo thành màng sóng có 3 -4 nếp gấp nên thường gọi là trùng màng ngắn. Cơ thể chúng tiếp tục phát triển ở trong ruột đỉa đến trùng trưởng thành. Đỉa hút máu cá qua miệng đỉa Trypanosoma vào được cơ thể cá và ký sinh trong máu.</p>\r\n', '<p>\r\n	Ký sinh trùng Trypanosoma ký sinh trong máu, mật của nhiều loài cá nước ngọt, nước biển. Các loài Trypanosma ký sinh trên cá biển có kích thước lớn hơn.</p>\r\n<p>\r\n	Tác hại của chúng là có khả năng tiết ra chất độc, phá vỡ hồng cầu, nhìn chung cường độ và tỷ lệ cảm nhiễm của chúng đối với cá còn thấp nên ở nước ta chưa được chú trọng về bệnh này (đã gặp ở cá he nuôi bè Châu Đốc - An Giang).</p>\r\n', '<p>\r\n	Để chẩn đoán bệnh Trypanosoma phải dùng phương pháp ly tâm máu, sau đó lấy dung dịch ở phần trên đem ra quan sát dưới kính hiển vi. Về dấu hiệu bệnh lý thường không rõ ràng nên khó chẩn đoán bằng mắt thường.<br />\r\n	&nbsp;</p>\r\n', '<p>\r\n	Ở các nước trên thế giới thường dùng phương pháp phòng là chủ yếu, thường dùng vôi tẩy ao, diệt đỉa cá là ký chủ môi giới truyền bệnh Trypanosma.</p>\r\n', 'bệnh cá, trùng roi, máu cá, ký sinh trùng, Trypanosoma', '', 1366014794, 1371050107, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 1439, 0),
(38, 1, 0, 'Cá tôm bị bênh do thiếu oxy', '', '', '<p>\r\n	Cá tôm sóng trong nước cần O<sub>2</sub> đầy đủ để thực hiện quá trình trao đổi chất. Tuy nhiên mỗi loài cá tôm, mỗi giai đôạn phát triển và điều kiện môi trường khác nhau, yêu cầu lượng oxy khác nhau. Lúc lượng oxy hòa tan trong nước thấp quá giới hạn sẽ làm làm cho cá tôm chết ngạt. Cá trắm cỏ, trắm đen, cá mè trắng, mè hoa thường hàm lượng Oxy trong nuowcs1mg/l, cá bắt đầu nổi đầu đến 0,4-0,6 mg/l, cá chết chết ngạt. Cá chép, cá diệc chết ngạt ở lượng oxy hòa tan 0,1-0,4 mg/l, cá vền 0,4-0,5 mg/l.</p>\r\n<p>\r\n	Đối với các ao nuôi tôm khi môi trường ao nuôi có hàm lượng Oxy hòa tan thấp hơn 3mg/l là nguyên nhân làm mang tôm chuyển màu hồng. Nhiều ao nuôi tôm ở ven biển miền Trung và Nam Bộ hàm lượng oxy vào ban đêm dao động 1-2,8 mg O<sub>2</sub>/ml thậm chó có lúc bằng không. Hiện tượng cá tôm chết ngạt do thiếu oxy xảy ra ở những ao hồ nước tĩnh nhất là những mặt nước tĩnh có nhiều mùn bã hữu cơ hoặc bón quá nhiều phân hữu cơ.</p>\r\n<p>\r\n	Có lúc trong môi trường đầy đủ nhưng CO<sub>2</sub> (Cacbonic) quá cao lên đến 80mg/l ở nhiệt độ 20-31<sup>o</sup>C, CO<sub>2</sub> trong máu cá không thoát ra ngoài được làm hôn mê thần kinh trung ương. Cá khó lấy O<sub>2</sub> hòa tan trong nước, nếu hàm lượng cacbonic trong nước 20mg/l mà cá nỗi đầu thì do nước thiếu O<sub>2</sub> là chủ yếu.</p>\r\n', '<p>\r\n	Mùa hè cá, tôm dễ bị nổi đầi nhất là khi trời sấm sét mà không có mưa hay trước mưa dong do các áp suất khô khí giảm thấp O<sub>2</sub> hòa tan và nước giảm làm cho cá, tôm nổi đầu hoặc có khi mưa giông rất ngắn, nhiệt độ nước ở tầng mặt giảm, tầng đáu cao gây ra hiện tượng đối lưu, các chất mùn bã hữu cơ ở tầng đáy được đảo lên tăng cường phân hủy tiêu hao nhiều O<sub>2</sub> đồng thời thấy khí độc như H<sub>2</sub>S, NH<sub>3</sub>, CO<sub>2</sub> làm cho cá tôm nổi đầu. Những ao hồ tảo phát triển mạnh, ban ngày chúng tiến hành quang hợp sản sinh ra nhiều O<sub>2</sub> , nhưng ngược lại vào ban đêm trong quá trình hô hấp, chúng lại lấy nhiều O<sub>2</sub> môi trường và thải ra nhiều CO<sub>2</sub> dễ làm cho cá nổi đầu.</p>\r\n', '<p>\r\n	Cá thiếu oxy thường nỗi lên mặt nước, đớp không khí để hô hấp gọi là hiện tượng cá nổi đầu. Nếu thiếu dưỡng khí kéo dài thì môi dưỡi nhô ra, màu sắc trên lưng biến nhạt, trong ao hồ nuôi cá, cá mè nổi đầu trước rang đông thì mức độ tương đối nhẹ, trái lại toàn bộ cá trong ao nổi đầu từ 12 giờ đêm về trước hoặc trong nước bơi lội toán loạn, tư thế nằm thẳng, lúc húc đầu vào bờ chứng tỏ thủy vực thiếu oxy nghiêm trọng, nếu không có biện pháp xử lý cá sẽ chết hàng loạt thậm chí chết toàn bộ. Thiều oxy kéo dài làm cho cơ thể cá thiếu máu, sinh trưởng châm, hàm dưới lồi ra ngoài.</p>\r\n<p>\r\n	Khi tôm bị bênh thiếu oxy dấu hiệu đầu tiên là nổi đầu, dạt vào bờ, chết từ rải rác đến hàng loạt, đăc biệt lượng tôm chết tập trung vào sáng sớm. Tôm bỏ ăn vì không xuống đáy ao bắt mồi do nồng độ oxy hòa tan ở đấy thấp hơn. Kiểm tra thấy mang tôm chuyển từ màu trắng ngà sang màu hồng.</p>\r\n<p style="text-align: center;">\r\n	<img alt="tôm nổi đầu do thiếu oxy" src="/upload/images/tom-noi-dau-thieu-oxy.jpg" style="width: 385px; height: 312px;" /><br />\r\n	<em>Tôm bị nổi đầu do thiếu oxy</em></p>\r\n<p style="text-align: center;">\r\n	<img alt="mang tôm đỏ do thiếu oxy" src="/upload/images/mang-tom-do-do-thieu-oxy-1.jpg" style="width: 385px; height: 182px;" /><br />\r\n	<img alt="mang tôm đỏ do thiếu oxy" src="/upload/images/mang-tom-do-do-thieu-oxy.jpg" style="width: 385px; height: 126px;" /><br />\r\n	<em>Mang tôm chuyển màu hồng do thiếu oxy</em></p>\r\n', '<p>\r\n	Ao hồ nôi cá, tôm cần tẩy dọn sạch sẽ, nạo vét bớt bùn để lượng bùn vừa phải sau đó phơi năng đáy ao trước khi khi cho nước và ao nuôi.</p>\r\n<p>\r\n	Phân bón cần được ủ kỹ và lượng bón tủy theo điều kiện thời tiết và chất lượng nước mà điều chỉnh cho thích hợp.</p>\r\n<p>\r\n	Mật độ cá tôm thả ương nuôi không nên quá dày để đảm bảo môi trường đủ oxy.</p>\r\n<p>\r\n	Thường xuyên theo dõi&nbsp; sự biến đổi của môi trường để bơm thêm nước sạch vào ao, nếu có điều kiện thì dùng máy sục khí để kịp thời bổ sung oxy cho ao nuôi.</p>\r\n<p>\r\n	Đối với ao nuôi tôm công nghiệp trường hợp phát hiện tôm thiếu oxy hoặc phòng ngừa tôm thiếu oxy dùng oxy dạng viên (<span style="color: rgb(48, 48, 48); font-family: Arial, Tahoma, Helvetica, sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); display: inline !important; float: none;">Sodiumpercarbonate)</span> kết hợp với Yucca để phòng hoặc cung cấp oxy tức thời cho tôm nuôi.</p>\r\n', 'thiếu oxy, tôm nổi đầu, cá nổi đầu, ao nuôi thiếu oxy, cấp cứu tôm nổi đầu, oxy tức thời', '', 1366016028, 1366018389, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 5065, 0),
(39, 8, 0, 'Bệnh trùng roi Cryptobiosis', 'Cryptobiosis', '', '<p>\r\n	Bộ Bodonidea Holland, 1895<br />\r\n	Họ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bodonidae Stun, 1878<br />\r\n	Giống&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em> Cryptobia</em> Leidy, 1846</p>\r\n<p style="text-align: center;">\r\n	<img alt="trùng roi crytobia" src="/upload/images/Cryptobia.png" style="width: 580px; height: 349px;" /><br />\r\n	Hình: A-C: Cryptobia branchialis; D: Cryptobia agitata: 1. Roi trước, 2. Thể gốc, 3. Hạch nhỏ, 4. Hạch tế bào, 7. màng sóng, 8. roi sau</p>\r\n<p>\r\n	Cơ thể dẹp, đoạn trước rộng, sau nhỏ dần giống như lá liễu. Phía trước cơ thể có 2 gốc roi, từ đó sinh ra roi trước hướng ra phía trước, roi sau tiếp với cơ thể hình thành màng sóng và vượt quá chiều dài cơ thể, đoạn cuối của roi sau nhọn, thẳng để cắm vào tổ chức ký chủ.</p>\r\n<p>\r\n	Màng sóng của <em>Cryptobia</em> có nếp gấp ít hơn ở <em>Trypanosoma</em>. Trong nguyên sinh chất có 1 hạch lớn hình tròn bắt màu đậm và các không bào, hạt vật chất dinh dưỡng. Kích thước cơ thể lớn hay nhỏ tuỳ theo loài. Lúc vận động, roi trước không rung chuyển, roi sau thẳng giống như một cái đuôi dài. Nhờ màng sóng đập lên đập xuống mà có thể vận động chậm<br />\r\n	chạp tiến về phía trước.</p>\r\n<p>\r\n	Kích thước một số loài <em>Cryptobia:</em></p>\r\n<table cellpadding="1" cellspacing="1" class="bang" style="width: 580px;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">\r\n				<strong>Loài</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Chiều dài (μ)</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Chiều rộng (μ)</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Chiều dài roi trước (μ)</strong></td>\r\n			<td style="text-align: center;">\r\n				<strong>Chiều dài roi sau (μ)</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<em>Cryptobia branchialis</em></td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<em>Cryptobia agiata</em></td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n			<td>\r\n				14-23</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	Phương pháp sinh sản: Sinh sản theo phương pháp phân chia theo chiều dọc cơ thể. Cơ thể mới lại sinh ra roi trước và roi sau.</p>\r\n', '<p>\r\n	<em>Cryptobia</em> lưu hành mạnh vào mùa xuân, hè. Ở nước ta đã phát hiện <em>Cryptobia branchialis</em> và <em>Cryptobia agitata</em> ký sinh trên mang, da cá chép, cá mè trắng, cá mè hoa, cá trắm cỏ, cá tra và nhiều loài cá nước ngọt với cường độ và tỷ lệ cảm nhiễm còn thấp nên tác hại chưa nghiêm trọng. Ở nhiều nước trên thế giới như Trung Quốc, ký sinh trùng <em>Cryptobia</em> gây tác hại cho cá hương, cá giống.</p>\r\n', '<p>\r\n	<em>Cryptobia</em> ký sinh trên mang và da của cá do đó để xác định tác nhân gây bệnh thường kiểm tra dịch nhờn của da và mang dưới kính hiển vi. Cá nhiễm <em>Cryptobia</em> tổ chức mang có màu&nbsp; đỏ không bình thường, da và mang có nhiều dịch nhờn. Roi sau cắm sâu vào tổ chức ký chủ đồng thời cơ thể tiết ra chất độc phá hoại tổ chức tế bào ký chủ.</p>\r\n<p>\r\n	Cá bị bệnh nặng hoạt động yếu cơ thể có màu sắc đen dần, vi khuẩn và nấm theo vết thương xâm nhập vào cơ thể. <em>Cryptobia</em> ký sinh trên mang, da nhiều loài cá nước ngọt, thường chúng tập chung thành từng đám. Cá càng nhỏ càng dễ bị cảm nhiễm và gây tác hại lớn hơn cá lớn.</p>\r\n', '<p>\r\n	Trước khi thả cá, dùng vôi tẩy ao, cải tạo ao. Giữ môi trường nước trong sạch đồng thời thực hiện tốt các biện pháp chăm sóc, cho ăn đầy đủ để cá lớn nhanh, có khả năng đề kháng tốt.</p>\r\n<p>\r\n	Cá giống trước khi thả ra nuôi cá thịt hoặc cá bị bệnh dùng CuSO<sub>4</sub> nồng độ 3-5 ppm tắm 15-30 phút, phun xuống ao nồng độ 0,5-0,7 ppm.</p>\r\n<p>\r\n	CuSO<sub>4</sub> độc với nguyên sinh động vật và các loại tảo hạ đẳng có màng keo do Cu++ kết hợp với albumin tạo thành muối kết tủa đông vón tổ chức.</p>\r\n', 'Cryptobia, trùng roi, ký sinh trùng, Cryptobiosis', '', 1371051853, 1371051853, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2006. Bện học thủy sản, Viện Nghiên Cứu Nuôi Trồng Thủy Sản 1.</li>\r\n</ol>\r\n', 'phongt94', 1322, 0),
(40, 5, 0, 'Bệnh nấm hạt', 'Ichthyophonosis', '', '<p>\r\n	Tác nhân gây bệnh là nấm hạt thuộc Eukaryota; <em>Ichthyosporea</em>;<em> Ichthyophonida</em>; <em>Ichthyophonus</em>. Thường gặp các loài <em>Ichthyophonus hoferi</em>; <em>Ich. irregularis</em>. Nấm<em> Ichthyophonus hoferi</em> thường quan sát thấy bào nang nghỉ trong mô cá dạng hình cầu.</p>\r\n<p>\r\n	Bào nang có đường kính từ 10-300 μm. Trong bào nang có một vài bào tử đến hàng trăm bào tử. Các bào tử phát triển trong bào nang. Sợi nấm nhô ra như chân giả từ thành bào nang và chân giả xâm nhập vào mô của vật chủ mới. Phương pháp sinh sản này thường gọi là phát triển dạng sợi. Phương pháp sinh sản thứ hai gọi là sinh sản hợp tử, phát triển<br />\r\n	của hợp tử cũng quan sát thấy trong bào nang chín.</p>\r\n<p>\r\n	Nấm phát triển ở nhiệt độ 3- 20<sup>0</sup>C, tối ưu là 10<sup>0</sup>C, 30<sup>0</sup>C nấm không phát triển.</p>\r\n<p>\r\n	<img alt="chu kỳ nấm hạt Ichthyophonosis" src="/upload/images/benh-nam-hat.jpg" style="width: 608px; height: 656px;" /><br />\r\n	<em>Chu kỳ phát triển của nấm hạt (I. hoferi) trong mô cá: A- chu kỳ sinh sản nhu mô (1- dạng sợi; 2- dạng bào tử; 3-&nbsp; ợp tử); B- bào nang không hoạt động (mô cá chết hoặc phân cá); C- Bào nang chín; D- Phát triển (1- hợp tử phát triển; 2- dạng sợi phát triển); chuyển ký sinh sang một vật chủ mới: E- phôi amip; F- phôi amip di động.</em></p>\r\n', '<p>\r\n	Nấm hạt <em>Ichthyophonus hoferi </em>ký sinh ở hơn 80 loài cá biển, như cá hồi sinuc, cá trích (<em>Clupea harengus</em>) và cá nuôi cảnh trong bể kính nước ngọt và nước mặn. Ngoài ra còn có báo cáo nấm ký sinh ở lưỡng thê và Copepod. Nấm <em>Ich. irregularis</em> ký sinh ở cá bơn vây vàng (<em>Limanda ferruginea</em>)</p>\r\n', '<p>\r\n	Dấu hiệu bên ngoài có thể xuất hiện các vết loét trên thân (hình 149). Nấm nội ký sinh là chủ yếu, khi giải phẫu các cơ quan nội tạng tim, gan, then, lá lách và buồng trứng có các đốm trắng nhỏ. Khi cắt mô thấy rõ các nấm hạt trong các tổ chức.</p>\r\n<p>\r\n	Dựa vào dấu hiệu bệnh lý để chẩn đoán. Chẩn đoán bằng phương pháp mô bệnh học. Chẩn đoán bằng nuôi cấy phân lập nấm bằng môi trường Eagle`s Minimum Essential Medium (MEM) hoặc môi trường Leibovitz L-15, cả hai môi trường cộng thêm với 5% fetal bovineserum, 100 IU mL-1 penicillin, 100 μg mL-1 streptomycin và 100 μg mL-1 gentamycin. Nuôi cấy ở nhiệt độ ≤15<sup>o</sup>C thời gian từ 7-10 ngày, sau đó kiểm tra nấm <em>Ichthyophonus</em> dưới kính hiển vi ở độ phóng đại 400 lần.</p>\r\n<p style="text-align: center;">\r\n	<img alt="tim cá bệnh nấm hạt" src="/upload/images/tim-ca-benh-nam-hat.jpg" style="width: 600px; height: 198px;" /><br />\r\n	Tim (A), gan (B) cá hồi có các đốm trắng nhỏ nhiễm các bào nang nấm hạt (<em>Ichthyophonus</em>) (theo R.Kocan, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="cơ tim cá bệnh" src="/upload/images/co-tim-nhiem-benh.jpg" style="width: 600px; height: 223px;" /><br />\r\n	Cơ tim (A), gan (B) cá hồi nhiễm các bμo nang nấm hạt (<em>Ichthyophonus</em>), mẫu cắt mô, nhuộm H&amp;E (theo G.Saunders, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="thận cá bênh nấm hạt" src="/upload/images/than-ca-hoi-hiem-benh.jpg" style="width: 600px; height: 209px;" /><br />\r\n	A- thận cá hồi nhiễm nấm hạt (<em>Ichthyophonus</em>); B- mẫu cắt mô thận cá hồi nhiễm các bμo nang nấm hạt, nhuộm H&amp;E (theo R.Kocan và G.Saunders, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="lá lách cá hồi bệnh nấm" src="/upload/images/la-ca-ca-hoi-nhiem-benh.jpg" style="width: 600px; height: 212px;" /><br />\r\n	A- lá lách cá hồi nhiễm nấm hạt (<em>Ichthyophonus</em>); B- mẫu cắt mô lá lách cá hồi nhiễm các bμo nang nấm hạt, nhuộm H&amp;E (theo R.Kocan và G.Saunders, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="cơ xương cá hồi bệnh nấm" src="/upload/images/xuong-ca-hoi-nhiem-benh.jpg" style="width: 600px; height: 211px;" /><br />\r\n	A- Cơ xương cá hồi nhiễm nấm hạt (<em>Ichthyophonus</em>); B- mẫu cắt mô cơ xương cá hồi nhiễm các bào nang nấm hạt, nhuộm H&amp;E (theo R.Kocan và G.Saunders, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="trứng cá nhiễm nấm" src="/upload/images/trung-ca-hoi-nhiem-benh.jpg" style="width: 600px; height: 196px;" /><br />\r\n	Buồng trứng cá hồi nhiễm nấm hạt (<em>Ichthyophonus</em>), (theo R.Kocan, 2003)</p>\r\n<p style="text-align: center;">\r\n	<img alt="cá hồi nhiễm nấm bị loét" src="/upload/images/ca-hoi-nhiem-benh-nam-loat.jpg" style="width: 600px; height: 195px;" /><br />\r\n	Cá hồi bị nhiễm nấm hạt (<em>Ichthyophonus</em>) trên thân có các vết loét.</p>\r\n', '<p>\r\n	Chưa nghiên biện pháp phòng trị bệnh. Nhưng để phòng bệnh này không cho cá ăn thức ăn là động vật sống nhiễm nấm. áp dụng đầy đủ biện pháp phòng bệnh tổng hợp.</p>\r\n', 'Ichthyophonus, cá hồi, nấm, Ichthyophonus hoferi, nấm hạt', '', 1394113970, 1394113970, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 1313, 0),
(41, 4, 1, 'Bệnh vi khuẩn NHP', 'Necrotising hepatopancreatitis', '<p>\r\n	Necrotising hepatopancreatitis (NHP) còn được gọi là nhiễm necrotising hepatobacterium.&nbsp;</p>\r\n<p>\r\n	Bốn giai đoạn khác biệt của nhiễm trùng được mô tả: ban đầu, cấp tính, quá trình chuyển đổi và mãn tính. Giai đoạn cấp tính và quá trình chuyển đổi được nhận biết bởi sự hiện diện tổn thương bệnh lý ở gan tụy.&nbsp;</p>\r\n', '<p>\r\n	Nguyên nhân là do khuẩn Gram âm lây nhiễm vào tế bào của alphaproteobacterium gan tụy của tôm, còn được gọi là vi khuẩn NHP.</p>\r\n', '<p>\r\n	Vi khuẩn NHP tồn tại dưới hai hình thức hình thái: một hình que nonflagellated, rickettsia như sinh vật; và một xoắn ốc dạng hình roi. Hầu hết tôm đều dễ nhiễm bệnh và tỷ lệ tử vong có thể là lên tới 90-95% trong vòng 30 ngày kể từ ngày một bùng phát. Dịch bệnh NHP xảy ra khi nhiệt độ trong nước dao động cao từ 29-31°C và độ mặn cao lên đến 40‰. NHP được truyền trực tiếp qua đường thức ăn (cá thể sống sót sau nhiễm khuẩn NHP có thể mang vi khuẩn gây bệnh) và thông qua nguồn nước bị ô nhiễm. Vi khuẩn NHP cũng có thể được đào thải ra phân và đóng góp vào sự lây truyền bệnh. Bệnh gây chết cho tôm ở giữa giai đoạn nuôi thương phẩm.</p>\r\n', '<p>\r\n	- Tôm bệnh có biểu hiện bơi lờ đờ, bỏ ăn, sinh vật đơn bào bị nhiễm khuẩn có thể bị tắc nghẽn và tốc độ tăng trưởng giảm.</p>\r\n<p>\r\n	- Mềm vỏ</p>\r\n<p>\r\n	- Cơ thể trạng thái bình thường</p>\r\n<p>\r\n	- Mang có màu đen</p>\r\n<p>\r\n	- Đường ruột trống rỗng</p>\r\n<p>\r\n	- Thoái hóa hoặc teo tuyến tiêu hóa (gan tụy ) có màu nhạt đến trắng</p>\r\n<p>\r\n	- Xuất hiện vệt đen (melanised) trong gan tụy.</p>\r\n<p>\r\n	- Tổn thương u hạt đa ổ trong ống gan tụy, tế bào biểu mô ống lượn gan tụy liền kề bị teo lại.</p>\r\n<p>\r\n	- Các tế bào ống trong các tổn thương u hạt có thể được hypertrophied và chứa các sinh vật ưa kiềm trong tế bào chất.</p>\r\n<p>\r\n	- Bong tróc các tế bào biểu mô ống lượn</p>\r\n<p>\r\n	- Viêm haemocytic nghiêm trọng trong không gian ống lượn.</p>\r\n', '<p>\r\n	Kỹ thuật phân tử cần thiết để chẩn đoán dương tính khi cá thể bị nhiễm vi khuẩn NHP trong giai đoạn đầu hoặc bệnh mãn tính.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'NHP, vi khuẩn, bệnh do khuẩn', '', 1401007066, 1401007771, '<p>\r\n	Australian Government, Department of Agriculture, Fisheries and Forestry</p>\r\n', 'duynhut', 1214, 0),
(42, 1, 2, 'Bệnh thiếu Protein và Acid amine', '', '', '<p>\r\n	Protein là chất quan trọng để cá sinh trưởng và phát triển. Vì vậy trong các ao nuôi mật độ dày, thành phần Protein trong thức ăn không thấp hơn 40% để đảm bảo cho các sinh trưởng nếu ít hơn các sẽ chậm lớn. Thức ăn có 25% Protein, tốc độ tăng trọng của cá chỉ bằng 12,8% cá chho ăn thức ăn có 40% Protein. Nếu cho ăn chỉ 10% Protein cá không tăng trọng lượng.</p>\r\n', '<p>\r\n	Cá chép: Trong thức ăn thiếu nhiều acid amine và vitamine làm cho cơ thể cá mất khả năng điều tiết sự thăng bằng, cột sống bị cong, nghiêm trọng ảnh hưởng đến tế bào tổ chức gan và lá lách.</p>\r\n<p>\r\n	Đối với lươn, trong thức ăn không có Protein, cơ thể giảm trọng lượng rõ rệt, trong thức ăn protein chiếm 8,9% trọng lượng cơ thể sẽ giảm nhẹ. Nếu trọng lượng protein trên 13,4% trọng lượng cơ tể tăng. Nược lại tỷ lệ Protein trong thức ăn vượt quá 44,5% sự sinh trưởng và tích lũy đạm gần như không thay đổi và ở một mức độ nào đó có tác dụng trở ngại cho quá trình trao đổi chất.</p>\r\n', '<p>\r\n	Trong thức ăn của cá các acid amin không cân bằng hoặc hàm lượng protein quá nhiều không những lãng phí mà còn gây tác hại cho cơ thể.</p>\r\n', '', 'Protein, Acid amine, đạm, dinh dưỡng', '', 1401341348, 1401341348, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 1064, 0),
(43, 1, 0, 'Bệnh liên quan đến chất đường Glucid', '', '', '<p>\r\n	Đường (Glucid) là nguồn cung cấp năng lượng chủ yếu cho hoạt động sống của cơ thể sinh vật nói chung, cho loài cá nói riêng. Một gram đường ở trong cơ thể, oxy hoá sản sinh ra 4.000 calo năng lượng. Theo thống kê có khoảng 50% nguồn năng lượng cung cấp cho hoạt động sống của cá lấy từ sự phân giải đường trong thức ăn cung cấp. Đường trong thức ăn đầy đủ, sự phân giải mỡ trong cơ thể và lượng đạm yêu cầu cũng giảm đi. Đường còn là thành phần cấu trúc tế bào cơ thể.</p>\r\n', '', '<p>\r\n	- Cơ vận động, não hoạt động cần năng lượng cung cấp từ oxy hoá đường glucogen, nhưng bản thân não dự trữ đường rất ít phải lấy từ máu nên khi thiếu đường trong máu làm cho chức năng hoạt động của máu bị tổn hại, dẫn đến co giật, hôn mê thậm chí cá có thể bị chết. Qua đó cho thấy đường trong thức ăn thiếu ảnh hưởng đến hoạt động bình thường của cá.</p>\r\n<p>\r\n	- Các loại đường trong thức ăn chủ yếu là tinh bột có một ít saccarose, lactose. Đường ở trong ống tiêu hoá phân giải ra đường đơn hấp thụ vào gan, khả năng hấp thụ các loại đường của từng loài cá và từng giai đoạn phát triển trong cùng loài có sự khác nhau. Cá hồi tỷ lệ tiêu hoá cellulo dưới 10%, tỷ lệ tiêu hoá các loại đường từ 20-40% do đó hàm lượng cellulo trong thức ăn không quá 10% tốt nhất chỉ 5-6%, các loại đường không quá 30%, trong đó phần có thể tiêu hoá không nên thấp hơn 10%.</p>\r\n<p>\r\n	Theo Hoàng Trung Chí (Trung Quốc) 1983, 1985 để tăng trọng cá trắm cỏ dùng tinh bột cho ăn tốt nhất 48% và chứng minh khả năng hấp thụ tinh bột cao hơn nhiều so với mỡ, nếu hàm lượng tinh bột 51,4% cá trắm sinh trưởng tốt. Từ đó suy ra nguồn cung cấp năng lượng chủ yếu của cá trắm cỏ lấy từ đường.</p>\r\n', '', 'Đường, Glucid, mỡ, năng lượng', '', 1401341870, 1401341870, '<p>\r\n	Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản</p>\r\n', 'phongt94', 809, 0),
(44, 1, 0, 'Bệnh liên quan đến chất béo Lipid', '', '', '<p>\r\n	- Mỡ lỡ vật chất chủ yếu dự trữ nguồn cung cấp năng lượng cho cơ thể cá, 1 gram mỡ oxy hoá cung cấp 9300 calo năng lượng.</p>\r\n<p>\r\n	- Mỡ ở trong cơ thể bảo vệ và cố định các cơ quan nội tạng.</p>\r\n<p>\r\n	- Mỡ lỡ thành phần cấu tạo mỡng của màng tế bào.</p>\r\n<p>\r\n	- Mỡ hoà tan vitamin trong cơ thể đồng thời nó có tác dụng chuyển hoá muối và các acid trong túi mật. Một số acid béo làm tăng sức đề kháng cho cơ thể cá, lỡm máu hoạt động bình thường.</p>\r\n', '', '<p>\r\n	- Trong thành phần thức ăn, nếu số lượng mỡ thích hợp cá sinh trưởng nhanh, hoạt động của các cơ quan không bị rối loạn.<br />\r\n	Các loài cá khác nhau yêu cầu lượng mỡ không giống nhau. Muốn xác định hàm lượng mỡ thích hợp trong khẩu phần thức ăn cần dựa vào tính ăn của cá và nguồn thức ăn. cá dữ khả năng hấp thụ mỡ trong thức ăn mạnh hơn cá ăn thực vật thuỷ sinh thượng đẳng. Cá ăn tạp có thể hấp thụ tốt mỡ và tinh bột làm nguồn năng lượng. Trong thức ăn nếu thiếu mỡ cá sinh trưởng chậm, vây bị đứt.</p>\r\n<p>\r\n	Ngược lại trong thức ăn thành phần mỡ quá cao lỡm cản trở tích luỹ đạm, chất lượng thịt giảm, cá sinh trưởng chậm, một số cơ quan nội tạng bị thoái hoá. Nhìn chung trong thức ăn của cá lượng mỡ nên dưới 15%. Đối với cá hồi, trong thức ăn lượng mỡ chỉ trên dưới 5%.</p>\r\n<p>\r\n	- Mỡ rất dễ bị oxy hoá, sản sinh ra các sản phẩm độc có hại cho sức khoẻ của cá, cá chép ăn phải mỡ bị oxy hoá, sau 1 tháng cột sống biến dạng, cá hồi gan bị vỡng vỡ phát sinh hiện tượng thiếu máu. Do đó để đề phòng hiện tượng trên, khi chế biến thức ăn cho cá, khi cho ăn mới bổ sung thỡnh phần mỡ vỡo, đồng thời cung cấp số lượng vitamin E trong khẩu phần thức ăn.</p>\r\n', '', 'lipid, mỡ, chất béo', '', 1401342397, 1401342397, '<ol>\r\n	<li>\r\n		Bùi Quang Tề, 2009. Bệnh Học Thủy Sản. Viện Nghiên Cứu Nuôi Trồng Thủy Sản I</li>\r\n</ol>\r\n', 'phongt94', 1042, 0),
(45, 1, 3, 'Bệnh vàng da, vàng kỳ ở cá tra nuôi công nghiệp', '', '<p style="text-align: center;">\r\n	<img alt="" src="/upload/images/Picture1.jpg" style="width: 452px; height: 250px;" /></p>\r\n<p style="text-align: center;">\r\n	(Nguồn:&nbsp;http://www.huonggiangagritech.com/)</p>\r\n<p>\r\n	Bệnh vàng da vàng kỳ, vàng thịt ở cá tra nuôi công nghiệp rất thường xuyên gặp phải, gây thiệt hại nghiêm trọng. Biện pháp sau sẽ giúp phòng trị bệnh này hiệu quả đồng thời nâng cao chất lượng thịt fillet xuất khẩu.</p>\r\n', '<p>\r\n	Do con giống khi ương nuôi đã dùng quá nhiều kháng sinh, ảnh hưởng đến hệ thống gan mật</p>\r\n<p>\r\n	Do trong quá trình nuôi không xổ giun sán định kỳ---&gt; giun sán tạo kén ở gan và cuống mật---&gt; cá bị tắt mật.</p>\r\n<p>\r\n	Do môi trường nước ao nuôi quá dơ, ô nhiễm hữu cơ nhiều, tảo lam phát triển dày đặc---&gt; thịt cá chuyển vàng và có mùi bùn đất.</p>\r\n<p>\r\n	Bệnh thường xuất hiện sau quá trình điều trị xuất huyết, gan thận mủ, trắng mang trắng gan---&gt; vàng da, vàng kỳ.</p>\r\n<p>\r\n	<span style="color:#0000cd;"><em><strong>TRONG QUÁ TRÌNH ĐIỀU TRỊ ĐÃ DÙNG NHIỀU LOẠI KHÁNG SINH HÓA HÓA CHẤT ĐỘC</strong></em></span></p>\r\n', '<p>\r\n	Bệnh thường xuất hiện vào đầu mùa mưa, khoảng tháng 6 đến tháng 7 nhưng rộ nhất vào cuối mùa nước đổ</p>\r\n<p>\r\n	Xuất hiện ở mọi giai đoạn cá nhưng thường thấy ở cá 350 gam trở lên</p>\r\n', '<p>\r\n	<u><strong>DẤU HIỆU BÊN NGOÀI</strong></u></p>\r\n<p>\r\n	Cá bơi lội lờ đờ trên mặt nước, không định hướng, nổi đầu và giảm ăn.</p>\r\n<p>\r\n	Cá tiết nhiều nhớt, tơ mang bị tưa, mang nhợt nhạt---&gt; cá khó hô hấp</p>\r\n<p>\r\n	Cá tập trung ở mép ao, gần cống nước và bất động ở khu vực này</p>\r\n<p>\r\n	Bên ngoài vây bị xuất huyết. Trên mắt có một vầng trắng hoặc vàng nhạt xung quanh, đỉnh đầu bị sưng nhẹ.</p>\r\n<p>\r\n	<span style="color:#0000cd;"><em><strong>NẾU CÁ BỊ XUẤT HUYẾT VÁ BỊ GAN THẬN MỦ THÌ THỜI GIAN ĐIỀU TRỊ KÉO DÀI VÀ MỨC ĐỘ HAO HỤT RẤT CAO (40%-50%)</strong></em></span></p>\r\n<p>\r\n	<u><strong>DẤU HIỆU BÊN TRONG</strong></u></p>\r\n<p>\r\n	Nội tạng có dịch vàng, cá bị hoại tử (gan bở, nhũng)</p>\r\n<p>\r\n	Máu có màu nhợt nhạt hơi pha vàng</p>\r\n<p>\r\n	Gan chai cứng, màu ngã vàng</p>\r\n<p>\r\n	Cuống mật sưng to, sậm đen, dịch mật lợn cợn, &nbsp;tắt mật, có giun kén ký sinh túi mật</p>\r\n<p>\r\n	Lá lách, thận bầm</p>\r\n<p>\r\n	Thịt cá từ vàng nhạt tới đậm</p>\r\n<p>\r\n	Đường tiêu hóa có dịch vàng, có mùi hôi ở dạ dày và ruột cá</p>\r\n', '<p>\r\n	Không dùng kháng sinh sau quá trình điều trị</p>\r\n<p>\r\n	Diệt khuẩn đáy ao (dùng Yucca kết hợp với muối), nếu đáy ao quá dơ thì ta cho hút bùn đáy ao.</p>\r\n<p>\r\n	Dùng thảo dược có tác dụng giải độc gan và kìm khuẩn&nbsp;</p>\r\n<p>\r\n	Nếu cho ăn cá chết tăng, ta cắt mòi và tạt Yucca cho cá khỏe, có thể dùng bã bia tạt cho cá khỏe và có tác dụng giải độc gan`</p>\r\n<p>\r\n	Nếu cá giảm chết, ta cho cá ăn ít kết hợp với giải độc gan + bổ gan + bổ máu.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'bệnh vàng da, bệnh vàng kỳ, bệnh cá tra, bệnh môi trường', 'KS DAO TRUNG HIEU (TEPBAC.COM)', 1440817365, 1440989347, '', 'hieuqt', 2108, 0),
(46, 1, 3, 'Bệnh trắng gan trắng mang trên cá tra nuôi', '', '<p>\r\n	Trong những năm gần đây dịch bệnh trên cá tra diễn ra ngày càng nhiều, đồng thời xuất hiện những bệnh vẫn chưa xác định chính xác tác nhân, một trong số đó là bệnh trắng gan trắng mang.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/upload/images/8-30-15 7-28-44 PM.jpg" style="width: 390px; height: 269px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Chưa xác định rõ ràng nhưng có thể là do những người sản xuất giống cho ăn kháng sinh liều cao tích tụ nhiều ở gan; làm cho gan kém hoạt động, cá bị thiếu máu, mang cá không lấy được oxy để vận chuyển máu cung cấp cho cơ thể.</p>\r\n', '<p>\r\n	Bệnh gần như xuất hiện quanh năm ở các ao nuôi cá giống và cá thịt. Thời gian bệnh thường kéo dài và mức độ hao hụt cũng có thể lên rất cao.</p>\r\n', '<h4>\r\n	Cá tra bị trắng gan trắng mang có hoạt động yếu, bơi lờ đờ, phản ứng chậm với tiếng động, tập trung nơi thay nước của ao vào buổi sáng.</h4>\r\n<p>\r\n	Toàn thân có màu trắng nhạt, ít chất nhầy trên cơ thể, các vùng sắc tố tự nhiên trên lưng và vi không rõ.</p>\r\n<p>\r\n	Mang chuyển dần từ đỏ sang hồng nhạt và trắng.</p>\r\n<p>\r\n	Nội quan bên trong có màu nhạt, gan cũng chuyển sang màu vàng nhạt hay kem, dạ dày có không có thức ăn và có ít dịch trong xoang nội quan. &nbsp;</p>\r\n', '<p>\r\n	Cắt mồi liên tục 3-5 ngày, cá tự khỏi bệnh</p>\r\n<p>\r\n	Kết hợp tạt yucca cho cá khỏe</p>\r\n<p>\r\n	Rải &nbsp;oxy viên ban đêm để tăng hàm lượng oxy trong ao</p>\r\n<p>\r\n	Chú ý quan trọng là không được dùng vôi, chlorine, BKC liều cao khi cá bệnh&nbsp;<span style="color: rgb(102, 102, 102); font-family: Arial, Helvetica, sans-serif; line-height: 20px;">&nbsp;vì bệnh trắng gan trắng mang cá không lấy được oxy; khi xử lý làm mất oxy trong nước cá chết càng nhiều.</span></p>\r\n<p>\r\n	Sau khi cá hết &nbsp;cho ăn bằng 1/3 lượng thức ăn cho cá khỏe rùi sau đó mới tăng mồi từ từ lên.</p>\r\n<p>\r\n	Cho cá ăn kết hợp trộn với thuốc bổ gan, bổ máu</p>\r\n<p>\r\n	Nếu thấy cá hết chết thì tiếp tục cho ăn, nếu cá chết tăng thì tiếp tục cắt mồi</p>\r\n', 'bệnh trắng mang, trắng gan, bệnh cá tra, bệnh thủy sản, bệnh trên cá', 'KS DAO TRUNG HIEU (TEPBAC.COM)', 1440938755, 1440989360, '', 'hieuqt', 1890, 0),
(47, 4, 3, 'Bệnh xuất huyết trên cá tra nuôi công nghiệp', '', '<p>\r\n	<strong>Trong quá trình nuôi, các ao nuôi tích tụ mùn bã, bùn, rác và các chất thải khác làm ô nhiễm nguồn nước, tiềm ẩn các mầm bệnh đối với cá nuôi; cá thường bị nhiễm bệnh xuất huyết do vi khuẩn, gây thiệt hại rất lớn. Do vậy, cần chú ý các dấu hiệu bệnh lý và tác nhân gây bệnh để phòng và trị bệnh kịp thời, góp phần giảm thiệt hại và phòng ngừa dịch bệnh lây lan.</strong></p>\r\n<p style="text-align: center;">\r\n	<strong><img alt="" src="/upload/images/XUAT HUYET-2.jpg" style="width: 427px; height: 288px;" /></strong></p>\r\n', '<p>\r\n	&nbsp;Bệnh do nhiều loại vi khuẩn như:&nbsp;<em>Aeromonas hydrophila</em>,&nbsp;<em>Pseudomona</em>s sp,&nbsp;<em>E.ictaluri</em>,<em>Clostridium</em>&nbsp;sp,… gây ra.<br />\r\n	&nbsp;</p>\r\n', '<p>\r\n	Bệnh xuất hiện tất cả giai đoạn cá nuôi nhất là lúc nước son đổ về, vào mùa mưa lũ....</p>\r\n', '<p>\r\n	- Xuất hiện đốm đỏ trên da và gốc vây, hậu môn xuất huyết.&nbsp;<br />\r\n	- Xoang bụng trương to và có nhiều dịch màu vàng, nội tạng xuất huyết.<br />\r\n	- Gan, thận, lách xưng to, nhũn gan và có màu đỏ sậm.&nbsp;<br />\r\n	- Trường hợp cá bị bệnh ác tính trên điểm đầu có chấm đỏ (lủng sọ), lồi mắt, tốc độ chết nhanh và tỷ lệ hao hụt cao.</p>\r\n', '<p>\r\n	<u><strong>PHÒNG BỆNH:</strong></u></p>\r\n<p>\r\n	- Chọn cá giống khỏe mạnh, không nhiễm bệnh.<br />\r\n	- Quản lý môi trường tốt (hút đáy định kỳ, không để nước quá xanh, tránh hiện tượng tảo tàn).<br />\r\n	- Bổ sung&nbsp;men tiêu hóa + vitamin C&nbsp;định kỳ 2-3 lần trong tuần để tăng cường miễn dịch, giúp cá hạn chế bệnh.<br />\r\n	- Diệt khuẩn định kỳ nguồn nước ao nuôi (7-10 ngày/lần)<br />\r\n	- Xổ ký sinh định kỳ 20 ngày/lần đối với cá giống hoặc 30 ngày/lần đối với cá thịt&nbsp;</p>\r\n<p>\r\n	<u><strong>QUY TRÌNH ĐIỀU TRỊ:</strong></u></p>\r\n<p>\r\n	- Ngày thứ 1: Cắt mồi, thay nước làm sạch môi trường tạt &nbsp;Yucca cho cá khỏe</p>\r\n<p>\r\n	- Ngày thứ 2: Diệt khuẩn nguồn nước ao nuôi bằng PV Iodine, Protec…</p>\r\n<p>\r\n	- Từ ngày thứ 2 đến ngày thứ 6: Trộn kháng sinh cho cá ăn liên lục 2-5 ngày.</p>\r\n<p>\r\n	- Nếu cá giảm bệnh thì cho ăn + bổ gan + men tiêu hóa cho cá mau phục hồi sức khỏe.</p>\r\n', 'bệnh xuất huyết trên cá tra, bệnh xuất huyết phù đầu, bệnh cá tra, bệnh trên cá, bệnh thủy sản', 'KS DAO TRUNG HIEU (TEPBAC.COM)', 1440988876, 1440989316, '', 'hieuqt', 2185, 0),
(48, 3, 2, 'Bệnh lở loét trên cá lóc', '', '<p>\r\n	Hiện nay phong trào nuôi cá lóc phát triển khá mạnh ở các tỉnh ĐBSCL, nhất là vào mùa nước nổi. Bà con thường nuôi cá trong ao, trong bể lót bạt nilon hay trong bè. Cá lóc trong tự nhiên thường rất khỏe, ít bệnh tật, nhưng trong điều kiện nuôi nhân tạo với mật số cao thì cá lóc cũng có nhiều bệnh gây hại. Nhiều gia đình mới nuôi cá lóc, chưa có nhiều kinh nghiệm đã thất bại do dịch bệnh gây ra.&nbsp;Trong mùa lũ, các ao, hồ nuôi cá thường tích tụ nhiều phù sa, nhiễm bẩn, mùn bã, rác và các chất thải làm ô nhiễm nguồn nước và tiềm ẩn những mầm bệnh cho cá nuôi. Cá lóc nuôi trong mùa lũ thường hay xuất hiện các loại bệnh do các loại kí sinh như trùng bánh xe, sán lá đơn chủ, xuất huyết do nhiễm virus, đốm đỏ do vi khuẩn, bệnh do nhóm giáp xác gây ra. Tuy nhiên, bệnh thường gặp nhất với cá lóc nuôi là bệnh ghẻ hay còn gọi là hội chứng lở loét. Bệnh lở&nbsp;loét xảy ra trên cá lóc nuôi không chỉ có ở nước ta mà còn có nhiều ở các nước Đông Nam Á, các nước trong khu vực Thái Bình Dương.</p>\r\n', '<p>\r\n	Những tác nhân gây bệnh cho cá gồm virus, vi khuẩn, nấm, kí sinh trùng và các yếu tố môi trường. Nguyên nhân gây bệnh trước nhất là virus, nấm cũng được coi là yếu tố quan trong gây ra hội chứng lở loét. Có thể chúng cùng với các loại kí sinh trùng làm cá bị thương tổn tạo điều kiện cho các tác nhân chính gây bệnh cho cá. Ngoài các yếu tố môi trường như nhiệt độ thay đổi, môi trường nước quá dơ bẩn, sự ô nhiễm có thể gây sốc và làm cá nhiễm bệnh, nhiều quan điểm cho rằng nấm ký sinh trong nội tạng Aphanomyces được coi là tác nhân chính gây ra bệnh này.</p>\r\n', '<p>\r\n	Bệnh thường xuất hiện vào cuối mùa mưa (tháng 10-12) và đầu mùa khô (tháng 1-2).</p>\r\n', '<p>\r\n	Dấu hiệu đầu tiên của bệnh là cá ăn ít hoặc bỏ ăn, bơi nhô đầu khỏi mặt nước, nổi lờ đờ, da xám, có vết loét hoặc các đốm đỏ phát triển ở đầu, thân, vây và đuôi. Sau một thời gian bị bệnh cá kiệt sức và chết. Quan sát bên ngoài cá thấy xuất hiện nhiều vết nhỏ màu xám hay đỏ. Mang, quanh mắt và da xuất huyết, toàn thân có màu xám tối. Thương tổn lan rộng thành những vết loét lớn trên vẩy, thân cá... Khi cá bệnh nặng sẽ thấy máu chảy ở hậu môn. Giải phẫu cá sẽ thấy bóng hơi xuất huyết và teo dần, gan thận cũng xuất huyết. Khoang bụng có dấu hiệu tích nước, có nhiều dịch nhờn và xuất huyết.</p>\r\n', '<p>\r\n	<em>Phòng bệnh cho cá: </em></p>\r\n<p>\r\n	Phòng bệnh có tính chất quyết định đến kết quả nuôi, trong đó tẩy dọn ao, bể nuôi theo đúng qui trình kỹ thuật để diệt các tác nhân gây bệnh là rất quan trọng. Quản lý tốt môi trường nuôi, cung cấp nước nuôi cá bằng nước sạch.</p>\r\n<div>\r\n	Định kỳ&nbsp;dùng vitamin C trộn vào thức ăn (5-10g/kg thức ăn)</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Thả&nbsp;lá&nbsp;xoan: cột thành từng bó&nbsp;cắm xuống ao (bó&nbsp;lá&nbsp;dầm thành từng bó, khoảng 30kg /100m<sup>2</sup>).</div>\r\n<p>\r\n	Ổn định môi trường, kìm hãm sự phát triển của mầm bệnh bằng cách xử lý định kì 15 ngày/lần bằng dung dịch Vimekon (1g/1m<sup>3</sup> nước). Tránh làm cá bị xây xát, không để cá bị nhiễm các loại bệnh ngoài da sẽ tạo điều kiện cho bệnh cá phát triển.</p>\r\n<p>\r\n	Cho cá ăn đủ thức ăn với hàm lượng dinh dưỡng cao làm cá khỏe sẽ có sức đề kháng tốt. Thường xuyên trộn thức ăn với men tiêu hóa, vitaminC, premix. Khoa Thủy sản (ĐH Cần Thơ) khuyến cáo cách phòng trị bệnh ghẻ cá, xử lý ao nuôi bằng vôi, giữ cho môi trường ổn định, dùng hóa chất formon và thuốc tím làm giảm bớt mật độ vi khuẩn và diệt nấm ký sinh trùng, dùng kháng sinh diệt vi khuẩn làm lành vết thương trên da cho cá.</p>\r\n<p>\r\n	<em>Trị bệnh: </em></p>\r\n<p>\r\n	Khi cá có dấu hiệu bị bệnh cần thay 50% nước bằng nước sạch, vệ sinh xung quanh ao nuôi. Xử lý nước bằng Fresh Water với lượng 1 kg (650 gói A + 350 gói B) cho 1.000-1.500m<sup>3</sup> nước. Trộn thuốc kháng sinh vào thức ăn cho cá liên tục trong 7 ngày theo liều 250 g Desery + 50 ml Vime-Fenfish 2000 cho 1 tấn cá.</p>\r\n<div>\r\n	Dùng vôi bột liều lượng 5-7 kg/100m<sup>2</sup>, hoà tan vào nước và tạt đều xuống ao.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Dùng 2 viên Oxytetraxyline + 01 viên Cotrimfor /1kg TA.</div>\r\n', 'cá lóc, lở loét, bệnh lở loét', '', 1446117527, 1446125718, '<p>\r\n	Nông nghiệp VN</p>\r\n<p>\r\n	Tepbac tổng hợp</p>\r\n', 'duynhut', 1548, 0);
INSERT INTO `tkb_aq_desease` (`id`, `cat_id`, `group_id`, `name`, `en_name`, `description`, `agent`, `zone`, `symptom`, `treatment`, `keyword`, `author`, `add_time`, `edit_time`, `reference`, `user_name`, `hit`, `focus`) VALUES
(49, 1, 3, 'Bệnh không truyền nhiễm trên cá da trơn và biện pháp ngăn ngừa', '', '<p>\r\n	Ông Prakash Chandra Behera, Ấn Độ, thảo luận một số bệnh không lây nhiễm chủ yếu trên cá da trơn và làm thế nào để ngăn chặn chúng.</p>\r\n<p>\r\n	Nuôi thủy sản đi kèm với một số vấn đề về bệnh do các mầm bệnh từ nhiều vi khuẩn khác nhau được phổ biến rõ rệt trong thực tế nuôi.</p>\r\n<p>\r\n	Mật độ cao, cho ăn quá mức và chất thải hữu cơ khác nhau kích thích sự tăng sinh của vi sinh vật gây bệnh. Vi khuẩn, virus, nấm và ký sinh trùng đơn bào là các vi sinh vật quan trọng gây bệnh trên cá nuôi.</p>\r\n<p>\r\n	Dịch bệnh liên tục bùng phát là vấn đề lớn đối với sự phát triển bền vững của ngành công nghiệp thủy sản. Dịch bệnh lâm sàng có thể tác động tiêu cực đến phúc lợi cá nuôi và các lợi ích kinh tế khác.</p>\r\n<p>\r\n	Bệnh không lây nhiễm trên cá, chủ yếu là do các yếu tố môi trường, thiếu dinh dưỡng, suy giảm miễn dịch…</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Sau đây là những điều kiện chính yếu mà dịch bệnh không ảnh hưởng nghiêm trọng đến cá da trơn:</strong></span></p>\r\n<p>\r\n	- Giảm tăng trưởng</p>\r\n<p>\r\n	- Vàng gan (vàng da)</p>\r\n<p>\r\n	- Bệnh máu nâu</p>\r\n', '<p>\r\n	<span style="color:#ff0000;"><strong>Bệnh "Crack Head" trên cá da trơn</strong></span></p>\r\n<p>\r\n	“Crack Head" là bệnh chỉ xuất hiện trên cá tra được báo cáo chi tiết từ ao nuôi. Hay còn được gọi là Broken Head Disease. Đây là bệnh rất nghiêm trọng trong nuôi cá tra thương phẩm ở Ấn Độ.</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Nguyên nhân</strong></span></p>\r\n<p>\r\n	“Crack Head” là do một hoặc thiếu một số chất dinh dưỡng như Vitamin C, Vitamin D, canxi, phốt pho và bệnh. Dịch bệnh “Crack Head” chỉ xảy ra khi ao nghèo dinh dưỡng hoặc chế độ cho ăn kém, chất lượng nước kém và thừa thức ăn.</p>\r\n<p style="text-align: center;">\r\n	<img alt="crack head" src="/upload/images/crack-head(1).jpg" /></p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh máu nâu</strong></span></p>\r\n<p>\r\n	Bệnh “máu nâu – brown blood” chủ yếu là do ngộ độc nitrit, là một bệnh ảnh hưởng đến cá nuôi – cá da trơn chủ yếu là do môi trường.</p>\r\n<p>\r\n	Nguyên nhân chính là do nồng độ nitrite trong nước ao nuôi cao. Bệnh xảy ra ở cá khi nước ao có chứa nồng độ nitrit cao. Nitrite là một sản phẩm của sự phân hủy amoniac bằng vi khuẩn. Nitrite vào hệ thống sinh thái ao sau khi thức ăn được tiêu hóa bởi cá và nitơ dư thừa được chuyển thành amoniac, sau đó được bài tiết dưới dạng chất thải vào trong nước. Thừa (lãng phí) thức ăn chăn nuôi và vật chất hữu cơ khác cũng phá vỡ và chuyển đổi thành ammonia, nitrite, nitrate theo một cách tương tự.</p>\r\n<p>\r\n	Nitrit vào máu qua mang và chuyển đổi màu máu sang màu sô cô la nâu. Hemoglobin vận chuyển oxy trong máu, kết hợp với nitrite để tạo thành methemoglobin và không có khả năng vận chuyển oxy. Máu nâu không thể mang đủ lượng oxy và cá bị ảnh hưởng có thể gây bị ngạt mặc dù nồng độ oxy trong nước đầy đủ.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cá bị máu nâu" src="/upload/images/ca-bi-mau-nau(1).jpg" /></p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh vàng da trên cá da trơn</strong></span></p>\r\n<p>\r\n	Bệnh vàng da biểu hiện rõ nhất là da vùng bụng có màu vàng, màng cứng của mắt và vây tương tự cũng có màu vàng.</p>\r\n<p>\r\n	Bệnh cho thấy sức đề kháng trên cá da trơn nước ngọt kém. Vàng da chủ yếu là do sự dư thừa sắc tố mật trong máu và bạch huyết. Tình trạng này được mô tả trong cá da trơn. Dịch bệnh tấn công cá khi bị ảnh hưởng bởi thiếu máu gây tán huyết làm vàng da. Mặc dù, tác nhân gây bệnh duy nhất có thể không được kết hợp, nhưng một loạt các mầm bệnh vi khuẩn có liên quan sẽ kéo theo.</p>\r\n<p>\r\n	Đây cũng là nguyên nhân góp phần gây nên tỷ lệ mắc bệnh và chết ở cá bị vàng da. Nhiễm Myxosporean được tìm thấy trong cả cá bình thường và cá vàng da. Mang cá bị ảnh hưởng bởi bệnh trở nên nhợt nhạt, dịch lỏngc, lá lách, thận và túi mật, và/hoặc cơ thịt màu vàng.</p>\r\n', '', '<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh crack head</strong></span></p>\r\n<p>\r\n	Bệnh bắt đầu với điểm nhỏ trên vùng đầu, thường chỉ xuất hiện trên mắt. Cá bị ảnh hưởng bởi bệnh thường xuất hiện rảnh nhỏ hộp sọ, nằm giữa hai buồng khí, song song với các khớp tấm sọ.</p>\r\n<p>\r\n	Ở giai đoạn sau, rãnh biến sâu hơn, trong đó, bệnh sẽ này là tiến dọc theo cơ quan đường bên. Khi kéo dài và lắp đầy cá sẽ chết.</p>\r\n<p>\r\n	Triệu chứng lâm sàng là bụng phình hơi to do xuất huyết, giảm tăng trưởng, vẹo đốt sống, xuất huyết, tổn thương trên vây, mang, mòn vây, sắc tố bất thường, tăng mao mạch, năng suất sinh sản kém và biến dạng đốt sống.</p>\r\n<p>\r\n	Gần đây, người ta đã nhận thấy rằng bệnh “Crack Head” được báo cáo là do hội chứng thiếu hụt vitamin C trong nuôi cá tra thâm canh. Tầm quan trọng của sự thiếu hụt vitamin C là một chất điều hòa miễn dịch và một yếu tố dinh dưỡng quan trọng cho cá da trơn.</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh máu nâu</strong></span></p>\r\n<p>\r\n	Điều này được biểu hiện rõ nhất ở cá bệnh máu nâu là thở thoi thóp, ngay cả khi nồng độ oxy tương đối cao gây "bệnh máu nâu".</p>\r\n<p>\r\n	Bệnh chỉ xuất hiện khi máu có nồng độ methemoglobin ở mức cao.</p>\r\n<p>\r\n	Bệnh máu nâu có thể được ngăn chặn, hoặc ít nhất là giảm thiểu, bởi sự giám sát chặt chẽ nitrite, clorua và TAN bằng cách duy trì các tỷ lệ clorua nitrite thích hợp.</p>\r\n<p>\r\n	Khi bệnh máu nâu xảy ra, tình trạng này có thể được cắt giảm bằng cách cho thêm muối vào nước. Cá da trơn (hay đối tượng khác) mắc bệnh máu nâu hay do stress bởi nitrite, nếu còn sống cũng rất dễ bị nhiễm trùng do vi khuẩn, thiếu máu (hoặc không có máu), và các bệnh liên quan đến stress khác. Nhiễm bệnh do khuẩn như Aeromonas hoặc Columnaris, thường xảy ra trong 1 đến 3 tuần sau khi mắc bệnh máu nâu.</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh vàng da</strong></span></p>\r\n<p>\r\n	- Cá bị nhiễm bệnh thường có da và mang vàng.</p>\r\n<p>\r\n	- Giảm ăn, bơi lờ đờ, kém ăn và thoi thóp.</p>\r\n<p>\r\n	- Gia tăng tỷ lệ chết và thối cơ, vây, mang.</p>\r\n<p>\r\n	- Lá lách, thận, túi mật và gan sưng to, vàng nhợt nhạt.</p>\r\n<p>\r\n	- Các mô mỡ trong khoang bụng cũng màu vàng.</p>\r\n<p>\r\n	- Da cá cũng có màu từ vàng sáng đến màu cam.</p>\r\n<p style="text-align: center;">\r\n	<img alt="sung gan" src="/upload/images/sung-gan(1).jpg" /></p>\r\n<p>\r\n	Cá bệnh thường xuất huyết vây đuôi. Bệnh xảy ra do chất lượng nước kém, mật độ thả cao, nhiệt độ thích hợp cho bệnh bùng phát là 30<sup>o</sup>C. Tuy nhiên, bệnh cũng xảy ra khi nhiệt độ dao động bất thường.&nbsp;<em>E. Italis</em>, là nguyên nhân gây bệnh mủ trong gan ở cá da trơn.</p>\r\n<p style="text-align: center;">\r\n	<img alt="vàng gan" src="/upload/images/vang-gan(1).jpg" /></p>\r\n<p>\r\n	Bệnh vàng da rất nguy hiểm và khó điều trị. Với tình trạng nghiêm trọng, cá có thể chết hàng loạt trong giai đoạn ngắn. Cá vàng da có nguy cơ dễ bị stress và có xu hướng chết trước khi đạt đến quy mô thị trường.</p>\r\n', '<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh "crack head"</strong></span></p>\r\n<p style="text-align: center;">\r\n	<img alt="cá bị to đầu" src="/upload/images/ca-bi-to-dau(1).jpg" /></p>\r\n<p>\r\n	- Tăng cường thay nước thường xuyên.</p>\r\n<p>\r\n	- Bổ sung thêm vitamin đến giàu hóa Vitamin C trong thức ăn.</p>\r\n<p>\r\n	- Hút cạn bùn đáy ao.</p>\r\n<p>\r\n	- Duy trì tối ưu DO, pH và giảm thiểu nồng độ amoniac trong nước ao nuôi.</p>\r\n<p>\r\n	- Cung cấp chế độ ăn cân bằng và thường xuyên.</p>\r\n<p>\r\n	<strong><span style="color:#0000cd;">Bệnh máu nâu:</span></strong>&nbsp;Natri clorua (muối thường) được sử dụng để điều trị bệnh. Calcium chloride cũng có thể được sử dụng nhưng thường đắt tiền hơn. Clorua muối cạnh tranh với nitrite thông qua sự hấp thụ qua mang</p>\r\n<p>\r\n	<span style="color:#0000cd;"><strong>Bệnh vàng gan:</strong></span>&nbsp;Chất béo trong chế độ ăn ôi thối dẫn đến nồng độ của các gốc tự do ở mức cao và peroxit gây thiệt hại oxy hóa màng sinh học và các bào quan. Đa axit béo không bão hòa (PUFA) là những thành phần thiết yếu của tất cả các màng sinh học. Vitamin E là chất chống oxy hóa quan trọng và bảo vệ màng sinh học từ peroxy lipid bằng cách hoạt động như một xác thối của các gốc tự do. Tuy nhiên, sự hiện diện của chất béo bị ôi làm suy giảm chế độ sử dụng vitamin E và chất chống oxy hóa khác, để lại lớp màng sinh học dễ bị oxy hóa. Biện pháp cải tiến nước và chất lượng thức ăn được khuyến khích.</p>\r\n<p>\r\n	<span style="color:#ff0000;"><strong>Các biện pháp phòng ngừa các bệnh ở các trang trại nuôi cá</strong></span></p>\r\n<p>\r\n	Quản lý tốt sức khỏe cá là thực hiện biện pháp an toàn sinh học nghiêm ngặt, ngăn chặn sự xâm nhập của mầm bệnh vào hệ thống nuôi. Thành phần quan trọng khác của việc quản lý sức khỏe cá bao gồm giảm thiểu stress và sử dụng các hợp chất mà tăng hoặc kích thích hệ thống miễn dịch cá.</p>\r\n<p>\r\n	<span style="color:#ff0000;"><strong>Khuyến nghị quan trọng để quản lý có hiệu quả sức khỏe cá nuôi</strong></span></p>\r\n<p>\r\n	Chuẩn bị ao, thả giống chất lượng và ao nuôi không có mầm bệnh, duy trì chất lượng nước, cho thức ăn với hàm lượng dinh dưỡng cao …là điều rất cần thiết để giảm thiểu rủi ro của dịch bệnh trên cá. Thường xuyên sử dụng&nbsp;probiotic&nbsp;và&nbsp;immunostimulants&nbsp;để có tác động lớn hơn đến cá nuôi và giảm thiểu các yếu tố nguy cơ do bệnh gây ra. Quản lý môi trường để giảm thiểu stress cũng đóng một vai trò quan trọng trong việc phòng chống bệnh.</p>\r\n<p style="text-align: center;">\r\n	<img alt="hinh ao ca tra" src="/upload/images/hinh-ao-ca-tra(1).jpg" style="width: 600px; height: 175px;" /></p>\r\n', 'cá, cá da trơn, bệnh cá, môi trường', '', 1452572447, 1452572762, '<p>\r\n	http://www.thefishsite.com/</p>\r\n', 'duynhut', 1044, 0),
(50, 3, 1, 'Bệnh đốm trắng ở tôm nuôi và công nghệ nuôi tôm nhằm phòng bệnh đốm trắng', '', '<p>\r\n	Bệnh đốm trắng được chia làm 2 dạng: dạng 1 là bệnh cấp tính thường làm cho tôm nuôi - nhất là các loài tôm thuộc giống tôm he (Penaeus) - bị chết hàng loạt với tỷ lệ cao trong vòng vài tuần; dạng 2 là bệnh tiềm ẩn, tồn tại độc lập trong các loài thuộc giống tôm càng xanh (Macrobrachium), các loài cua và tôm hùm sống trong tự nhiên và thường không có dấu hiệu bệnh lý.</p>\r\n', '<p>\r\n	Bệnh đốm trắng do virus (White Spot Syndrome Virus - WSSV) là một trong các bệnh gây nên hiện tượng tôm nuôi chết hàng loạt trong các vùng nuôi tôm tại các địa phương ven biển ở nước ta từ nhiều năm qua.</p>\r\n', '<p>\r\n	Bệnh đốm trắng thường xảy ra ở cả 2 loài tôm nuôi chính là tôm chân trắng và tôm sú, với độ tuổi từ 10 đến 110 ngày sau khi thả giống. Diện tích nuôi tôm sú bị bệnh này nhiều hơn, chiếm khoảng 60% số diện tích nuôi tôm bị bệnh đốm trắng.&nbsp; Các nhà nghiên cứu bệnh học thủy sản cho biết, bệnh đốm trắng thường xuất hiện ở tôm nuôi vào mùa xuân và đầu mùa hè khi khí hậu, thời tiết thay đổi nhiều như sự biến thiên quá lớn của biên độ nhiệt giữa ngày và đêm dẫn đến tôm bị sốc và dễ nhiễm bệnh.</p>\r\n<div>\r\n	Virus gây bệnh đốm trắng phân bố rộng ở những vật chủ khác nhau, không chỉ ở một số loài thuộc giống tôm he mà còn ở nhiều loài khác trong bộ Mười chân (Decapoda) như cua, ghẹ, tôm hùm...&nbsp;</div>\r\n', '<p>\r\n	Điều này rất nguy hiểm do có nguy cơ làm tăng khả năng lây lan rộng rãi bệnh trong môi trường. Bệnh lan truyền chủ yếu theo chiều ngang qua nước, thức ăn và&nbsp; rất dễ bùng phát khi môi trường biến đổi theo chiều hướng xấu làm cho tôm nuôi bị sốc.</p>\r\n', '<p>\r\n	Trước tình hình diện tích nuôi tôm sú bị bệnh đốm trắng tăng nhiều, trong vụ nuôi tôm năm nay, người nuôi tôm cần áp dụng các công nghệ nuôi thân thiện với môi trường để phòng, chống bệnh hiệu quả, đạt năng suất, sản lượng cao và đảm bảo chất lượng sản phẩm tôm nuôi. Trong quá trình nuôi tôm, cần đặc biệt chú ý đến việc duy trì ổn định môi trường nuôi bằng các biện pháp cơ học như quạt nước, sục khí đáy và xi phông đáy ao; các biện pháp hóa dược như bón vôi để duy trì pH, tăng độ kiềm; các biện pháp sinh học như dùng chế phẩm sinh học để cải tạo môi trường ao nuôi... Bên cạnh đó, rất chú trọng khâu nâng cao sức đề kháng cho tôm nuôi thông qua việc sử dụng các biện pháp quản lý chăm sóc tốt, dùng thức ăn chất lượng cao để nuôi tôm, cho tôm ăn bổ sung các loại enzym, vitamin, chất khoáng vi lượng nhằm giúp tôm tăng cường khả năng tiêu hóa và sức đề kháng với bệnh.</p>\r\n<p>\r\n	Việc áp dụng các quy trình nuôi tôm tiên tiến - trong đó có quy trình nuôi tôm dùng chế phẩm sinh học và quy trình nuôi tôm theo công nghệ biofloc có tác dụng rất lớn nhằm phòng, chống bệnh đốm trắng cho tôm nuôi.</p>\r\n<p>\r\n	Quy trình nuôi tôm dùng chế phẩm sinh học do Bộ Nông nghiệp và PTNT ban hành, theo đó, trong quá trình nuôi có dùng các loại chế phẩm vi sinh (có tên trong Danh mục được Bộ cho phép sử dụng) để làm sạch môi trường nước ao nuôi, đồng thời bổ sung các loại men tiêu hóa vào thức ăn và cho tôm ăn hàng ngày với lượng phù hợp. Định kỳ hàng tháng trong suốt vụ nuôi, cho tôm ăn thêm các loại vitamin A, C, D, E và bổ sung các chất khoáng vi lượng vào ao nuôi.</p>\r\n<p>\r\n	Quy trình nuôi tôm theo công nghệ biofloc là quy trình ứng dụng công nghệ biofloc để nuôi tôm thâm canh. Công nghệ biofloc dựa trên nguyên lý bổ sung một tỷ lệ phù hợp nguồn cacbon làm thức ăn cho vi sinh vật dị dưỡng với nguồn nitơ có sẵn trong nước ao nuôi, tạo điều kiện cho vi sinh vật phát triển ưu thế trong ao nuôi. Công nghệ biofloc giải quyết được 2 vấn đề trong quá trình nuôi, đó là loại bỏ các chất dinh dưỡng chuyển hóa vào sinh khối vi sinh vật dị dưỡng xử lý nước ao nuôi, và sử dụng tập hợp các biofloc làm nguồn thức ăn tự nhiên giàu dinh dưỡng bổ sung tại chỗ cho tôm nuôi. Khi nuôi tôm theo công nghệ biofloc, người nuôi không cần thiết phải xây dựng hệ thống lọc sinh học phụ trợ vì các quá trình vi sinh giúp khử độc tính của các hợp chất chứa nitơ đều diễn ra ngay trong môi trường nước của hệ thống nuôi, đồng thời cũng không cần dùng các biện pháp khác để khử trùng nước, ví dụ khử trùng bằng khí ôzôn, vì chúng sẽ cản trở hoạt động của vi sinh vật trong nước. Do đó, công nghệ biofloc giúp làm giảm chi phí thức ăn, nâng cao an toàn sinh học, hạn chế tối đa sự nhiễm bệnh, nhất là các bệnh do virus gây ra cho tôm nuôi như bệnh đốm trắng.&nbsp;</p>\r\n', 'tôm, bệnh tôm, đốm trắng, dịch bệnh', '', 1453779885, 1453779885, '', 'duynhut', 1062, 0);
