INSERT INTO `tkb_aq_product` (`id`, `parrent_id`, `shop_id`, `name`, `image`, `cat_id`, `group_id`, `description`, `indication`, `dosage`, `ingredient`, `packing`, `co_id`, `user_id`, `prise`, `keyword`, `rate_t`, `rate_c`, `price`, `sale`, `edit_time`, `add_time`, `hit`, `allow`) VALUES
(64, 0, 0, 'MERA ™ BAC W', 'Mera-Bac-goi.jpg', 1, 1, 'Vi sinh tác động kép: xử lý ô nhiễm và ức chế vi khuẩn gây hại (Vibrio spp)', '<p>\r\n	Các thành phần vi sinh trong&nbsp;MERA<sup>TM</sup>&nbsp;Bac W&nbsp;được phân lập từ nguồn nước, bùn đáy ao &amp; đường ruột tôm của các ao nuôi thành công có năng suất cao nhất để chọn lọc các dòng vi sinh khỏe mạnh, điển hình vì chúng giữa vai trò quan trọng trong các ao nuôi thành công này.</p>\r\n<p>\r\n	Quá trình phân lập và chọn lọc còn dựa trên khả năng ức chế vi khuẩn <em>Vibrio spp</em> gây bệnh để phát triển các vi khuẩn có lợi &amp; loại trừ Vibrio có hại.</p>\r\n', '<p>\r\n	Chuẩn &nbsp;bị thả: &nbsp; &nbsp;500g/ha.</p>\r\n<p>\r\n	Tháng 1-2: &nbsp; &nbsp; &nbsp; &nbsp; 300g/ha.</p>\r\n<p>\r\n	Tháng 3: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;500g/ha</p>\r\n<p>\r\n	Tháng 4: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;750g/ha</p>\r\n<p>\r\n	<em>Trường hợp ao ô nhiễm nặng: Tăng gấp đôi so với liều hướng dẫn.</em></p>\r\n<p>\r\n	Đặc biệt khi tôm bị bệnh phân trắng: bổ sung 10g/kg thức ăn, liên tục 1 tuần.</p>\r\n', '<p>\r\n	<em>Pediococcus acidilactici</em></p>\r\n<ul>\r\n	<li>\r\n		Là chủng vi sinh rộng nhiệt, có thể phát triển ở nhiệt độ khác nhau, lên đến 60<sup>o</sup>C.</li>\r\n	<li>\r\n		Có thể phát triển ở độ pH thấp (pH=6)</li>\r\n	<li>\r\n		Có khả năng sản xuất chuỗi peptide kháng khuẩn giúp kiểm soát nhiều loại vi khuẩn có hại khác nhau.</li>\r\n</ul>\r\n<p>\r\n	<em>Bacillus spp</em></p>\r\n<ul>\r\n	<li>\r\n		Khử và phóng thích khí độc amoniac (NH<sup>3</sup>) vào không khí.</li>\r\n	<li>\r\n		Tiết ra các enzyme có khả năng phân hủy mùn bã hữu cơ ô nhiễm.</li>\r\n	<li>\r\n		Kiểm soát vi khuẩn gây hại Vibrio loại trừ bệnh đen mang ở tôm.</li>\r\n	<li>\r\n		Nâng cao khả năng miễn dịch giups tôm chống lại các bệnh do vi khuẩn.</li>\r\n</ul>\r\n', 'Gói 250g', 55, 39, 0, '', 89, 15, 0, 0, 1399306211, 1399306187, 5118, 1),
(23, 0, 11, 'OTC 100', '[["/upload/product/thumb_image/2019/09/anh-girl-xinh-full-hd-lam-hinh_1569305867.jpg"]]', 2, 1, 'Thử sản phẩm mới1', 'Thử sản phẩm mới2', 'Thử sản phẩm mới3', 'Thử sản phẩm mới4', 'Thử sản phẩm mới5', 21, 17, 0, 'Thử sản phẩm mới6', 15, 2, 10000, 0, 1568776875, 1333083169, 2017, 1),
(109, 0, 6, 'Nước dinh dưỡng 1909', '[["/upload/product/thumb_image/2019/09/hinh-anh-dep-hot-girl_1569404049.jpg"]]', 4, 2, 'Nước dinh dưỡng 1909', 'Nước dinh dưỡng 1909', 'Nước dinh dưỡng 1909', 'Nước dinh dưỡng 1909', 'Nước dinh dưỡng 1909', 0, 17, 0, 'Nước dinh dưỡng 1909', 0, 0, 200000, 0, 0, 1568866650, 0, 0),
(110, 0, 5, 'Men vi sinh xử lý nước', '[["/upload/product/thumb_image/2019/09/anh-girl-xinh-full-hd-lam-hinh_1569305930.jpg"]]', 0, 0, 'Men vi sinh xử lý nước', 'Men vi sinh xử lý nước', 'Men vi sinh xử lý nước', 'Men vi sinh xử lý nước', '', 0, 17, 0, '', 0, 0, 10000, 0, 0, 1568867554, 0, 0),
(8, 0, 0, 'Yucca 75 liquid', 'pic_yucca_75_liquidlw.png', 4, 0, 'Hấp thụ khí độc NH3 có trong ao nuôi, giúp ổn định pH, cải thiện môi trường nước.', '<p>\r\n	Hấp thụ kh&iacute; độc NH3 c&oacute; trong ao nu&ocirc;i, gi&uacute;p ổn định pH, cải thiện m&ocirc;i trường nước.</p>\r\n', '<p>\r\n	- Trại giống: 25ml/ 20m3 nước bể ương.<br />\r\n	- Nu&ocirc;i t&ocirc;m thịt: sử dụng định kỳ 7-10 ng&agrave;y 1 lần v&agrave;o buổi chiều tối.<br />\r\n	-T&ocirc;m giai đoạn từ mới thả đến hai th&aacute;ng tuổi: 100ml/ 1.000m3 nước bể ương.<br />\r\n	-T&ocirc;m giai đoạn từ 2 th&aacute;ng tuổi đến 4 th&aacute;ng tuổi: 150ml/ 1.000m3 nước bể ương.<br />\r\n	- Giảm nhanh kh&iacute; độc NH3, cấp cứu t&ocirc;m nổi đầu, k&eacute;o đ&agrave;n, bơi lờ đờ do thiếu oxy khi m&ocirc;i trường nước ao bị &ocirc; nhiễm nặng: 200ml/ 1.000 m3 nước bể ương.<br />\r\n	-Loại trừ kh&iacute; độc v&agrave; ổn định pH trước khi thả t&ocirc;m bột: 200ml/ 1.000 m3 nước bể ương.</p>\r\n<p>\r\n	H&ograve;a yucca 75 liquid với một lượng nước vừa đủ rồi tạt đều xuống ao, n&ecirc;n sử dụng v&agrave;o l&uacute;c chiều tối, kh&ocirc;ng sử dụng thuốc s&aacute;t tr&ugrave;ng trong v&ograve;ng 48 giờ.</p>\r\n', '<p>\r\n	Yucca schidigera&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;100%<br />\r\n	Hoạt lực&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;75%<br />\r\n	<br />\r\n	&nbsp;</p>\r\n', 'Chai 1 lít', 14, 0, 0, 'yucca 75, hpa thu khi doc, nh3', 108, 16, 0, 0, 1329410198, 1324447808, 2389, 1),
(9, 0, 0, 'Framin 500', 'pic_framin_500.png', 3, 1, 'Gia tăng độ ngon miệng, kích thích bắt mồi', '<p>\r\n	+ Cung cấp hỗn hợp acid amin và vitamin thiết yếu giúp cải thiện chất lượng thức ăn.<br />\r\n	+ Gia tăng độ ngon miệng, kích thích bắt mồi.<br />\r\n	+ Giảm chi phí sử dụng thức ăn, nâng cao chất lượng thịt, mang lại hiệu quả kinh tế cao nhất.<br />\r\n	&nbsp;</p>\r\n', '<p>\r\n	+ Sử dụng thường xuyên:&nbsp; 2-4 ml/1kg thức ăn.<br />\r\n	+ Chống stress, tăng trọng nhanh: sử dụng liều gấp đôi.<br />\r\n	<br />\r\n	Hòa tan FRAMIN hoàn toàn vào một lượng nước sạch vừa đủ rồi phun lên thức ăn, để ngấm 15 phút, sau đó cho cá ăn.</p>\r\n', '<p>\r\n	Acid amin tự do &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;15%<br />\r\n	(Methionine &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;13,73%<br />\r\n	Lysine &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 8,07%<br />\r\n	Cystine &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 1,67%<br />\r\n	Phenylalanyl &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 3,13%<br />\r\n	Leucine&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 4,73%<br />\r\n	Isoleucine&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 3,27%<br />\r\n	Glycine&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 5,73%<br />\r\n	Glutamic acid&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 9,53%<br />\r\n	Threonine &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 3,53%<br />\r\n	Proline, Analine, Aspartic acid,<br />\r\n	Valine, Polysorbat, Serine, &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 46,6%&nbsp;&nbsp; &nbsp;Arginine, Histidine,… )<br />\r\n	Vitamin nhóm B (B1, B2, B6)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 3000mg&nbsp;&nbsp;&nbsp;<br />\r\n	Dung môi vừa đủ&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;1000ml&nbsp;&nbsp;&nbsp;</p>\r\n', 'Chai 500 ml', 14, 0, 0, 'framin, dinh duong, acid amine', 83, 11, 0, 0, 1333009530, 1324448118, 1755, 1),
(22, 0, 0, 'FLUME-BATH F/S2', 'flume-bath.jpg', 2, 1, 'Hòa tan trong nước - Tắm tôm', '<p>\r\n	Hòa vào nước rộng tôm để trị các bệnh nhiễm khuẩn kháng với các kháng sinh thông thường như Ampi, Tetra .....<br />\r\n	Các bệnh đỏ thân, đốm đen, rụng đuôi, râu, càng, bệnh gan trắng và bệnh phát sáng. Đặc hiệu trên các chủng Vibrio spp, Pseudomonas spp, Enterobacteria, Aeromonas, Chitinoverous bacteria, nhóm vi khuẩn dạng sợi và bội nhiễm nấm.</p>\r\n', '<p>\r\n	Gói 100g dùng cho khối lượng 200 lít nước. Hòa nước trong thau hay xô nhựa 5 lít (kỵ đồ sắt và đồ nhôm) trước khi đổ vào bể chứa tôm. Vớt tôm muốn điều trị bỏ vào bể pha thuốc trong 3 giờ, sau đó thay nước sạch lại cho tôm 1- 2 lần.<br />\r\n	* Ngưng sử dụng thuốc 4 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	<font face="Tahoma">Flumequine</font></p>\r\n', 'Gói 100g', 21, 0, 0, '', 18, 3, 0, 0, 1358774860, 1333082992, 1770, 1),
(10, 0, 0, 'Anato liver', 'pic_anato_liverw.png', 3, 0, 'Nhuận tràng, giải độc gan', '<p>\r\n	- Cung cấp sorbitol v&agrave; c&aacute;c acid amin, vitamin thiết yếu gi&uacute;p cải thiện v&agrave; tăng cường chức năng của gan, mật, đồng thời dẫn dụ bắt mồi, gia tăng độ ngon miệng, k&iacute;ch th&iacute;ch tăng trọng, n&acirc;ng cao sức đề kh&aacute;ng.<br />\r\n	- Gi&uacute;p nhuận tr&agrave;ng thẩm thấu, giải độc gan, mật. Tăng cường sự vận chuyển qua ống ti&ecirc;u h&oacute;a gi&uacute;p t&ocirc;m ti&ecirc;u h&oacute;a v&agrave; hấp thụ c&aacute;c chất dinh dưỡng tốt hơn, giảm ti&ecirc;u hao thức ăn.<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', '<p>\r\n	+ Trộn 2 -3 ml/kg thức ăn. D&ugrave;ng li&ecirc;n tục trong suốt qu&aacute; tr&igrave;nh nu&ocirc;i. Trong thời gian trị bệnh sử dụng liều gấp đ&ocirc;i.</p>\r\n', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				- Sorbitol&nbsp;&nbsp;</td>\r\n			<td>\r\n				540.000 mg</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				- Total Amino Acids<br />\r\n				(Methionine 2.975mg,<br />\r\n				Lysine 1.400 mg,<br />\r\n				Alanine 350 mg,Glycine 4.200 mg,<br />\r\n				Aspartic acid 735 mg, Glutamic acid 840 mg)</td>\r\n			<td>\r\n				10.500 mg</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				- Vitamin:&nbsp;&nbsp;&nbsp;</td>\r\n			<td>\r\n				100 mcg&nbsp;&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;+ Biotin&nbsp;&nbsp;</td>\r\n			<td>\r\n				12.000 IU</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				+ Vitamin E</td>\r\n			<td>\r\n				5.000 mg</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'Chai 1 lit', 14, 0, 0, 'giai doc gan, nhan trang, dinh duong tom su', 141, 19, 0, 0, 1328715101, 1324448600, 1796, 1),
(18, 0, 0, 'VIME - FENFISH', 'VIME-FENFISH.gif', 2, 1, 'Đặc trị bệnh phân trắng', '<p>\r\n	- Đặc trị bệnh phân trắng, phát sáng trên tôm do các giống vi khuẩn Vibrio spp, và các vi khuẩn gây bệnh đường ruột.<br />\r\n	- Phòng và trị các bệnh hoại tử ở vỏ tôm, càng tôm, đen mang, đốm đen (thường thấy ở bụng ), hoại tử gan.<br />\r\n	- Phòng trị các bệnh thường thấy trên tôm sú, tôm càng xanh như: đỏ thân, mang đỏ, sưng mang do các vi khuẩn Pseudomonas, Filamentous, Aeromonas gây ra.</p>\r\n', '<p>\r\n	Trộn vào thức ăn cho tôm, dùng liên tục 5-7 ngày.<br />\r\n	Liều dùng : 4ml/ kg thức ăn (100ml trộn với 25kg thức ăn dùng cho khoảng 500 kg tôm nuôi).<br />\r\n	- Nếu là thức ăn viên nên áo bên ngoài bằng Vime - Dầu gan mực hoặc Vime - Lecithin.<br />\r\n	- Ngưng sử dụng 2 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	Florfenicol............................50.000 mg<br />\r\n	Exp.qsp...................................1.000 ml</p>\r\n', 'Chai 250 ml', 21, 0, 0, 'trị bệnh phân trắng, trị bệnh phát sáng, trị các bệnh vi khuẩn đường ruột, bệnh trên tôm sú, bênh trên tôm càng xanh', 64, 10, 0, 0, 1332927157, 1332922286, 2677, 1),
(19, 0, 0, 'KILL-ALGAE', 'kill_algae_tom.gif', 1, 0, 'Diệt rong nhớt', '<p>\r\n	- Diệt trừ tảo độc, giảm mật độ tảo trong ao nuôi.<br />\r\n	- Đặc trị các bệnh do nấm , nguyên sinh động vật , ký sinh trùng gây bệnh ngoài võ tôm như: tôm bị đóng rong, mòn đuôi, đen mang, đóng nhớt và mòn võ tôm..<br />\r\n	- Phòng các bệnh do vi khuẩn và virus gây ra : đốm trắng, đầu vàng, phát sáng, bệnh đen mang, sưng phồng nắp mang.<br />\r\n	- Kích thích tôm lột vỏ, mau lớn.</p>\r\n', '<p>\r\n	- Phòng tảo độc xuất hiện trong ao: 1 lít/2000-2500m3 nước, định kỳ 2-3 tuần/1 lần.<br />\r\n	- Diệt tảo độc, điều chỉnh mật độ tảo trong ao nuôi: 1 lít / 1500-2000 m3 nước.<br />\r\n	- Trị các bệnh do nấm, nguyên sinh động vật, ký sinh truìng gây bệnh ngoài võ tôm: 1lít/1000m3 nước .<br />\r\n	- Diệt trừ vi khuẩn gây bệnh phát sáng và các loại virus gây bệnh: 1-2lít/1000m3 nước .</p>\r\n', '<p>\r\n	<font face="Tahoma">Copper as Elemental , Edtic acid sod</font></p>\r\n', 'Chai 1 lít', 21, 0, 0, 'Copper as Elemental , Edtic acid sod, diệt tảo độc, kích thích tôm lột vỏ', 130, 22, 0, 0, 1332922985, 1332922985, 3365, 1),
(20, 0, 0, 'ANTI RED', 'antired_tom.gif', 2, 0, 'Đặc trị bệnh phát sáng ở tôm', '<p>\r\n	-&nbsp;Anti RED&nbsp;là sản phẩm mới phối hợp gồm nhiều loại kháng sinh phổ rộng tác dụng mạnh hầu hết các vi khuẩn gây bệnh trên tôm.<br />\r\n	- Phòng và trị bệnh tôm sú và tôm thẻ : bệnh phát sáng do vi khuẩn Vibrio, bệnh đỏ mang, phồng nắp mang, rụng chân và càng tôm, lở và mòn vỏ tôm, thối đuôi, nhiễm trùng đường tiêu hóa, hoại tử gan.</p>\r\n', '<p>\r\n	- Tôm lớn : 100g / 200kg tôm hoặc trộn đều với 8 - 10 kg thức ăn để nguội. Dùng liên tục từ 5 - 7 ngày.<br />\r\n	- Tôm nhỏ : 100g trộn đều 2-3 kg thức ăn để nguội dùng cho 7.500 - 9.000 con tôm (khoảng 10g/con). Dùng liên tục từ 3 - 5 ngày.</p>\r\n<p>\r\n	* Chú ý :<br />\r\n	- Để đảm bảo đủ lượng thuốc, khi điều trị nên tính theo trọng lượng tôm vì khi bệnh tôm thường giảm ăn.<br />\r\n	- Ngưng sử dụng thuốc 4 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	Sulfamethoxazole, Trimethoprim</p>\r\n', 'Gói 100g', 21, 0, 0, 'Sulfamethoxazole, Trimethoprim, đặc trị phát sáng ở tôm, trị phát sáng', 73, 10, 0, 0, 1332925693, 1332925693, 2437, 1),
(21, 0, 0, 'VIME-N333 (SHRIMP)', 'VIME-N333.jpg', 2, 1, 'Đặc trị hoại tử cơ, hoại tử gan, rụng râu, gãy càng', '<p>\r\n	Phòng và trị bệnh do các vi khuẩn giống Vibrio, Aeromonas, Leucothrix... gây ra thể hiện các triệu chứng như phồng nắp mang, mang đen, đốm đen, phụ bộ bị rụng, đuôi sưng mọng nước, thối đuôi hoại tử gan.</p>\r\n', '<p>\r\n	Dùng liên tục 5 - 7 ngày.<br />\r\n	* Hộp 100g trộn với 50kg thức ăn dùng cho 1500kg tôm lớn.<br />\r\n	* Bệnh nặng có thể dùng hộp 100g trộn với 25 - 40 kg thức ăn.<br />\r\n	* Ngưng sử dụng thuốc 4 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	<font face="Tahoma">Norfloxacin</font></p>\r\n', 'Hộp 100g', 21, 0, 0, 'Đặc trị hoại tử cơ, hoại tử gan, rụng râu, gãy càng', 85, 13, 0, 0, 1332925991, 1332925991, 2339, 1),
(24, 0, 0, 'VIMENRO', 'vimenro.jpg', 2, 1, 'Kháng sinh thế hệ mới nhất', '<p>\r\n	Trị tôm phồng nắp mang, mang bị đen, đốm đen, chân càng bị rụng, đuôi sưng mọng nước, thối đuôi, hoại tử gan, phát sáng...</p>\r\n', '<p>\r\n	Cho ăn: sử dụng liên tục 10 - 14 ngày<br />\r\n	- Tôm lớn: 100g thuốc trộn với 30 - 35kg thức ăn (500g thuốc trộn với 150 - 175 kg thức ăn ).<br />\r\n	- Tôm nhỏ : 100g thuốc trộn với 10 - 15kg thức ăn ( 500g trộn với 50 - 75 kg thức ăn)<br />\r\n	- Phòng bệnh dùng nửa liều ghi trên.<br />\r\n	Tắm: sử dụng trong trường hợp bệnh nặng và tôm bỏ ăn. Pha 500g thuốc vào 2000 lít nước để ngâm tôm trong vòng 3 - 6 giờ, sau đó thay bằng nước sạch.<br />\r\n	* Ngưng sử dụng thuốc 4 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	<font face="Tahoma">Enrofloxacin </font></p>\r\n', 'Hộp 100g', 21, 0, 0, 'Enrofloxacin, Trị tôm phồng nắp mang, mang bị đen, đốm đen, chân càng bị rụng, đuôi sưng mọng nước, thối đuôi, hoại tử gan, phát sáng', 102, 14, 0, 0, 1333084943, 1333083351, 3749, 1),
(26, 0, 0, 'DE-AMIN', 'de-amin.jpg', 3, 1, 'Tỉ lệ sống cao - tăng trọng nhanh', '<p>\r\n	-Giúp cho tôm mau lớn, vượt qua các tác nhân gây bệnh xâm nhập.<br />\r\n	-Tạo nền tảng cho sự phát triển cho của tôm khi chuyển sang nuôi tôm thịt.<br />\r\n	-Loại trừ các hiện tượng tôm bị xuất huyết mắt, phát triển dị dạng, hoại cơ, kém ăn,... do thiếu vitamin A,D,E.</p>\r\n', '<p>\r\n	Trộn vào thức ăn cho tôm theo liều sau:<br />\r\n	- Tôm nhỏ: 1kg trộn với 200kg thức ăn<br />\r\n	- Tôm lớn: 1kg trộn với 500kg thức ăn.<br />\r\n	* Để pha trộn sản phẩm này với thức ăn đồng đều, nên trộn sản phẩm với 1/10 lượng thức ăn trước rồi trộn với toàn bô lượng thức ăn còn lại.Nếu là loại thức ăn viên nên áo dầu mực hoặc dầu cá bên ngoài, để khô 15 phút trước khi cho ăn.</p>\r\n', '<p>\r\n	Vitamin A, Vitamin D3, Vitamin E, L- Lysine, L- Methionine, L-Cystine, L-Threonin, L- Leucine, L-Isoleucine, L-Phenylalanine, L-Tyrosine, L-Tryptophan, L-Valine, L-Glycin, ...</p>\r\n', 'Gói 100g', 21, 0, 0, 'dinh dưỡng thủy sản, Vitamin A, Vitamin D3, Vitamin E, L- Lysine, L- Methionine, L-Cystine', 8, 2, 0, 0, 1333196633, 1333196633, 1034, 1),
(25, 0, 0, 'VIMEQUINE 10%', 'VIMEQUINE.jpg', 2, 1, 'Đặc trị bệnh phát sáng', '<p>\r\n	Ngừa và trị nhiễm trùng đường hô hấp và tiêu hóa do vi khuẩn Gram âm. Đặc trị nhiễm trùng máu do Aeromonas, Yesinia, Vibrio, Edwardsiella, nhiễm trùng sau bệnh siêu vi hay Stress, gây ra các bệnh xuất huyết ruột, hoại tử gan, cơ.</p>\r\n', '<p>\r\n	100g Vimequin 10% trộn đều cho 20 kg thức ăn để nguội. Dùng cho 500 -600kg tôm.<br />\r\n	* Ngưng sử dụng thuốc 4 tuần trước khi thu hoạch.</p>\r\n', '<p>\r\n	Flumequin</p>\r\n', 'Hộp 100g', 21, 0, 0, 'Flumequin, tôm phát sáng, trị tôm phát sáng', 41, 8, 0, 0, 1333084951, 1333083626, 1518, 1),
(27, 0, 0, 'NUTRITION FOR SHRIMP', 'nutrition-for-shrimp.jpg', 3, 1, 'Dinh dưỡng cho tôm', '<p>\r\n	* Giúp tôm tăng trưởng nhanh, khỏe mạnh, tỉ lệ sống cao.<br />\r\n	- Đối với tôm bột : tạo nền tảng cho sự phát triển của tôm khi chuyển sang nuôi tôm thịt.<br />\r\n	- Đối với tôm lớn : giúp tôm đề kháng tốt với mầm bệnh, sự thay đổi đột ngột của môi trường, nhiệt độ.</p>\r\n', '<p>\r\n	- Chai 250ml dùng trộn cho 250kg thức ăn cho tôm.<br />\r\n	- Nên trộn NUTRITION for shrimp vào thức ăn mỗi lần cho ăn.</p>\r\n', '<p>\r\n	<font face="Tahoma">Calcium gluconate,<br />\r\n	Vitamin A,D3,E, B12,B6,PP </font></p>\r\n', 'Chai 250 ml', 21, 0, 0, 'Calcium gluconate, Vitamin A,D3,E, B12,B6,PP ,NUTRITION', 50, 6, 0, 0, 1333196964, 1333196964, 1859, 1),
(28, 0, 0, 'PROZYME FOR SHRIMP', 'prozym-for-shrimp.gif', 3, 1, 'Men tiêu hóa cho tôm', '<p>\r\n	- Prozyme for Shrimp chứa nhiều loại vitamin và enzyme rất cần thiết cho sự tiêu hóa và tăng trọng của tôm.<br />\r\n	- Trộn thêm Prozyme vào thức ăn sẽ giúp tôm tiêu hóa hoàn toàn các chất bổ dưỡng có trong thức ăn đồng thời cũng tiêu hóa tốt các loại thức ăn tự nhiên mà tôm ăn như : tảo, cua, cá nhỏ.<br />\r\n	- Giúp tôm đề kháng tốt với sự thay đổi đột ngột của môi trường như chất lượng nước, nhiệt độ.<br />\r\n	- Kích thích tôm mau lớn và mau cứng vỏ sau khi lột vỏ.</p>\r\n', '<p>\r\n	Trộn 500g thật đều với 50kg thức ăn hỗn hợp ( hoặc tấm, cám, bột cá...) hoặc 500g trộn với 200kg thức ăn nấu chín để nguội.</p>\r\n', '<p>\r\n	Protease, Amylase, Cellulase, Lipase, Pectinase, Vitamin A,<br />\r\n	Vitamin D3, Vitamin E, Vitamin K3, Stay C</p>\r\n', 'Hộp 1kg', 21, 0, 0, 'Protease, Amylase, Cellulase, Lipase, Pectinase, men tiêu hóa, men tôm, tiêu hóa', 16, 3, 0, 0, 1333197488, 1333197488, 1161, 1),
(29, 0, 0, 'Virkon A', 'VirkonA.gif', 1, 1, 'Sát trùng phổ cực rộng', '<p>\r\n	- Sát trùng, diệt virus, vi khuẩn, nấm... trong nước ao để phòng bệnh virus Đốm trắng, Taura..lây nhiễm qua nguồn nước, đồng thời phòng các bệnh do vi khuẩn Vibrio.sp gây ra cho tôm</p>\r\n', '<p>\r\n	<strong>Xử lý nước trong nuôi tôm thịt</strong></p>\r\n<p>\r\n	- Để xử lý nước trước khi thả tôm: liều 0.6 - 1 kg/1.000m3 . Sau 1 ngày có thể thả tôm an toàn và gây tảo dễ dàng.</p>\r\n<p>\r\n	- Để xử lý trực tiếp nước ao trong lúc đang nuôi khi các ao xung quanh đang bị dịch bệnh virus, vi khuẩn...hoặc khi môi trường nước trong ao xấu, có nguy cơ bùng nổ dịch bệnh.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; Phòng định kỳ 1 tháng/1 lần: liều 0.6 - 1 kg/1.000m3<br />\r\n	&nbsp;&nbsp;&nbsp; Khi xung quanh có dịch bệnh: liều 1-1,2 kg/1.000m3</p>\r\n<p>\r\n	- Hòa tan với nước rồi tạt đều xuống ao vào bất cứ thời điểm nào trong ngày</p>\r\n<p>\r\n	<strong>Xử lý nước trong trại tôm giống</strong></p>\r\n<p>\r\n	- Để xử lý nguồn nước phục vụ cho ương ấp ấu trùng hoặc xử lý trực tiếp trong bể ương tôm từ Post 2 đến Post 15: liều 1-2 gam/m3 nước</p>\r\n<p>\r\n	- Để tắm cho ấu trùng Nauplius: liều 50 ppm trong 1 phút</p>\r\n<p>\r\n	Sát trùng dụng cụ</p>\r\n<p>\r\n	- Để ngăn ngừa virus lây lan trong trại nuôi, ngâm các dụng cụ dùng chung (lưới, vợt...) từ 2 - 3 giờ trong dung dịch Virkon A pha với tỷ lệ 100 g trong 10 lít nước.</p>\r\n', '<p>\r\n	Peroxygen, Organic acid, Inorganic buffer system, Surfactant</p>\r\n', 'Hộp 1kg', 22, 0, 0, 'sát trùng, Peroxygen, Organic acid, Inorganic buffer system, Surfactant', 525, 78, 0, 0, 1333201636, 1333201464, 7081, 1),
(30, 0, 0, 'VIME-AQUAZYME', 'vime-aquazyme.jpg', 3, 1, 'Men vi sinh của Mỹ + Vi khuẩn hữu ích', '<p>\r\n	Thức ăn vi sinh chất lượng cao, phòng hiệu quả bệnh phân trắng.<br />\r\n	- Tăng khả năng hấp thu các chất dinh dưỡng, khoáng, vitamin thiết yếu từ thức ăn, giúp tôm cá tăng trưởng nhanh.<br />\r\n	- Đặc biệt có Enzyme quan trọng là Phytase giúp chuyển hóa phospho trong thức ăn thành phospho dễ hấp thu, phòng và trị hiệu quả bệnh mềm vỏ kinh niên.</p>\r\n', '<p>\r\n	Trộn Aquazyme vào thức ăn mỗi tuần 2-3 lần để phòng bệnh. Sau khi tôm bệnh hoặc sau khi dùng thuốc kháng sinh thì sử dung liên tục trong 1 tuần.<br />\r\n	- 5g/kg thức ăn hỗn hợp . (Gói 100g dùng cho 20kg thức ăn khô hoặc 60 kg thức ăn nấu nhão để nguội).<br />\r\n	Chú ý : Khi trộn với thức ăn viên nên áo 1lớp dầu mực hoặc dầu cá bên ngoài để bảo đảm lượng thuốc bám vào thức ăn và kích thích tôm ăn nhiều.</p>\r\n', '<p>\r\n	Lactobacillus acdophilus, Bacillus subtilis, Saccharomyces, Aspergillus oryzae, Phytase, Protease, Amylase, Cellulase, Lipase, Pectinase, Vitamin C coated, Vitamin B1, Sodium benzoate, Vitamin B6</p>\r\n', 'Hộp 500g', 21, 0, 0, 'men vi sinh, enzyme cho tom, men vi sinh cho tom, men vi sinh cho tôm', 26, 5, 0, 0, 1334137394, 1334137394, 1323, 1),
(31, 0, 0, 'VIME-LECITHIN', 'vime_lecithin_tom.gif', 3, 1, 'Kích thích bắt mồi - Cung cấp dinh dưỡng Bao bọc viên thức ăn', '<p>\r\n	- Tăng tính thèm ăn, tôm mau lớn, khỏe mạnh, đạt năng suất cao.<br />\r\n	- Có mùi thơm, kích thích tôm ăn nhiều, tiêu hóa tốt.<br />\r\n	- Bao bọc viên thức ăn, tăng hiệu quả các các loại thuốc trộn kèm với thức ăn, thuốc không bị thất thoát ra môi trường ngoài.<br />\r\n	- Cung cấp Vitamin A, D, E, năng lượng và các acid béo cần thiết cho sự tăng trưởng của tôm.</p>\r\n', '<p>\r\n	- Trộn thường xuyên mỗi lần cho ăn.<br />\r\n	- Trộn 10-15g/kg thức ăn (tương đương với 10-15ml/kg thức ăn). 1lít Vime-Lecithin trộn với 75-100kg thức ăn.<br />\r\n	- Nếu thức ăn có trộn thuốc, thì hòa tan thuốc với 1 ít nước phun đều lên thức ăn, sau đó dùng Vime-Lecithin bao bọc bên ngoài thức ăn, rồi để yên 15 phút trước khi cho tôm ăn.<br />\r\n	Chú ý : Lắc đều trước khi sử dụng</p>\r\n', '<p>\r\n	<font face="Tahoma">Vitamin A, D3,E </font></p>\r\n', 'Chai 1 lít', 21, 0, 0, 'Vitamin A, D3,E , lecithin, bao bọc thức ăn, áo thức ăn, bọc thức ăn tôm', 91, 13, 0, 0, 1334137638, 1334137638, 1903, 1),
(32, 0, 0, 'VIME-SUBTYL', 'vime-subtyl.jpg', 3, 1, 'Vi Khuẩn Hữu Ích Dùng Cho Thủy Sản', '<p>\r\n	- Ổn định hệ vi khuẩn có lợi đường ruột, tiêu diệt các vi khuẩn có hại, giúp phòng chống hữu hiệu các bệnh đường tiêu hóa, giúp hoàn thiện hệ tiêu hóa tôm, cá nhanh lớn hơn.<br />\r\n	- Giúp tôm, cá tiêu hóa tốt, tăng trưởng nhanh nhờ vi sinh vật sản sinh ra men để tiêu hóa bột, protein và chất béo.<br />\r\n	- Làm thuỷ phân nhanh chóng xác thực vật, động vật, thức ăn thừa và các chất mùn bã hữu cơ khác để đem lại môi trường nước trong lành.<br />\r\n	- Làm màu nước ổn định (tảo không bị tàn lụi thường xuyên) gây màu nước.</p>\r\n', '<p>\r\n	Trộn vào khẩu phần thức ăn hoặc pha vào nước rồi đem tạt khắp ao.<br />\r\n	Trộn vào thức ăn :<br />\r\n	- Cá : 1kg trộn với 500 - 700kg thức ăn, trộn đều ủ khoảng 2 giờ cho cá ăn.<br />\r\n	- Tôm : 1kg trộn với 400 - 500kg thức ăn, trộn đều ủ khoảng 2 giờ cho tôm ăn.<br />\r\n	Sử dụng tạt xuống ao : 1kg/10.000m3nước, pha trước với khoảng 5 - 10 lít nước để yên 2 giờ rồi tạt đều xuống ao nuôi, 7 ngày tạt một lần.</p>\r\n', '<p>\r\n	<font face="Tahoma">Bacillus subtilis </font></p>\r\n', 'Gói 1kg', 21, 0, 0, 'Bacillus subtilis , vi sinh cho tôm, vi khuẩn hữu ích', 28, 4, 0, 0, 1334138084, 1334138084, 2021, 1),
(33, 0, 0, 'VIMERAL FOR SHRIMP', 'vimeral.jpg', 3, 1, 'Bổ sung khoáng cho tôm', '<p>\r\n	- Tạo điều kiện cho tôm mau lột xác, nhanh cứng vỏ sau khi lột xác.<br />\r\n	- Bổ sung khoáng cần thiết cho tôm, giúp tôm tăng trưởng nhanh, rút ngắn thời gian nuôi.<br />\r\n	- Tăng cường sức đề kháng cho tôm khi môi trường thay đổi.</p>\r\n', '<p>\r\n	- 1kg Vimeral trộn với 400kg thức ăn.<br />\r\n	+ Từ Poslarvae (tôm 12 ngày tuổi) đến 1 tháng tuổi : trộn thường xuyên vào thức ăn.<br />\r\n	+ Tôm trên 1 tháng tuổi trở đi : dùng trước và sau khi lột xác 7 ngày.</p>\r\n<p>\r\n	Chú ý : trộn thuốc trước với một lượng thức ăn vừa đủ sau đó trộn vào toàn bộ lượng thức ăn còn lại. Khi trộn thuốc xong nên áo một lớp dầu mực hoặc dầu cá bên ngoài để đảm bảo lượng thuốc bám vào thức ăn và kích thích tôm ăn nhiều.</p>\r\n', '<p>\r\n	<font face="Tahoma">Copper,Iron,Cobalt, Zinc, Selenium, Magnesium, Manganese, Iodine, Calcium,Phosphorous </font></p>\r\n', 'Gói 1kg', 21, 0, 0, 'Copper,Iron,Cobalt, Zinc, Selenium, Magnesium, Manganese, Iodine, Calcium,Phosphorous, kháng tôm, khoáng cho tôm', 56, 7, 0, 0, 1334138342, 1334138342, 1894, 1),
(34, 0, 0, 'PROBAI', 'Probai-1kg.gif', 1, 1, 'Vi sinh xử lý nước.', '<p>\r\n	- Bổ sung vi khuẩn có lợi vào đường ruột để cạnh tranh và loại bỏ vi khuẩn có hại gây các bệnh như phân trắng, phát sáng, sưng gan...<br />\r\n	- Hỗ trợ quá trình tiêu hóa thức ăn, giúp tôm nuôi sử dụng thức ăn hiệu quả.</p>\r\n', '<p>\r\n	- Trộn 5 - 10 g/kg thức ăn, liên tục từ khi mới thả cho đến khi thu hoạch để duy trì số lượng vi sinh có lợi trong đường ruột tôm<br />\r\n	- Khi ao nuôi gặp điều kiện bất lợi (thời tiết biến đổi, tảo chết, môi trường biến động, ao nhiều vi khuẩn, xung quanh có dịch bệnh...) cần tăng liều Probai lên tối đa<br />\r\n	- Không trộn chung với thuốc kháng sinh. Nếu đã dùng thuốc kháng sinh trị bệnh thì 7 ngày sau khi hết dùng kháng sinh mới trộn cho ăn Probai<br />\r\n	- Hòa thuốc vào nước sạch, phun đều lên thức ăn, sau đó áo bằng chất kết dính, để ráo 15 - 20 phút trước khi cho ăn</p>\r\n', '<p>\r\n	<em>Lactobacillus plantarum<br />\r\n	Bacillus subtilis<br />\r\n	Bacillus licheniformis</em></p>\r\n', 'Gói 1kg', 22, 0, 0, 'Lactobacillus plantarum, Bacillus subtilis, Bacillus licheniformis, vi sinh, cho tôm', 162, 29, 0, 0, 1338116171, 1338116171, 4879, 1),
(35, 0, 0, 'Osamet Shrimp', 'Osamet_shrimp.gif', 2, 1, 'Phòng trị bệnh do vi khuẩn.', '<p>\r\n	Trị bệnh do vi khuẩn Vibrio (Vibrio vulnificus, V.alginolitycus, V.harveyi,... ) gây ra cho tôm như bệnh như phát sáng, phân trắng, đứt râu, phồng đuôi, hoại tử, đen mang, đốm nâu...</p>\r\n', '<p>\r\n	- Tôm 1 - 2 tháng tuổi: Trộn 5g/kg thức ăn, điều trị liên tục trong 5 - 7 ngày<br />\r\n	- Tôm 3 - 4 tháng tuổi trở đi: Trộn 10g/kg thức ăn, điều trị liên tục trong 5 - 7 ngày<br />\r\n	- Hòa thuốc vào nước sạch, phun đều lên thức ăn, sau đó áo bằng chất kết dính, để khô 15 - 20 phút trước khi cho ăn<br />\r\n	Lưu ý: Ngưng sử dụng sản phẩm trước khi thu hoạch 4 tuần.</p>\r\n', '<p>\r\n	Romet ® (Sulfadimethoxine 25%, Ormetoprim 5%)<br />\r\n	Vitamin C<br />\r\n	Vitamin E<br />\r\n	Selenium hữu cơ</p>\r\n', 'Gói 500g', 22, 0, 0, 'Sulfadimethoxine,Ormetoprim,Romet', 216, 35, 0, 0, 1338116449, 1338116449, 5364, 1),
(36, 0, 0, 'GREENZYME', 'Green-zyme.jpg', 3, 2, 'Tiêu hóa hấp thụ dinh dưỡng tối ưu.', '<p>\r\n	Bổ sung các vitamin thiết yếu giúp cải thiện chất lượng thức ăn, tăng trưởng nhanh. Tăng khả năng tái tạo máu.<br />\r\n	Bổ sung các enzyme tiêu hóa giúp tiệu hóa nhanh, hấp thu dinh dưỡng tối đa.<br />\r\n	Ngăn ngừa các bệnh trên đường tiêu hóa, phục hồi nhanh sau khi sử dụng kháng sinh.<br />\r\n	Giúp phân hủy nhanh các chất cặn bã, khí độc, làm môi trường ao nuôi luôn sạch.</p>\r\n', '<p>\r\n	Trộn 2g Greenzyme/kg thức ăn.</p>\r\n', '<p>\r\n	Vitamin A&nbsp; 6.000.000 IU<br />\r\n	Vitamin D3&nbsp; 1.500.000 IU<br />\r\n	Vitamin E&nbsp; 8.000mg<br />\r\n	Vitamin B1&nbsp; 2.500mg<br />\r\n	Vitamin B2&nbsp; 2.500mg<br />\r\n	Vitamin B6&nbsp; 40mg<br />\r\n	Vitamin PP&nbsp; 5.000mg<br />\r\n	Acid formic&nbsp; 500mg<br />\r\n	Enzyme&nbsp; 10%<br />\r\n	Tá dược vừa đủ 1.000g</p>\r\n', 'Lon 1kg', 24, 0, 0, '', 25, 4, 0, 0, 1338865257, 1338865257, 1527, 1),
(37, 0, 0, 'Quillaja Yucca Blend', 'Quillaja-yucca.jpg', 1, 1, 'Hấp thụ nhanh khí độc - Cấp cứu tôm nổi đầu.', '<p>\r\n	Hấp thụ nhanh khí độc NH3, H2S,NO2… có trong ao nuôi, cải thiện môi trường nước.<br />\r\n	Cấp cứu khẩn cấp tôm nổi đầu do môi trường ô nhiễm hoặc thiếu oxy.<br />\r\n	Chống sốc khi môi trường ao nuôi biến động.</p>\r\n', '<p>\r\n	• Trại giống: 10 - 15 ml/10m3 nước bể ương.<br />\r\n	• Nuôi tôm thịt: Loại trừ khí độc NH3 trong ao nuôi, sử dụng định kỳ 7- 10 ngày 1 lần vào buổi chiều tối.<br />\r\n	• Tôm giai đoạn từ mới thả đến 2 tháng tuổi: 500ml/ 8.000 – 10.000m3 nước ao nuôi.<br />\r\n	• Tôm giai đoạn từ 2 tháng tuổi đến 4 tháng tuổi: 500ml/ 7.000 – 8.000m3 nước ao nuôi.<br />\r\n	• Giảm nhanh khí độc NH3, cấp cứu tôm nổi đầu, kéo đàn do thiếu oxy khi môi trường nước ao bị ô nhiễm nặng: 500ml/ 5.000 – 6.000m3&nbsp; nước ao nuôi.</p>\r\n', '<p>\r\n	Yucca schidigera 50,00%<br />\r\n	Quillaja saponia 50,00%</p>\r\n', 'Chai', 24, 0, 0, 'Yucca schidigera, Quillaja saponia', 71, 9, 0, 0, 1338865675, 1338865627, 2465, 1),
(38, 0, 5, 'Yucca 100 Liquid', 'Yucca-100.jpg', 1, 1, 'Cấp cứu tôm nổi đầu - Hấp thu nhanh khí độc', '<p>\r\n	- Hấp thụ cấp tốc khí độc NH3, H2S,NO2... trong ao tôm.<br />\r\n	- Cấp cứu tôm nổi đầu do ngộ độc.<br />\r\n	- Sử dụng cho tôm ăn giúp tăng cường chức năng ruột, nâng cao đáp ưng miễn dịch, tôm nuôi có màu sắc đẹp.</p>\r\n', '<p>\r\n	- Định kỳ 1 lít/8.000-10.000m3 nước ao nuôi, cứ 10-15 ngày sử dụng 1 lần.<br />\r\n	- Cấp cứu khi tôm nổi đầu do môi trường ô nhiễm hoăc thiếu oxy: 1 lít/ 8.000m3 nước ao nuôi.</p>\r\n<p>\r\n	Hòa sản phẩm với một lượng nước vừa đủ rồi tạt đều xuống ao, nên sử dụng vào lúc nhiệt độ nước ao nuôi cao nhất (14-16g giờ).</p>\r\n', '<p>\r\n	Yucca schidigera 100%</p>\r\n', 'Chai 1 lít', 24, 0, 20000, 'Yucca schidigera, Yucca, NH3, hấp thu khí độc', 74, 13, 0, 0, 1339553830, 1339553765, 2322, 1),
(39, 0, 5, 'Yucca 30 Powder', '[["/upload/product/thumb_image/2019/09/2a94f261fc101b4e4201_1569305476.jpg"],["/upload/product/thumb_image/2019/09/9bdd997e3a5ddd03844c_1569398446.jpg"]]', 1, 1, 'Ổn định môi trường nước.', '<p>\r\n	Hấp thụ khí độc NH3 có trong ao nuôi, cải thiện môi trường nước.</p>\r\n', '<p>\r\n	+ Nuôi tôm, cá thịt: sử dụng định kỳ 10- 15 ngày 1 lần.<br />\r\n	- Tôm, cá giai đoạn từ mới thả đến 2 tháng tuổi: 1.000g/6.000 -8.000m3 nước ao nuôi.<br />\r\n	- Tôm, cá giai đoạn từ 2 tháng tuổi đến 4 tháng tuổi: 1.000g/ 5.000 – 6.000m3 nước ao nuôi.<br />\r\n	Cấp cứu tôm, cá nổi đầu do thiếu oxy khi môi trường nước ao bị ô nhiễm nặng: 1.000g/ 4.000m3 nước ao nuôi</p>\r\n', '<p>\r\n	Yucca schidigera 100%</p>\r\n', 'Lon 1kg', 24, 17, 0, 'Yucca schidigera, Yucca powder, NH3, hấp thu khí độc', 72, 11, 100000, 0, 1339659581, 1339659581, 1960, 1),
(40, 0, 0, 'Green Bio', 'Green-bio.jpg', 3, 1, 'Tiêu hóa tối ưu - Ngăn ngừa bệnh đường ruột', '<p>\r\n	- Bổ sung vi khuẩn có lợi giúp tiệu hóa nhanh, hấp thu dinh dưỡng tối đa.<br />\r\n	- Ngăn ngừa các bệnh trên đường tiêu hóa, phục hồi nhanh sau khi sử dụng kháng sinh.<br />\r\n	- Giúp phân hủy nhanh các chất cặn bã, khí độc, làm môi trường ao nuôi luôn sạch.</p>\r\n', '<p>\r\n	Trộn 1kg GREEN-BIO cho 400-500kg thức ăn, ngày 1 lần. nên cho ăn liên tục trong suốt vụ nuôi.</p>\r\n', '<p>\r\n	<i>Bacilus subtilis ………………………….….....…9 x 10<sup>10 </sup>CFU.</i><br />\r\n	<i>Lacto Acidophilus………………………...………9 x 10<sup>9 </sup>CFU.</i><br />\r\n	<i>Saccharomyces……………………………..….…. 9 x10<sup>9 </sup>CFU.</i><br />\r\n	<i>Bacilus Licheniformis………………………..……9 x10<sup>9 </sup>CFU.</i><br />\r\n	<i>Tá dược vđ……………………………….……………..1.000g.</i></p>\r\n', 'Gói 1kg', 24, 0, 0, 'Bacilus subtilis, tiêu hóa, vi sinh đường ruột, thức ăn cá', 75, 11, 0, 0, 1344311071, 1344310908, 1906, 1),
(43, 0, 0, 'Potassium monopersulfate', 'image021.jpg', 1, 1, 'Sát trùng nước, ức chế vi sinh vật gây bệnh, cải tạo đáy ao, diệt sứa bi', '<p>\r\n	Sát trùng nước, ức chế vi sinh vật gây bệnh, cải tạo đáy ao, diệt sứa bi</p>\r\n', '<p>\r\n	Sử dụng trực tiếp trên ao nuôi, không gây shock cho thủy sản</p>\r\n', '<p>\r\n	&nbsp;Potassium monopersulfate 25%, 50%, 100% tùy theo nhu cầu sử dụng của khách hàng</p>\r\n', 'gói 500gr, 5 kg và theo yêu cầu', 31, 0, 0, '', 47, 8, 0, 0, 1369119819, 1363144102, 1805, 1),
(45, 0, 0, 'Aqua-C', 'AquaC_1.jpg', 1, 1, '100% Tự nhiên - Vi sinh cho nuôi trồng Thủy Sản', '<p>\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giữ cân bằng sinh thái cho ao nuôi bằng cách làm giảm chất thải hữu cơ và hạn chế hình thành lớp bùn đáy<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Làm giảm vi sinh gây bệnh và mầm bệnh thông qua ảnh hưởng của cơ chế cộng sinh<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tăng tỉ lệ sống, giảm tỉ lệ chết của tôm cá<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tôm cá lớn hơn khi thu hoạch, giá bán ra cao hơn<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cho phép kéo dài thời gian để đạt trọng lượng khi thu hoạch cao hơn<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cho phép thả với mật độ cao hơn, giảm tỉ lệ chết do đó sản lượng thu hoạch cao hơn<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giảm hệ số chuyển đổi thức ăn (FCR), tiết kiệm được chi phí thức ăn<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giảm tỉ lệ thay đổi nước, đảm bảo môi trường nuôi luôn sạch<br />\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giảm mùi , khí độc gây hại trong nước, tôm cá phát triển nhanh và chất lượng thịt tốt hơn</p>\r\n', '<p>\r\n	Theo hướng dẫn</p>\r\n', '<p>\r\n	các vi sinh vật</p>\r\n', 'chai 1 lít', 33, 0, 0, '', 107, 15, 0, 0, 1369119842, 1366705463, 1829, 1),
(48, 0, 0, 'TOSEL', 'tosel-to-truc.jpg', 3, 1, 'PHÒNG VÀ TRỊ HIỆN TƯỢNG TÔM BỊ CONG THÂN, ĐỤC CƠ', '<p>\r\n	- TOSEL là sản phẩm được tinh chế từ selenium hữu cơ, giúp tôm kích thích mau lớn,&nbsp; đặc biệt nhằm ngăn chặn triệt để sự phát triển bệnh đục cơ, trắng cơ, cong thân trên tôm thẻ và tôm sú.</p>\r\n', '<p>\r\n	-&nbsp; Trộn thức ăn liên tục cho tôm 5-10g/ 1kg thức ăn tôm</p>\r\n<p>\r\n	-&nbsp; Kết hợp tạt ao, trường hợp tôm có biểu hiện đục cơ , cong thân nhiều . 227g + 1kg MixCare / 1.000 m<sup>3</sup> nước ao.</p>\r\n', '<p>\r\n	Tinh chế từ selenium hữu cơ</p>\r\n', 'Gói 227g', 39, 0, 0, '', 80, 10, 0, 0, 1371697384, 1371697384, 1653, 1),
(49, 0, 0, 'COPPER', 'copper-to-truc.gif', 1, 1, 'DIỆT KHUẨN, CẮT TẢO ĐỘC', '<p>\r\n	- Diệt khuẩn môi trường ao nuôi thủy sản<br />\r\n	- Cắt tảo độc trong ao nuôi: Tảo đỏ, tảo nước sơn, tảo sợi. Không gây sốc, ảnh hưởng đến sức khoẻ tôm, cá.<br />\r\n	- Kích thích tạo máu, tăng cường miễn dịch.<br />\r\n	- Trị bệnh lở loét ở cá. Chuyên trị bệnh khói đèn, sâu đuôi ở tôm.</p>\r\n', '<p>\r\n	- Cho ăn: 2-4g/ 1kg thức ăn. Cho ăn liên tục suốt vụ nuôi. Khi tôm, cá yếu sử dụng liều gấp đôi.</p>\r\n<p>\r\n	- Tạt ao: + Diệt khuẩn: 454g/ 5.000 m<sup>3</sup> nước<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Diệt tảo đỏ: 454g/ 3.000m<sup>3</sup> nước</p>\r\n', '<p>\r\n	Cập nhật...</p>\r\n', 'Gói 454g', 39, 0, 0, '', 77, 11, 0, 0, 1371697781, 1371697781, 1938, 1),
(50, 0, 0, 'DN-BIOCARBON', 'biocacbon.jpg', 1, 1, 'Than hoạt tính sinh học chuyên làm lắng, hạ phèn, hấp thụ khí độc', '<p>\r\n	- Chống ô nhiễm đáy ao, phân hủy chất thải, thức ăn dư thừa, hấp thụ khí độc như: NH<sub>3</sub>, H<sub>2</sub>S, NO<sub>2</sub>,….. Ổn định độ pH và màu nước thích hợp.<br />\r\n	- Khống chế vi sinh vật có hại, giúp ngăn ngừa hiện tượng tôm cá nổi đầu vào lúc sáng sớm hoặc chiều tối.<br />\r\n	- Giảm độ đục do các chất lơ lửng trong nước như phù sa, xác tảo…</p>\r\n', '<p>\r\n	- Tôm, cá giống: 1-2kg/1.000m<sup>3</sup>, dùng định kỳ 7 - 10 ngày/lần<br />\r\n	- Cá thịt: 1kg/1.000m<sup>3</sup>, dùng định kỳ 10 - 15 ngày/lần.</p>\r\n', '<p>\r\n	- <em>Bacillus subtillis</em>……….......1,5x10<sup>9</sup>CFU<br />\r\n	- <em>Lactobacillus acidophilus</em>..1,5x10<sup>9</sup>CFU<br />\r\n	- <em>Saccharomyces cereviae</em>..1,5x10<sup>9</sup>CFU<br />\r\n	- Aspergillus oryzae……......1,5x10.<sup>9</sup>CFU<br />\r\n	- Protease……………...…................100UI<br />\r\n	- Amylase……………..................80.000UI<br />\r\n	- Pectinase……………...…..............400UI<br />\r\n	- Beta-glucanase…………….............40UI<br />\r\n	- Than hoạt tính…………................1.000g</p>\r\n', 'Bao 5kg', 40, 0, 0, 'than hoạt tính, làm trong nước, lắng tự, sạch đáy ao', 180, 21, 0, 0, 1372739024, 1372738811, 2169, 1),
(54, 0, 0, 'GLUTARALDEHYDE (GDA)', 'Glutal2.JPG', 6, 1, 'GLUTARALDEHYDE (GDA), Protectol nguyên liệu dùng trong nuôi trồng thủy sản', '<p>\r\n	Glutaraldehyde là dung dịch sát trùng trong&nbsp;thủy sản, có tác dụng nhanh và phổ rộng, trị các bệnh do vi khuẩn, virus, nấm và nguyên sinh động vật, tiêu diệt các loại tảo độc và nhiều ứng dụng khác trong công nghiệp...</p>\r\n', '<p>\r\n	Hòa với nước, tạt trực tiếp xuống ao</p>\r\n', '<p>\r\n	Glutaraldehyde ..... 60% (1,5 - Pentanedial)</p>\r\n<p>\r\n	Xuất xứ: Đức, Trung quốc</p>\r\n', '220kg/phuy', 45, 0, 0, '', 72, 12, 0, 0, 1380909222, 1380788106, 2136, 1),
(56, 0, 0, 'Saponin Nguyên liệu', 'saponin.jpg', 6, 1, 'Saponin Nguyên liệu diệt tạp, Teaseed powder nguyên liệu chuyên dùng diệt tạp trước khi thả tôm', '<p>\r\n	Saponin&nbsp;nguyên liệu chuyên dùng diệt tạp trước khi thả tôm, kích tôm lột...</p>\r\n', '<p>\r\n	Theo hướng dẫn của nhà sản xuất</p>\r\n', '<p>\r\n	Saponin Nguyên liệu (nguyên đai)</p>\r\n', '50kg/bao', 45, 0, 0, '', 70, 10, 0, 0, 1386494675, 1386316849, 1993, 1),
(57, 0, 0, 'AZOMITE MÀU XANH CHO THỦY SẢN', 'AZOMITE.jpg', 1, 1, 'Xuất xứ: Hoa Kỳ\r\nPhân phối độc quyền tại VN: BEHN MEYER VIETNAM', '<p>\r\n	<em>AZOMITE được bổ sung vào thức ăn nhằm cung cấp thêm nhiều loại khoáng cho vật nuôi, giúp vật nuôi lớn nhanh hơn, giảm FCR, tăng năng suất.</em></p>\r\n<p>\r\n	AZOMITE giúp cải thiện chất lượng nước, ổn định tảo, kích thích phiêu sinh động vật phát triển, giúp tôm mau lột vỏ, chắc vỏ, lớn nhanh, tránh cong thân, đục cơ.</p>\r\n', '<p>\r\n	Tùy vào tình hình thời tiết, mùa vụ và điều kiện nuôi, chuyên gia của chúng tôi sẽ hướng dẫn sử dụng cụ thể để mang lại hiệu quả cao nhất.</p>\r\n', '<p>\r\n	KHOÁNG TỰ NHIÊN CHỨA 67 KHOÁNG VI LƯỢNG VÀ NGUYÊN TỐ ĐẤT HIẾM.</p>\r\n', '20KG/BAG', 46, 0, 0, '', 76, 15, 0, 0, 1389753716, 1389683000, 2882, 1),
(58, 0, 0, 'IMMUNOWALL - B-GLUCAN', 'product_immunowall.png', 3, 1, 'Sản phẩm nhập khẩu từ Brazil.\r\nPhân phối độc quyền tại VN: Behn Meyer Việt Nam', '<ul>\r\n	<li>\r\n		Nâng cao sức đề kháng</li>\r\n	<li>\r\n		Tăng cường hệ vi sinh vật có lợi trong đường ruột</li>\r\n	<li>\r\n		Giảm số lượng vi sinh vật có hại bám trên đường ruột.</li>\r\n	<li>\r\n		Cung cấp nhiều vitamin, khoáng và acid amin thiết yếu cho vật nuôi.</li>\r\n</ul>\r\n', '<p>\r\n	- Cá: 1-2kg/tấn thức ăn.</p>\r\n<p>\r\n	- Tôm: 2-3kg/tấn thức ăn.</p>\r\n', '<p>\r\n	Chiết xuất từ Saccharomyces cerevisiae</p>\r\n<p>\r\n	32% b-glucan</p>\r\n<p>\r\n	21% MOS</p>\r\n', '25KG/BAG', 46, 0, 0, 'Behn Meyer, Saccharomyces cerevisiae,b-glucan', 108, 15, 0, 0, 1390310107, 1390274330, 1947, 1),
(59, 0, 5, 'Bio CURB® brand Dry', '[["/upload/product/thumb_image/2019/09/5debfea5ffd4188a41c5_1569305845.jpg"]]', 1, 1, 'Hấp thu và ngăn chặn khí độc NH3, NO2, H2S', '<ul>\r\n	<li>\r\n		Hấp thu nhanh khí độc NH3, H2S</li>\r\n	<li>\r\n		Ngăn chặn quá trình hình thành chuyển hóa khí độc NH3, H2S</li>\r\n	<li>\r\n		Khử mùi, cải thiện môi trường ao nuôi</li>\r\n	<li>\r\n		Loại trừ giun sán, ký sinh trùng và dư lượng kháng sinh</li>\r\n</ul>\r\n', '<p>\r\n	- Trong quá trình nuôi 100grs/1000m<sup>3 </sup>định kỳ 15-30 ngày.</p>\r\n<p>\r\n	- Xử lý sự cố 150grs/1000m<sup>3</sup></p>\r\n<p>\r\n	- Cho ăn 3-5grs/1kg thức ăn (3-5 lần/tuần)</p>\r\n', '<ul>\r\n	<li>\r\n		Yucca Schidigera</li>\r\n	<li>\r\n		Acid hữu cơ</li>\r\n	<li>\r\n		Chất khử mùi</li>\r\n</ul>\r\n', 'Chai 500 ml', 55, 17, 0, '', 49, 7, 300000, 0, 1399255904, 1399254689, 1425, 1),
(60, 0, 0, 'Cyano CURB', 'Cyano-curb.jpg', 1, 1, 'Giải pháp ức chế sự phát triển của tảo Lam', '<p>\r\n	Cyano CRUB chứa Trihydroxy benzoic acid được chiết tách từ Polyphenolic có nguồn gốc từ thực vật. Chất này làm tế bào tảo Lam sản sinh ra thành phần ROS có tác dụng ức chế hoạt động, khả năng sinh trưởn và phát triển của tảo Lam thông qua việc ức chế quá trình quang hợp, làm giảm khả năng tồn tại, sinh sản và phát triển của chúng.</p>\r\n<p>\r\n	Với sự nghiên cứ chuyên biệt, Cyano CRUB chỉ tác động chọn lọc trên tảo Lam mà không ảnh hưởng đến các loại tảo có lợi khác trong ao nuôi.</p>\r\n', '<p>\r\n	- Định kỳ 1kg/4.000m<sup>3</sup> nước</p>\r\n<p>\r\n	- Khi tảo phát triển nhiều 1kg/3.000m<sup>3</sup> lập lại lần 2 sau 2-3 ngày</p>\r\n', '<p>\r\n	Acid hữu cơ Trihydroxy benzoic acid</p>\r\n', 'Lon 1kg', 55, 39, 0, '', 77, 12, 0, 0, 1399305641, 1399256856, 1183, 1),
(61, 0, 0, 'Alu se', 'alu-se.jpg', 3, 1, 'Trị bệnh cong thân,đục cơ,trắng cơ ở tôm sú, tôm thẻ', '<p>\r\n	ALU SE&nbsp;là vi khoáng không có khả năng bị bắt giữ bởi acid amin hoặc chuổi peptide mạch ngắn như Fe, Zn, Cu nên được hữu cơ hóa qua con đường vi sinh vật .giúp tôm kích thích mau lớn,&nbsp; đặc biệt nhằm ngăn chặn triệt để sự phát triển bệnh đục cơ, trắng cơ, cong thân trên tôm thẻ và tôm sú một cách hiệu quả trong vòng 2-3 ngày.</p>\r\n', '<p>\r\n	- Trộn thức ăn liên tục cho tôm 3-5g/ 1kg thức ăn tôm</p>\r\n<p>\r\n	- Trị bệnh đục cơ ở tôm thẻ, sú: trộn 10g/kg thức ăn tôm và kết hợp tạt xuống ao nuôi 500g/1000m<sup>3</sup> nước, sau 2-3 ngày tôm sẻ hết đục cơ.</p>\r\n<p>\r\n	- Trộn thức ăn liên tục cho cá 1kg/1 tấn thức ăn cá</p>\r\n', '<p>\r\n	ALU SE là sản phẩm được tinh chế có nguồn gốc selenium hữu cơ.</p>\r\n', 'Gói 500g', 56, 39, 0, '', 16, 3, 0, 0, 1399304688, 1399304666, 898, 1),
(62, 0, 0, 'Deodor end', 'deoor.jpg', 1, 1, 'Hấp thụ khí độc, cấp cứu tôm cá nổi đầu', '<p>\r\n	- Hấp thụ khí độc NH3, H2S,... ngay lập tức</p>\r\n<p>\r\n	- Cấp cứu tôm nổi đầu, ngăn chặn tôm chạy đàn</p>\r\n<p>\r\n	- Kích thích tôm lột xác</p>\r\n<p>\r\n	- Ngăn ngừa ô nhiễm ao, kích thích tăng trưởng</p>\r\n', '<p>\r\n	- Định kỳ: 454g/ 5.000m<sup>3</sup> nước.</p>\r\n<p>\r\n	- Cấp cứu tôm nổi đầu: 454g/ 3.000m<sup>3</sup> nước.</p>\r\n<p>\r\n	- Kích thích tăng trưởng: 5g/ 1kg thức ăn.</p>\r\n', '<p>\r\n	Yucca Schidigera</p>\r\n', 'Gói 454g', 56, 39, 0, '', 13, 2, 0, 0, 1399304989, 1399304965, 1969, 1),
(63, 0, 0, 'Alu Chrome', 'alu-chrome.jpg', 3, 1, 'Chắc thịt, nặng ký cho tôm thẻ, tôm sú', '<p>\r\n	Giúp tôm mau cứng vỏ, chắc thịt, nặng ký ngay tức thì.</p>\r\n<p>\r\n	Phòng chống bệnh cong thân, đục cơ trên tôm sú, tôm thẻ.</p>\r\n<p>\r\n	Kích thích tôm tăng trọng, lớn nhanh, mau lột xác</p>\r\n', '<p>\r\n	Trộn 5-10 g/1kg thức ăn tôm, tôm sẻ săn chắc và nặng ký trong vòng 3 ngày.</p>\r\n<p>\r\n	Tôm chậm lớn, ốp thân, cong thân, đục cơ: tăng liều gấp đôi</p>\r\n', '<p>\r\n	Chromium………………………………100ppm</p>\r\n<p>\r\n	Phụ gia vừa đủ ……………..………….1kg</p>\r\n', 'Gói 500g', 56, 39, 0, '', 19, 5, 0, 0, 1399305370, 1399305351, 1962, 1),
(65, 0, 0, 'NEXT ENHANCE® 150 Premix', 'NE-150.jpg', 3, 1, 'Tinh dầu thiết yếu tiêu diệt vi khuẩn gây bệnh trong gan tụy, tăng sức đề kháng.', '<p>\r\n	Tinh dầu thiết yếu trong sản phẩm có khả năng ức chế &amp; tiêu diệt vi khuẩn gây bệnh gan tụy. Là chất có tính khử trùng mạnh, tác động trực tiếp lên tế bào vi khuẩn gây bệnh.</p>\r\n<p>\r\n	Làm suy giảm khả năng đề kháng với kháng sinh của vi khuẩn thông qua tác dụng hiệp đồng với kháng sinh (có thể sử dụng kết hợp với kháng sinh)</p>\r\n<p>\r\n	Có khả năng trừ nấm (chẳng hạn như nấm mốc)</p>\r\n<p>\r\n	Tăng cường sức đề kháng cho tôm.</p>\r\n<p>\r\n	Kích thích tôm tăng trưởng và phát triển.</p>\r\n<p>\r\n	An toàn hiệu quả, không gây lờn thuốc và không làm tổn thương nhung mao thành ruột</p>\r\n', '<p>\r\n	5-10g/kg thức ăn, 1 cữ/ngày, xen kẽ giữa các tuần.</p>\r\n', '<p>\r\n	Tinh dầu thiết yếu với hai hoạt chất Carvarol &amp; Thymol được chiết xuất từ cây bạc hà có chất lượng tốt nhất.</p>\r\n<p>\r\n	Được phối hợp với tỷ lệ cân đối nâng cao hoạt lực của hai thành phần tinh dầu.</p>\r\n', 'Lon 1kg', 55, 39, 0, 'Tinh dầu, Carvarol, Thymol, kháng sinh, gan tụy', 34, 6, 0, 0, 1399825694, 1399825635, 4710, 1),
(66, 0, 0, 'Acimin', 'dichtrunAcimin.jpg', 3, 1, 'Đạm dịch trùn quế', '<p>\r\n	- Bổ sung Vitamin và Acid amin đáp ứng đủ dinh dưỡng vào khẩu phần ăn giúp tôm nuôi cân bằng dinh dưỡng cho tốc độ phát triển nhanh và khả năng kháng bệnh tốt.</p>\r\n<p>\r\n	- Mùi, vị trùn quế tôm thích ăn và bắt mồi nhanh.</p>\r\n<p>\r\n	- Liên kết bền chậm tan trong nước giúp giữ lại các chất dinh dưỡng trong thức ăn tránh thất thoát ra môi trường nước.</p>\r\n', '<p>\r\n	- Thời điểm:&nbsp; Tất cả các cử ăn.</p>\r\n<p>\r\n	- Cách dùng: Lắc kỹ sản phẩm, trộn đều vào thức ăn.</p>\r\n<p>\r\n	- Liều dùng: 5-10ml/kg thức ăn.</p>\r\n<p>\r\n	- Liều dùng: 10-15ml/kg thức ăn khi sắp thu hoạch.</p>\r\n', '<p>\r\n	Dịch trùn quế thủy phân phối chế với dạng vitamin và acid amin đơn gồm có: Vitamin B1, Vitamin B6, Vitamin B12, Alanine, Glycine, Leucine, Serine, Asparic acid, Glutaric acid, Lysine...</p>\r\n<p>\r\n	- Mùi, vị trùn quế đặc trưng tôm rất thích ăn.</p>\r\n<p>\r\n	- Liên kết bền vững và lâu tan trong nước.</p>\r\n<p>\r\n	- Đậm đặc hàm lượng&nbsp; Vitamin và Acid amin.</p>\r\n<p>\r\n	- Đạm cao.</p>\r\n', 'chai 1 lít', 57, 11661, 0, 'đạm, trùn quế, tăng trọng', 105, 13, 0, 0, 1431309086, 1431308984, 3857, 1),
(67, 0, 0, 'Licin garlic', 'Licin-garlic1431416386.jpg', 3, 1, 'Tinh dầu tỏi', '<p>\r\n	- Tinh chất tỏi có tác dụng kháng khuẩn tự nhiên kiểm soát lượng vi khuẩn bên trong cơ thể tôm.<br />\r\n	- Tổ hợp công thức chuyên biệt tăng khả năng miễn dịch của tôm giúp phòng bệnh hiệu quả.<br />\r\n	- Đặc biệt phòng trị&nbsp;hiệu quả bệnh đường ruột tôm khi sử dụng liên tục.</p>\r\n', '<p>\r\n	- Thời điểm sử dụng: Cử ăn cuối cùng trong ngày.<br />\r\n	- Cách dùng: Lắc kỹ sản phẩm, trộn đều vào thức ăn.<br />\r\n	- Liều dùng: 2ml/kg thức ăn.<br />\r\n	- Liều dùng: 4ml/kg thức ăn khi thời tiết biến động hoặc dịch bệnh.</p>\r\n', '<p>\r\n	<span style="color: rgb(68, 68, 68); font-family: Arial, sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 19.5px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(250, 250, 250);">Tinh dầu tỏi tươi, Betaglucan, Vitamin B9</span></p>\r\n<p>\r\n	<strong><span style="color: rgb(68, 68, 68); font-family: Arial, sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 19.5px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb(250, 250, 250);">Đặc tính:</span></strong></p>\r\n<p style="font-family: Arial, sans-serif; color: rgb(68, 68, 68); font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 19.5px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);">\r\n	- Tinh chất kháng khuẩn chiết xuất từ tỏi có mùi nồng có thể sử dụng liên tục mà không ảnh hưởng đến tăng trưởng.<br />\r\n	- Tổ hợp vừa đủ các chất hổ trợ khả năng miễn dịch dạng dung dịch.<br />\r\n	- Nguyên liệu chiết xuất từ củ tỏi tươi, không dùng tinh dầu tỏi tổng hợp.</p>\r\n', 'chai 500 ml', 57, 11661, 0, '', 164, 18, 0, 0, 1431416635, 1431416386, 4654, 1);
INSERT INTO `tkb_aq_product` (`id`, `parrent_id`, `shop_id`, `name`, `image`, `cat_id`, `group_id`, `description`, `indication`, `dosage`, `ingredient`, `packing`, `co_id`, `user_id`, `prise`, `keyword`, `rate_t`, `rate_c`, `price`, `sale`, `edit_time`, `add_time`, `hit`, `allow`) VALUES
(68, 0, 0, 'Ultimate Cleaner', 'Ultimate-Cleaner1432394191.png', 1, 0, 'Chế phẩm sinh học EM thứ cấp 1 (F1) chất lượng cao', '<ul><li>Phân hủy các chất ô nhiễm hữu cơ, làm sạch nước và đáy ao.</li><li>Ức chế vi sinh vật và tảo có hại.</li></ul>', '<li>Giai đoạn kích hoạt : Hòa 1 lít UC và 2kg mật đường vào 30 lít nước sạch (nước ngọt). Trộn đều, để 72 giờ.</li><li>Giai đoạn xử lý : liều lượng 1 lít UC (sau khi kích hoạt) cho 5.000 m3&nbsp;nước ao.</li><ul><li>Tháng đầu tiên: 5 - 7 ngày / lần</li><li>Tháng thứ hai: 3 - 5 ngày / lần</li></ul><p>&nbsp; &nbsp;Dùng gấp đôi liều lượng nêu trên trong các trường hợp sau :</p><ul><li>Ao bị ô nhiễm nặng.</li><li><i>Tảo nở hoa.</i></li><li><i>Mật độ thả tôm dày (trên 100 con / m2)</i></li></ul><p><i>Bảo quản nơi khô thoáng, tránh ánh nắng trực tiếp.</i></p>', '<ul><li>Vi khuẩn Lactic &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;10<sup>9</sup>&nbsp;CFU/ml</li><li>Vi khuẩn Bacillus &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 10<sup>9&nbsp;</sup>CFU/ml</li><li>Vi khuẩn Rhodobacteria &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;10<sup>8</sup>&nbsp;CFU/ml&nbsp;</li><li>Nấm men và xạ khuẩn &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 10<sup>9&nbsp;</sup>CFU/ml</li></ul>', 'Can 20 lít', 0, 11967, 0, '', 5, 1, 0, 0, 1432394191, 1432394191, 60, 0),
(71, 0, 0, 'Yucca Star Liquid, Powder', '', 1, 0, 'Xử lý nước\r\nKhử mùi chuồng trại', '<p>Được sử dụng làm chất bổ sung trong thức ăn tôm, cá. Chất chiết xuất từ cây Yucca là một trong những nguyên liệu bổ sung trong thức ăn tôm nhằm quản lý và giảm nồng độ NH3&nbsp;và các hợp chất khác. Khi bổ sung chất chiết xuất Yucca vào thức ăn, tôm tăng trưởng tốt hơn, giảm tỷ lệ chết. Vì chiết xuất từ thân cây Yucca có tác dụng kết hợp với ammonia, làm giảm ammonia tự do. Khi thức ăn đi qua dạ dày, ammonia sẽ bị giữ lại bởi chất chiết xuất có trong thức ăn.</p><p>Được ứng dụng làm chất xử lý nước: &nbsp;kết hợp với ammonia ngoài môi trường và chuyển đổi chúng sang dạng hợp chất nitrogen không độc khác.</p><p>Saponin còn có khả năng diệt protozoa trong ống tiêu hóa của động vật. Cơ chế tác động đến protozoa là saponin kết hợp với cholesterol hoặc sterol của màng tế bào làm cho màng tế bào của protozoa bị phá hủy.</p>', '<p><em>Định kỳ:&nbsp; 1kg cho 10.000m<sup>3 </sup>(1hecta) </em></p><p><em>+ Cấp cứu: 1kg cho 2000 - 3000m<sup>3</sup>.</em></p><p><em>hoặc&nbsp;</em></p><p>&nbsp;</p><p><em>Định kỳ:&nbsp; 1kg cho 10.000m<sup>3 </sup>(1hecta) </em></p><p><em>+ Cấp cứu: 1kg cho 3.000-5.000m<sup>3</sup>.</em></p>', '<p>Yucca schdigera extract 100%</p>', '10 Lít/Can,  208 Lít/Phuy, 25kg/thùng', 0, 12093, 0, '', 5, 1, 0, 0, 1433305158, 1433305158, 59, 0),
(72, 0, 5, 'Yucca Star Liquid, Powder', '[["/upload/product/thumb_image/2019/09/00c2bcdfe7b300ed59a2_1569291986.jpg"]]', 1, 0, 'Xử lý nước\r\nKhử mùi chuồng trại', '<p>Được sử dụng làm chất bổ sung trong thức ăn tôm, cá. Chất chiết xuất từ cây Yucca là một trong những nguyên liệu bổ sung trong thức ăn tôm nhằm quản lý và giảm nồng độ NH3&nbsp;và các hợp chất khác. Khi bổ sung chất chiết xuất Yucca vào thức ăn, tôm tăng trưởng tốt hơn, giảm tỷ lệ chết. Vì chiết xuất từ thân cây Yucca có tác dụng kết hợp với ammonia, làm giảm ammonia tự do. Khi thức ăn đi qua dạ dày, ammonia sẽ bị giữ lại bởi chất chiết xuất có trong thức ăn.</p><p>Được ứng dụng làm chất xử lý nước: &nbsp;kết hợp với ammonia ngoài môi trường và chuyển đổi chúng sang dạng hợp chất nitrogen không độc khác.</p><p>Saponin còn có khả năng diệt protozoa trong ống tiêu hóa của động vật. Cơ chế tác động đến protozoa là saponin kết hợp với cholesterol hoặc sterol của màng tế bào làm cho màng tế bào của protozoa bị phá hủy.</p>', '<p><em>Định kỳ:&nbsp; 1kg cho 10.000m<sup>3 </sup>(1hecta) </em></p><p><em>+ Cấp cứu: 1kg cho 2000 - 3000m<sup>3</sup>.</em></p><p><em>hoặc&nbsp;</em></p><p>&nbsp;</p><p><em>Định kỳ:&nbsp; 1kg cho 10.000m<sup>3 </sup>(1hecta) </em></p><p><em>+ Cấp cứu: 1kg cho 3.000-5.000m<sup>3</sup>.</em></p>', '<p>Yucca schdigera extract 100%</p>', '10 Lít/Can,  208 Lít/Phuy, 25kg/thùng', 0, 17, 0, '', 5, 1, 150000, 0, 1433305297, 1433305297, 55, 1),
(75, 0, 0, 'Men vi sinh xử lý nước', 'Men-vi-sinh-xu-ly-nuoc1436151893.jpg', 1, 0, 'Men vi sinh xử lý nước cho ao nuôi thủy sản', '<p>*&nbsp;<i>Giúp làm sạch môi trường ao nuôi</i></p><p>* Phân hủy mùn bã hữu cơ trong ao nuôi</p>', '<p>*&nbsp;<i>100-200 gam/1000m3 nước, dùng định kỳ 5-7 ngày/lần</i></p><p>* Ao nước bị ô nhiễm, dùng liều gấp đôi</p>', '<p>* Bacillus subtilis</p><p>* Bacillus lichenifomis</p><p>* Enzyme: Protease, Amylase, Xenlullase</p>', '25 kg/bao', 0, 12417, 0, '', 5, 1, 0, 0, 1436151893, 1436151893, 1, 0),
(76, 0, 0, 'EM.Gốc', 'EMGoc1437098449.png', 1, 0, 'Sản phẩm giúp ổn định PH, cải thiện môi trường ao nuôi tốt nhất thị trường hiện nay', '<p><em>* Phân hủy các chất ô nhiễm hưu cơ làm sạch nước</em></p><p><em>* Phân hủy bùn, bã hưu cơ, thức ăn dư thừa ở đáy ao</em></p><p><em>* Ức chế vi khuẩn gây bệnh giúp Tôm, Cá luôn được khỏe mạnh và có khả năng miễn dịch cao hơn</em></p><p><em>* Ức chế sự phát triển của vi sinh vật và Tảo có hại</em></p>', '<p>a. Giai đoạn kích hoạt : Hòa 1l U.C và 2kg mật đường vào 30l nước sạch, trộn đều và ủ trong 72h</p><p>b. Giai đoạn xử lý : Xử lý ao với liều lượng 1l U.C/5.000m3 nước</p><p>* Tháng đầu tiên : 5-7 ngày /lần</p><p>* Tháng thứ hai trở đi : 3-5 ngày/lần</p><p><em>Lưu ý :&nbsp;</em>Chạy quạt 20-30 phút trước khi đánh, để đạt hiệu quả cao nhất nên đánh vào 9-10h sáng hoặc 14h-16h chiều, vào lúc này ao có hàm lượng Oxy cao nhất</p>', '<p>* Vi khuẩn Lactic : 10<sup>9</sup>&nbsp;CFU/ml</p><p>* Vi khuẩn Bacillus : 10<sup>9</sup>&nbsp;CFU/ml</p><p>* Vi khuẩn Rhodobacteria 10<sup>8</sup>&nbsp;CFU/ml</p><p>* Nấm men và xạ khuẩn : 10<sup>9</sup>&nbsp;CFU/ml</p>', 'Chai 1 lít', 0, 12470, 0, '', 5, 1, 0, 0, 1437098449, 1437098449, 2, 0),
(77, 0, 0, 'Men tiêu hóa BioOne', 'Men-tieu-hoa-BioOne1437703484.jpg', 3, 0, 'Giúp cho vật nuôi tiêu hóa tốt thức ăn. Hạn chế bệnh đường ruột, tiêu chảy trên vật nuôi', '<p>- Điều trị rối loạn tiêu hóa, bệnh tiêu chảy, các bệnh đường ruột ttrên gia súc gia cầm. Gia tăng sản xuất acid lactic và Bacteriocin (kháng sinh do bacillus subtilis tiết ra) ức chế sự&nbsp; phát triển của vi khuẩn gây bệnh như ecoli, samonella…</p><p>- Tăng khả năng tiêu hóa tinh bột, protein và chất xơ, kích thích tiêu hóa và hấp thu&nbsp; tối đa các chất dinh dưỡng giảm chi phí thức ăn, giảm mùi hôi chuồng</p>', '<p>Từ&nbsp;3 - 5 gram/ 1kg thức ăn, cho ăn liên tục trong suốt vụ nuôi</p>', '<p>Pedioccocus acidilactici<br />Bacillus subtilis<br />Lactobacillus plantarum<br />Enzyme: Protease, Cellulase, &nbsp;Amylase, Xylanase.</p>', 'Bao 25 kg', 0, 12417, 0, '', 5, 1, 0, 0, 1437703484, 1437703484, 1, 0),
(78, 0, 0, 'Yucca (Mexico, Mỹ)', '', 1, 0, 'Xuất xứ - Quy cách: \r\n- Mỹ: 25 Kg/Thùng, 200 L/Phuy\r\n- Mexico: 25 Kg/Thùng, 10 L/can, 208 L/Phuy', '<p>- Hấp thu cực nhanh các khí như NH3, H2S và các khí độc khác làm ảnh hưởng sức khỏe của tôm.</p><p>- Giúp giải quyết các trường hợp cá nổi đầu lên tầng mặt do giảm pH đột ngột, nhiệt độ tăng cao, thiếu oxy tầng đáy, làm cho nồng độ H2S tăng cao.</p><p>- Kích thích sự phân hủy các chất cặn bã, làm sạch môi trường nước tránh hiện tượng mỡ, thịt cá bị vàng, da cá bị đen.</p><p>- Hấp thu các chất độc hại do thuốc trừ sâu có nguồn gốc phốt phát hữu cơ, thuốc diệt giáp xác, diệt tạp tồn dư trong ao nuôi.</p><p>- Khử mùi hôi chuồng trại.</p><p>- Pha vào nước uống, thức ăn của gia súc, gia cầm để giảm mùi hôi cho chuồng trại, đồng thời cải thiện hệ tiêu hóa vật nuôi, nâng cao sức khỏe, giúp hấp thu chất dinh dưỡng tối đa, tăng năng suất</p>', '<p>* Dạng Bột</p><p>- Xử lý định kỳ: 300g/1000m³ nước, 200ml/1000m³ nước, 10 – 14 ngày/lần</p><p>- Trường hợp cấp cứu: 1000g cho 1000m³, liên tục trong 3 ngày.</p><p>- Thú y: 150-200g/tấn thức ăn</p><p>* Dạng nước: </p><p>- Xử lý định kỳ: 200ml/1000m³ nước, 10 – 14 ngày/lần</p><p>- Trường hợp cấp cứu: 500ml/1000m³ nước liên tục trong 3 ngày.</p><p>- Heo con, gà đẻ trứng: 500ml/1000 lít nước.</p><p>- Xuất chồng, nuôi thịt: 40ml/1000 lít nước.</p><p>* Xử lý mùi hôi chuồng trại: Pha loãng sản phẩm với nước để dễ phun xịt theo tỉ lệ 1:10.</p><p>&nbsp;</p><p>- Hố phân lỏng: 40ml/1m3 nước mỗi tuần một lần trong suốt 4 tuần đầu hoặc cho đến khi giảm hết mùi hôi theo mong muốn. Sau đó giảm còn 15ml. mỗi tuần một lần.</p><p>- Chuồng nuôi: 10 - 15ml/ 1.000 lit nước. Dùng để rửa sàn, tường, ngăn chuồng. Dùng như chất tẩy rửa bình thường.</p><p>- Hố phân, vũng phân: Phun 180ml/100m2 bề mặt vũng phân, mỗi tuần một lần cho đến khi lớp váng bên trên tan ra và mùi hôi được khống chế. Đồng thời áp dụng 4ml/ 1000lit xử lý nước phân.</p><p>- Chuồng gà thịt: Xịt 80ml /100m2, chất độn, lót chuồng. Mỗi tuần 2 lần cho đến khi mùi hôi giảm.</p>', '<p>Yucca schidigera chiết xuất đậm đặc 100%.</p>', 'Can 10 lít', 0, 12093, 0, '', 5, 1, 0, 0, 1438138031, 1438138031, 56, 0),
(79, 0, 0, 'Yucca (Mexico, Mỹ)', '', 1, 0, 'Xuất xứ - Quy cách: \r\n- Mỹ: 25 Kg/Thùng, 200 L/Phuy\r\n- Mexico: 25 Kg/Thùng, 10 L/can, 208 L/Phuy', '<p>- Hấp thu cực nhanh các khí như NH3, H2S và các khí độc khác làm ảnh hưởng sức khỏe của tôm.</p><p>- Giúp giải quyết các trường hợp cá nổi đầu lên tầng mặt do giảm pH đột ngột, nhiệt độ tăng cao, thiếu oxy tầng đáy, làm cho nồng độ H2S tăng cao.</p><p>- Kích thích sự phân hủy các chất cặn bã, làm sạch môi trường nước tránh hiện tượng mỡ, thịt cá bị vàng, da cá bị đen.</p><p>- Hấp thu các chất độc hại do thuốc trừ sâu có nguồn gốc phốt phát hữu cơ, thuốc diệt giáp xác, diệt tạp tồn dư trong ao nuôi.</p><p>- Khử mùi hôi chuồng trại.</p><p>- Pha vào nước uống, thức ăn của gia súc, gia cầm để giảm mùi hôi cho chuồng trại, đồng thời cải thiện hệ tiêu hóa vật nuôi, nâng cao sức khỏe, giúp hấp thu chất dinh dưỡng tối đa, tăng năng suất</p>', '<p>* Dạng Bột</p><p>- Xử lý định kỳ: 300g/1000m³ nước, 200ml/1000m³ nước, 10 – 14 ngày/lần</p><p>- Trường hợp cấp cứu: 1000g cho 1000m³, liên tục trong 3 ngày.</p><p>- Thú y: 150-200g/tấn thức ăn</p><p>* Dạng nước: </p><p>- Xử lý định kỳ: 200ml/1000m³ nước, 10 – 14 ngày/lần</p><p>- Trường hợp cấp cứu: 500ml/1000m³ nước liên tục trong 3 ngày.</p><p>- Heo con, gà đẻ trứng: 500ml/1000 lít nước.</p><p>- Xuất chồng, nuôi thịt: 40ml/1000 lít nước.</p><p>* Xử lý mùi hôi chuồng trại: Pha loãng sản phẩm với nước để dễ phun xịt theo tỉ lệ 1:10.</p><p>&nbsp;</p><p>- Hố phân lỏng: 40ml/1m3 nước mỗi tuần một lần trong suốt 4 tuần đầu hoặc cho đến khi giảm hết mùi hôi theo mong muốn. Sau đó giảm còn 15ml. mỗi tuần một lần.</p><p>- Chuồng nuôi: 10 - 15ml/ 1.000 lit nước. Dùng để rửa sàn, tường, ngăn chuồng. Dùng như chất tẩy rửa bình thường.</p><p>- Hố phân, vũng phân: Phun 180ml/100m2 bề mặt vũng phân, mỗi tuần một lần cho đến khi lớp váng bên trên tan ra và mùi hôi được khống chế. Đồng thời áp dụng 4ml/ 1000lit xử lý nước phân.</p><p>- Chuồng gà thịt: Xịt 80ml /100m2, chất độn, lót chuồng. Mỗi tuần 2 lần cho đến khi mùi hôi giảm.</p>', '<p>Yucca schidigera chiết xuất đậm đặc 100%.</p>', 'Can 10 lít', 0, 12093, 0, '', 5, 1, 0, 0, 1438138051, 1438138051, 58, 0),
(80, 0, 0, 'Khoáng AZOMITE', 'Khoang-AZOMITE1441616075.jpg', 3, 0, 'Dạng bột màu xám, tan trong nước', '<p>Dùng trong nuôi tôm thủy sản và chăn nuôi gia súc</p><p><em>Cung cấp thêm nhiều loại khoáng cho vật nuôi.</em></p><p>&nbsp;- Giúp vật nuôi lớn nhanh hơn, giảm FCR, tăng năng suất.</p><p>- Phòng ngừa một số bệnh của tôm&nbsp;do thiếu khoáng ( đục cơ, công thân, lột dính, … )</p><p>- Tăng cường sức đề kháng, giúp tôm&nbsp;cứng vỏ.</p><p>- Cải thiện chất lượng nước, ổn định tảo, kích thích phiêu sinh động vật phát triển, giúp tôm mau lớn, đồng đều và chắc vỏ.</p>', '<p>* Dùng tạt</p><p>- Trước khi thả giống khoảng 2 ngày, tạt 2,5 kg /1000 m3</p><p>- Lần thứ 2: Sau khi nuôi tôm được 30 ngày, tạt 1-2 kg /1000&nbsp;m3</p><p>- Định kỳ: 1-1,5kg /1000&nbsp;m3, 7-10 ngày tạt một lần</p><p>*&nbsp;Trộn cho tôm&nbsp;ăn:</p><p>Trộn 5g /kg thức ăn, ngày ăn 2 lần, cho ăn liên tục 5 ngày ngưng 5-7 ngày. Cứ tiếp tục như vậy cho đến hết vụ nuôi.</p>', '<p>Hỗn hợp gồm 67 nguyên tố khoáng vi lượng, đa lượng: K2O 0.2%, Ca 1.8 %, Mg 0.5%, Cl 0.1%, Na 0.1%,…</p>', 'Bao 20 kg', 0, 12093, 0, '', 5, 1, 0, 0, 1441616075, 1441616075, 2, 0),
(81, 0, 0, 'Khoáng AZOMITE', 'Khoang-AZOMITE1441616100.jpg', 3, 0, 'Dạng bột màu xám, tan trong nước', '<p>Dùng trong nuôi tôm thủy sản và chăn nuôi gia súc</p><p><em>Cung cấp thêm nhiều loại khoáng cho vật nuôi.</em></p><p>&nbsp;- Giúp vật nuôi lớn nhanh hơn, giảm FCR, tăng năng suất.</p><p>- Phòng ngừa một số bệnh của tôm&nbsp;do thiếu khoáng ( đục cơ, công thân, lột dính, … )</p><p>- Tăng cường sức đề kháng, giúp tôm&nbsp;cứng vỏ.</p><p>- Cải thiện chất lượng nước, ổn định tảo, kích thích phiêu sinh động vật phát triển, giúp tôm mau lớn, đồng đều và chắc vỏ.</p>', '<p>* Dùng tạt</p><p>- Trước khi thả giống khoảng 2 ngày, tạt 2,5 kg /1000 m3</p><p>- Lần thứ 2: Sau khi nuôi tôm được 30 ngày, tạt 1-2 kg /1000&nbsp;m3</p><p>- Định kỳ: 1-1,5kg /1000&nbsp;m3, 7-10 ngày tạt một lần</p><p>*&nbsp;Trộn cho tôm&nbsp;ăn:</p><p>Trộn 5g /kg thức ăn, ngày ăn 2 lần, cho ăn liên tục 5 ngày ngưng 5-7 ngày. Cứ tiếp tục như vậy cho đến hết vụ nuôi.</p>', '<p>Hỗn hợp gồm 67 nguyên tố khoáng vi lượng, đa lượng: K2O 0.2%, Ca 1.8 %, Mg 0.5%, Cl 0.1%, Na 0.1%,…</p>', 'Bao 20 kg', 0, 12093, 0, '', 5, 1, 0, 0, 1441616100, 1441616100, 1, 0),
(82, 0, 0, 'YUCCA', 'YUCCA1446022872.jpg', 1, 0, 'Yucca hấp thu khí độc, chống sốc cho tôm cá', '<p>Hấp thu cực nhanh khí độc trong ao nuôi như: NH3, H2S NO2&nbsp;...<em>} </em></p><p><em>Tăng lượng phiêu sinh vật bổ sung nguồn thức ăn tự nhiên cho tôm, giảm hệ số thức ăn, và giúp tôm lớn nhanh.</em></p><p>&nbsp;Kích thích sự phân hủy các chất cặn bã, làm sạch môi trường nước&nbsp;</p><p>Giúp giải quyết các trường hợp tôm cá nổi đầu do nồng độ&nbsp;H2S tăng cao đột ngột.&nbsp;</p><p>Tăng cường sức đề kháng, nâng cao quá trình miễn nhiễm ở tôm. Giúp tôm mau lướt qua bệnh tật.</p><p>&nbsp;Giữ cho môi trường ao nuôi ổn định. Giảm thay nước trong quá trình nuôi</p>', '<p>Cách dùng: Hòa với nước, tạt trực tiếp.</p><p>VUI LÒNG LIÊN HỆ:</p><p>CÔNG TY TNHH PG VINA</p><p>ĐC: Tòa nhà JVPE, khu công viên phần mềm&nbsp; Quang Trung,&nbsp; p.Tân Chánh Hiệp, Q.12, TPHCM.&nbsp;</p><p>ĐT: 0961. 840. 228</p><p>Mail: hailong.tinvp@gmail.com</p><p>Skype: huynhtin93pgvina</p><p>Xem thông tin sản phẩm tại trang web:</p><p>&nbsp; &nbsp; &nbsp;&nbsp;<em>&nbsp;&nbsp;&nbsp;&nbsp;Web:</em> <em>http://www.pgvina.com.vn/</em></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>', '<p>Thành phần:<em>&nbsp;</em>Yucca schidigera extract.</p>', '25kg/ xô, 20 lít/ can', 0, 13743, 0, '', 5, 1, 0, 0, 1446022872, 1446022872, 28, 0),
(83, 0, 0, 'YUCCA', 'YUCCA1446023156.jpg', 1, 0, 'Hấp thu khí độc, chống sốc cho tôm cá khi thời tiết thay đổi .', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hấp thu cực nhanh khí độc trong ao nuôi như: NH3, H2S NO2&nbsp;...</p><p>Tăng lượng phiêu sinh vật bổ sung nguồn thức ăn tự nhiên cho tôm, giảm hệ số thức ăn, và giúp tôm lớn nhanh.</p><p>Kích thích sự phân hủy các chất cặn bã, làm sạch môi trường nước&nbsp;</p><p>Giúp giải quyết các trường hợp tôm cá nổi đầu do nồng độ&nbsp;H2S tăng cao đột ngột.&nbsp;</p><p>Tăng cường sức đề kháng, nâng cao quá trình miễn nhiễm ở tôm. Giúp tôm mau lướt qua bệnh tật.</p><p>&nbsp;Giữ cho môi trường ao nuôi ổn định. Giảm thay nước trong quá trình nuôi.</p>', '<p>Cách dùng: Hòa với nước, tạt trực tiếp.</p><p>VUI LÒNG LIÊN HỆ:</p><p>CÔNG TY TNHH PG VINA</p><p>ĐC: Tòa nhà JVPE, khu công viên phần mềm&nbsp; Quang Trung,&nbsp; p.Tân Chánh Hiệp, Q.12, TPHCM.&nbsp;</p><p>ĐT: 0961. 840. 228</p><p>Mail: hailong.tinvp@gmail.com</p><p>Skype: huynhtin93pgvina</p><p>Xem thông tin sản phẩm tại trang web:</p><p><i>&nbsp; &nbsp; &nbsp; &nbsp;</i><em>&nbsp;&nbsp;&nbsp;&nbsp;Web:</em><i> </i><em>http://www.pgvina.com.vn/</em></p>', '<p>Thành phần:&nbsp;Chiết xuất 100% Yucca Schidigera</p>', 'Quy cách:  25kg/ xô, 20 lít/ can', 0, 13743, 0, '', 5, 1, 0, 0, 1446023156, 1446023156, 34, 0),
(84, 0, 0, 'Chlorine Dioxide dạng nước', '', 1, 0, 'Trường Thịnh cung cấp Chlorine Dioxide dùng trong xử lý công nghiệp, thủy sản và thực phẩm', '<p>◇ Sử dụng trong lĩnh vực diệt khuẩn khử mùi</p><p>（1） Nhà máy chon lắp rác、Nhà máy xử lý nước cống,nhà máy xử lý phân</p><p>（2）Ngành Da thuộc,y dược,thực phẩm</p><p>（3）Chợ Thuỷ sản,lò mổ、Chợ gia súc&nbsp;gia cầm</p><p>（4）Trại&nbsp;nuôi gà,heo, bán hải sản .</p><p>（5）Nhà Vệ sinh công cộng</p><p>（6）Bệnh viện,trường học,khách sạn vv…..</p><p>◇ Lỉnh vực xử lý nước</p><p>（1）Nước cấp、Nước uống、Nước giếng vv..</p><p>（2）Nhà máy thực phẩm ,y dược,mỹ phẩm nước sản xuất và nước thải</p><p>（3）Bồn nước dữ trữ cho ngư dân trên tàu</p><p>（4）Hồ bơi 、Hồ tắm、hồ cá</p><p>（5）Nước sản xuất,nước tháp giải nhiệt,lò hơi</p><p>（6）Các loại nước thải</p><p>◇ Lĩnh vực tẩy trắng</p><p>（1）Công nghiệp in ấn ,ngành giấy</p><p>（2）Sợi Poly</p><p>（3）Thực phẩm</p><p>(4) Làm sạch trái cây. Chất này có tác dụng làm sạch trái cây, trái cây không bị biến màu khi bảo quản lâu ngày.</p>', '<p>Để biết thêm thông tin sản phẩm liên hệ Ms Hạnh 01638229558</p><p>Skype:hanh.truongthinh</p>', '<p>Nồng độ cao, ổn định 5%</p>', 'thùng 25kg', 0, 14024, 0, '', 5, 1, 0, 0, 1447839158, 1447839158, 1, 0),
(85, 0, 0, 'Chlorine Dioxide dạng nước', 'Chlorine-Dioxide-dang-nuoc1447839298.jpg', 1, 0, 'Trường Thịnh cung cấp chất tẩy rửa thực phẩm, công nghiệp và thủy sản', '<p>◇ Sử dụng trong lĩnh vực diệt khuẩn khử mùi</p><p>（1） Nhà máy chon lắp rác、Nhà máy xử lý nước cống,nhà máy xử lý phân</p><p>（2）Ngành Da thuộc,y dược,thực phẩm</p><p>（3）Chợ Thuỷ sản,lò mổ、Chợ gia súc&nbsp;gia cầm</p><p>（4）Trại&nbsp;nuôi gà,heo, bán hải sản .</p><p>（5）Nhà Vệ sinh công cộng</p><p>（6）Bệnh viện,trường học,khách sạn vv…..</p><p>◇ Lỉnh vực xử lý nước</p><p>（1）Nước cấp、Nước uống、Nước giếng vv..</p><p>（2）Nhà máy thực phẩm ,y dược,mỹ phẩm nước sản xuất và nước thải</p><p>（3）Bồn nước dữ trữ cho ngư dân trên tàu</p><p>（4）Hồ bơi 、Hồ tắm、hồ cá</p><p>（5）Nước sản xuất,nước tháp giải nhiệt,lò hơi</p><p>（6）Các loại nước thải</p><p>◇ Lĩnh vực tẩy trắng</p><p>（1）Công nghiệp in ấn ,ngành giấy</p><p>（2）Sợi Poly</p><p>（3）Thực phẩm</p><p>(4) Làm sạch trái cây. Chất này có tác dụng làm sạch trái cây, trái cây không bị biến màu khi bảo quản lâu ngày.</p>', '<p>Để biết thêm thông tin sản phẩm liên hệ Ms Hạnh 01638229558</p><p>Skype:hanh.truongthinh</p>', '<p>nồng độ cao, ổn định 5%</p>', 'thùng 25kg', 0, 14024, 0, '', 5, 1, 0, 0, 1447839298, 1447839298, 1, 0),
(86, 0, 0, 'Saponin Bò Cạp', 'Saponin-Bo-Cap1448269715.png', 6, 0, 'Saponin dùng diệt tạp, kích thích tôm lột vỏ', '<p>-Diệt cá tạp, diệt nguyên sinh động vật gây bệnh. Kích thích tôm lột xác đúng chu kỳ, tăng trưởng nhanh.<br />-Cải thiện màu nước,&nbsp;giảm độ phèn, ổn định pH, tránh hiện tượng cắn lẫn nhau.</p><p>-Diệt ốc bưu vàng.</p>', '<p>5 - 10 ppm</p>', '<p>Chất chiết từ bả cây trà</p>', '10kg/bao', 0, 14077, 0, '', 5, 1, 0, 0, 1448269715, 1448269715, 22, 0),
(87, 0, 0, 'BKC Trung Quốc', 'BKC-Trung-Quoc1448328872.png', 6, 0, 'Dạng lỏng, không màu, tan trong nước.\r\n-Hàm lượng: 80% min.', '<p>Chất khử trùng tốt, xử lý nước.</p>', '<p>xử lý nước</p>', '<p>BKC (Benzalkonium Chloride)</p><p>Công thức hóa học:</p><p>C21H38NCl &nbsp;</p>', '200kg/phuy, 20kg/can', 0, 14077, 0, '', 5, 1, 0, 0, 1448328872, 1448328872, 1, 0),
(88, 0, 0, 'phụ gia tăng trọng', '', 5, 0, 'phụ gia tăng trọng, tẩy trắng, giữ màu tự nhiên, giữ nước', '<p>Công Ty Trường Thịnh xin chân thành cảm ơn sự ủng hộ của Quý công ty dành cho các sản phẩm phụ gia hóa chất do công ty chúng tôi phân phối. Chúng tôi hân hạnh gửi đến quý Công ty bảng chào giá như sau:</p><p>STPP FCC (Tăng độ dai và giữ độ tự nhiên, giữ nước cho sp) Connel</p><p>FA2 (tạo dai giòn và kéo dài thời gian bảo quản) kết hợp rong biển theo nghiên cứu của Trường ĐH Nha TrangSodium</p><p>Bicarbonate FCC (bột nổi tao xốp nhẹ trong bánh kẹo, tạo bọt trong nước uống) Solvay</p><p>Sodium Carbonate (Soda Ash Light) (chất tẩy trắng dùng trong cn, thực phẩm) Solvay, Echemi&nbsp;</p><p>Maltodextrin D 10 -15 (Chất bổ sung vị ngọt) Dongxiao</p><p>Citric Acid Anhydrous (Điểu chỉnh độ chua) Weifang</p><p>Sodium Citrate (chất ổn định) Weifang</p><p>Chlorine Dioxide 5% (Safe clean V) (Tẩy rửa, diệt khuẩn hiệu quả hơn Chlorine) dùng trong TP Outriger</p><p>New Tech 19: Là hợp chất không chứa Phosphate dùng trong công nghệ chế biến thủy sản tôm cá, tp. Giúp sp có khả năng giữ nước tốt, cải thiện màu sắc và độ tự nhiên cho sp. Sp sau khi xử lý vẫn giữ được cấu trúc cơ thịt tự nhiên, không bóng or trong suốt. Đang bán cho nhiều nhà máy công ty thủy sản lớn.</p><p>New Tech 45: Là hợp chất chứa Phosphate dùng trong công nghệ chế biến thủy sản tôm cá, tp. Giúp sp có khả năng giữ nước tốt, cải thiện màu sắc và độ tự nhiên cho sp. Sp sau khi xử lý vẫn giữ được cấu trúc cơ thịt tự nhiên, không bóng or trong suốt. Đang bán cho nhiều nhà máy công ty thủy sản lớn.</p><p>Vui lòng liên hệ Hạnh 01638229558</p>', '<p>Sử dụng trong phụ gia thực phẩm và thủy sản</p>', '<p>Phosphate, Non phosphate</p>', '25', 0, 14024, 0, '', 5, 1, 0, 0, 1452497050, 1452497050, 2, 0),
(89, 0, 0, 'Men vi sinh VANPRO', 'Men-vi-sinh-VANPRO1455853265.jpg', 3, 0, 'Men vi sinh vừa có thể gây màu nước trước khi thả, vừa có thể bổ sung vào thức ăn.', '<p>- Giúp cải thiện tiêu hóa và hấp thu dinh dưỡng tốt.</p><p>- Đảm bảo tăng trọng và rút ngắn thời gian nuôi.</p><p>- Tăng cường sức đề kháng với môi trường và vi khuẩn gây bệnh, hỗ&nbsp;trợ điều trị trong các trường hợp sử dụng kháng sinh nhiều.</p>', '<p>- Sử dụng VANPRO để đánh gây màu nước, cung cấp men vi sinh cho ao nuôi.</p><p>- Khi tôm được 20 ngày tuổi thì cho ăn thêm VANPRO:</p><p>&nbsp; &nbsp;+ Lấy 2g VANPRO cho 1 kg thức ăn.</p><p>&nbsp; &nbsp;+ Pha VANPRO với nước, để yên 30 phút, rồi trộn vào thức ăn, để cho khô mới đem cho tôm ăn.</p><p>&nbsp; &nbsp;+&nbsp;Ăn liên tục trong 1 tuần vào cử thứ 2 trong ngày. Sau đó nghỉ 1 tuần. Rồi bắt đầu cho ăn lại 1 tuần. Cứ như vậy cho đến cuối vụ.</p>', '<p>gồm có&nbsp;Bacillus subtilis,&nbsp;Bacillus megaterium,&nbsp;Bacillus pumilus,&nbsp;Bacillus polymyxa,&nbsp;Bacillus licheniformis,&nbsp;Lacticacid,&nbsp;Ntrosomonas sp,&nbsp;Aluminium,&nbsp;Magnesium Sulphate.</p>', 'Hộp 1 kg', 0, 13334, 0, '', 5, 1, 0, 0, 1455853265, 1455853265, 0, 0),
(90, 0, 0, 'Men vi sinh VANPRO', 'Men-vi-sinh-VANPRO1455853321.jpg', 3, 0, 'Men vi sinh vừa đánh gây màu nước trước khi thả, vừa bổ sung vào thức ăn giúp tôm tăng trưởng.', '<p>- Giúp cải thiện tiêu hóa và hấp thu dinh dưỡng tốt.</p><p>- Đảm bảo tăng trọng và rút ngắn thời gian nuôi.</p><p>- Tăng cường sức đề kháng với môi trường và vi khuẩn gây bệnh, hỗ&nbsp;trợ điều trị trong các trường hợp sử dụng kháng sinh nhiều.</p>', '<p>- Sử dụng VANPRO để đánh gây màu nước, cung cấp men vi sinh cho ao nuôi.</p><p>- Khi tôm được 20 ngày tuổi thì cho ăn thêm VANPRO:</p><p>&nbsp; &nbsp;+ Lấy 2g VANPRO cho 1 kg thức ăn.</p><p>&nbsp; &nbsp;+ Pha VANPRO với nước, để yên 30 phút, rồi trộn vào thức ăn, để cho khô mới đem cho tôm ăn.</p><p>&nbsp; &nbsp;+&nbsp;Ăn liên tục trong 1 tuần vào cử thứ 2 trong ngày. Sau đó nghỉ 1 tuần. Rồi bắt đầu cho ăn lại 1 tuần. Cứ như vậy cho đến cuối vụ.</p>', '<p>gồm có&nbsp;Bacillus subtilis,&nbsp;Bacillus megaterium,&nbsp;Bacillus pumilus,&nbsp;Bacillus polymyxa,&nbsp;Bacillus licheniformis,&nbsp;Lacticacid,&nbsp;Ntrosomonas sp,&nbsp;Aluminium,&nbsp;Magnesium Sulphate</p>', 'Hộp 1 kg', 0, 13334, 0, '', 5, 1, 0, 0, 1455853321, 1455853321, 0, 0),
(91, 0, 0, 'Men vi sinh VANPRO', 'Men-vi-sinh-VANPRO1455853352.jpg', 3, 0, 'Men vi sinh vừa đánh gây màu nước trước khi thả, vừa bổ sung vào thức ăn giúp tôm tăng trưởng.', '<p>- Giúp cải thiện tiêu hóa và hấp thu dinh dưỡng tốt.</p><p>- Đảm bảo tăng trọng và rút ngắn thời gian nuôi.</p><p>- Tăng cường sức đề kháng với môi trường và vi khuẩn gây bệnh, hỗ&nbsp;trợ điều trị trong các trường hợp sử dụng kháng sinh nhiều.</p>', '<p>- Sử dụng VANPRO để đánh gây màu nước, cung cấp men vi sinh cho ao nuôi.</p><p>- Khi tôm được 20 ngày tuổi thì cho ăn thêm VANPRO:</p><p>&nbsp; &nbsp;+ Lấy 2g VANPRO cho 1 kg thức ăn.</p><p>&nbsp; &nbsp;+ Pha VANPRO với nước, để yên 30 phút, rồi trộn vào thức ăn, để cho khô mới đem cho tôm ăn.</p><p>&nbsp; &nbsp;+&nbsp;Ăn liên tục trong 1 tuần vào cử thứ 2 trong ngày. Sau đó nghỉ 1 tuần. Rồi bắt đầu cho ăn lại 1 tuần. Cứ như vậy cho đến cuối vụ.</p>', '<p>gồm có&nbsp;Bacillus subtilis,&nbsp;Bacillus megaterium,&nbsp;Bacillus pumilus,&nbsp;Bacillus polymyxa,&nbsp;Bacillus licheniformis,&nbsp;Lacticacid,&nbsp;Ntrosomonas sp,&nbsp;Aluminium,&nbsp;Magnesium Sulphate</p>', 'Hộp 1 kg', 0, 13334, 0, '', 5, 1, 0, 0, 1455853352, 1455853352, 0, 0),
(92, 0, 0, 'Kẽm sulphate monohydrate', 'Kem-sulphate-monohydrate1456364799.jpg', 6, 0, 'Nhóm nguyên liệu bổ sung khoáng trong NTTS, phân bón, chăn nuôi...', '<p>Tên Nguyên liệu:&nbsp;Kẽm sulphate monohydrate, Manganese sulphate monohydrate, Sắt sulphate mono, magie sulphate heptahydrate</p><p>Công thức:&nbsp;ZnSO4, FeSO4, MnSO4, MgSO4.7H2O</p><p>-&nbsp;Chuyên dùng:&nbsp;</p><p>&nbsp; &nbsp;Ứng dụng:&nbsp;Tăng độ ngọt cho cây ăn trái, dùng trong sản xuất thức ăn gia súc,&nbsp;sử dụng trong phân bón vi lượng, sản xuất mực in, thuốc nhuộm, thuốc khử trùng&nbsp; ...&nbsp;&nbsp;</p><p>-&nbsp;Quy cách: 25kg/bao</p><p>-&nbsp;Xuất xứ: &nbsp;Trung Quốc</p><p>Giá: Vui lòng gọi&nbsp;(08) 22169449 - 0903399537</p><p>Cty TNHH XNK&nbsp;BIO CHEM chuyên NK&amp;PP cung cấp nhiều nguyên liệu khác dùng trong Nuôi trồng thủy sản:</p><p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Nhóm nguyên liệu xử lý&nbsp;:&nbsp;Vi sinh đáy và nước (dạng bột, dạng hạt), enzyme cắt tảo, Yucca bột, Yucca nước (Mỹ và Mexico)...</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;Nhóm nguyên liệu Bổ sung thức ăn:&nbsp;men tiêu hóa, men trị đường ruột, tăng trọng gel và bột, bổ gan dạng bột và thảo dược, Vitamin C 99%, Sorbitol, Beta glucan 40%, Vitamin hỗn hợp chống stress (Best vitalec) Tăng trọng Pháp (hòa tan), Dinh dưỡng Pháp, Dịch cá ngừ (Tuna fish sobluble extract), Áo bọc thức ăn, khoáng nước, khoáng trộn, methionine, lysine..</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;Nhóm&nbsp;Hóa Chất xử lý nước:&nbsp;khoáng tạt, khoáng trộn, Sodium bicarbonate (soda lạnh tăng kiềm),&nbsp;Sodium&nbsp;percarbonate (oxy viên, oxy bột), Chlorine cá heo&nbsp;70%, BKC 80% (Benzalkonium chloride), Protectol (glutaraldehyde 50), Pvp Iodine (dạng bột), Iodine complex (dung dịch), SDIC (Chlorine hữu cơ), Sodium thiosulphate, Mangan sulphate (MnSO4), kẽm sulphate (ZnSO4), Sắt sulphate (FeSO4), Calcium Formate...</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;Nguyên liệu phụ gia, hương liệu:&nbsp;hương cam, hương sữa, hương vani, hương trái cây, màu thực phẩm...và một số kháng sinh (Oxytetracycline HCL)</p><p>&nbsp;</p>', '<p>Dùng trong nuôi trồng thủy sản,&nbsp;chăn nuôi, thú y, phân bón</p>', '<p>MnSO4.H2O</p>', '25kg/bao', 0, 12212, 0, '', 5, 1, 0, 0, 1456364799, 1456364799, 0, 0),
