INSERT INTO `tkb_aq_technical` (`id`, `species_id`, `cat_id`, `title`, `content`, `add_time`, `edit_time`, `keyword`, `author`, `reference`, `user_name`, `hit`) VALUES
(8, 1, 1, 'Mùa vụ nuôi và mật độ thả', '<p>\r\n	<strong>M&ugrave;a vụ thả nu&ocirc;i</strong></p>\r\n<p>\r\n	Bệnh đốm trắng ph&aacute;t triển rất mạnh nếu chon m&ugrave;a vụ nu&ocirc;i kh&ocirc;ng th&iacute;ch hợp. Ở Thanh H&oacute;a v&agrave; Nghệ An, m&ugrave;a vụ nu&ocirc;i kh&ocirc;ng th&iacute;ch hợp l&agrave; thả giống trước t&ecirc;t thanh minh (th&aacute;ng 3 dương lịch trở lại) v&agrave; sau th&aacute;ng 4 dương lịch. Thời gian thả giống th&iacute;ch hợ nhất của c&aacute;c tỉnh n&agrave;y l&agrave; v&agrave;o th&aacute;ng 4 dương lịch.</p>\r\n<p>\r\n	<img alt="lich_nuoi_tom_su" src="/upload/images/mua_vu_lic_nuoi_tom_su.gif" style="width: 679px; height: 428px;" /></p>\r\n<p>\r\n	<strong>X&aacute;c đinh mật độ thả</strong>:</p>\r\n<p>\r\n	Nu&ocirc;i th&acirc;m canh đ&ograve;i hỏi người nu&ocirc;i phải c&oacute; kinh nghi&ecirc;m, tự tin trong kỹ thuật nu&ocirc;i, thiết kế hệ thống nu&ocirc;i ph&ugrave; hợp.</p>\r\n<p>\r\n	Để đảm bảo năng suất nu&ocirc;i bền vững qua c&aacute;c năm, mật độ thả n&ecirc;n duy tr&igrave; ở mức độ vừa phải từ 25-25 P15/m<sup>2</sup> v&agrave; năng suất nu&ocirc;i ổn định khảng 3-4 tấn/ha. C&oacute; thể nu&ocirc;i với mật độ cao hơn tuy nhi&ecirc;n kh&ocirc;ng n&ecirc;n nu&ocirc;i mật độ qu&aacute; cao (&gt;40 con/m<sup>2</sup> ) v&agrave; năng suất qu&aacute; 6 tấn/ha.</p>\r\n', 1322037265, 1329818430, 'Lich nuoi tom su, mat do tha nuoi tom su, nuoi tom su tham canh, tai lieu nuoi tom su thanh canh', '', '', '', 1532),
(9, 1, 1, 'Lựa chọn địa điểm và xây dựng ao nuôi', '<p>\r\n	<strong>1. Lựa chọn địa điểm</strong></p>\r\n<p>\r\n	Vị tr&iacute; để x&acirc;y dựng &aacute;o nu&ocirc;i t&acirc;m canh phải đ&aacute;p ứng c&aacute;c điều kiện sau: ở v&ugrave;ng cao triều, nguồn nước c&oacute; độ mặn từ 10-30&permil;, đất đ&aacute;y c&oacute; độ pH&gt;5 v&agrave; c&oacute; khả năng giữ nước tốt (đ&aacute;t thị pha c&aacute;t l&agrave; tốt nhất).</p>\r\n<p>\r\n	<strong>2. X&acirc;y dựng hệ thống ao nu&ocirc;i</strong></p>\r\n<p>\r\n	Tốt nhất l&agrave; x&acirc;y dựng đủ 3 ao: ao nu&ocirc;i, ao chứa v&agrave; ao xử l&yacute; chất thải. Nếu điều kiện chưa cho ph&eacute;p ngo&agrave;i nu&ocirc;i cần thiết phải c&oacute; ao chứa lắng.</p>\r\n<p>\r\n	<img alt="so do bo tri ao nuoi tom su" src="/upload/images/technical/bo_tri_ao_nuoi_tom_su.gif" style="width: 530px; height: 266px;" /></p>\r\n<p>\r\n	H&igrave;nh: sơ đồ bố tr&iacute; ao.</p>\r\n<p>\r\n	- Ao nu&ocirc;i: c&oacute; h&igrave;nh chữ nhật hoặc h&igrave;nh vu&ocirc;ng, diện t&iacute;ch từ 0,4-0,6 ha, độ s&acirc;u trung b&igrave;nh 1,3-1,6m c&oacute; cống cấp v&agrave; cống tho&aacute;t ri&ecirc;ng biệt.</p>\r\n<p>\r\n	- Ao xữ l&yacute; chất thải: chứa nước v&agrave; xử l&yacute; nước trước khi đưa v&agrave;o ao nu&ocirc;i. Diện tuchs bằng 20-25% diện t&iacute;ch ao nu&ocirc;i. N&ecirc;n đ&agrave;o s&acirc;u hơn ai nu&ocirc;i để c&oacute; thể chứa được nhiều nước.</p>\r\n<p>\r\n	- Ao xữ l&yacute; chất thải: chứ nước thải từ ao nu&ocirc;i, thời gian xử l&yacute; 7-10 ng&agrave;y (xử l&yacute; bằng sinh hoc) th&igrave; c&oacute; thể th&aacute;o ra m&ocirc;i trường ngo&agrave;i hoặc được cấp trở lại ao chứa lắng. Diện t&iacute;ch bằng 10-15% diện t&iacute;ch ao nu&ocirc;i.</p>\r\n', 1323679068, 1329817752, 'Lua chon vi tri nuoi tom su, bo tri ao nuoi tom su, so do ao nuoi tom su', '', '', '', 1332),
(10, 20, 3, 'Kỹ thuật sản xuất giống nhân tạo cá lăng', '<h3>\r\n	Nu&ocirc;i vỗ c&aacute; bố mẹ</h3>\r\n<p style="margin-left:18.0pt;">\r\n	C&aacute; bố mẹ lăng nha được bố tr&iacute; nu&ocirc;i vỗ tại ao A3 c&oacute; diện t&iacute;ch l&agrave; 1200 m<sup>2</sup>, độ s&acirc;u mực nước từ 1,7 &ndash; 1,8 m.</p>\r\n<p style="margin-left:18.0pt;">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trước khi thả c&aacute; v&agrave;o nu&ocirc;i vỗ, ao đ&atilde; được tẩy dọn kỹ. Lượng v&ocirc;i CaO được sử dụng để b&oacute;n l&agrave; 10kg/100m<sup>2</sup>, ngo&agrave;i ra trại c&ograve;n sử dụng BKC diệt tr&ugrave;ng đ&aacute;y ao.</p>\r\n<p style="margin-left:18.0pt;">\r\n	Ao được ngăn l&agrave;m hai, một b&ecirc;n thả c&aacute; hậu bị, một b&ecirc;n thả c&aacute; chuẩn bị cho đẻ, thả th&ecirc;m 2 &ndash; 4 % c&aacute; r&ocirc; phi. Sau 15 &ndash; 20 ng&agrave;y tiến h&agrave;nh thay nước một lần, thay từ 10 &ndash; 20 % lượng nước trong ao. Sử dụng granulite daimetine (th&agrave;nh phần ch&iacute;nh l&agrave; SiO<sub>2</sub>) rải điều khắp ao với lượng 20 kg/1200 m<sup>2</sup> để hấp thu kh&iacute; độc, l&agrave;m sạch đ&aacute;y ao.</p>\r\n<p style="margin-left:18.0pt;">\r\n	&nbsp;&nbsp; Sử dụng thức ăn vi&ecirc;n c&ocirc;ng nghiệp c&oacute; độ đạm tr&ecirc;n 28%, cho ăn 3 &ndash; 5% trọng lượng th&acirc;n.</p>\r\n<p style="margin-left:18.0pt;">\r\n	&nbsp;&nbsp; C&aacute; được chọn nu&ocirc;i vỗ l&agrave;m c&aacute; bố mẹ l&agrave; những c&aacute; c&oacute; tuổi lớn hơn hay bằng tuổi th&agrave;nh thục. C&aacute; bố mẹ chủ yếu lấy từ Đồng Nai, c&aacute;c b&egrave; c&aacute; ở An Giang. C&aacute; phải c&oacute; nguồn gốc kh&aacute;c nhau để tr&aacute;nh hiện tượng cận huyết. C&aacute; bố mẹ phải khỏe mạnh kh&ocirc;ng nhiễm bệnh, ngoại h&igrave;nh đẹp, kh&ocirc;ng s&acirc;y s&aacute;t v&agrave; mất nhớt. C&aacute; vận chuyển về trại cho v&agrave;o bể composite khi trời m&aacute;t chuyển ra ao nu&ocirc;i vỗ, tạo d&ograve;ng chảy nhẹ để cho c&aacute; hồi phục sức khỏe, c&oacute; thể tạt th&ecirc;m NOVA &ndash; ANTI SHOCK FISH để giảm stress cho c&aacute;. C&aacute; bố mẹ nu&ocirc;i chung với nhau, mật độ nu&ocirc;i vỗ l&agrave; 450 &ndash; 500 con tr&ecirc;n 1200m<sup>2</sup>. Ng&agrave;y 18/07/2008 thả th&ecirc;m 105 con vận chuyển từ Đồng Nai.</p>\r\n', 1323679355, 1323851733, 'sinh san ca lang, ca lang nha', 'PHONG', '', '', 1404);
