(55, 17, 113, 'video', 'Cảm ơn nhiều nhé!', 'baiviet', 1340125696, '58.186.180.11', 0, '', 0, '', 1),
(56, 84, 112, 'video', 'cảm ơn Video bổ ích của bạn', 'baiviet', 1340196384, '168.131.231.49', 0, '', 0, '', 1),
(57, 84, 91, 'video', 'kinh nghiệm chọn ếch giống của bác ở phút thứ 6 rất hay', 'baiviet', 1340197197, '168.131.231.49', 0, '', 0, '', 1),
(58, 39, 129, 'video', 'Đây là loài cá Bỗng mọi người xem tại đây: http://tepbac.com/species/full/103/Ca-bong.htm', 'baiviet', 1342318233, '58.186.188.243', 0, '', 0, '', 1),
(59, 84, 129, 'video', 'các cá thể cá bỗng này tập trung quanh năm ở đây chứ ko phải chỉ trong mùa sinh sản', 'baiviet', 1342431454, '168.131.231.49', 0, '', 0, '', 1),
(60, 3816, 88, 'video', 'cư dân dưới nước rất sinh động,những chú tôm vệ sinh rất đáng yêu giúp cho vật chủ dược sạch hơn.sự hợp tác nhau để sống và cùng có lợi là bản năng sinh tồn của sinh vật dưới đáy biển.hãy bảo vệ sự đáng yêu này bằng cách bảo vệ môi trường sống của chúng cũng chính là bảo vệ môi trường sinh thái của chúng ta và thế giới.', 'baiviet', 1348677359, '115.78.13.19', 0, '', 0, '', 1),
(61, 3807, 132, 'video', 'đối tượng nuôi là cá gì?', 'baiviet', 1349668615, '113.162.228.227', 0, '', 0, '', 1),
(62, 4325, 37, 'video', 'hihi.mình rất thích học thầy Ngọc, thầy hiền và dạy rất thú vị,ngồi học mà ko muốn về cứ muốn nghe thầy nói miết', 'baiviet', 1350462648, '123.20.107.230', 0, '', 0, '', 1),
(63, 4511, 48, 'video', 'tuyệt vời', 'baiviet', 1351174293, '123.28.219.229', 0, '', 0, '', 1),
(64, 17, 2744, 'news', 'Bong bóng cá này dùng để làm gì vậy nhỉ?', 'baiviet', 1351226439, '113.172.166.238', 0, '', 0, '', 1),
(65, 84, 2745, 'news', 'ếch xào lá cách :)', 'baiviet', 1351231349, '168.131.231.49', 0, '', 0, '', 1),
(66, 39, 2750, 'news', 'Bài viết hay quá.', 'baiviet', 1351247451, '123.20.230.197', 0, '', 0, '', 1),
(67, 4154, 2748, 'news', 'mong rằng tình hình kinh tế sẽ đi vào thế ổn định', 'baiviet', 1351265101, '123.23.26.208', 0, '', 0, '', 1),
(68, 83, 2817, 'news', '- Bà con nông dân nên xem kĩ bao bì nhãn mác (tên công ty sản xuất, tên nhà nhập khẩu, hạn sử dụng, hình thức đóng góp…) của các sản phẩm men vi vinh trước khi quyết định mua hàng để tránh trường hợp “tiền mất tật mang”.\r\n- Không nên mua hàng theo giới thiệu của đại lý. \r\n- Khi sử dụng bà con nông dân cũng nên theo dõi đánh giá hiệu quả của loại men vi sinh đang xử lý (bằng cách so sánh  màu nước, màu bùn, đo hàm lượng khí độc: NH3, NO2… trước và sau khi dùng các sản phẩm men vi sinh', 'baiviet', 1351648432, '123.23.101.204', 0, '', 0, '', 1),
(69, 81, 2866, 'news', 'Bây zờ Cty thuốc thủy sản nhiều vô số. Không biết đâu là thuốc thật hay là thuốc giả. Mình chỉ thích sử dụng các sản phẩm của Cty TNHH TMSX Thảo Mộc Xanh thôi!', 'baiviet', 1351848815, '171.242.79.62', 0, '', 0, '', 1),
(70, 4777, 119, 'news', 'con cá dĩa trong ảnh này xấu wa', 'baiviet', 1352209877, '65.49.14.79', 0, '', 0, '', 1),
(71, 39, 2934, 'news', 'Lên rồi lại xuống, xuống rồi lên...', 'baiviet', 1352359167, '115.73.26.148', 0, '', 0, '', 1),
(72, 39, 2931, 'news', 'Vấn đề là chúng ta thiếu tuyên truyền ý thức chung.', 'baiviet', 1352360006, '115.73.26.148', 0, '', 0, '', 1),
(73, 1814, 2949, 'news', 'Xin hỏi ông cục trưởng, vận chuyển mà để thất thoát như vậy thì có được phép vận chuyển không. như vậy thì kiểm tra của mình quá lỏng lẻo rồi, tôi nghỉ cá sấu dù lớn hay nhỏ đều rất nguy hiểm cho con người và ngành chức năng cần có biện pháp xử lý kịp thời mới được', 'baiviet', 1352424841, '27.77.80.139', 0, '', 0, '', 1),
(74, 39, 2994, 'news', 'Cung hên là nó không ở Việt Nam. Nếu ở VN thì được liệt vào doanh mục quí hiếm dành cho đại gia rồi.', 'baiviet', 1352707143, '115.73.28.124', 0, '', 0, '', 1),
(76, 2078, 3126, 'news', 'Chủ tịch xã mà không hay biết gì hết. Bó tay. Thời buổi này mà tìm hiểu thì chắc không dám ăn thứ gì hết. Khổ...', 'baiviet', 1353141426, '203.162.36.145', 0, '', 0, '', 1),
(77, 83, 3126, 'news', 'Chắc cần có đề tài nghiên cứu dùng chất bảo quản gì để ruồi không đậu, bọ không phát triển trong khô nhưng không ảnh hưởng đến sức khỏe con người,để nghề làm khô được thuận lợi và người tiêu dùng được an tâm !!!', 'baiviet', 1353305697, '123.23.94.155', 0, '', 0, '', 1),
(78, 39, 3197, 'news', 'Sao mà kiểu gì cũng đầu độc được hết vậy? Nể thật', 'baiviet', 1353481513, '115.73.47.69', 0, '', 0, '', 1),
(79, 39, 3178, 'news', 'Cá lóc nằm yên để chụp hình thì hiên quá. Chắc không qua được mấy ngày đâu. Hicc', 'baiviet', 1353482392, '115.73.47.69', 0, '', 0, '', 1),
(80, 39, 3208, 'news', 'Đây là cá sấu hỏa tiễn nuôi cảnh mà sao nhiều người cứ cho đó là lạ?', 'baiviet', 1353489467, '115.73.47.69', 0, '', 0, '', 1),
(88, 5077, 3091, 'news', 'Có thể do nguồn nước bị ô nhiễm, hoặc không đủ lượng oxi cho cá. Bà con thường nuôi theo kinh nghiệm nên thường không kiểm tra các chỉ  tiêu của nước như độ kiềm, độ pH, hàm lượng oxi, NH3/NH4... Vì vậy cần kiểm tra các thông số của nước để biết cách xử lí.', 'baiviet', 1353559625, '118.69.71.74', 0, '', 0, '', 1),
(99, 2455, 3208, 'news', 'Đây theo như được biết là một giống cá sấu hỏa tiển, loài này   được nuôi làm kiểng.', 'baiviet', 1353576669, '171.242.245.121', 0, '', 0, '', 1),
(100, 5114, 37, 'video', 'Thầy dễ thật. Dạy hay nữa', 'baiviet', 1353688063, '1.54.72.251', 0, '', 0, '', 1),
(101, 1116, 3337, 'news', 'Cho gui chuc  suc khoe den bac sau! Vua ca loc lun khoe vi su nghiep phat truen thuy San', 'baiviet', 1354190810, '115.77.144.25', 0, '', 0, '', 1),
(102, 83, 3346, 'news', 'Hết mực có lưu huỳnh, bây giờ tôm khô lại có phẩm nhuộm, không biết con người lấy gì để ăn nữa....', 'baiviet', 1354236442, '123.23.102.129', 0, '', 0, '', 1),
(103, 5279, 3223, 'news', 'Đây là phác đồ điều trị một số bệnh trên cá điêu hồng em mới đọc được, các bác nghiên cứu xem thế nào nhé. http://vinavetco.com/index.php?option=com_content', 'baiviet', 1354586615, '27.73.150.13', 0, '', 0, '', 1),
(104, 5279, 3391, 'news', 'http://vinavetco.com/index.php?option=com_content', 'baiviet', 1354608318, '171.252.54.219', 0, '', 0, '', 1),
(105, 5279, 3330, 'news', 'Các bác dùng thử Chlorine Dioxide để khử trùng nước xem có cải thiện được tình hình không?', 'baiviet', 1354608531, '171.252.54.219', 0, '', 0, '', 1),
(106, 4030, 3391, 'news', 'Không biết tài liệu này của tác giả nào, nhưng đề nghị ban biên tập nên chỉnh sửa kỹ càng trước khi đăng bài. Gì mà có cả cá và tôm sú trong này vậy. tài liệu kỹ thuật gì mà giống được cóp nhặt chỗ này một tý, chỗ kia một tí vậy?', 'baiviet', 1354634535, '113.187.16.121', 0, '', 0, '', 1),
