(242, 748, 57, 'video', '<p>\r\n	Kỹ thuật nuôi lươn đã cập nhật đầy đủ, xem chi tiết: <a href="http://tepbac.com/video/full/250/video-Ki-thuat-nuoi-luon-khong-can-dat-bun-full.htm" target="_self">http://tepbac.com/video/full/250/video-Ki-thuat-nuoi-luon-khong-can-dat-bun-full.htm</a></p>\r\n', 'baiviet', 1373733953, '113.182.189.41', 0, '', 0, '', 1),
(238, 84, 25, 'qa', 'Giáo sư Kevin Michael Fitzsimmon-Giám đốc Cơ quan Hợp tác Nông nghiệp Quốc tế, Đại học Arizona (Mỹ), cho biết: “Cả tôm và cá rô phi đều thích ăn mùn bã hữu cơ, nên khi thả chung, chúng có thể xử lý chất thải của nhau. Khi có cá rô phi nhiều, chúng sẽ khuấy đảo đáy ao, tạo kháng sinh tự nhiên, giúp đáy ao và nguồn nước ao nuôi tốt hơn nên giảm được nhóm khuẩn vibrio và virus gây bệnh. Các nghiên cứu còn cho thấy có tảo lục trong ao sẽ hạn chế được vi khuẩn gram âm, chất thải rắn nhưng không ảnh hưởng đến sự phát triển của tảo khuê và một số loại tảo có ích khác. Ngoài ra, cá rô phi còn giúp tiêu diệt một số vật chủ trung gian mang mầm bệnh. Khi có tôm bệnh chết trong ao, cá rô phi ăn ngay, hạn chế lây lan dịch bệnh”...', 'baiviet', 1373204840, '168.131.231.49', 0, '', 0, '', 1),
(243, 8219, 28, 'qa', 'bà con quan tâm xin gọi 01694113355 để được tư vấn và mua hàng \ntruy cập website 68mua.com\nchúng tôi giao hàng trên toàn quốc', 'baiviet', 1374017783, '123.18.79.84', 0, '', 0, '', 1),
