INSERT INTO `tkb_news` (`news_id`, `cat_id`, `topic_id`, `group_id`, `author`, `add_time`, `edit_time`, `title`, `home_text`, `home_img_file`, `alt_img`, `op_img`, `note_img`, `body_text`, `source`, `allowed_comm`, `hit`, `keywords`, `focus`, `user_name`, `like`, `liked`, `count_comment`) VALUES
(67, 1, 0, 0, 'Minh Văn', 1319531778, 0, 'Lộc Bình (Thừa Thiên Huế): 5 tấn cá chết vì mưa lũ', 'Những ngày gần đây, tại xã Lộc Bình (Phú Lộc - Thừa Thiên Huế) nhiều người dân nuôi trồng thủy sản bên đầm Cầu Hai lo lắng khi gần 60% lồng cá mú, cá hồng bị chết.', 'cachethaongloat.jpg', '', 0, '', '<p>\n	Qua khảo s&aacute;t thực tế của ch&iacute;nh quyền địa phương, hầu hết số c&aacute; chết nằm trong độ tuổi 6 - 7 th&aacute;ng; trọng lượng 0,5 - 0,7 kg/con của 41/86 hộ, ước khoảng 5 tấn. T&iacute;nh theo gi&aacute; hiện tại, thiệt hại khoảng tr&ecirc;n 1 tỷ đồng. &Ocirc;ng Lương Thế Vĩnh - Ph&oacute; Chủ tịch UBND x&atilde; Lộc B&igrave;nh, nguy&ecirc;n nh&acirc;n h&agrave;ng loạt lồng c&aacute; nu&ocirc;i tr&ecirc;n đầm Cầu Hai chết do ảnh hưởng những cơn mưa lũ vừa qua, c&aacute;c nguồn nước c&aacute;c s&ocirc;ng hồ, khe suối đồi n&uacute;i đổ về dẫn đến thay đổi m&ocirc;i trường sống của c&aacute;.</p>\n', '', 0, 1001, 'ca chet, thiet hai, loc binh, moi truong thay doi', 0, '0', 0, '', 0),
(80, 1, 0, 0, 'Phong', 1323618168, 0, 'Nuôi ốc hương, trồng rong sụn cải tiến', 'Trung tâm khuyến nông, khuyến ngư Ninh Thuận khuyến cáo mô hình nuôi ốc hương và trồng rong sụn với cách nuôi trồng cải tiến có nhiều ưu điểm đang mang lại hiệu quả kinh tế cao cho người nuôi ở vùng ven biển.', '8_TRONG_RONG_SUN_1.jpg', '', 0, '', '<p>\r\n	Chọn vị tr&iacute; đặt lồng hoặc cắm đăng nu&ocirc;i ở v&ugrave;ng nước trong sạch, chất đ&aacute;y c&aacute;t hoặc c&aacute;t san h&ocirc;, &iacute;t b&ugrave;n, c&oacute; độ mặn từ 25 - 35%o v&agrave; ổn định. Nguồn nước kh&ocirc;ng bị ảnh hưởng nước ngọt do t&aacute;c động nước s&ocirc;ng v&agrave;o m&ugrave;a mưa. Lồng, đăng l&agrave;m chắc chắn, c&oacute; lưới bảo vệ b&ecirc;n ngo&agrave;i ngăn kh&ocirc;ng cho c&aacute; dữ, cua, ghẹ lọt v&agrave;o ăn ốc. Độ s&acirc;u đặt lồng hoặc cắm đăng từ 1,5 m nước trở l&ecirc;n. Đăng nu&ocirc;i phải ch&ocirc;n s&acirc;u xuống dưới c&aacute;t &iacute;t nhất 10 cm để tr&aacute;nh ốc chui ra ngo&agrave;i. Độ cao lưới cắm đăng phải vượt qua mức nước triều cao nhất 1 m để ốc kh&ocirc;ng bị s&oacute;ng đ&aacute;nh ra ngo&agrave;i. Trong qu&aacute; tr&igrave;nh nu&ocirc;i thường xuy&ecirc;n kiểm tra lưới, ph&aacute;t hiện kịp thời dịch hại, l&agrave;m vệ sinh lồng lưới để nước lưu th&ocirc;ng. Lồng nu&ocirc;i phải được ch&ocirc;n s&acirc;u dưới lớp c&aacute;t đ&aacute;y 5 cm để c&oacute; nền c&aacute;t cho ốc v&ugrave;i m&igrave;nh. K&iacute;ch cỡ giống thả tối thiểu 8.000 - 10.000 con/kg trở l&ecirc;n, mật độ thả 500 - 1.000 con/m2. Thức ăn d&agrave;nh cho ốc hương l&agrave; c&aacute;, cua, ghẹ, trai nước ngọt, don, s&uacute;t... Theo d&otilde;i lượng thức ăn thừa h&agrave;ng ng&agrave;y để điều chỉnh hệ số thức ăn ph&ugrave; hợp. Ch&uacute; &yacute;, trường hợp nu&ocirc;i l&acirc;u đ&aacute;y lồng qu&aacute; bẩn, c&oacute; m&ugrave;i h&ocirc;i, ốc sẽ kh&ocirc;ng ăn v&agrave; yếu dần, l&uacute;c n&agrave;y cần chuyển lồng sang vị tr&iacute; mới. Nếu nu&ocirc;i trong đăng cắm cố định cần ngăn th&agrave;nh nhiều ngăn, cần chuyển sang ngăn mới khi ngăn cũ nu&ocirc;i l&acirc;u ng&agrave;y đ&aacute;y bẩn, h&ocirc;i. Từ 4 - 5 th&aacute;ng, t&ugrave;y điều kiện m&ocirc;i trường nu&ocirc;i, quản l&yacute; chăm s&oacute;c, ốc nu&ocirc;i đạt k&iacute;ch cỡ 90 - 130 con/kg c&oacute; thể thu hoạch.</p>\r\n<p>\r\n	<strong>Trồng rong sụn cải tiến</strong></p>\r\n<p>\r\n	C&oacute; nhiều m&ocirc; h&igrave;nh trồng rong sụn, trồng bằng h&igrave;nh thức d&agrave;n căng tr&ecirc;n đ&aacute;y c&oacute; nhiều ưu điểm. M&ocirc; h&igrave;nh n&agrave;y c&oacute; thể &aacute;p dụng trồng b&atilde;i ngang v&ugrave;ng triều đ&aacute;y c&aacute;t, c&aacute;t b&ugrave;n, sỏi đ&aacute; nhỏ tr&ecirc;n nền c&aacute;t, ven đầm ph&aacute;, vũng, vịnh ven biển v&agrave; ven đảo, nơi c&oacute; mức thủy triều r&uacute;t thấp nhất c&ograve;n 0,5 - 1,2 m. C&aacute;ch trồng rong sụn d&agrave;n căng tr&ecirc;n đ&aacute;y ở c&aacute;c b&atilde;i, vịnh &iacute;t s&oacute;ng gi&oacute; như sau: trước ti&ecirc;n l&agrave; chọn khu vực bằng phẳng, dọn sạch rong tảo v&agrave; c&aacute;c thực vật kh&aacute;c. D&ugrave;ng cọc tre c&oacute; đường k&iacute;nh 3 - 5 cm, d&agrave;i 1 - 1,2 m đ&oacute;ng th&agrave;nh h&agrave;ng xuống đ&aacute;y, c&aacute;ch nhau 0,7 - 1 m, hai h&agrave;ng cọc c&aacute;ch nhau 10 m, ở khoảng giữa c&oacute; thể xen cọc phụ. C&aacute;c h&agrave;ng cọc đặt thẳng g&oacute;c với hướng gi&oacute; để sau n&agrave;y d&acirc;y rong song song hướng gi&oacute;. Cọc ở g&oacute;c c&oacute; thể d&ugrave;ng d&acirc;y neo cố định tăng độ vững chắc. Buộc d&acirc;y căng bằng sợi cước nylon đường k&iacute;nh 1 - 2 mm ở giữa hai h&agrave;ng cọc. D&acirc;y căng c&aacute;ch đ&aacute;y 0,2 - 0,5 m, tr&ecirc;n c&aacute;c d&acirc;y căng c&oacute; buộc c&aacute;c phao để cố định c&aacute;ch mặt nước 0,3 - 0,4 m. D&ugrave;ng d&acirc;y mềm cắt đoạn 20 cm, một đầu buộc v&agrave;o bụi rong giống, đầu kia buộc v&agrave;o sợi d&acirc;y căng.</p>\r\n<p>\r\n	Ở c&aacute;c b&atilde;i triều, khu vực c&oacute; nước s&acirc;u th&igrave; trồng theo phương ph&aacute;p gi&agrave;n b&egrave; c&oacute; phao nổi. Bố tr&iacute; bằng c&aacute;ch d&ugrave;ng gỗ hoặc tre d&agrave;i 4 - 5 m l&agrave;m th&agrave;nh khung h&igrave;nh chữ nhật c&oacute; k&iacute;ch thước 3 x 4 m, xung quanh bao lưới l&agrave;m giảm s&oacute;ng, tr&aacute;nh c&aacute; tạp ăn rong. D&ugrave;ng d&acirc;y thừng hay d&acirc;y nylon đường k&iacute;nh 0,25 - 1 cm buộc c&aacute;c cạnh nối nhau. Buộc c&aacute;c d&acirc;y căng trong khung th&agrave;nh từng d&atilde;y c&aacute;ch nhau 0,4 m, h&agrave;ng c&aacute;ch h&agrave;ng 0,4 m. C&aacute;c đầu g&oacute;c khung trồng buộc d&acirc;y v&agrave; neo chặt xuống đ&aacute;y, tr&ecirc;n buộc với c&aacute;c phao nổi...</p>\r\n', 'KHPT', 0, 1015, 'Nuoi oc huong trong rong sun', 0, '0', 0, '', 0),
(84, 14, 0, 0, '', 1323654788, 0, 'Sắp hết hạn lưu hành thuốc thú y dạng nước: DN lại “nài” được gia hạn', 'Theo lộ trình áp dụng GMP (thực hành SX thuốc tốt) đối với thuốc thú y đã được Bộ NN-PTNT ấn định, đến hết ngày 31/12/2011, tất cả các sản phẩm thuốc thú y dạng dung dịch (gồm thuốc tiêm và thuốc uống) của các DN chưa đạt GMP trước năm 2011 sẽ bị cấm lưu hành trên thị trường.', 'thuoc_thu_y_dang_nuoc.JPG', '', 0, '', '<p>\r\n	Mặc d&ugrave; kế hoạch &aacute;p dụng GMP được khởi động từ những năm 2004 &ndash; 2005, thế nhưng sau nhiều lần phải tr&igrave; ho&atilde;n do sự &quot;ch&acirc;y ì&quot; từ ph&iacute;a c&aacute;c DNSX thuốc th&uacute; y, phải tới năm 2010, việc &aacute;p dụng GMP trong SX thuốc th&uacute; y mới được ch&iacute;nh thức &aacute;p dụng.</p>\r\n<p>\r\n	Đi&ecirc;̀u này cho th&acirc;́y 2 khía cạnh: Từ phía các DN hoặc c&ocirc;́ tình níu kéo vi&ecirc;̣c thực hi&ecirc;̣n l&ocirc;̣ trình GMP hoặc vi&ecirc;̣c đ&acirc;̀u tư cho 1 NM thu&ocirc;́c GMP quá sức với các DN nhỏ n&ecirc;n DN kh&ocirc;ng kham n&ocirc;̉i. V&ecirc;̀ phía Cục Thú y đã &quot;nu&ocirc;ng chi&ecirc;̀u&quot; DN quá múc d&acirc;̃n đ&ecirc;́n l&ocirc;̣ trình GMP bị di dời nhi&ecirc;̀u l&acirc;̀n (&quot;quá tam ba b&acirc;̣n&quot;) đ&ecirc;́n n&ocirc;̃i DN &quot;nhờn thu&ocirc;́c&quot;. Tuy nhi&ecirc;n thời gian g&acirc;̀n đ&acirc;y, Cục Thú y tỏ ra quy&ecirc;́t t&acirc;m &quot;&acirc;́n định&quot; thời đi&ecirc;̉m thực hi&ecirc;̣n l&ocirc;̣ trình GMP.</p>\r\n<p>\r\n	Cụ thể, theo ấn định cuối c&ugrave;ng của Bộ NN-PTNT th&igrave; những d&acirc;y chuyền SX thuốc ti&ecirc;m, thuốc uống dạng dung dịch (gọi chung l&agrave; thuốc nước); thuốc bột cho uống, thuốc bột trộn thức ăn (gọi chung l&agrave; thuốc bột) c&oacute; thời hạn &aacute;p dụng GMP đến hết năm 2010. Qu&aacute; thời gian n&agrave;y, nếu DN kh&ocirc;ng c&oacute; chứng chỉ GMP th&igrave; c&aacute;c sản phẩm đ&atilde; SX chỉ được ph&eacute;p lưu h&agrave;nh đến hết ng&agrave;y 31/12/2011 (đối với thuốc nước) v&agrave; đến hết ng&agrave;y 31/12/2012 (đối với dạng thuốc bột).&nbsp;</p>\r\n<p>\r\n	&ETH;ối với d&acirc;y chuyền SX thuốc bột v&agrave; c&aacute;c dạng thuốc kh&aacute;c, thời hạn thực hiện &aacute;p dụng GMP sẽ được k&eacute;o d&agrave;i hết năm 2012. &ETH;ến thời hạn n&agrave;y, c&aacute;c sản phẩm đ&atilde; SX chỉ được ph&eacute;p lưu h&agrave;nh đến hết ng&agrave;y 31/12/2014. Như vậy, nghĩa l&agrave; chỉ c&ograve;n chưa đầy 1 th&aacute;ng nữa, c&aacute;c sản phẩm thuốc nước do c&aacute;c DN chưa đạt GMP trước năm 2010 SX sẽ bị cấm lưu h&agrave;nh tr&ecirc;n thị trường v&agrave; bị thu hồi.&nbsp;</p>\r\n<p>\r\n	Trước hạn ch&oacute;t n&agrave;y, một lần nữa, c&aacute;c DNSX thuốc th&uacute; y lại tìm cách níu kéo quyết định của Bộ NN-PTNT. Tại cuộc họp b&agrave;n phương &aacute;n xử l&iacute; do Cục Th&uacute; y tổ chức h&ocirc;m qua (6/12), đ&ocirc;ng đảo c&aacute;c DNSX thuốc th&uacute; y cho rằng, quyết định tr&ecirc;n l&agrave; bất hợp l&iacute; v&agrave; ảnh hưởng mạnh mẽ tới quyền lợi của DN (!?).</p>\r\n<p>\r\n	&Ocirc;ng Ho&agrave;ng Triều, Chủ tịch Hiệp hội SX thuốc th&uacute; y Việt Nam cho nhận x&eacute;t: Chủ trương &ldquo;GMP h&oacute;a&rdquo; SX thuốc th&uacute; y l&agrave; chủ trương đ&uacute;ng đắn đ&atilde; được đ&ocirc;ng đảo c&aacute;c DN ủng hộ v&agrave; thực hiện nghi&ecirc;m t&uacute;c trong thời gian qua. Đến nay, đ&atilde; c&oacute; hơn 70 DN kh&ocirc;ng đạt ti&ecirc;u chuẩn GMP (chủ yếu tại miền Bắc) phải giải thể. Từ khi hết hạn &aacute;p dụng GMP đối với c&aacute;c loại thuốc nước v&agrave;o đầu năm 2011 đến nay, đ&atilde; c&oacute; th&ecirc;m khoảng 18 DN kh&aacute;c kh&ocirc;ng đạt GMP giải thể hoặc chuyển đổi h&igrave;nh thức kinh doanh. Về hạn ch&oacute;t phải &aacute;p dụng GMP (thuốc nước đến hết năm 2010, thuốc bột đến hết năm 2012), &ocirc;ng Triều cho rằng quyết định n&agrave;y l&agrave; ho&agrave;n to&agrave;n hợp l&iacute;, được c&aacute;c DN ủng hộ thực hiện.</p>\r\n<p>\r\n	Cũng theo &ocirc;ng Triều th&igrave; hiện nay, thời hạn lưu h&agrave;nh đối với hầu hết c&aacute;c loại thuốc th&uacute; y theo th&ocirc;ng lệ quốc tế cũng như c&aacute;c quy định ở nước ta l&agrave; 2 năm sau ng&agrave;y SX. V&igrave; vậy, Bộ NN-PTNT quyết định hạn cuối &aacute;p dụng GMP đối với d&acirc;y chuyền SX thuốc bột đến hết năm 2012, đồng thời quy định hạn cuối được lưu h&agrave;nh c&aacute;c sản phẩm thuốc bột do c&aacute;c DN chưa đạt GMP trước năm 2012 đến hết năm 2014 l&agrave; ho&agrave;n to&agrave;n hợp l&iacute;. Thế nhưng đối với thuốc nước, hạn cuối &aacute;p dụng GMP l&agrave; hết năm 2010, nhưng hạn cuối được ph&eacute;p lưu th&ocirc;ng sản phẩm do c&aacute;c DN chưa đạt GMP trước năm 2010 lại chỉ đến 31/12/2012 &ndash; nghĩa l&agrave; c&ograve;n tới hơn 1 năm nữa mới hết hạn lưu h&agrave;nh sản phẩm.&nbsp;</p>\r\n', '', 0, 492, 'thuo thu y dang nuoc, ngung luu hanh', 0, '0', 0, '', 0),
(112, 25, 0, 0, 'P.Thuận', 1330108138, 0, 'Mùa cá bông lau trên sông Hậu', 'Đến hẹn lại lên, khi mùa gió chướng về là mùa “săn” cá bông lau trên sông Hậu lại đến. Những ngày này, dân theo nghề “bà cậu” ở huyện Lai Vung, Lấp Vò, Thanh Bình-Đồng Tháp cùng nhau bủa lưới đánh bắt cá bông lau... vui như hội.', 'ca_bong_lau.jpg', 'bat ca bong lau', 0, '', '<p>\r\n	<strong>Quà tặng của thiên nhiên</strong><br />\r\n	<br />\r\n	Vào mùa <strong>cá bông lau</strong>, trên sông Hậu, đoạn thuộc huyện Lấp Vò và huyện Lai Vung là nơi có nhiều người đánh bắt cá bông lau nhất. Qua lời rủ của một người bạn, một ngày gần cuối tháng Giêng, chúng tôi tìm đến xã Vĩnh Thới, huyện Lai Vung gặp lão ngư Hai Giảng - Bùi Văn Giảng, người có gần 20 năm kinh nghiệm “săn” cá bông lau để tìm hiểu. Ông Hai Giảng cho biết, nghề săn <strong>cá bông lau</strong> ở huyện Lai Vung và Lấp Vò đã có trên 30 năm, nghề này do một số Việt Kiều (Campuchia) truyền lại cho người dân địa phương.</p>\r\n<p>\r\n	Nhân con nước đang lớn vào buổi sáng, chúng tôi có dịp theo ghe của vợ chồng ông Hai Giảng ra sông Hậu để có dịp tận mắt thấy cảnh “săn” cá bông lau. Đến khúc sông chọn sẵn, vợ chồng ông Hai Giảng nhanh chóng thả tay lưới dài 400 m, dạo sâu gần 10 m để đánh bắt. Trong lúc chờ kéo lưới lên, ông Hai Giảng nói: “Cá bông lau là quà tặng của thiên nhiên. Vào mùa cá, dân theo nghề nếu đánh bắt trúng sẽ có thu nhập rất khá, có thể vài chục triệu đồng như chơi. Nghề này sống được lắm”.</p>\r\n<p>\r\n	Theo lời của ông Hai Giảng, mấy ngày qua do thời tiết bị ảnh hưởng bởi áp thấp nhiệt đới nên dân đánh bắt <strong>cá bông lau</strong> như ông dính cá không nhiều. Một ngày, ông Hai Giảng và nhóm bạn “săn” <strong>cá bông lau</strong> khoảng 10 người, chỉ dính 1-2 con cá bông lau, mỗi con nặng 5-7kg.<br />\r\n	Ông Hai Giảng cho biết, người “săn” cá bông lau như chú muốn đánh bắt được cá phải chạy xuồng ra sông Hậu thả lưới lúc con nước lớn cho đến khi nước ròng mới về. Muốn bắt được nhiều cá phải đi vào dịp nước rong 14-15 hay 29-30 âm lịch - lúc nước chảy mạnh và đi đánh bắt vào ban đêm sẽ nhiều hơn ban ngày vì ghe, xuồng chạy ít, cá đi kiếm ăn nhiều.</p>\r\n<p>\r\n	Đoạn sông Hậu thuộc khu vực Vàm Cái Mít và Vàm Cái Dứa, huyện Lai Vung là nơi nổi tiếng có nhiều cá bông lau nên có rất nhiều người làm nghề “bà cậu” đến đánh bắt. Tại khúc các sông này, ngoài vợ chồng ông Hai Giảng còn có trên 20 xuồng lưới khác đến đánh bắt. Vì vậy, dân theo nghề phải tuân theo qui luật “nằm tài” - người đến trước thả trước, đến sau thả sau để đánh bắt cho trật tự. Có những “tay sát cá” tuy đến sau nhưng vẫn dính được nhiều bất chấp người thả lưới trước không dính cá.</p>\r\n<p>\r\n	Mùa <strong>cá bông lau</strong> năm rồi, vợ chồng ông Hai Giảng đánh bắt liên tục khoảng 30 ngày và kiếm được gần 20 triệu đồng, sắm sửa các vật dụng trong gia đình.</p>\r\n<p>\r\n	Sau gần 30 phút thả lưới, vợ chồng ông Hai Giảng kéo lưới lên. Tay lưới nhẹ tênh vì không có con cá bông lau nào mắc lưới. Lòng ông Hai Giảng cũng nhẹ tênh theo tay lưới. Thoáng chút u buồn, ông Hai Giảng nói. “Không được mẻ này ta bầy mẻ khác”. Đống lưới lại được vợ chồng lão ngư phủ thả xuống nước.</p>\r\n<p>\r\n	May mắn hơn, anh Bùi Văn Quang con chú ông Giảng dính được <strong>cá bông lau </strong>nặng 7kg, bán cho thương lái 110 ngàn đồng/kg anh kiến được gần 800 ngàn đồng.</p>\r\n<p>\r\n	<strong>Nghề cha truyền con nối</strong><br />\r\n	<br />\r\n	Vì có thu nhập hấp dẫn nên có khá nhiều người làm nghề “săn” <strong>cá bông lau</strong> theo kiểu “cha truyền con nối”. Tại xã Định Yên có chú Ngô Văn Tận (Tư Tận), chú Hai Thành, chú Tư Măng sống bằng nghề “săn” cá bông lau... Chú Tư Tận cho biết, gia đình chú đã ba đời theo nghề “săn” cá bông lau. Chú được cha mình truyền nghề, hiện nay lại truyền tiếp cho các con chú là các anh: Ngô Văn Gần (sinh năm 1983), Ngô Văn Tấn (sinh năm 1984).</p>\r\n<p>\r\n	Theo chú Tư Tận, muốn theo nghề “săn” cá bông lau rất dễ, ngoài kinh nghiệm học từ gia đình hoặc từ người khác, người theo nghề chỉ cần đầu tư chiếc xuồng máy nhỏ và tay lưới trị giá vài triệu đồng là có thể hành nghề sống được. Do dễ kiếm sống nên hiện có rất nhiều người theo nghề.</p>\r\n<p>\r\n	Tâm sự với chúng tôi, chú Tư Tận nói gia đình mình không có ruộng đất nhưng nhờ có nghề săn cá bông lau, kiếm được trên 30 triệu đồng mỗi mùa mà gia đình chú cất được nhà, mua sắm các tiện nghi trong gia đình.<br />\r\n	Mùa cá bông lau trên sông Hậu năm nay đến muộn so với năm 2011 khoảng một tháng. Số lượng cá bông lau bắt được hiện nay ít hơn năm rồi. Tuy nhiên, nhiều người dân sống chuyên bằng nghề “bà cậu” này đang hy vọng một mùa <strong>cá bông lau</strong> bội thu như mọi năm.</p>\r\n', 'Đồng Tháp Online', 0, 568, 'ca bong lau, mua ca bong lau, san ca bong lau, cá bông lau, đồng tháp', 0, '0', 0, '', 0),
(15, 19, 0, 0, 'Minh Long', 1287244958, 1312879055, 'Phát hiện hàng trăm sinh vật mới ở tiểu vùng Mekong', 'Các nhà khoa học phát hiện 145 loài mới xung quanh sông Mekong ở khu vực Đông Nam Á, trong đó có cá răng nanh, cây bắt mồi và tắc kè môi đỏ.', 'frog_leptolalax_applebyi.jpg', '', 0, '', '<p class="Normal">\r\n	Quỹ Bảo tồn thiên nhiên quốc tế (WWF) vừa công bố danh sách những loài mới được tìm thấy gần sông Mekong trong một báo cáo mang tên "New Blood: Greater Mekong New Species Discoveries 2009" vào ngày 6/10. Theo báo cáo này, cứ mỗi tuần các nhà khoa học lại phát hiện ba loài sinh vật mới tại Tiểu vùng sông Mekong (bao gồm Campuchia, Lào, Myanmar, Thái Lan, Việt Nam và tỉnh Vân Nam của Trung Quốc).</p>\r\n<p class="Normal">\r\n	<em>AFP</em> cho biết, với hai chiếc răng nanh và đôi mắt lồi, cá <em>dracula minnow</em> là một trong những loài đáng chú ý nhất được tìm thấy trong Tiểu vùng sông Mekong. Các nhà khoa học nhìn thấy chúng trong một dòng suối nhỏ tại Myanmar. Cơ thể chúng trong mờ và đạt chiều dài khoảng 1,7 cm. Hiện các chuyên gia chưa rõ chúng là loài đặc hữu của Myanmar hay phân bố khắp vùng Đông Nam Á.</p>\r\n<p class="Normal">\r\n	Những loài thú vị khác bao gồm: tắc kè môi đỏ, chim hói đầu (miền trung Lào), cây bắt mồi <em>Nepenthes bokorensis</em> cao tới 7 m (Campuchia), rắn không răng, ếch có tiếng kêu như loài dế, cá bám đá, dơi mũi ống.</p>\r\n<p class="Normal">\r\n	"Tốc độ phát hiện loài mới tại Tiểu vùng sông Mekong hầu như lớn hơn mọi nơi khác trên thế giới. Đó là do khu vực này có mức độ đa dạng lớn về địa chất và khí hậu. Rõ ràng khu vực này là một trong những nơi có mức độ da dạng sinh học lớn nhất, đồng thời cũng bị đe dọa nhiều nhất", <em>AFP</em> dẫn lời ông tuart Chapman, giám đốc WWF tại Tiểu vùng sông Mekong.</p>\r\n<p class="Normal">\r\n	Tiểu vùng sông Mekong là khu vực có nhiều loài động vật có nguy cơ tuyệt chủng như hổ, voi châu Á, cá heo sông Mekong, cá da trơn khổng lồ. Mới đây WWF cảnh báo cá da trơn khổng lồ có thể tuyệt chủng nếu các dự án đập thủy điện trên sông Mekong được thực hiện. Sự hiện diện của những đập thủy điện khiến chúng không thể tới nơi sinh sản.</p>\r\n', 'VNEX', 0, 536, 'Leptolalax applebyi, son mekong, loai moi, tieu vung me kong', 0, '0', 0, '', 0),
(93, 7, 0, 0, '', 1329704722, 0, 'Hội thảo “Áp dụng VIETGAP trong nuôi trồng thủy sản”', 'Ngày 12 tháng 11 năm 2011 tại Hà Nội, Bộ Nông nghiệp và PTNT tổ chức hội thảo “Áp dụng VIETGAP trong nuôi trồng thủy sản”.', 'thu_hoac_ca.jpg', '', 0, '', '<p>\r\n	&nbsp;Th&agrave;nh phần tham dự hội thảo bao gồm hơn 100 đại biểu đến từ Bộ N&ocirc;ng nghiệp &amp;PTNT, Tổng cục Thủy sản, Trung t&acirc;m X&uacute;c tiến Thương mại N&ocirc;ng nghiệp, Sở N&ocirc;ng nghiệp &amp;PTNT, Chi Cục Thủy sản, Trung t&acirc;m Nghi&ecirc;n cứu v&agrave; người nu&ocirc;i. Chủ tr&igrave; hội thảo PGS.TS Nguyễn Thị Xu&acirc;n Thu, Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n.</p>\r\n<p>\r\n	TS. Như Văn Cẩn, đại diện Vụ Nu&ocirc;i trồng Thủy sản, trong b&agrave;i tr&igrave;nh b&agrave;y về ti&ecirc;u chuẩn VietGAP v&agrave; kế hoạch triển khai &aacute;p dụng, phần trọng t&acirc;m của hội thảo đ&atilde; n&ecirc;u bật những kh&oacute; khăn ch&iacute;nh m&agrave; VietGAP đang phải đối mặt như l&agrave; y&ecirc;u cầu của thị trường đối với VietGAP c&ograve;n yếu, sự cạnh tranh gay gắt với c&aacute;c ti&ecirc;u chuẩn chứng nhận tự nguyện kh&aacute;c (BAP, GlobalGAP, ASC) đặc biệt l&agrave; một hệ thống đồng bộ trong triển khai VietGAP.</p>\r\n<p>\r\n	Phần thảo luận giữa l&atilde;nh đạo Bộ/Tổng cục v&agrave; đại biểu đ&atilde; diễn ra cởi mở v&agrave; s&ocirc;i nổi. Trong đ&oacute; đại biểu đ&atilde; đ&oacute;ng g&oacute;p những &yacute; kiến quan trọng như sự hỗ trợ từ cấp quản l&yacute; về tập huấn, phổ biến ti&ecirc;u chuẩn cho người d&acirc;n, hỗ trợ về t&igrave;m kiếm thị trường. Đặc biệt l&agrave; những ch&iacute;nh s&aacute;ch hỗ trợ về t&agrave;i ch&iacute;nh, tiếp cận với nguồn vốn đầu tư, để tham thực h&agrave;nh theo m&ocirc; h&igrave;nh sản xuất tốt hơn. Tất cả đại biểu đều b&agrave;y tỏ sự mong muốn sớm đưa ti&ecirc;u chuẩn VietGAP v&agrave;o &aacute;p dụng trong thời gian sớm nhất.</p>\r\n<p>\r\n	PGS.TS Nguyễn Thị Xu&acirc;n Thu, Thứ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n đ&atilde; ph&aacute;t biểu &yacute; kiến v&agrave; n&ecirc;u quan điểm chỉ đạo của Bộ NN&amp;PTNT trong thời gian tới nỗ lực th&uacute;c đẩy ti&ecirc;u chuẩn VietGAP đến với người n&ocirc;ng d&acirc;n, đặc biệt trong việc &aacute;p dụng cho hoạt động nu&ocirc;i trồng thủy sản. Mục ti&ecirc;u tới năm 2020 sẽ c&oacute; 80% người d&acirc;n tham gia thực h&agrave;nh sản xuất theo ti&ecirc;u chuẩn VietGAP.</p>\r\n', 'Hoi nghe ca', 0, 684, 'viet gap, hoi nghe ca, thu hoach ca', 0, '0', 0, '', 0),
(79, 12, 0, 0, 'Phương Thanh', 1323540904, 0, 'Cảnh giác với cá bống vân mây', 'Cá bống vân mây có tên khoa học là Ctenubobius eriniger, là loài cá sống ở ven biển, các vùng cửa sông nước lợ. Ở nước ta, cá bống vân mây tập trung nhiều ở các tỉnh ven biển miền Trung.', '300_7_ca_bong.jpg', '', 0, '', '<p>\r\n	Đặc điểm nhận dạng lo&agrave;i c&aacute; n&agrave;y l&agrave; đầu to, th&acirc;n c&aacute; ngắn v&agrave; tr&ograve;n, to&agrave;n th&acirc;n m&agrave;u n&acirc;u đỏ, mỗi b&ecirc;n th&acirc;n c&oacute; bốn vệt đen h&igrave;nh đ&aacute;m m&acirc;y, m&agrave;ng v&acirc;y lưng v&agrave; m&agrave;ng v&acirc;y đu&ocirc;i c&oacute; nhiều h&agrave;ng chấm đen.</p>\r\n<p>\r\n	Theo c&aacute;c nh&agrave; khoa học, trong c&aacute; bống v&acirc;n m&acirc;y c&oacute; độc tố tetrodotoxin, tương tự độc tố của c&aacute; n&oacute;c. Đ&acirc;y l&agrave; một trong c&aacute;c chất c&oacute; độc t&iacute;nh mạnh gấp 275 lần so với xyanua v&agrave; gấp 50 lần so với m&atilde; tiền. Độc tố tetrodotoxin kh&ocirc;ng bị ph&acirc;n hủy ở nhiệt độ cao. Do đ&oacute;, cho d&ugrave; c&aacute; bống v&acirc;n m&acirc;y được nấu ch&iacute;n kỹ th&igrave; người ăn c&aacute; vẫn bị ngộ độc. Biểu hiện ngộ độc tetrodotoxin xảy ra rất nhanh, c&oacute; thể sau khi ăn 5 - 15 ph&uacute;t, thường l&agrave; t&ecirc; m&ocirc;i, lưỡi, ch&acirc;n tay, đau bụng, n&ocirc;n, yếu mệt, liệt c&aacute;c cơ, loạn nhịp tim, tụt huyết &aacute;p, v&atilde; mồ h&ocirc;i, gi&atilde;n đồng tử, tăng tiết đ&agrave;m nhớt, co giật... tử vong c&oacute; thể xảy ra trong v&ograve;ng 30 - 60 ph&uacute;t, do suy h&ocirc; hấp kết hợp loạn nhịp tim, tụt huyết &aacute;p. Hiện thế giới chưa c&oacute; thuốc giải độc đặc hiệu cho ngộ độc tetrodotoxin. Khi một người lớn ăn phải 10 g da của c&aacute; n&agrave;y c&oacute; thể dẫn đến tử vong nếu kh&ocirc;ng được chữa trị kịp thời.</p>\r\n<p>\r\n	C&aacute; bống xưa nay vốn l&agrave; m&oacute;n ăn ngon v&agrave; bổ nổi tiếng v&agrave; được sử dụng rất phổ biến ở nước ta. Sở dĩ c&oacute; sự ngộ độc chết người l&agrave; do nhiều người d&acirc;n vẫn nhầm lẫn giữa c&aacute; bống v&acirc;n m&acirc;y v&agrave; c&aacute; bống hoa. Ở nhiều địa phương người d&acirc;n vẫn sử dụng lẫn lộn t&ecirc;n giữa 2 lo&agrave;i c&aacute; n&agrave;y. Tuy h&igrave;nh d&aacute;ng b&ecirc;n ngo&agrave;i 2 lo&agrave;i c&aacute; n&agrave;y rất kh&aacute;c nhau nhưng c&oacute; thể do bề ngo&agrave;i đều c&oacute; c&aacute;c đốm tr&ecirc;n da n&ecirc;n đ&atilde; dẫn đến hiểu nhầm hai lo&agrave;i l&agrave; một.</p>\r\n<p>\r\n	Để ph&ograve;ng tr&aacute;nh ngộ độc, tốt nhất khi ăn, người d&acirc;n cần loại trừ những con c&aacute; c&oacute; h&igrave;nh d&aacute;ng nghi ngờ giống c&aacute; bống v&acirc;n m&acirc;y. Sự ngộ độc cũng c&oacute; khi diễn ra ngay khi ăn, khi đ&oacute; phải ngừng ngay kh&ocirc;ng ăn m&oacute;n đ&oacute; nữa. Khẩn trương g&acirc;y n&ocirc;n để tống hết thức ăn ra ngo&agrave;i. C&oacute; thể g&acirc;y n&ocirc;n bằng c&aacute;ch uống đầy nước rồi m&oacute;c họng, ngo&aacute;y v&agrave;o họng. Sau khi g&acirc;y n&ocirc;n n&ecirc;n uống một &iacute;t than hoạt, uống oresol b&ugrave; điện giải. Sau khi sơ cứu, khẩn trương đưa đến bệnh viện để xử l&yacute; tiếp. Cần mang theo thức ăn nghi ngờ g&acirc;y ngộ độc, chất n&ocirc;n để gi&uacute;p b&aacute;c sĩ chẩn đo&aacute;n v&agrave; điều trị nhanh hơn.</p>\r\n<p>\r\n	Ngo&agrave;i đường thức ăn, cũng phải cảnh gi&aacute;c nguy cơ ngộ độc qua phản ứng tự vệ do v&ocirc; t&igrave;nh hoặc cố &yacute; đụng chạm, sờ m&oacute; phải c&aacute;, bị c&aacute; l&agrave;m tổn thương. C&aacute;c chất độc trong tuyến nước bọt của c&aacute; c&oacute; thể chuyển v&agrave;o cơ thể người th&ocirc;ng qua vết thương.</p>\r\n', '', 0, 1626, 'ca bong, bong van hoa, bong van may, doc to', 0, '0', 0, '', 0),
(39, 13, 1, 0, 'phong', 1312879352, 0, 'Biến đổi khí hậu: Tìm cách thích ứng cho nuôi trồng thủy sản ', 'Biến đổi khí hậu đã tác động xấu về nhiều mặt đến nuôi trồng thủy sản địa phương. Việc thực hiện các giải pháp ứng phó với vấn đề này là không hề dễ dàng. ', 'bien-doi-khi-hau-1.jpg', 'Bien doi khi hau', 0, '', '<p>\r\n	Với hơn 7.000 ha nu&ocirc;i trồng thủy sản, Quảng Nam đang phải chịu những t&aacute;c động xấu của c&aacute;c biểu hiện tăng nhiệt độ đột biến, d&ocirc;ng b&atilde;o thường xuy&ecirc;n, nước biển d&acirc;ng cao&hellip; Theo nhiều ph&acirc;n t&iacute;ch, nhiệt độ đột ngột tăng cao trong m&ocirc;i trường nước đ&atilde; l&agrave;m cho sự th&iacute;ch ứng v&agrave; mức độ chịu đựng của c&aacute;c sinh vật thủy sản giảm s&uacute;t. Điều n&agrave;y kh&ocirc;ng những khiến cho sự sinh trưởng của c&aacute;c đối tượng nu&ocirc;i chậm lại m&agrave; c&ograve;n l&agrave;m cho mật độ v&agrave; mức độ dịch bệnh c&agrave;ng nghi&ecirc;m trọng hơn. Đặc biệt, hầu hết ao nu&ocirc;i tr&ecirc;n địa b&agrave;n tỉnh kh&ocirc;ng c&oacute; hệ thống xử l&yacute; nước trước khi cấp v&agrave;o ao nu&ocirc;i cũng như khi thải ra m&ocirc;i trường xung quanh n&ecirc;n những t&aacute;c động n&agrave;y c&agrave;ng nghi&ecirc;m trọng hơn. Theo thống k&ecirc; của Chi cục Nu&ocirc;i trồng thủy sản Quảng Nam, nếu như năm 2008, to&agrave;n tỉnh chỉ c&oacute; 10 ha t&ocirc;m nu&ocirc;i bị dịch bệnh do thời tiết thay đổi thất thường th&igrave; đến năm 2010, con số n&agrave;y đ&atilde; l&agrave; 600 ha. T&ocirc;m nu&ocirc;i sinh trưởng chậm v&agrave; dịch bệnh đồng loạt xảy ra ở nhiều nơi đ&atilde; khiến cho hiệu quả sản xuất thấp.</p>\r\n', '', 0, 513, 'bien doi khi hau', 0, '0', 0, '', 0),
(88, 12, 0, 0, 'Minh Long', 1326274638, 0, 'Tôm sát thủ ''thôn tính'' sinh vật châu Phi', 'Một loài tôm mà người dân ở miền nam nước Mỹ thường mua để chế biến thức ăn đang bành trướng và tiêu diệt hàng loạt sinh vật ở châu Phi.', 'tom-dam-lay-do-6.jpg', '', 0, '', '<p class="Normal">\r\n	<em>National Geographic</em> đưa tin t&ocirc;m đồng Louisiana, c&ograve;n được gọi l&agrave; t&ocirc;m đầm lầy đỏ, đang ti&ecirc;u diệt c&aacute; nước ngọt, trứng c&aacute;, động vật th&acirc;n mềm, động vật gi&aacute;p x&aacute;c v&agrave; c&acirc;y thủy sinh tại ch&acirc;u Phi. Với chiều d&agrave;i trung b&igrave;nh khoảng 15 cm v&agrave; c&oacute; khả năng di chuyển theo tư thế &quot;đứng thẳng&quot;, ch&uacute;ng đang tung ho&agrave;nh trong c&aacute;c ao, hồ v&agrave; s&ocirc;ng tại Kenya, Nam Phi, Rwanda, Uganda, Zambia, Ai Cập v&agrave; nhiều nước kh&aacute;c.</p>\r\n<p class="Normal">\r\n	C&aacute;c nh&agrave; bảo tồn lo ngại t&ocirc;m đầm lầy đỏ sẽ di chuyển tới c&aacute;c nước Đ&ocirc;ng Phi như Malawi, Tanganyika v&agrave; Victoria. Những hồ nước ngọt ở Đ&ocirc;ng Phi l&agrave; nơi sinh sống của h&agrave;ng ngh&igrave;n lo&agrave;i sinh vật m&agrave; con người kh&ocirc;ng thể t&igrave;m thấy ở nơi kh&aacute;c.</p>\r\n<p class="Normal">\r\n	&ldquo;Bằng c&aacute;ch ti&ecirc;u diệt c&aacute;c lo&agrave;i động vật v&agrave; thực vật trong c&aacute;c s&ocirc;ng, hồ v&agrave; đầm lầy, t&ocirc;m đầm lầy đỏ c&oacute; thể ph&aacute; vỡ sự c&acirc;n bằng của hệ sinh th&aacute;i v&agrave; l&agrave;m giảm những chức năng sinh th&aacute;i qu&yacute; gi&aacute;&rdquo;, Geoffrey Howard, điều phối vi&ecirc;n to&agrave;n cầu về c&aacute;c lo&agrave;i x&acirc;m lấn của Li&ecirc;n minh Bảo tồn Thi&ecirc;n nhi&ecirc;n Quốc tế (IUCN), ph&aacute;t biểu.</p>\r\n<p class="Normal">\r\n	Kenya v&agrave; Nam Phi l&agrave; hai nước đầu ti&ecirc;n ở ch&acirc;u Phi nhập khẩu t&ocirc;m đầm lầy đỏ v&agrave;o thập ni&ecirc;n 70. Ch&uacute;ng được nu&ocirc;i trong c&aacute;c bể c&aacute;, c&ocirc;ng vi&ecirc;n hải dương trước người ta thả ch&uacute;ng v&agrave;o hồ Naivasha tại Kenya để nu&ocirc;i. Ch&uacute;ng được b&aacute;n sang khu vực Scandinavia, nơi ch&uacute;ng được coi l&agrave; đặc sản. Trước đ&oacute; Kenya cũng c&oacute; t&ocirc;m đồng, song ch&uacute;ng đ&atilde; chết hết bởi một căn bệnh.</p>\r\n<p class="Normal">\r\n	&ldquo;Hồi ấy chẳng ai nghĩ t&ocirc;m đầm lầy đỏ l&agrave; một mối đe dọa, nhưng thực tế ch&uacute;ng đ&atilde; g&acirc;y t&aacute;c động xấu tới nguồn thủy sản trong hồ Naivasha. Do ch&uacute;ng ăn trứng c&aacute; v&agrave; c&aacute; nhỏ n&ecirc;n số lượng c&aacute; trong hồ giảm mạnh&rdquo;, Howard kể.</p>\r\n<p class="Normal">\r\n	Người ta tiếp tục thả t&ocirc;m đầm lầy đỏ v&agrave;o c&aacute;c v&ugrave;ng nước ngọt xung quanh c&aacute;c th&agrave;nh phố Nairobi, Kiambu, Limuru của Kenya để ti&ecirc;u diệt ốc s&ecirc;n mang k&yacute; sinh tr&ugrave;ng. Nhưng do t&ocirc;m đ&agrave;o hang trong đập, bờ s&ocirc;ng v&agrave; bờ hồ n&ecirc;n cơ sở hạ tầng v&agrave; diện mạo của một số khu vực đ&atilde; thay đổi đ&aacute;ng kể theo hướng ti&ecirc;u cực. Chẳng hạn, hoạt động đ&agrave;o hang của t&ocirc;m khiến nước trong c&aacute;c k&ecirc;nh r&ograve; rỉ, đập sụp xuống v&agrave; bờ s&ocirc;ng, hồ x&oacute;i m&ograve;n.</p>\r\n<p class="Normal">\r\n	Kh&ocirc;ng c&oacute; kẻ th&ugrave; tự nhi&ecirc;n, lại c&oacute; khả năng th&iacute;ch nghi với m&ocirc;i trường n&ecirc;n t&ocirc;m đầm lầy đỏ thực sự l&agrave; một lo&agrave;i x&acirc;m lấn th&agrave;nh c&ocirc;ng.</p>\r\n<p class="Normal">\r\n	&ldquo;Ch&uacute;ng c&oacute; thể di chuyển ở tư thế đứng vừa bơi ngược d&ograve;ng. Bơi xu&ocirc;i d&ograve;ng trong s&ocirc;ng v&agrave; suối l&agrave; việc dễ d&agrave;ng đối với ch&uacute;ng&rdquo;, Howard n&oacute;i.</p>\r\n<p class="Normal">\r\n	Arne Witt, một chuy&ecirc;n gia của Trung t&acirc;m N&ocirc;ng nghiệp Sinh học Quốc tế (CABI), cho hay, t&ocirc;m đầm lầy đỏ c&ograve;n c&oacute; khả năng thay đổi thức ăn theo điều kiện m&ocirc;i trường. Ch&uacute;ng c&oacute; thể ăn nhiều lo&agrave;i, từ sinh vật ph&ugrave; du tới động vật lưỡng cư.</p>\r\n<p class="Normal">\r\n	&ldquo;Nhiều lo&agrave;i thực vật thủy sinh biến mất bởi sự ph&agrave;m ăn của t&ocirc;m đầm lầy đỏ&rdquo;, Witt khẳng định.</p>\r\n<p class="Normal">\r\n	Sự ph&agrave;m ăn của t&ocirc;m đầm lầy đỏ khiến lượng mồi của c&aacute;, chim v&agrave; nhiều lo&agrave;i động vật săn mồi kh&aacute;c giảm.</p>\r\n', 'VNE', 0, 970, 'tom dam lay do, tom sat thu', 0, '0', 0, '', 0),
(68, 19, 0, 0, '', 1319871164, 0, 'Loài cá đao khổng lồ ở Australia', 'Cá đao răng nhọn là loài cá nước ngọt lớn nhất trên dòng sông ở Australia. Chúng có chiếc răng cưa dài hai bên trông không khác chiếc cưa máy.', 'sawfish7.jpg', '', 0, '', '<p style="text-align: center;">\r\n	<img alt="" p="" src="/upload/news/fornews/sawfish/sawfish1.jpg" /></p>\r\n<p>\r\n	Những con cá đao có thể phát triển dài đến 7 mét. Chúng thích ăn tôm nước ngọt.&nbsp; Cá đao tập trung nhiều ở phía bắc Australia.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" p="" src="/upload/news/fornews/sawfish/sawfish3.jpg" /></p>\r\n<p>\r\n	Loài cá này rất khó sống trong điều kiện nuôi nhốt. Các chuyên gia động vật cũng ít biết về điều kiện sinh sống và khả năng giao phối của chúng</p>\r\n<p style="text-align: center;">\r\n	<img alt="" p="" src="/upload/news/fornews/sawfish/sawfish4.jpg" /></p>\r\n<p>\r\n	Jeremy Wade, nhà động vật học phải mất 20 năm mới có thể câu được một con cá đao khổng lồ.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" p="" src="/upload/news/fornews/sawfish/sawfish5.jpg" /></p>\r\n<p>\r\n	Cá đao nước ngọt có khoảng 17.000 chiếc răng nhỏ. Những chiếc răng của chúng mọc tua tủa hai bên trông như gì một chiếc cưa máy.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" p="" src="/upload/news/fornews/sawfish/sawfish6.jpg" /></p>\r\n<p>\r\n	Loài cá này đang đứng trước nguy cơ tuyệt chủng nguy cấp. Con người bắt chúng để lây vây và da, trứng, gan, mật được sử dụng trong y học cổ truyền.</p>\r\n', '', 0, 4401, 'Sawfish,cá đao, Úc, australia, ca khong lo', 0, '0', 0, '', 0),
(136, 13, 0, 0, 'GS.TSKH Trương Quang Học', 1330499664, 0, 'Biến đổi khí hậu: Những nguy cơ hiện hữu', 'Để hiểu khái niệm biến đổi khí hậu (BĐKH), trước hết có thể hiểu nôm na là sự thay đổi, biến đổi của thời tiết có liên quan đến nhiệt độ, hiện tượng mưa, nắng, bão, gió, độ ẩm...', 'bien_doi_khi_hau.jpg', 'bien doi khi hau', 0, '', '<p>\r\n	Ví dụ, người ta vẫn biết rằng, khí hậu của Việt Nam là nhiệt đới gió mùa, nóng ẩm, mưa nhiều và thay đổi theo mùa: mùa hè nóng ẩm, mưa nhiều hơn; mùa đông mát, ít mưa và khô hơn. Thế nhưng, càng ngày, các đặc trưng khí hậu theo mùa không lặp lại đúng như trước kia và nhiệt độ tăng dần. Tại miền Nam trước kia không lạnh, song dần lại xuất hiện những đợt lạnh bất thường. Miền Bắc thời tiết nóng hơn, mùa hè kéo dài hơn, hiện tượng thời tiết cực đoan (nắng nóng, rét đậm, rét hại, mưa lớn…) xuất hiện nhiều hơn. Điển hình có thể kể tới đợt mưa trái mùa vào cuối tháng 10, đầu tháng 11.2008 ở Hà Nội gây ngập lụt trong một thời gian dài và nhiều lũ lụt, bão lốc xảy ra nhiều nơi.</p>\r\n<p>\r\n	Từ những đặc điểm trên, ai cũng có thể dễ dàng nhận biết về sự biến đổi của khí hậu. Chưa cần nhìn các số liệu đo đạc, chúng ta có thể nhận biết đơn giản trong cuộc sống hàng ngày là tại sao mùa đông năm nay ngắn lại, nhiệt độ khắc nghiệt hơn; hạn hán, mưa lũ bất thường xảy ra khắp nơi mà không giống quy luật của mấy chục năm về trước. Có thể hiểu, BĐKH là sự nóng lên toàn cầu.</p>\r\n<p>\r\n	Các bằng chứng khoa học cũng chứng minh sự BĐKH ngày càng rõ rệt hơn. Các trạm đo nhiệt độ đều có thể đo, ghi nhận và đánh giá. Từ khoảng giữa thế kỷ XIX, các dụng cụ đo đạc chính xác đã định lượng chi tiết về BĐKH trong hơn một thế kỷ qua. Các số liệu chứng minh, từ cuối thế kỷ XIX đến nay nhiệt độ trung bình toàn cầu đã tăng lên đáng kể (0,740C); trên đất liền nhiệt độ tăng nhiều hơn trên biển và thập kỷ 1990 là thập kỷ nóng nhất trong thiên nhiên kỷ vừa qua. Nhiệt độ trung bình bề mặt Trái đất đã tăng lên rõ rệt trong thời kỳ 1920-1940, giảm dần trong khoảng giữa những năm 1960 và lại tăng lên từ sau năm 1975. Bằng cách đo đạc các thớ cây, diện tích các vùng băng, người ta nhận thấy đây là thời kỳ nhiệt độ cao nhất trong vòng 600 năm trở lại đây.</p>\r\n<p>\r\n	Hầu hết giới khoa học đều công nhận BĐKH là do nồng độ của khí hiệu ứng nhà kính tăng lên trong khí quyển ở mức độ cao. Bản thân nó đã làm cho Trái đất ấm lên, nhiệt độ bề mặt Trái đất nóng lên, nhiệt độ nóng lên này đã tạo ra các biến đổi trong các vấn đề thời tiết hiện nay. Theo báo cáo mới nhất của Liên hiệp quốc, nguyên nhân của hiện tượng BĐKH 90% do con người gây ra, 10% là do tự nhiên.</p>\r\n', 'TT Dự Báo KTTV TW', 0, 1855, 'biến đổi khí hâu, hiệu ứng nhà kính, việt nam, trái đất nóng lên', 0, '0', 0, '', 0),
(69, 13, 0, 0, '', 1319871854, 0, 'Đông Nam Á bị ảnh hưởng nặng do biến đổi khí hậu', 'Việt Nam là một trong năm nước Đông Nam Á có nguy cơ hứng chịu tác động khắc nghiệt nhất của hiện tượng ấm lên toàn cầu.', 'Quang-Binh-4.jpg', '', 0, '', '<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Năm nước ở Đ&ocirc;ng Nam &Aacute; gồm Indonesia, Myanmar, Việt Nam, Philippines v&agrave; Campuchia nằm trong danh s&aacute;ch 30 nước chịu t&aacute;c động khắc nghiệt của thay đổi kh&iacute; hậu,</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Đ&acirc;y l&agrave; kết quả đ&aacute;nh gi&aacute; 193 nước của Maplecroft, một c&ocirc;ng ty Anh chuy&ecirc;n ph&acirc;n t&iacute;ch rủi ro. Maplecroft nghi&ecirc;n cứu mức độ hứng chịu c&aacute;c sự kiện thời tiết khắc nghiệt - như hạn h&aacute;n, lốc xo&aacute;y, ch&aacute;y rừng v&agrave; b&atilde;o &ndash; của mỗi nước. Từ kết quả nghi&ecirc;n cứu họ lập chỉ số dễ bị tổn thương do biến đổi kh&iacute; hậu (CCVI),<span class="Apple-converted-space">&nbsp;</span><em>The Indian Express</em><span class="Apple-converted-space">&nbsp;</span>cho hay.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Năm nước Đ&ocirc;ng Nam &Aacute; - gồm ndonesia, Myanmar, Việt Nam, Philippines v&agrave; Campuchia - được Maplecroft xếp v&agrave;o nh&oacute;m 30 nước đối mặt với nguy cơ rủi ro cao nhất do biến đổi kh&iacute; hậu. Hai phần ba trong số 30 nước nằm ở ch&acirc;u Phi v&agrave; thuộc nh&oacute;m nước đang ph&aacute;t triển.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	&ldquo;Một số nước vẫn chịu t&aacute;c động đặc biệt d&ugrave; thay đổi kh&iacute; hậu ở chỉ ở mức tương đối thấp&rdquo;, nh&oacute;m t&aacute;c giả b&igrave;nh luận.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Nền kinh tế yếu, kh&ocirc;ng đủ điều kiện chăm s&oacute;c y tế v&agrave; nạn tham nhũng cũng cản trở nỗ lực giải quyết t&aacute;c động của biến đổi kh&iacute; hậu đối với x&atilde; hội.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Ngược lại, c&aacute;c nước Iceland, Phần Lan, Ireland, Thụy Điển v&agrave; Estonia đứng đầu danh s&aacute;ch c&aacute;c nước c&oacute; nguy cơ thấp nhất. Trừ Israel v&agrave; hai nước giầu dầu mỏ l&agrave; Qatar v&agrave; Bahrain, 20 nước &iacute;t bị t&aacute;c động nhất thuộc về Bắc v&agrave; Trung &Acirc;u.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Trung Quốc v&agrave; Mỹ l&agrave; hai nước đứng đầu thế giới về ph&aacute;t thải kh&iacute; carbon, song lại nằm trong nh&oacute;m c&aacute;c nước đối mặt với nguy cơ trung b&igrave;nh v&agrave; thấp.</p>\r\n<p class="Normal" style="font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: -webkit-auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(244, 245, 246); ">\r\n	Những nghi&ecirc;n cứu gần đ&acirc;y &ndash; được đưa ra trong một b&aacute;o c&aacute;o đặc biệt của tổ chức Ủy ban li&ecirc;n ch&iacute;nh phủ về thay đổi kh&iacute; hậu của Li&ecirc;n Hợp Quốc (IPCC) v&agrave; sẽ c&ocirc;ng bố v&agrave;o th&aacute;ng 11 tới &ndash; chỉ r&otilde; những bằng chứng chắc chắn về mối li&ecirc;n hệ giữa hiện tượng n&oacute;ng l&ecirc;n to&agrave;n cầu v&agrave; những hiện tượng thời tiết khắc nghiệt.</p>\r\n', '', 0, 631, 'khi hau bien doi', 0, '0', 0, '', 0),
(70, 19, 0, 0, '', 1319872303, 0, 'Cá voi trắng quý hiếm xuất hiện tại Australia', 'Một con cá voi lưng gù trắng, sinh vật mà rất ít người thấy trong đời, vừa xuất hiện gần rạn san hô khổng lồ Great Barrier của Australia vào ngày 24/9.', 'ca-voi-trang-0.jpg', '', 0, '', '<p>\r\n	AP&nbsp;dẫn lời Wayne Fewings, một người d&acirc;n địa phương, cho hay anh thấy một đ&agrave;n c&aacute; voi lưng g&ugrave; gần quần đảo Whitsunday khi đang ngồi tr&ecirc;n thuyền c&ugrave;ng gia đ&igrave;nh. Quần đảo Whitsunday l&agrave; một phần của rạn san h&ocirc; Great Barrier.</p>\r\n<p>\r\n	&ldquo;T&ocirc;i ph&aacute;t hiện một con c&aacute; voi nhỏ m&agrave;u trắng trong đ&agrave;n. Ngay lập tức t&ocirc;i chộp chiếc m&aacute;y ảnh. Sau đ&oacute; con c&aacute; voi m&agrave;u trắng bơi về ph&iacute;a thuyền. Dường như n&oacute; muốn ch&uacute;ng kiểm tra ch&uacute;ng t&ocirc;i. T&ocirc;i cứ ng&acirc;y ra khi ngắm con vật. Đ&oacute; l&agrave; cơ hội chỉ xuất hiện duy nhất một lần trong đời&rdquo;, Fewings n&oacute;i.</p>\r\n<p>\r\n	Mark Read, một quan chức phụ tr&aacute;ch việc bảo vệ rạn Great Barrier, n&oacute;i rằng c&aacute; voi trắng l&agrave; những động vật cực kỳ hiếm. Cứ trung b&igrave;nh 10.000 tới 15.000 con c&aacute; voi lưng g&ugrave; sống dọc theo bờ biển ph&iacute;a đ&ocirc;ng của Australia th&igrave; chỉ c&oacute; 10-15 con c&oacute; m&agrave;u trắng. C&aacute; voi m&agrave;u trắng ho&agrave;n to&agrave;n như Fewings thấy lại c&agrave;ng hiếm hơn.</p>\r\n<p>\r\n	Rất c&oacute; thể bố mẹ của con c&aacute; voi trắng c&oacute; da m&agrave;u sẫm, song cả hai đều mang gene quy định da m&agrave;u trắng ở thể lặn. Một khả năng kh&aacute;c, theo Read, l&agrave; bố hoặc mẹ của con vật c&oacute; da m&agrave;u trắng.</p>\r\n<p>\r\n	C&aacute; voi lưng g&ugrave; đang di cư về ph&iacute;a nam n&ecirc;n Read nhận định con c&aacute; voi m&agrave;u trắng đang được mẹ chăm s&oacute;c chu đ&aacute;o để n&oacute; c&oacute; thể t&iacute;ch mỡ trước khi tới v&ugrave;ng biển thuộc Nam Cực. Lớp mỡ d&agrave;y gi&uacute;p c&aacute; voi giữ ấm cơ thể trong m&ocirc;i trường nước lạnh gi&aacute;.</p>\r\n', '', 0, 597, 'Cá voi trắng', 0, '0', 0, '', 0),
(71, 12, 0, 0, '', 1320160633, 0, 'Sáng tạo để tiết kiệm năng lượng', 'Bằng những ý tưởng giản đơn, gần gũi với cuộc sống, các bạn trẻ đã sáng tạo những sản phẩm giúp tiết kiệm năng lượng hữu ích', '178987422213-anh-1--may-ap-trung.jpg', '', 0, '', '<h3>\r\n	Bằng những &yacute; tưởng giản đơn, gần gũi với cuộc sống, c&aacute;c bạn trẻ đ&atilde; s&aacute;ng tạo những sản phẩm gi&uacute;p tiết kiệm năng lượng hữu &iacute;ch</h3>\r\n<p>\r\n	Tiết kiệm năng lượng đang l&agrave; chủ trương được Nh&agrave; nước v&agrave; người d&acirc;n hết sức quan t&acirc;m. Điều đ&aacute;ng mừng l&agrave; hiện nay c&aacute;c bạn trẻ ch&iacute;nh l&agrave; những người đang ủng hộ v&agrave; t&igrave;m ra những giải ph&aacute;p t&iacute;ch cực nhất để giải quyết vấn đề tiết kiệm năng lượng.</p>\r\n<p>\r\n	<strong>&Yacute; tưởng khoa học gần gũi</strong></p>\r\n<div>\r\n	Trong thời gian gần đ&acirc;y c&oacute; rất nhiều sản phẩm gi&uacute;p tiết kiệm năng lượng hiệu quả đ&atilde; được nhiều bạn trẻ học sinh, sinh vi&ecirc;n (SV) s&aacute;ng tạo v&agrave; giới thiệu. Ch&uacute;ng đều xuất ph&aacute;t từ những &yacute; tưởng hết sức đơn giản, gần gũi với đời sống hằng ng&agrave;y. Điều đ&aacute;ng ghi nhận l&agrave; c&aacute;c &yacute; tưởng n&agrave;y lại đến từ những bạn học sinh, SV thế hệ 8X, 9X.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	L&ecirc; Tấn Ph&uacute;c (SV Khoa Cơ kh&iacute; Trường ĐH N&ocirc;ng L&acirc;m TPHCM) s&aacute;ng tạo chiếc m&aacute;y ấp trứng g&agrave; bằng năng lượng mặt trời. C&ograve;n với nh&oacute;m c&aacute;c bạn L&ecirc; Ho&agrave;n Thiện, Mai Thủy Ti&ecirc;n, Trần Văn C&ocirc;ng T&iacute;nh (học sinh Trường THCS Đặng Trần C&ocirc;n, quận T&acirc;n Ph&uacute; - TPHCM) th&igrave; sản phẩm đ&oacute; ch&iacute;nh l&agrave; chiếc xe đạp c&oacute; gắn đ&egrave;n b&aacute;o t&iacute;n hiệu như xe gắn m&aacute;y, c&oacute; đầy đủ đ&egrave;n xi-nhan, đ&egrave;n chiếu s&aacute;ng&hellip; &Yacute; tưởng n&agrave;y rất thực tế, nảy sinh từ ch&iacute;nh chiếc xe đạp m&agrave; c&aacute;c bạn sử dụng hằng ng&agrave;y. Hoặc như Phạm Ngọc Anh T&ugrave;ng (SV Trường ĐH B&aacute;ch khoa TPHCM) chế tạo chiếc xe hai b&aacute;nh Scooter sử dụng điện tiết kiệm, kh&ocirc;ng tạo ra kh&iacute; thải&hellip;</div>\r\n', '', 0, 1303, 'Sang tao sang kein', 0, '0', 0, '', 0),
(74, 12, 0, 0, '', 1320214490, 0, 'Con cá ba mắt', 'Các ngư dân vừa bắt được con cá có 3 mắt tại hồ nước gần nhà máy điện hạt nhân ở Argentina.', 'cabamat.jpg', '', 0, '', '<p class="Normal">\r\n	Hồ chứa nước t&ecirc;n l&agrave; Chorro de Agua Caliente, nhận nước từ một nh&agrave; m&aacute;y hạt nh&acirc;n ở tỉnh C&oacute;rdoba.</p>\r\n<p class="Normal">\r\n	Con c&aacute; kỳ lạ bất ngờ lao v&agrave;o thuyền của năm ngư d&acirc;n khi họ đang dọn dẹp thuyền.</p>\r\n<p class="Normal">\r\n	&quot;V&igrave; ban đ&ecirc;m, n&ecirc;n ban đầu ch&uacute;ng t&ocirc;i chưa nh&igrave;n r&otilde; con c&aacute;. Sau khi kiểm tra bằng &aacute;nh s&aacute;ng đ&egrave;n lồng, ch&uacute;ng t&ocirc;i ph&aacute;t hiện con c&aacute; c&oacute; 3 mắt&quot;, <em>Discovery</em> tr&iacute;ch lời ngư d&acirc;n Julian Zmutt n&oacute;i với h&atilde;ng tin <em>Cadena 3</em>.</p>\r\n<p class="Normal">\r\n	&ldquo;Ch&uacute;ng t&ocirc;i l&agrave;m nghề đ&aacute;nh bắt c&aacute; ở đ&acirc;y đ&atilde; nhiều năm, nhưng đ&acirc;y l&agrave; lần đầu ti&ecirc;n bắt gặp con c&aacute; kỳ lạ n&agrave;y&quot;, Zmutt n&oacute;i th&ecirc;m.</p>\r\n<p class="Normal">\r\n	C&aacute;c ngư d&acirc;n cho rằng, nước chảy từ nh&agrave; m&aacute;y hạt nh&acirc;n ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n đột biến tr&ecirc;n con c&aacute;. Con c&aacute; n&agrave;y tr&ocirc;ng như họ h&agrave;ng của Blinky - c&aacute; 3 mắt v&agrave; kh&ocirc;ng c&oacute; dạ d&agrave;y được t&igrave;m thấy ở một nh&agrave; m&aacute;y hạt nh&acirc;n trong bộ phim <em>The Simpson</em> của Mỹ.</p>\r\n<p class="Normal">\r\n	C&aacute;c ngư d&acirc;n kh&ocirc;ng ăn con c&aacute; m&agrave; đưa n&oacute; tới ph&ograve;ng th&iacute; nghiệm để kiểm tra xem c&oacute; bị nhiễm xạ hay kh&ocirc;ng.</p>\r\n', 'VNE', 0, 801, 'Ca ba mat', 0, '0', 0, '', 0),
(75, 1, 0, 0, '', 1320215247, 0, 'Giá tôm sú và tôm thẻ chân trắng diễn biến trái chiều', 'Giá tôm sú và tôm thẻ chân trắng trên địa bàn tỉnh Tiền Giang có diễn biến trái ngược nhau, cụ thể tôm sú có xu hướng tăng giá trong khi tôm thẻ chân trắng lại giảm giá.', 'tom-su.jpg', '', 0, '', '<p>\r\n	Chi cục Thủy sản Tiền Giang cho biết, hiện <a href="http://tepbac.com/price/">tôm sú loại 30 con/kg có giá 192.000 đồng/kg</a>, tăng 7.000 đồng/kg so với tuần trước; tôm sú loại 40 con/kg có giá 165.000 đồng/kg, tăng 10.000 đồng/kg so với tuần trước; tôm thẻ chân trắng loại 100 con/kg có giá 68.000 đồng/kg, giảm 6.000 đồng/kg so với tuần trước.</p>\r\n<p>\r\n	Nguyên nhân dẫn đến hiện tượng này là nhu cầu tôm sú nguyên liệu của các nhà máy chế biến xuất khẩu tăng, trong khi thời điểm này đã vào cuối vụ thu hoạch tôm chính vụ. Mặt khác, hầu hết diện tích thả nuôi tôm biển vụ 2 trên địa bàn tỉnh đều tập trung vào đối tượng tôm thẻ chân trắng, nên sản lượng loại tôm này có xu hướng tăng.</p>\r\n<p>\r\n	Theo báo cáo của Chi cục Thủy sản Tiền Giang, toàn tỉnh hiện có 3.120 hộ nuôi tôm trên diện tích ao nuôi là 3.802 ha, trong đó diện tích nuôi tôm thâm canh và bán thâm canh là 2.115,9 ha; quảng canh cải tiến là 1.686,06 ha.</p>\r\n<p>\r\n	Từ đầu năm đến nay, tổng diện tích thả nuôi tôm là 6.854,76 ha, trong đó diện tích thả tôm sú thâm canh và bán thâm canh là 1.899 ha; diện tích thả tôm thẻ chân trắng là 1.583,7 ha; diện tích thả tôm sú quảng canh cải tiến là 3.372,12 ha. Mật độ thả giống trung bình đối với tôm sú thâm canh và bán thâm canh là 30 con/m2, tôm thẻ thâm canh là 100 con/m2; mật độ trung bình đối với tôm sú quảng canh cải tiến là 5 con/m2. Đến nay, bà con nuôi tôm đã thu hoạch 4.000,2 ha với tổng sản lượng tôm là 11.641 tấn, đạt 69,98% diện tích thả nuôi.</p>\r\n', 'tepbac', 0, 762, 'tom su', 0, '0', 0, '', 0);
INSERT INTO `tkb_news` (`news_id`, `cat_id`, `topic_id`, `group_id`, `author`, `add_time`, `edit_time`, `title`, `home_text`, `home_img_file`, `alt_img`, `op_img`, `note_img`, `body_text`, `source`, `allowed_comm`, `hit`, `keywords`, `focus`, `user_name`, `like`, `liked`, `count_comment`) VALUES
(188, 14, 0, 0, 'Hoàng Lâm', 1331269923, 0, 'Mỹ thay đổi mức chống phá giá với cá tra Việt Nam', 'Theo Văn phòng Thương vụ Việt Nam tại Mỹ, ngày 8/3, Bộ Thương mại Mỹ đã công bốquyết định cuối cùng của đợt xem xét hành chính thuế chống bán phá giá lần thứbảy (POR 7) đối với cá tra phi lê nhập khẩu từ Việt Nam.', 'avatar_cfe9d.jpg', 'ca tra', 0, '', '<p>\r\n	<em>Ảnh: chế biến cá tra xuất khẩu</em></p>\r\n<p>\r\n	Theo quyết định này, Bộ Thương mại Mỹ lấy Bangladesh là nước thay thế duy nhất trong tính toán biên độ phá giá và quyết định áp dụng mức thuế 0,00 USD/kg cho Công ty Vĩnh Hoàn và 0,03 USD/kg cho 12 công ty là bị đơn bắt buộc còn lại.</p>\r\n<p>\r\n	Các công ty khác ngoài 13 công ty này sẽ phải chịu mức thuế 2,11 USD/kg.</p>\r\n<p>\r\n	Trước đây, theo quyết định sơ bộ được Bộ Thương m ại Mỹ đưa ra ngày 9/9/2011, căn cứ vào số liệu của hai nước thay thế là Indonesia và Bangladesh thì ngoại trừ Công ty Vĩnh Hoàn tiếp tục được xác định mức thuế bằng 0, mức thuế đối với các bị đơn bắt buộc khác tăng lên tới 28 lần, từ 0,02 USD/kg lên 0,56 USD/kg, tức khoảng 15%.</p>\r\n<p>\r\n	Trước đó, ngày 6/3, với tỷ lệ 370 phiếu thuận và 39 phiếu chống, Hạ viện Mỹ đã thông qua dự luật áp đặt các khoản thuế mới đối với những hàng hóa trị giá hàng tỷ USD nhập khẩu từ Trung Quốc và các quốc gia khác được cho là có nền kinh tế do nhà nước điều tiết (phi thị trường).</p>\r\n<p>\r\n	Thượng viện Mỹ đã có phiên bỏ phiếu tương tự về dự luật trên hôm 5/3 và Tổng thống Barack Obama dự kiến sẽ ký ban hành văn kiện này.</p>\r\n<p>\r\n	Bộ Thương mại Mỹ trước đó cho biết các khoản thuế chống bán phá giá này được áp dụng đối với 23 mặt hàng của Trung Quốc và một mặt hàng của Việt Nam có trị giá tổng cộng 4,7 tỷ USD kim ngạch nhập khẩu mỗi năm.</p>\r\n<p>\r\n	Nếu không có sự can thiệp của Quốc hội, đến giữa tháng Ba, Mỹ có thể phải bãi bỏ các loại thuế áp đặt lên hàng chục mặt hàng nhập khẩu từ Trung Quốc và Việt Nam vì một tòa án phúc thẩm liên bang mới đây ra phán quyết rằng Bộ Thương mại không có quyền hợp pháp để áp đặt các loại thuế chống bán phá giá nói trên.</p>\r\n', 'Theo Báo Dân Trí 09/03/2012', 0, 454, 'cá tra xuất khẩu, chống phá giá', 0, 'goldforest12345', 0, '', 0),
(76, 14, 0, 0, '', 1320243540, 0, 'Khai trương Trạm trung chuyển thủy hải sản đầu tiên ở ĐBSCL', 'Sáng ngày 2/11, tại TP.Cần Thơ, Công ty Metro Cash &amp; Cary Việt Nam chính thức khai trương Trạm trung chuyển thủy hải sản đầu tiên tại ĐBSCL, đặt tại siêu thị Metro Hưng Lợi (QL 91B, phường Hưng Lợi, quận Ninh Kiều).', 'Hinh-thuy-hai-san_83467.jpg', '', 0, '', '<p>\r\n	Theo l&atilde;nh đạo C&ocirc;ng ty cho biết, trạm c&oacute; nhiệm vụ thu mua, ph&acirc;n loại, chế biến thủy hải sản khu vực ĐBSCL với sức chứa 5.000 tấn/ng&agrave;y (dự kiến), cung cấp cho 17 hệ thống si&ecirc;u thị của Metro tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<div align="justify">\r\n	C&aacute;c sản phẩm qua trạm trung chuyển n&agrave;y đều đạt ti&ecirc;u chuẩn quốc tế về vệ sinh an to&agrave;n thực phẩm; đặc biệt c&oacute; thể truy nguy&ecirc;n nguồn gốc xuất sức của c&aacute;c l&ocirc; h&agrave;ng.</div>\r\n<div align="justify">\r\n	&nbsp;</div>\r\n<div align="justify">\r\n	Việc đưa Trạm trung chuyển v&agrave;o khai th&aacute;c sử dụng đ&atilde; tạo c&ocirc;ng ăn việc l&agrave;m cho khoảng 1.000 lao động trong ng&agrave;nh đ&aacute;nh bắt thủy hải sản ở ĐBSCL.</div>\r\n', 'DTO', 0, 684, 'Trung chuyen thuy san', 0, '0', 0, '', 0),
(77, 1, 0, 0, '', 1323539968, 0, '&quot;Vua tôm bất bại&quot; kiện công ty sản xuất tôm giống', 'Ngày 9/12, ông Võ Hồng Ngoãn, được gọi là “vua tôm bất bại” tỉnh Bạc Liêu, gửi đơn đến các ngành chức năng khởi kiện Công ty TNHH sản xuất tôm giống thủy sản Nam Mỹ (gọi tắt là Công ty Nam Mỹ, ấp Mỹ Tường, xã Nhân Hải, huyện Hải Ninh, tỉnh Ninh Thuận).', 'vuatom_165e1.jpg', '', 0, '', '<p>\r\n	&Ocirc;ng Ngo&atilde;n kiện C&ocirc;ng ty Nam Mỹ tự &yacute; d&ugrave;ng h&igrave;nh ảnh của &ocirc;ng để in tờ rơi quảng c&aacute;o nhằm mục đ&iacute;ch trục lợi, l&agrave;m ảnh hưởng đến uy t&iacute;n, danh dự của &ocirc;ng.</p>\r\n<p>\r\n	&Ocirc;ng V&otilde; Hồng Ngo&atilde;n cho biết, trong mấy ng&agrave;y qua C&ocirc;ng ty Nam Mỹ đ&atilde; cấp ph&aacute;t h&agrave;ng ng&agrave;n tờ rơi quảng c&aacute;o cho người nu&ocirc;i t&ocirc;m ở 2 tỉnh C&agrave; Mau v&agrave; Bạc Li&ecirc;u. Điều đ&aacute;ng n&oacute;i l&agrave; trong tờ rơi n&agrave;y, c&ocirc;ng ty tự tiện in h&igrave;nh ảnh của vợ chồng &ocirc;ng Ngo&atilde;n đang bưng rổ t&ocirc;m vừa thu hoạch m&agrave; kh&ocirc;ng hỏi hoặc được sự cho ph&eacute;p của &ocirc;ng. C&ocirc;ng ty c&ograve;n c&oacute; chủ &yacute; bố tr&iacute; h&igrave;nh ảnh từ kh&acirc;u sản xuất giống, mua giống thả nu&ocirc;i, hợp t&aacute;c giữa C&ocirc;ng ty Nam Mỹ với người nu&ocirc;i t&ocirc;m, đến kiểm tra t&ocirc;m nu&ocirc;i v&agrave; sau c&ugrave;ng l&agrave; ảnh thu hoạch ở ao &ocirc;ng Ngo&atilde;n.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Trong khi đ&oacute;, từ trước đến nay &ocirc;ng Ngo&atilde;n ho&agrave;n to&agrave;n kh&ocirc;ng biết về C&ocirc;ng ty Nam Mỹ ở đ&acirc;u v&agrave; cũng chưa từng mua t&ocirc;m giống của c&ocirc;ng ty n&agrave;y về thả nu&ocirc;i. Do đ&oacute;, việc C&ocirc;ng ty Nam Mỹ d&ugrave;ng h&igrave;nh ảnh của &ocirc;ng Ngo&atilde;n đăng tr&ecirc;n tờ rơi quảng c&aacute;o l&agrave; để đ&aacute;nh lừa người nu&ocirc;i t&ocirc;m nhằm trục lợi, v&igrave; người d&acirc;n cứ tưởng l&acirc;u nay &ldquo;vua t&ocirc;m&rdquo; V&otilde; Hồng Ngo&atilde;n mua t&ocirc;m giống của C&ocirc;ng ty Nam Mỹ thả nu&ocirc;i mới tr&uacute;ng lớn như thế.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Nhiều người d&acirc;n ở nhiều nơi đ&atilde; điện thoại hỏi thăm &ocirc;ng Ngo&atilde;n mới biết. Trong nhiều năm qua h&igrave;nh ảnh, quy tr&igrave;nh nu&ocirc;i t&ocirc;m sạch, bền vững của &ocirc;ng Ngo&atilde;n được đa số người nu&ocirc;i t&ocirc;m cả nước biết đến, thậm ch&iacute; ở một số nước c&oacute; thế mạnh về nu&ocirc;i t&ocirc;m cũng biết.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Theo &ocirc;ng Ngo&atilde;n, việc C&ocirc;ng ty Nam Mỹ sử dụng h&igrave;nh ảnh của &ocirc;ng đăng tr&ecirc;n tờ rơi quảng c&aacute;o kh&ocirc;ng những ảnh hưởng đến uy t&iacute;n của gia đ&igrave;nh, đặc biệt vi phạm nghi&ecirc;m trọng đến h&igrave;nh ảnh, thương hiệu nu&ocirc;i t&ocirc;m sạch, bền vững của &ocirc;ng. Cụ thể, ng&agrave;y 9/3/2010, Cục sở hữu tr&iacute; tuệ (Bộ KH-CN) đ&atilde; cấp giấy chứng nhận đăng k&yacute; nh&atilde;n hiệu độc quyền &ldquo;Trang trại nu&ocirc;i t&ocirc;m sạch S&aacute;u Ngo&atilde;n (VN)&rdquo;. Do đ&oacute;, thương hiệu nu&ocirc;i t&ocirc;m sạch của &ocirc;ng Ngo&atilde;n được ph&aacute;p luật bảo hộ v&agrave; bất kỳ những h&igrave;nh ảnh về hoạt động nu&ocirc;i t&ocirc;m của &ocirc;ng ho&agrave;n to&agrave;n kh&ocirc;ng cho ph&eacute;p C&ocirc;ng ty Nam Mỹ sử dụng t&ugrave;y tiện, nhất l&agrave; để quảng c&aacute;o nhằm trục lợi.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Theo nội dung đơn gửi c&aacute;c cơ quan chức năng, &ocirc;ng Ngo&atilde;n y&ecirc;u cầu C&ocirc;ng ty Nam Mỹ phải c&ocirc;ng khai xin lỗi gia đ&igrave;nh &ocirc;ng, thu hồi lại tất cả c&aacute;c tờ rơi quảng c&aacute;o c&oacute; in h&igrave;nh của &ocirc;ng, đồng thời đang xem x&eacute;t sẽ khởi kiện ra t&ograve;a.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Đ&acirc;y l&agrave; vụ khởi kiện đầu ti&ecirc;n của người nu&ocirc;i t&ocirc;m tỉnh n&agrave;y đối với c&ocirc;ng ty sản xuất giống t&ocirc;m. &Ocirc;ng Ngo&atilde;n cũng như người nu&ocirc;i t&ocirc;m ở địa phương rất mong vụ việc sớm được điều tra, giải quyết theo luật định, nhằm bảo vệ quyền lợi cho nh&agrave; n&ocirc;ng.</p>\r\n', 'Huỳnh Sử', 0, 850, 'Vua tom, vu kien tom, sau ngoan', 0, '0', 0, '', 0),
(78, 10, 0, 0, '', 1323540407, 0, 'Bán hải sản phải có chứng nhận ADN', 'Các nhà hàng khắp thế giới sẽ sớm sử dụng công nghệ ADN mới để đảm bảo rằng khách hàng được phục vụ đúng món cá, trứng cá… mà họ gọi.', 'salmon.jpg', '', 0, '', '<p style="text-align: justify;">\r\n	Cơ quan quản l&yacute; thực phẩm v&agrave; dược phẩm Mỹ đ&atilde; cấp ph&eacute;p m&atilde; mạch ADN ti&ecirc;u chuẩn để x&aacute;c định ch&iacute;nh x&aacute;c một lo&agrave;i giống như m&aacute;y qu&eacute;t ở si&ecirc;u thị đọc m&atilde; vạch.</p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">M&atilde; vạch n&agrave;y sẽ gi&uacute;p ph&ograve;ng chống việc d&atilde;n nh&aacute;n sai, gian lận về thủy hải sản sản xuất trong nước hoặc nhập khẩu v&agrave;o Mỹ.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Cơ quan quản l&yacute; ở c&aacute;c nước kh&aacute;c tr&ecirc;n thế giới cũng đang c&oacute; kế hoạch &aacute;p dụng m&atilde; mạch ADN; v&igrave; đ&acirc;y l&agrave; c&ocirc;ng cụ x&aacute;c định sản phẩm hữu cơ một c&aacute;ch tin cậy, nhanh ch&oacute;ng, kh&ocirc;ng tốn k&eacute;m.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">C&ocirc;ng nghệ mới sẽ được &aacute;p dụng ở c&aacute;c t&agrave;u c&aacute;, nh&agrave; h&agrave;ng&hellip;, theo Li&ecirc;n minh M&atilde; vạch cuộc sống (Mỹ) để x&aacute;c định ch&iacute;nh x&aacute;c lo&agrave;i v&agrave; ph&ograve;ng chống nguy cơ sức khỏe, gian lận. Nhiều nh&agrave; h&agrave;ng d&ugrave;ng sản phẩm tương tự với chất lượng thấp để lừa tiền kh&aacute;ch h&agrave;ng.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Năm 2007, một số người Mỹ bị ốm nặng sau khi ăn c&aacute; n&oacute;c nhập khẩu tr&aacute;i ph&eacute;p từ Trung Quốc. Lo&agrave;i c&aacute; độc n&agrave;y được d&aacute;n nh&atilde;n l&agrave; c&aacute; &ocirc;ng sư để l&aacute;ch quy định nhập khẩu của Mỹ. Nhiều lo&agrave;i c&aacute; qu&yacute; hiếm được bảo vệ nghi&ecirc;m ngặt cũng được b&aacute;n dưới t&ecirc;n c&aacute; th&ocirc;ng thường.</span></p>\r\n', 'Đất Việt', 0, 636, 'salmon, cá hồi, mã vạch', 0, '0', 0, '', 0),
(81, 1, 0, 0, 'PHONG', 1323619398, 0, 'Nuôi cá rô đồng trong vườn dừa', 'Ông Đặng Văn Xích (ấp Tân Hậu 2, xã Tân Trung, huyện Mỏ Cày Nam, Bến Tre) là người nuôi thành công cá rô đồng trong vườn dừa, mỗi năm lãi khoảng 60 triệu đồng. Trước đây, gia đình ông sống chủ yếu nhờ vào mảnh vườn 7.000 m2. Ông bắt đầu tìm hiểu mô hình làm ăn hiệu quả, và quyết định nuôi cá rô đồng: đào ao 500 m2, lặn lội sang Đồng Tháp để học hỏi kinh nghiệm ươm, nuôi và kỹ thuật sinh sản nhân tạo, rồi mua 5 ly trứng về... Ông Xích chia sẻ kinh nghiệm ươm nuôi cá rô đồng như sau:', 'ca_ro_dong.jpg', '', 0, '', '<p>\r\n	L&agrave;m vệ sinh xung quanh ao, t&aacute;t cạn, lấp c&aacute;c hang mội, diệt c&aacute; tạp, nạo v&eacute;t b&ugrave;n đ&aacute;y ao, bỏ v&ocirc;i v&agrave;o ao (10 kg/100 m2), d&ugrave;ng lưới m&ugrave;ng bịt hai đầu cống, chonước v&agrave;o khoảng 30 cm, b&oacute;n bằng ph&acirc;n chuồng (30 kg/100 m2) cho đến khi nước c&oacute; m&agrave;u xanh đọt chuối th&igrave; tiếp tục cho nước v&agrave;o ở mức 0,8 m; trứng mua về được cho v&agrave;o bể l&oacute;t bạt để ấp, 3 ng&agrave;y sau khi trứng nở th&igrave; tiến h&agrave;nh thả c&aacute; giống xuống ao; thức ăn cho c&aacute; trong 10 ng&agrave;y đầu l&agrave; 4 l&ograve;ng đỏ trứng vịt v&agrave; 400 g bột đậu n&agrave;nh (khẩu phần t&iacute;nh cho 100.000 c&aacute; bột), mỗi ng&agrave;y cho ăn 3 lần (7 giờ, 10 giờ, 17 giờ); l&ograve;ng đỏ trứng được b&oacute;p nhuyễn v&agrave; h&ograve;a tan v&agrave;o trong nước c&ugrave;ng bột đậu n&agrave;nh đem tạt đều khắp ao. Ng&agrave;y thứ 10 - 20, cho c&aacute; ăn 300 g bột đậu n&agrave;nh + 300 g bột c&aacute; + 300 g c&aacute;m (cho 100.000 c&aacute; bột). Sau 45 ng&agrave;y ươm, trung b&igrave;nh 500 con c&aacute; nặng 1 kg; sử dụng thức ăn vi&ecirc;n c&oacute; độ đạm 40% v&agrave; giảm dần độ đạm xuống khi trọng lượng c&aacute; tăng. Ở giai đoạn n&agrave;y mỗi ng&agrave;y cho ăn 2 lần (s&aacute;ng,&nbsp; chiều) với lượng thức ăn bằng 7% trọng lượng th&acirc;n c&aacute;, v&agrave; tỷ lệ n&agrave;y được cho giảm dần khi trọng lượng c&aacute; tăng l&ecirc;n. Cứ khoảng 10 ng&agrave;y th&igrave; s&aacute;t tr&ugrave;ng nước một lần bằng BKC. Sau 4 th&aacute;ng nu&ocirc;i l&agrave; c&oacute; thể thu hoạch được 1,5 tấn/ 500 m2, b&aacute;n với gi&aacute; 32.000 đ/kg, thu về 48 triệu đồng, trừ chi ph&iacute; mua trứng, cải tạo ao, thức ăn, thuốc h&oacute;a chất c&ograve;n l&atilde;i được 18 triệu đồng.<br />\r\n	<span style="font-size: 12px; ">&Ocirc;ng X&iacute;ch lựa chọn được 400 cặp c&aacute; bố mẹ để cho sinh sản, đ&agrave;o th&ecirc;m 800 m2 ao ở những vụ nu&ocirc;i tiếp theo, &ocirc;ng l&atilde;i 30 &ndash; 40 triệu đồng/năm (năm 2009 tr&ecirc;n 60 triệu đồng).</span></p>\r\n', '', 0, 1322, 'ca ro dong, nuoi ca ro dong', 0, '0', 0, '', 0),
(82, 1, 0, 0, 'KIỆN BÌNH', 1323651158, 0, 'Tôm chết hàng loạt ở ĐBSCL: Do ngộ độc hóa chất nông dược', 'Vụ tôm 2011, cả vùng ĐBSCL thiệt hại nặng nề, ước tính đến 85.000 ha (80.000 ha tôm sú, 5.000 ha tôm chân trắng), thiệt hại trên 1.000 tỷ đồng.', 'tom-cang_xanh_benh_1.jpg', '', 0, '', '<p>\r\n	Một điều đ&aacute;ng lưu &yacute; nguy&ecirc;n nh&acirc;n t&ocirc;m chết h&agrave;ng loạt l&agrave; do hội chứng ngộ độc bắt nguồn từ h&oacute;a chất n&ocirc;ng dược.</p>\r\n<p>\r\n	<strong>T&ugrave;y tiện sử dụng chất xử l&yacute; ao nu&ocirc;i</strong></p>\r\n<p>\r\n	Tại hội nghị tổng kết vụ nu&ocirc;i t&ocirc;m 2011 diễn ra tại S&oacute;c Trăng, kết quả nghi&ecirc;n cứu của c&aacute;c chuy&ecirc;n gia trong nước v&agrave; quốc tế bước đầu ghi nhận nguy&ecirc;n nh&acirc;n t&ocirc;m chết h&agrave;ng loạt ở ĐBSCL vừa qua l&agrave; do hội chứng ngộ độc. Thiệt hại phần lớn do hộ nu&ocirc;i sử dụng sản phẩm diệt tạp c&oacute; th&agrave;nh phần h&oacute;a chất n&ocirc;ng dược l&agrave; cypermethrin, dipterex. C&oacute; hộ sử dụng trực tiếp thuốc bảo vệ thực vật (BVTV) như Padan, Dexit, Visher&hellip; khi chuẩn bị ao nu&ocirc;i. Kết luận n&agrave;y khiến người nu&ocirc;i t&ocirc;m v&agrave; ng&agrave;nh chức năng giật m&igrave;nh v&igrave; l&acirc;u nay người d&acirc;n vẫn lơ l&agrave; với nguy hại của h&oacute;a chất n&ocirc;ng nghiệp.</p>\r\n<p>\r\n	TS. Nguyễn Văn Hảo, viện trưởng Viện nghi&ecirc;n cứu thủy sản 2 lưu &yacute;, qua c&aacute;c lần thu mẫu x&eacute;t nghiệm ao nu&ocirc;i c&oacute; t&ocirc;m bị chết cho thấy nguồn nước v&agrave; đất đều c&oacute; h&agrave;m lượng cypermethrin kh&aacute; cao. Ph&oacute; cục trưởng Cục BVTV, TS. Nguyễn Hữu Hu&acirc;n cảnh b&aacute;o, cypermethrin l&agrave; chất rất độc, chỉ cần ở nồng độ 0,05 ppm (phần tỷ) cũng đủ c&oacute; để t&ocirc;m chết khoảng 50%, trong khi người nu&ocirc;i t&ocirc;m sử dụng nồng độ đến 2 ppm (phần triệu)!</p>\r\n<p>\r\n	<strong>Giải ph&aacute;p n&agrave;o?</strong></p>\r\n<p>\r\n	Hiện nay, người nu&ocirc;i t&ocirc;m đang rối trước rừng thuốc th&uacute; y thủy sản n&ecirc;n rất kh&oacute; chọn lựa nếu kh&ocirc;ng c&oacute; khuyến c&aacute;o từ ng&agrave;nh chức năng. &Ocirc;ng Nguyễn Văn Nhiệm, chủ tịch Hiệp hội t&ocirc;m sạch Mỹ Thanh (S&oacute;c Trăng) cho biết, t&ocirc;m chết h&agrave;ng loạt vừa qua c&oacute; một phần nguy&ecirc;n nh&acirc;n từ thuốc th&uacute; y thủy sản k&eacute;m chất lượng. Chỉ ri&ecirc;ng sản phẩm diệt gi&aacute;p x&aacute;c c&oacute; tới h&agrave;ng ng&agrave;n loại, khiến người nu&ocirc;i rối rắm. Đ&oacute; l&agrave; chưa kể trong thuốc th&uacute; y một số loại c&oacute; chứa th&agrave;nh phần n&ocirc;ng dược, n&ecirc;n n&ocirc;ng d&acirc;n kh&ocirc;ng ngần ngại sử dụng lu&ocirc;n thuốc BVTV cho ao nu&ocirc;i v&igrave; rẻ tiền. Theo &ocirc;ng Nhiệm, qua thiệt hại cần thấy b&agrave;i học, r&uacute;t ra nhiều vấn đề cần giải quyết từ chất lượng giống, quy tr&igrave;nh nu&ocirc;i, thức ăn, thuốc th&uacute; y thủy sản, m&ugrave;a vụ...</p>\r\n<p>\r\n	ThS. V&otilde; Văn B&eacute;, ph&oacute; gi&aacute;m đốc Trung t&acirc;m khuyến n&ocirc;ng S&oacute;c Trăng lưu &yacute;, để giảm độc tố, kim loại nặng tồn lưu trong đất do qu&aacute; tr&igrave;nh sử dụng thuốc, h&oacute;a chất của vụ nu&ocirc;i trước, b&agrave; con n&ocirc;ng d&acirc;n cần phơi nền đất sau c&agrave;y xới đ&aacute;y ao rồi b&oacute;n v&ocirc;i, cho nước ra v&ocirc; nhiều lần nhằm hoạt h&oacute;a nền đ&aacute;y l&agrave; rất cần thiết. Ngo&agrave;i ra kh&ocirc;ng n&ecirc;n lấy nước trực tiếp từ k&ecirc;nh v&agrave;o ao nu&ocirc;i để xử l&yacute; m&agrave; n&ecirc;n lấy v&agrave;o ao lắng, xử l&yacute; xong mới cấp qua ao nu&ocirc;i. Điều n&agrave;y tr&aacute;nh t&igrave;nh trạng kim loại nặng, h&oacute;a chất độc hại t&iacute;ch tụ dưới đ&aacute;y ao nu&ocirc;i l&acirc;u ng&agrave;y.</p>\r\n<p>\r\n	Để giảm thiệt hại cho vụ nu&ocirc;i mới, TS. Nguyễn Văn Hảo đề xuất &aacute;p dụng giải ph&aacute;p &ldquo;3 kh&ocirc;ng, 3 c&oacute;&rdquo;. &ldquo;3 kh&ocirc;ng&rdquo; l&agrave; kh&ocirc;ng sử dụng thuốc diệt tảo, ốc, rong, c&aacute; tạp bằng những h&oacute;a chất c&oacute; nguồn gốc thuốc trừ s&acirc;u. Thứ hai l&agrave; kh&ocirc;ng xả nước thải, b&ugrave;n lắng ra m&ocirc;i trường khi chưa được xử l&yacute;. Thứ ba l&agrave; kh&ocirc;ng nu&ocirc;i thả con giống khi chưa được kiểm nghiệm chất lượng. C&ograve;n &ldquo;3 c&oacute;&rdquo;, một l&agrave; phải c&oacute; ao lắng khi nu&ocirc;i t&ocirc;m th&acirc;m canh, khu vực ao lắng c&oacute; xử l&yacute; nguồn nước cấp. Thứ hai l&agrave; c&oacute; quy chuẩn thực h&agrave;nh nu&ocirc;i thủy sản tốt. Thứ ba l&agrave; người nu&ocirc;i n&ecirc;n tham gia v&agrave;o c&aacute;c c&acirc;u lạc bộ, hợp t&aacute;c x&atilde;, tổ sản xuất, hạn chế l&agrave;m ăn ri&ecirc;ng lẻ. Theo TS. Hảo, lịch thời vụ nu&ocirc;i ở ĐBSCL c&oacute; thể t&ocirc;m ch&acirc;n trắng thả sớm hơn t&ocirc;m s&uacute;, do t&ocirc;m thẻ ch&acirc;n trắng kh&ocirc;ng bị ảnh hưởng của m&ugrave;a vụ nhưng vẫn bị đốm trắng. Ri&ecirc;ng t&ocirc;m s&uacute;, nu&ocirc;i một vụ l&agrave; tốt nhất, c&ograve;n t&ocirc;m thẻ ch&acirc;n trắng cũng chỉ n&ecirc;n nu&ocirc;i 2 vụ.</p>\r\n', 'KHPT', 0, 1198, 'tom cang xanh, DBSCL, tom benh', 0, '0', 0, '', 0),
(83, 14, 0, 0, '', 1323653856, 0, 'Thức ăn thủy sản: Chất lượng một đằng, giá bán một nẻo', 'Chất lượng và giá cả thức ăn thủy sản đang là vấn đề nổi cộm được đưa ra mổ xẻ tại Hội nghị “Tìm giải pháp nâng cao chất lượng, hạ giá thành thức ăn thủy sản và triển khai thông tư 66/2011BNNPTNT trong nuôi trồng thủy sản”, do Bộ NN-PTNT tổ chức tại TP HCM.', 'ch_ca_tra_an.JPG', '', 0, '', '<p>\r\n	<strong>Kh&ocirc;ng thể kiểm so&aacute;t chất lượng</strong></p>\r\n<p>\r\n	Theo &ocirc;ng B&ugrave;i Đức Qu&yacute;, GĐ Trung t&acirc;m Khảo nghiệm, Kiểm nghiệm v&agrave; Kiểm định Nu&ocirc;i trồng Thủy sản (TTKNKN&amp; KĐNTTS), để đạt sản lượng nu&ocirc;i thủy sản 2,8 triệu tấn trong năm nay, ng&agrave;nh thủy sản cần tới 4,4 triệu tấn thức ăn. Hiện tại, ở nước ta c&oacute; khoảng 130 NMSX thức ăn thủy sản, với c&ocirc;ng suất 4,5 triệu tấn. Nhưng tr&ecirc;n thực tế, lượng thức ăn thủy sản sản xuất c&ocirc;ng nghiệp trong nước chỉ khoảng 3,77 triệu tấn, đạt 85% nhu cầu. 15% nhu cầu c&ograve;n lại l&agrave; NK v&agrave; tự chế biến.</p>\r\n<p>\r\n	Kết quả kiểm tra chất lượng thức ăn thủy sản trong thời gian qua cho thấy tỷ lệ thức ăn k&eacute;m chất lượng l&agrave; kh&ocirc;ng nhỏ. Năm 2008, Đồng Th&aacute;p lấy 131 mẫu kiểm tra th&igrave; c&oacute; tới 56 mẫu kh&ocirc;ng đạt (chiếm 42,7%). Năm 2011, lại lấy 37 mẫu, th&igrave; c&oacute; 6 mẫu kh&ocirc;ng đạt (16%). Năm nay, Đồng Nai kiểm tra 7 mẫu, c&oacute; tới 2 mẫu kh&ocirc;ng đạt (28,5%). Trong 2 năm 2010 v&agrave; 2011, TTKNKN&amp; KĐNTTS lấy 74 mẫu, ph&aacute;t hiện 10 mẫu kh&ocirc;ng đạt&hellip;</p>\r\n<p>\r\n	T&iacute;nh ra, từ năm 2008 đến nay, Trung t&acirc;m n&agrave;y c&ugrave;ng Vụ NTTS v&agrave; c&aacute;c tỉnh Đồng Th&aacute;p, Đồng Nai, Long An, đ&atilde; lấy tổng cộng 367 mẫu thức ăn thủy sản để kiểm tra th&igrave; c&oacute; 74 mẫu kh&ocirc;ng đạt chất lượng (20,1%). Số mẫu kh&ocirc;ng đạt n&oacute;i tr&ecirc;n, chủ yếu do kh&ocirc;ng đạt chất lượng về chỉ ti&ecirc;u protein hay chỉ ti&ecirc;u lipid hoặc chỉ ti&ecirc;u về xơ v&agrave; tro theo ti&ecirc;u chuẩn c&ocirc;ng bố.</p>\r\n<p>\r\n	&Ocirc;ng Trần Cao Mưu, TTK Hội Nghề c&aacute; Việt Nam cho rằng nhiều loại thức ăn thủy sản k&eacute;m chất lượng đang được tung ra thị trường m&agrave; c&aacute;c cơ quan chức năng kh&ocirc;ng thể n&agrave;o kiểm so&aacute;t được. Thậm ch&iacute; c&oacute; những DN sử dụng thức ăn k&eacute;m chất lượng để t&aacute;i chế lại. Thứ trưởng Bộ NN-PTNT Nguyễn Thị Xu&acirc;n Thu cho hay, kh&acirc;u lưu th&ocirc;ng ph&acirc;n phối hiện nay cũng g&oacute;p phần kh&ocirc;ng nhỏ v&agrave;o việc l&agrave;m giảm chất lượng thức ăn thủy sản. C&oacute; những l&ocirc; thức ăn, l&uacute;c rời khỏi NM chất lượng tốt, nhưng khi đến tay người n&ocirc;ng d&acirc;n, chất lượng đ&atilde; bị giảm mất 20-30%.</p>\r\n<p>\r\n	<strong>Mỗi năm tăng gi&aacute; 6- 7 lần</strong></p>\r\n<p>\r\n	Kh&ocirc;ng chỉ khốn khổ với tỷ lệ kh&ocirc;ng nhỏ thức ăn thủy sản k&eacute;m chất lượng như tr&ecirc;n, người nu&ocirc;i thủy sản c&ograve;n lu&ocirc;n phải lo lắng với t&igrave;nh trạng gi&aacute; thức ăn thủy sản li&ecirc;n tục tăng. Trong năm 2011, gi&aacute; thức ăn thủy sản tăng từ 500-1.100 đ/kg so với năm 2010. C&oacute; những thời điểm gi&aacute; thức ăn thủy sản tăng tới 16-30%. &Ocirc;ng Trần Cao Mưu cho biết trong năm nay, gi&aacute; thức ăn thủy sản đ&atilde; tăng tới 6-7 lần, mỗi lần tăng từ 200-300 đ/kg.</p>\r\n<p>\r\n	Cũng theo &ocirc;ng Mưu, một trong những nguy&ecirc;n nh&acirc;n khiến cho gi&aacute; thức ăn thủy sản tăng cao, l&agrave; do c&aacute;c DN đ&atilde; chiết khấu qu&aacute; cao cho c&aacute;c đại l&yacute;. Nhiều DN hiện đang chiết khấu 3.200 đ/kg thức ăn. Thậm ch&iacute; c&aacute;c DN Trung Quốc chiết khấu tới 5.500-6.000 đ/kg. &Ocirc;ng Mai Văn Ho&agrave;ng, Ph&oacute; TGĐ Cty Grobest, cũng cho rằng nhiều DN nhỏ, DN Trung Quốc, đang đẩy lợi nhuận cho kh&acirc;u trung gian l&ecirc;n qu&aacute; cao, khiến cho gi&aacute; thức ăn thủy sản phải tăng cao v&agrave; kh&oacute; giảm xuống được.</p>\r\n<p>\r\n	Trong khi đ&oacute;, đại diện Bộ C&ocirc;ng thương cũng khẳng định, ch&iacute;nh c&aacute;c DNSX thức ăn chưa đồng h&agrave;nh với người nu&ocirc;i trồng thủy sản, do đ&oacute; đẩy gi&aacute; thức ăn l&ecirc;n cao, nhất l&agrave; những DN c&oacute; vốn nước ngo&agrave;i. &Ocirc;ng Nguyễn Văn Nhiệm, Chủ tịch Hiệp hội t&ocirc;m Mỹ Thanh (S&oacute;c Trăng), than &ldquo;Chi ph&iacute; cho c&aacute;c đại l&yacute; thức ăn chăn nu&ocirc;i qu&aacute; lớn. Đại l&yacute; ng&agrave;y c&agrave;ng gi&agrave;u c&oacute;, c&ograve;n n&ocirc;ng d&acirc;n th&igrave; ng&agrave;y c&agrave;ng khó khăn&rdquo;.</p>\r\n<p>\r\n	<strong>Giảm ngay kh&acirc;u trung gian</strong></p>\r\n<p>\r\n	Theo Thứ trưởng Nguyễn Thị Xu&acirc;n Thu, thức ăn chiếm tới 60-75% trong gi&aacute; th&agrave;nh sản phẩm thủy sản. Do đ&oacute;, chất lượng, gi&aacute; cả thức ăn c&oacute; &yacute; nghĩa quyết định tới sự th&agrave;nh bại của người nu&ocirc;i trồng thủy sản. Tuy nhi&ecirc;n, việc kiểm tra chất lượng thức ăn thủy sản hiện nay vẫn c&ograve;n nhiều hạn chế, bởi thiếu thốn về con người, phương tiện v&agrave; cả kinh ph&iacute; cho c&ocirc;ng việc n&agrave;y.</p>\r\n<p>\r\n	Kh&ocirc;ng những thế, ch&uacute;ng ta đang c&oacute; &iacute;t ph&ograve;ng ph&acirc;n t&iacute;ch chất lượng đạt chuẩn (ISO/IEC; 17025) v&agrave; ph&ograve;ng thử nghiệm được chỉ định của Bộ NN-PTNT. Do đ&oacute;, kết quả ph&acirc;n t&iacute;ch thường thiếu ch&iacute;nh x&aacute;c v&agrave; sai số cao, n&ecirc;n c&ugrave;ng một mẫu thức ăn thủy sản m&agrave; mỗi ph&ograve;ng kiểm nghiệm lại cho một kết quả. Bởi vậy, việc giảm tỷ lệ thức ăn thủy sản k&eacute;m chất lượng xuống kh&ocirc;ng thể n&agrave;o l&agrave;m được trong ng&agrave;y một ng&agrave;y hai.</p>\r\n', 'NNVN', 0, 580, 'gia ca tra, thuc an', 0, '0', 0, '', 0),
(85, 14, 0, 0, 'phong', 1323655042, 0, 'Thủy sản vẫn là lĩnh vực đầu tư mũi nhọn trong giai đoạn 2011 - 2015', 'Bộ Nông nghiệp và Phát triển nông thôn xác định thủy sản vẫn là lĩnh vực mũi nhọn tập trung đầu tư và có thể tạo bước đột phá trong giai đoạn 2011 – 2015.', 'thuy_san.jpg', '', 0, '', '<p>\r\n	Theo đ&oacute;, c&aacute;c đối tượng nu&ocirc;i cần được ưu ti&ecirc;n ph&aacute;t triển l&agrave; c&aacute; tra, t&ocirc;m v&agrave; nhuyễn thể, với h&igrave;nh thức mở rộng nu&ocirc;i c&ocirc;ng nghiệp, th&acirc;m canh, c&oacute; năng suất cao, c&ocirc;ng nghệ sạch theo quy chuẩn quốc tế, &aacute;p dụng rộng r&atilde;i quy tr&igrave;nh thực h&agrave;nh nu&ocirc;i tốt (GAP). Bộ cũng tập trung ưu ti&ecirc;n đầu tư ph&aacute;t triển cơ sở hạ tầng để khai th&aacute;c lợi thế của v&ugrave;ng đồng bằng s&ocirc;ng Cửu Long, c&aacute;c v&ugrave;ng ven biển. Ng&agrave;nh thủy sản ch&uacute; trọng ph&aacute;t triển hệ thống giống thủy sản, ho&agrave;n thiện hệ thống nghi&ecirc;n cứu, sản xuất, cung ứng giống thủy sản để đến năm 2015 sẽ cung cấp 100% giống thủy sản cho nhu cầu nu&ocirc;i; 70% giống sạch bệnh cho c&aacute;c đối tượng nu&ocirc;i chủ lực như: t&ocirc;m s&uacute;, t&ocirc;m thẻ ch&acirc;n trắng, c&aacute; tra, t&ocirc;m c&agrave;ng xanh, r&ocirc; phi, nhuyễn thể. Ng&agrave;nh phấn đấu tổng sản lượng đạt từ 6,5 đến 7 triệu tấn, trong đ&oacute; nu&ocirc;i trồng thủy sản chiếm từ 65 &ndash; 70%.<br />\r\n	Trước mắt trong năm 2012, Bộ N&ocirc;ng nghiệp y&ecirc;u cầu cần cơ bản giữ ổn định về sản lượng v&agrave; diện t&iacute;ch nu&ocirc;i c&aacute; tra, tập trung n&acirc;ng cao chất lượng gia tăng gi&aacute; trị mặt h&agrave;ng n&agrave;y. C&aacute;c địa phương ph&aacute;t triển sản xuất c&aacute;c đối tượng t&ocirc;m nước lợ, nhuyễn thể, c&aacute; biển; đẩy mạnh ph&aacute;t triển sản xuất t&ocirc;m thẻ ch&acirc;n trắng ở tất cả c&aacute;c v&ugrave;ng miền trong cả nước, ch&uacute; trọng v&agrave; khuyến kh&iacute;ch nh&acirc;n rộng m&ocirc; h&igrave;nh nu&ocirc;i t&ocirc;m c&ocirc;ng nghiệp quy m&ocirc; hộ, đẩy mạnh đầu tư ph&aacute;t triển khu vực b&aacute;n đảo C&agrave; Mau, nhất l&agrave; đầu tư thủy lợi phục vụ nu&ocirc;i trồng thủy sản. Bộ cũng giao Tổng cục Thủy sản tập trung r&agrave; so&aacute;t lại c&aacute;c quy hoạch, x&aacute;c định v&agrave; hướng dẫn m&ocirc; h&igrave;nh nu&ocirc;i ph&ugrave; hợp cho c&aacute;c đối tượng cụ thể ở từng v&ugrave;ng, miền trong cả nước.<br />\r\n	Trong lĩnh vực khai th&aacute;c thủy sản, Bộ khuyến kh&iacute;ch c&aacute;c địa phương chuyển từ khai th&aacute;c t&agrave;u c&ocirc;ng suất nhỏ hoạt động gần bờ sang khai th&aacute;c bằng t&agrave;u c&ocirc;ng suất lớn hoạt động xa bờ v&agrave; viễn dương. Đối tượng khai th&aacute;c c&oacute; gi&aacute; trị kinh tế v&agrave; thị trường ti&ecirc;u thụ tốt, &aacute;p dụng c&ocirc;ng nghệ ti&ecirc;n tiến bảo quản sản phẩm sau thu hoạch./.</p>\r\n', 'BNN', 0, 531, 'dau tu thuy san', 0, '0', 0, '', 0),
(86, 4, 0, 0, '', 1323655312, 0, 'Quảng lý đội tàu đánh cá Việt Nam bằng công nghệ hiện đại', 'Năm 2010 Chính phủ ban hành Quyết định 48 và năm 2011 có Thông tư liên tịch số 11 về một số chính sách hỗ trợ cho khai thác biển xa.', 'quan_ly_tau.jpg', '', 0, '', '<p>\r\n	Trong đ&oacute;, lần đầu ti&ecirc;n ở Việt Nam triển khai quản l&yacute; đội t&agrave;u khai th&aacute;c biển xa bằng c&ocirc;ng nghệ hiện đại đương đại</p>\r\n<p>\r\n	Để c&aacute;c c&aacute;n bộ quản l&yacute; ở c&aacute;c trạm bờcủa c&aacute;c Chi cục KT&amp;BVNLTS v&agrave; ngư d&acirc;n nắm bắt những kiến thức cơ bản ứng dụng những c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute; đội t&agrave;u khai th&aacute;c xa bờ, ch&uacute;ng t&ocirc;i xin giới thiệu những vấn đề kh&aacute;i qu&aacute;t nhất th&ocirc;ng qua việc thực hiện Dự &aacute;n: &ldquo;X&acirc;y dựng hệ thống th&ocirc;ng tin địa l&yacute; phục vụ c&ocirc;ng t&aacute;c quản l&yacute; t&agrave;i nguy&ecirc;n v&agrave; gi&aacute;m s&aacute;t nguồn t&agrave;i nguy&ecirc;n biển của Kh&aacute;nh Ho&agrave;&rdquo; của Sở Thuỷ sản Kh&aacute;nh Ho&agrave; v&agrave;o năm 1996 v&agrave; ứng dụng tiếp theo của nh&oacute;m thực hiện dự &aacute;n tr&ecirc;n.</p>\r\n<p>\r\n	II. T&igrave;m hiểu về hệ thống th&ocirc;ng tin địa l&yacute; (GIS) v&agrave; c&aacute;c ứng dụng&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>\r\n	Ng&agrave;y 11/12/1995 Hợp đồng triển khai v&agrave; ph&aacute;t triển c&ocirc;ng nghệ th&ocirc;ng tin địa l&yacute; (GIS) số 04-95/HĐ-PTCN-DA được k&yacute; kết giữa &Ocirc;. Trần Văn Đắc Vụ trưởng Vụ Ph&aacute;t triển C&ocirc;ng nghệ đại diện Bộ Khoa học - C&ocirc;ng nghệ v&agrave; M&ocirc;i trường (KHCN&amp;MT) ki&ecirc;m chủ nhiệm Dự &aacute;n GIS quốc gia v&agrave; đại diện Sở Thuỷ sản Kh&aacute;nh Ho&agrave; &Ocirc;. V&otilde; Thi&ecirc;n Lăng, Gi&aacute;m đốc Chủ nhiệm Dự &aacute;n nh&aacute;nh của Dự &aacute;n GIS quốc gia (PTS. Nguyễn Thạch ph&oacute; chủ nhiệm Dự &aacute;n nh&aacute;nh).&nbsp;</p>\r\n<p>\r\n	Thời gian thực hiện từ ng&agrave;y 1/12/1995 đến 30/6/1996.</p>\r\n<p>\r\n	T&ecirc;n gọi Dự &aacute;n nh&aacute;nh: &ldquo;X&acirc;y dựng hệ thống th&ocirc;ng tin địa l&yacute; phục vụ c&ocirc;ng t&aacute;c quản l&yacute; t&agrave;i nguy&ecirc;n v&agrave; gi&aacute;m s&aacute;t nguồn t&agrave;i nguy&ecirc;n biển của Kh&aacute;nh Ho&agrave;&rdquo; thuộc Dự &aacute;n GIS của Bộ KHCN&amp;MT.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trước ti&ecirc;n, t&oacute;m lược những kh&aacute;i niệm cơ bản về Hệ thống th&ocirc;ng tin địa l&yacute;, thường được gọi tắt theo t&ecirc;n tiếng Anh l&agrave; GIS (Geographical Information System).</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; Kh&aacute;i niệm GIS(H&igrave;nh 1)</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GIS l&agrave; g&igrave;? GIS l&agrave; hệ quản trị cơ sở dữ liệu t&iacute;ch hợp cho qu&aacute; tr&igrave;nh thu thập, lữu trữ, t&igrave;m kiếm, ph&acirc;n t&iacute;ch, xử l&yacute; v&agrave; hiển thị c&aacute;c dữ liệu kh&ocirc;ng gian nhằm giải quyết c&aacute;c vấn đề tổng thể về quản l&yacute; v&agrave; quy hoạch.</p>\r\n', 'HNCVN', 0, 504, 'quan ly tau ca cong nghe hien dai', 0, '0', 0, '', 0),
(87, 4, 0, 0, '', 1323655507, 0, 'Đại hội Hội Nghề cá TP. Móng Cái - tỉnh Quảng Ninh', 'Từ khi thành lập đến nay, HNC Móng Cái đã trải qua một kỳ đại hội (2007-2010), đến nay, Hội đã có những chuyển biến tích cực và đạt được kết quả trên nhiều lĩnh vực, góp phần đưa kinh tế thủy sản TP Móng Cái phát triển ổn định và có tốc độ tăng trưởng đạt chỉ tiêu đề ra hàng năm.', 'dai-hoi-hnc-mong-cai.jpg', '', 0, '', '<p>\r\n	Hội đ&atilde; x&acirc;y dựng được 17 chi hội cơ sở với 302 hội vi&ecirc;n.</p>\r\n<p>\r\n	<strong>Qua một kỳ đại hội</strong></p>\r\n<p>\r\n	Trong lĩnh vực nu&ocirc;i trồng thủy sản, c&aacute;c chi hội đ&atilde; mạnh dạn đầu tư nu&ocirc;i t&ocirc;m thẻ ch&acirc;n trắng thương phẩm cho năng suất cao, b&igrave;nh qu&acirc;n đạt 3,7 tấn/ha, c&oacute; những hộ đạt từ 8-10 tấn/ha. Trong lĩnh vực hậu cần dịch vụ nghề c&aacute;, đ&atilde; x&acirc;y dựng được 3 cơ sở sản cuất t&ocirc;m giống cung cấp cho nhu cầu của địa phương, hỗ trợ cho 1.381 t&agrave;u c&aacute; với kinh ph&iacute; hơn 27 tỷ đồng.</p>\r\n<p>\r\n	B&ecirc;n cạnh đ&oacute;, Hội cũng thường xuy&ecirc;n mở nhiều lớp tập huấn kỹ thuật, tổ chức tham quan m&ocirc; h&igrave;nh trong v&agrave; ngo&agrave;i nước cho c&aacute;c hội vi&ecirc;n nhằm n&acirc;ng cao kiến thức, vận dụng khoa học v&agrave;o sản xuất nhằm n&acirc;ng cao hiệu quả kinh tế v&agrave; giảm thiểu những rủi ro trong sản xuất.</p>\r\n<p>\r\n	<strong>Mục ti&ecirc;u mới</strong></p>\r\n<p>\r\n	Do bộ m&aacute;y v&agrave; đội ngũ c&aacute;n bộ của Hội chưa ho&agrave;n chỉnh, nội dung hoạt động của c&aacute;c chi hội c&ograve;n hạn chế, ph&aacute;t triển chưa gắn kết được bề rộng v&agrave; chiều s&acirc;u n&ecirc;n Hội chưa ph&aacute;t huy hết hiệu quả. Để khắc phục những hạn chế n&agrave;y, n&ecirc;n Đại hội HNC TP M&oacute;ng C&aacute;i lần thứ 2 nhiệm kỳ 2011-2016 đ&atilde; x&aacute;c định mục ti&ecirc;u ch&iacute;nh l&agrave;: Thu h&uacute;t sự tham gia rộng r&atilde;i mọi tập thể, c&aacute; nh&acirc;n hoạt động trong lĩnh vực thủy sản. Đổi mới triệt để v&agrave; đa dạng h&oacute;a c&aacute;c hoạt động nhằm đ&aacute;p ứng tốt c&aacute;c y&ecirc;u cầu sản xuất, kinh doanh. Đồng thời, b&aacute;m s&aacute;t những mục ti&ecirc;u chung của Chiến lược ph&aacute;t triển ng&agrave;nh thủy sản của tỉnh Quảng Ninh, của th&agrave;nh phố M&oacute;ng C&aacute;i đến năm 2015 v&agrave; tầm nh&igrave;n 2020.</p>\r\n<p>\r\n	Tham dự Đại hội, Chủ tịch Hội Nghề c&aacute; Việt Nam Nguyễn Việt Thắng đ&aacute;nh gi&aacute; cao những cố gắng m&agrave; HNC M&oacute;ng C&aacute;i đ&atilde; đạt được trong thời gian qua. Đồng thời, mong muốn Hội ph&aacute;t triển mạnh hơn nữa, kh&ocirc;ng chỉ trở th&agrave;nh điểm s&aacute;ng của tỉnh Quảng Ninh m&agrave; c&ograve;n trở th&agrave;nh một trong những đơn vị Hội ti&ecirc;u biểu của cả nước. &nbsp;&nbsp;</p>\r\n', 'HNCVN', 0, 720, 'dai hoi nghe ca quang ninh', 0, '0', 0, '', 0),
(89, 4, 0, 0, 'TTXVN', 1329463826, 0, 'Cần giảm tổn thất sau khai thác thủy hải sản', 'Theo Tổng cục Thủy sản, Bộ Nông nghiệp và Phát triển nông thôn, tổn thất sau khai thác thủy hải sản ở nước ta hàng năm ước tính 20 - 30% so với tổng sản lượng khai thác, khoảng 400.000 tấn, tương đương gần 8.000 tỷ đồng/năm.', 'danh_bat.jpg', '', 0, '', '<div class="ExternalClass1E7BCE70A8E9476EBEEDADC4962C683B">\r\n	<div>\r\n		<span>Nguy&ecirc;n nh&acirc;n ch&iacute;nh do số lượng t&agrave;u c&aacute; gia tăng nhanh trong 10 năm qua, nhưng phần lớn l&agrave; t&agrave;u c&ocirc;ng suất nhỏ, thiếu c&aacute;c điều kiện thiết bị bảo quản tốt sản phẩm sau khai th&aacute;c. Việc bảo quản sản phẩm đ&aacute;nh bắt chủ yếu sử dụng nước đ&aacute; c&acirc;y xay nhuyễn chỉ cho ph&eacute;p bảo quản tối đa 10 ng&agrave;y, nhưng một chuyến đi biển của ngư d&acirc;n từ 20 - 25 ng&agrave;y mới v&agrave;o bờ n&ecirc;n chất lượng nhiều lo&agrave;i t&ocirc;m, c&aacute;, mực giảm do kh&ocirc;ng đủ độ lạnh tối thiểu cần thiết. Việc ph&acirc;n loại sản phẩm ngay tr&ecirc;n t&agrave;u c&aacute; chỉ thực hiện đối với c&aacute;c đối tượng thủy sản c&oacute; gi&aacute; trị kinh tế cao, l&agrave;m nguy&ecirc;n liệu chế biến xuất khẩu ở những t&agrave;u c&ocirc;ng suất lớn. Quy tr&igrave;nh khai th&aacute;c đ&aacute;nh bắt thủy hải sản thường kh&ocirc;ng ổn định, t&agrave;u c&aacute; tr&uacute;ng đ&agrave;n v&agrave;o bờ sớm, r&uacute;t ngắn thời gian b&aacute;m ngư trường v&agrave; ngược lại kh&ocirc;ng tr&uacute;ng đ&agrave;n th&igrave; k&eacute;o d&agrave;i thời gian đi biển. Đ&oacute; c&ograve;n chưa kể khi gi&aacute; nhi&ecirc;n liệu, chi ph&iacute; sản xuất tăng li&ecirc;n tục trong thời gian qua buộc c&aacute;c chủ phương tiện phải k&eacute;o d&agrave;i chuyến biển để đảm bảo sản lượng n&ecirc;n ảnh hưởng bất lợi đến chất lượng sản phẩm sau khai th&aacute;c. </span></div>\r\n	<div>\r\n		<span>Hiện nay, cả nước c&oacute; khoảng 130.000 t&agrave;u c&aacute; c&aacute;c loại, nhưng t&agrave;u lắp m&aacute;y c&ocirc;ng suất từ 90 CV trở l&ecirc;n chiếm chưa đến 20%, tập trung v&agrave;o 5 nh&oacute;m nghề, gồm: lưới k&eacute;o đ&aacute;y, lưới r&ecirc;, c&acirc;u, v&acirc;y v&agrave; một số nghề kh&aacute;c. </span></div>\r\n	<div>\r\n		<br />\r\n		<span>Để giảm tổn thất sau thu hoạch đối với khai th&aacute;c thủy hải sản trong thời gian tới, Tổng cục Thủy sản phối hợp với c&aacute;c tỉnh, th&agrave;nh phố ven biển tr&ecirc;n cả nước x&acirc;y dựng v&agrave; ban h&agrave;nh c&aacute;c văn bản quy phạm ph&aacute;p luật, ti&ecirc;u chuẩn kỹ thuật về bảo quản, sơ chế sản phẩm thủy sản tr&ecirc;n c&aacute;c t&agrave;u c&aacute;. Nghi&ecirc;n cứu ứng dụng, chuyển giao c&aacute;c quy tr&igrave;nh, quy phạm kỹ thuật về sơ chế, bảo quản sản phẩm thủy sản tr&ecirc;n biển, quy tr&igrave;nh khai th&aacute;c thủy sản. </span></div>\r\n	<div>\r\n		<span>Tổ chức lại sản xuất tr&ecirc;n biển được xem l&agrave; một trong những giải ph&aacute;p quan trọng, hữu hiệu g&oacute;p phần giảm tổn thất sau khai th&aacute;c thủy hải sản của ngư d&acirc;n. Tổng cục Thủy sản chỉ đạo, hướng dẫn c&aacute;c địa phương triển khai đồng bộ nhiều giải ph&aacute;p tổ chức lại sản xuất tr&ecirc;n c&aacute;c v&ugrave;ng biển như: tăng cường chất lượng hoạt động dự b&aacute;o ngư trường, khai th&aacute;c thủy hải sản theo m&ugrave;a vụ, củng cố c&ocirc;ng t&aacute;c hậu cần dịch vụ phục vụ khai th&aacute;c đ&aacute;nh bắt, x&acirc;y dựng v&agrave; ban h&agrave;nh c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ ngư d&acirc;n khai th&aacute;c đ&aacute;nh bắt thủy hải sản. C&aacute;c địa phương tiếp tục đầu tư, nh&acirc;n rộng c&aacute;c m&ocirc; h&igrave;nh &ldquo;tổ đo&agrave;n kết tr&ecirc;n biển&rdquo; ng&agrave;y c&agrave;ng ph&aacute;t triển về số lượng v&agrave; hoạt động c&oacute; chiều s&acirc;u; triển khai thực hiện hoạt động hỗ trợ nhau trong vận chuyển sản phẩm v&agrave;o đất liền, đồng thời cung cấp nhi&ecirc;n liệu, nhu yếu phẩm đảm bảo thời gian b&aacute;m biển của ngư d&acirc;n tại c&aacute;c v&ugrave;ng biển xa bờ, r&uacute;t ngắn thời gian lưu giữ sản phẩm tr&ecirc;n t&agrave;u. C&aacute;c địa phương ph&aacute;t triển đội t&agrave;u c&ocirc;ng &iacute;ch gắn với khuyến kh&iacute;ch c&aacute;c doanh nghiệp tham gia hoạt động n&agrave;y trong việc thu gom, vận chuyển sản phẩm khai th&aacute;c biển kết hợp cung ứng dịch vụ hậu cần nghề c&aacute;; x&acirc;y dựng c&aacute;c khu dịch vụ hậu cần nghề c&aacute; tại c&aacute;c đảo tiền ti&ecirc;u, đảo xa bờ c&oacute; hệ thống kho lạnh đảm bảo lưu giữ sản phẩm khi chưa b&aacute;n hoặc chuyển đi nơi kh&aacute;c ti&ecirc;u thụ./. </span></div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 'AgroViet', 0, 528, 'giam khai thac, giam danh bat', 0, '0', 0, '', 0),
(90, 1, 0, 0, 'PHƯƠNG DUY', 1329556040, 0, 'Đừng để người nuôi cá tra “bó gối” nhìn ao', 'Đầu tư nuôi 1 hecta cá tra cho sản lượng 300 tấn, người dân cần vốn đầu tư trên 6 tỷ đồng, nhưng ngân hàng cho vay thế chấp hơn 1 tỷ đồng, chưa đủ mua cá giống. Đã vậy, người nuôi còn bức xúc trước việc “chiếm dụng vốn” của doanh nghiệp. Trước thực trạng này, không ít nông dân miền Tây ngồi “bó gối” nhìn ao chứ không có vốn nuôi cá...', 'treo_ao_ca_tra.jpg', '', 0, '', '<p>\r\n	<strong>Người nu&ocirc;i c&aacute; kh&aacute;t vốn</strong></p>\r\n<p>\r\n	Ch&iacute;nh s&aacute;ch &ldquo;siết chặt&rdquo; vốn vay của ng&acirc;n h&agrave;ng v&ocirc; t&igrave;nh b&oacute;p nghẹt khả năng đầu tư của người nu&ocirc;i c&aacute; tra - ng&agrave;nh thủy sản xuất khẩu thế mạnh của Việt Nam. Theo ước t&iacute;nh của &ocirc;ng Dương Ngọc Minh, ph&oacute; chủ tịch Hiệp hội chế biến v&agrave; xuất khẩu thủy sản Việt Nam (Vasep), chủ tịch Ủy ban c&aacute; nước ngọt, để đầu tư nu&ocirc;i 1 ha c&aacute; tra, chỉ t&iacute;nh ri&ecirc;ng vốn c&aacute; giống l&ecirc;n mức 1,4 tỷ đồng, c&aacute;c chi ph&iacute; kh&aacute;c như thức ăn, thuốc th&uacute; y&hellip; cần tr&ecirc;n 6 tỷ đồng. Với số vốn kh&ocirc;ng nhỏ, thời gian nu&ocirc;i d&agrave;i 8 - 10 th&aacute;ng trong khi mức cho vay đối với n&ocirc;ng d&acirc;n qu&aacute; &iacute;t, lại l&agrave; vốn vay ngắn hạn (6 - 9 th&aacute;ng) th&igrave; người d&acirc;n kh&ocirc;ng thể lo nổi. Trong năm 2012, để c&oacute; sản lượng theo chỉ ti&ecirc;u 1,3 triệu tấn th&igrave; cần nguồn vốn khoảng 26.000 tỷ đồng. Điều n&agrave;y trở th&agrave;nh nỗi lo lớn kh&ocirc;ng chỉ ri&ecirc;ng n&ocirc;ng d&acirc;n m&agrave; cả ng&agrave;nh nu&ocirc;i c&aacute; tra.</p>\r\n<p>\r\n	Tổng cục thủy sản cho biết, để ph&aacute;t triển sản xuất, người nu&ocirc;i rất cần vay nguồn vốn với l&atilde;i suất ưu đ&atilde;i của Ch&iacute;nh phủ với mức đầu tư 6 - 10 tỷ đồng/ha. Nhưng do t&agrave;i sản đ&atilde; thế chấp ng&acirc;n h&agrave;ng n&ecirc;n kh&ocirc;ng đủ điều kiện tiếp cận với nguồn vốn vay dẫn đến nhiều hộ nu&ocirc;i kh&ocirc;ng c&oacute; vốn để thả lại c&aacute;. Hiện tượng treo hồ ao nu&ocirc;i c&ograve;n phổ biến ở một số địa phương tập trung v&agrave;o c&aacute;c hộ nu&ocirc;i c&aacute; tra nhỏ lẻ. Người d&acirc;n thiếu vốn nhưng kh&ocirc;ng &iacute;t trường hợp gặp doanh nghiệp &ldquo;mượn vốn của n&ocirc;ng d&acirc;n&rdquo; bằng c&aacute;ch thu mua c&aacute; của d&acirc;n nhưng chậm thanh to&aacute;n từ 1 - 2 th&aacute;ng, c&oacute; khi đến 3 th&aacute;ng. &Ocirc;ng L&ecirc; Ch&iacute; B&igrave;nh, ph&oacute; chủ tịch Hiệp hội thủy sản An Giang cho rằng, người nu&ocirc;i c&aacute; bức x&uacute;c nhưng chưa c&oacute; biện ph&aacute;p xử l&yacute; một số doanh nghiệp chiếm dụng vốn. Nếu đưa ra t&ograve;a th&igrave; phải chờ 3 - 6 th&aacute;ng, v&agrave; lại phải chờ thi h&agrave;nh &aacute;n. H&igrave;nh thức n&agrave;y chiếm dụng vốn, ảnh hưởng đến lợi &iacute;ch v&agrave; hiệu quả kinh tế của c&aacute;c hộ nu&ocirc;i thủy sản xuất khẩu.</p>\r\n<p>\r\n	Ngo&agrave;i ra, hoạt động nu&ocirc;i c&aacute; tra c&ograve;n gặp nhiều kh&oacute; khăn do gi&aacute; vật tư đầu v&agrave;o c&ograve;n tăng cao l&agrave;m cho gi&aacute; th&agrave;nh sản xuất nguy&ecirc;n liệu cao, trong khi đ&oacute; gi&aacute; b&aacute;n nguy&ecirc;n liệu kh&ocirc;ng ổn định (c&oacute; thời điểm thấp hơn gi&aacute; th&agrave;nh sản xuất) l&agrave;m cho người nu&ocirc;i nhỏ lẻ kh&ocirc;ng c&oacute; l&atilde;i hoặc bị thua lỗ. Chất lượng c&aacute; giống giảm b&aacute;o động, dẫn đến hiệu quả nu&ocirc;i chưa cao, thể hiện tỷ lệ ươm từ c&aacute; bột l&ecirc;n c&aacute; giống chỉ đạt khoảng 10%, tốc độ tăng trưởng trong qu&aacute; tr&igrave;nh nu&ocirc;i thương phẩm chậm, bệnh xuất hiện nhiều hơn. Nguy&ecirc;n nh&acirc;n ch&iacute;nh do chất lượng đ&agrave;n c&aacute; bố mẹ bị tho&aacute;i h&oacute;a, lai cận huyết, cạnh tranh thiếu l&agrave;nh mạnh n&ecirc;n c&aacute;c cơ sở sản xuất c&aacute; bột cho đẻ &eacute;p, đẻ non v&agrave; đẻ nhiều lần trong năm. Tổng cục thủy sản vừa cảnh b&aacute;o, hiện nay hầu hết c&aacute;c cơ sở sinh sản nh&acirc;n tạo c&aacute; bột đều sử dụng thuốc k&iacute;ch dục kh&ocirc;ng r&otilde; nguồn gốc được nhập từ Trung Quốc g&acirc;y ảnh hưởng đến chất lượng c&aacute; bột c&aacute;c giống.</p>\r\n<p>\r\n	<b>Th&aacute;o gỡ cho vụ nu&ocirc;i năm 2012</b></p>\r\n<p>\r\n	&Ocirc;ng Phạm Anh Tuấn, ph&oacute; tổng cục trưởng Tổng cục thủy sản cho biết, vụ nu&ocirc;i năm 2012 khoảng 5.500 - 6.000 ha (tăng giảm t&ugrave;y t&iacute;n hiệu thị trường), sản lượng 1,2 - 1,5 triệu tấn. Kim ngạch xuất khẩu dự kiến 1,85 - 2 tỷ USD. Điều đ&aacute;ng lưu &yacute; l&agrave; 100% cơ sở nu&ocirc;i c&aacute; tra được đ&aacute;nh số, đăng k&yacute; nu&ocirc;i c&aacute; tra c&oacute; điều kiện v&agrave; thực hiện truy xuất nguồn gốc. V&agrave; c&oacute; 60% diện t&iacute;ch nu&ocirc;i được cấp chứng chỉ ti&ecirc;n tiến (VietGap, GlobalGAP, SQF1000CM, ASC&hellip;). Để đ&aacute;p ứng nguồn giống 2,5 - 2,6 tỷ con, ng&agrave;nh thủy sản khuyến kh&iacute;ch c&aacute;c doanh nghiệp sản xuất giống c&aacute; tra chất lượng. Vụ nu&ocirc;i mới dự kiến kh&ocirc;ng &iacute;t kh&oacute; khăn, theo đề nghị Tổng cục thủy sản, Vasep phối hợp địa phương c&oacute; kế hoạch tổ chức lại sản xuất đối với c&aacute;c hộ nu&ocirc;i c&aacute; tra với quy m&ocirc; nhỏ lẻ th&agrave;nh tổ đội sản xuất, gắn với doanh nghiệp ti&ecirc;u thụ. X&uacute;c tiến nhanh việc th&agrave;nh lập Hiệp hội c&aacute; tra Việt Nam. Ch&uacute; &yacute; nu&ocirc;i rải vụ trong khung m&ugrave;a vụ, khuyến kh&iacute;ch doanh nghiệp chế biến x&acirc;y dựng c&aacute;c dự &aacute;n ph&aacute;t triển v&ugrave;ng nguy&ecirc;n liệu th&ocirc;ng qua c&aacute;c loại h&igrave;nh li&ecirc;n kết (nu&ocirc;i gia c&ocirc;ng, hợp đồng ti&ecirc;u thụ c&aacute; theo h&igrave;nh thức đặt cọc trước, hợp đồng ti&ecirc;u thụ c&aacute; theo h&igrave;nh thức ứng vốn trước&hellip;) nhằm giảm bớt rủi ro. Về vốn, địa phương triển khai Quyết định số 01/2012/QĐ-TTg ng&agrave;y 9/1/2012 của Thủ tướng Ch&iacute;nh phủ về một số ch&iacute;nh s&aacute;ch hỗ trợ &aacute;p dụng VietGAP trong nu&ocirc;i trồng thủy sản. Ng&acirc;n h&agrave;ng nh&agrave; nước chủ tr&igrave; phối hợp với Bộ NN&amp;PTNT v&agrave; c&aacute;c địa phương lập đo&agrave;n kiểm tra, r&agrave; so&aacute;t việc vay vốn theo c&aacute;c quyết định của Thủ tướng, th&aacute;o gỡ c&aacute;c kh&oacute; khăn vướng mắc về vay vốn tại cơ sở.</p>\r\n<p>\r\n	Bộ trưởng Bộ NN&amp;PTNT Cao Đức Ph&aacute;t lưu &yacute;, năm 2012 tập trung n&acirc;ng cao chất lượng, gi&aacute; trị cho c&aacute; tra Việt Nam duy tr&igrave; v&agrave; mở rộng thị trường. Về vốn, đề nghị ng&acirc;n h&agrave;ng nh&agrave; nước c&oacute; giải ph&aacute;p cụ thể tạo điều kiện vốn cho người nu&ocirc;i c&aacute; v&agrave; doanh nghiệp. Đồng thời kiến nghị Bộ t&agrave;i ch&iacute;nh xem x&eacute;t ho&agrave;n thuế VAT cho người nu&ocirc;i c&aacute;. C&aacute;c ng&agrave;nh chức năng gi&aacute;m s&aacute;t v&agrave; bảo đảm chất lượng c&aacute; giống, vật tư, thức ăn cho người nu&ocirc;i.</p>\r\n', 'KHPT', 0, 565, 'gion ca tra, nuoi ca tra, ao ca tra, treo ao ca tra', 0, '0', 0, '', 0);
INSERT INTO `tkb_news` (`news_id`, `cat_id`, `topic_id`, `group_id`, `author`, `add_time`, `edit_time`, `title`, `home_text`, `home_img_file`, `alt_img`, `op_img`, `note_img`, `body_text`, `source`, `allowed_comm`, `hit`, `keywords`, `focus`, `user_name`, `like`, `liked`, `count_comment`) VALUES
(91, 14, 0, 0, 'HOÀNG MAI', 1329559992, 0, 'Lấp ao vì cá rô đầu vuông', 'Nông dân nuôi cá rô đầu vuông ở các tỉnh đồng bằng sông cửu long đang điêu đứng vì ca liên tục rớt giá. Từ giống các thu mang về thu nhập tiền tỉ thì nay nông dân phải lấp ao để chuyển sang trông lúa hoặc nuôi thủy sản khác.', 'ao_nuoi_ca_ro_dau_vuong.jpg', '', 0, '', '<p>\r\n	<strong>&quot;Vua c&aacute; r&ocirc;&quot; cũng lấp ao</strong></p>\r\n<p>\r\n	V&agrave;o năm 2010 nhiều n&ocirc;ng d&acirc;n x&atilde; Vĩnh Thuận T&acirc;y (huyện Vị Thủy, tỉnh Hậu Giang) li&ecirc;n tục đ&agrave;o ao nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng</strong> bất chấp khuyến c&aacute;o của ch&iacute;nh quyền địa phương.. th&igrave; nay nhiều người lấp ao v&igrave; thua lỗ cũng v&igrave; con <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng</strong> n&agrave;y. L&uacute;c bấy giờ, nhiều hộ c&ograve;n đ&agrave;o ao khi l&uacute;a đang trổ đ&ograve;ng v&igrave; lợi nhuận con c&aacute; l&uacute;c &acirc;y qu&aacute; lớn so với tr&ocirc;ng l&uacute;a. Phong tr&agrave;o kh&ocirc;ng chỉ ở tỉnh Hậu Giang m&agrave; lan rộng ra khắp c&aacute;c tỉnh th&agrave;nh kh&aacute;c ở khu vực ĐBSCL.</p>\r\n<p>\r\n	Thế nhưng sau tết cổ truyền ch&uacute;ng t&ocirc;i trở lại vung nu&ocirc;i c&aacute; t&ocirc; đầu ti&ecirc;n ở x&atilde; Vĩnh Thuận T&acirc;y. Kh&ocirc;ng kh&iacute; ở đ&acirc;y kh&aacute; ti&ecirc;u điều, ảm đạm do hầu hết c&aacute;c hộ nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>đều lấp ao chuyển sang trồng l&uacute;a. &Ocirc;ng Trương Ph&uacute; Quốc ở ấp 3, x&atilde; Vinh Thuận T&acirc;y buồn b&atilde;: &quot;T&ocirc;i l&agrave; một trong những người nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>đầu ti&ecirc;n trong x&atilde; cũng phải bỏ nghề. Nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; gi&aacute; c&aacute; qu&aacute; thấp trong khi gi&aacute; thức ăn lại li&ecirc;n tục tăng, tỷ lệ hao hụt lơn.. Hiện nay, rất &iacute;t người d&aacute;m thả nu&ocirc;i v&igrave; gi&aacute; th&agrave;nh cao hơn gi&aacute; b&aacute;n ra n&ecirc;n đ&agrave;nh lấp ao để troogn l&uacute;a&quot;.</p>\r\n<p>\r\n	L&uacute;c đĩnh điểm năm 2010, gia đ&igrave;nh &ocirc;ng Quốc nu&ocirc;i 3 &aacute;o với diện t&iacute;ch gần 6.000m<sup>2</sup> . Tuy nhi&ecirc;n, chỉ sau 1 m&ugrave;a vụ, gia đ&igrave;nh &ocirc;ng đ&atilde; bị lỗ hơn 200 triệu đồng. Sau đợt thua lỗ n&agrave;y th&igrave; gia đ&igrave;nh &ocirc;ng đ&atilde; lấp 2 ao để tr&ocirc;ng l&uacute;a, ao c&ograve;n lại bỏ kh&ocirc;ng để t&igrave;m hướng nu&ocirc;i lo&agrave;i thủy sản kh&aacute;c.</p>\r\n<p>\r\n	Nhiều n&ocirc;ng d&acirc;n trước đ&acirc;y được xem l&agrave; &quot;vua&quot; <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>khi thu về tiền tỷ mỗi năm như &ocirc;ng Nguy&ecirc;n Văn H&ugrave;ng, Nguyễn Văn Khải ở ấp 5 giờ cũng phải lấp ao, bỏ nghề v&igrave; gi&aacute; xuống qu&aacute; th&acirc;p. &Ocirc;ng Khải l&agrave; người đầu ti&ecirc;n ph&aacute;t hiện v&agrave; nh&acirc;n giống <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>nay phải chuyển sang nu&ocirc;i c&aacute; sặc rằn v&agrave; chăn nu&ocirc;i g&agrave;, rắn ... &Ocirc;ng khải cho biết: &quot; Do th&aacute;y lợi nhuận từ <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>qu&aacute; lớn n&ecirc;n nhiều người đ&atilde; đổ x&ocirc; v&agrave;o nu&ocirc;i, thậm ch&iacute; đ&agrave;o ao ngay thời điểm l&uacute;a trổ. Phong tr&agrave;o đ&atilde; lan rộng ra &aacute;c đia phương kh&aacute;c n&ecirc;n dẫn đ&ecirc;n dội chợ, rớt gi&aacute;&quot;. C&ograve;n hộ &ocirc;ng H&ugrave;ng Anh th&ugrave; thu tiền tỷ từ những vụ nu&ocirc;i đầu nhờ v&agrave;o b&aacute;n c&aacute; giống v&agrave; c&aacute; thịt th&igrave; gần đ&acirc;y cũng thua lổ nặng. Mấy trăm con c&aacute; giống l&uacute;c đầu gi&aacute; cả triệu đ&ocirc;ng/con nhưng đến nay b&aacute;n kh&ocirc;ng ai mua. &Ocirc;ng đ&agrave;nh đem ra chợ b&aacute;n với gi&aacute; hơn 20 ng&agrave;n đồng/kg. &Ocirc;ng H&ugrave;ng Anh than thở: &quot;b&acirc;y giờ nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>kh&oacute; hơn trước đ&acirc;y rất nhiều, tỷ lệ hao hụt lớn c&oacute; lẽ do tho&agrave;i h&oacute;a giống, gi&aacute; thức ăn tăng cao. V&igrave; từ giống c&aacute; đme lại tiền ty th&igrave; b&acirc;y giờ nhiều n&ocirc;ng d&acirc;n lại mắc nợ từ con c&aacute; n&agrave;y.</p>\r\n<p>\r\n	Thoe thống k&ecirc;, to&agrave;n tỉnh Hậu Giang c&oacute; khoảng 300 ha nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng. </strong>Tuy nhi&ecirc;n, hiện nay đ&atilde; c&oacute; 2/3 diện t&iacute;ch đ&atilde; được lấp ao đẽ trồng l&uacute;a v&igrave; li&ecirc;n tục thua lỗ. Diện t&iacute;ch c&ograve;n lại cung treo ao ay t&igrave;m nu&ocirc;i c&aacute;c lo&agrave;i thủy sản kh&aacute;c.</p>\r\n<p>\r\n	C&aacute;c địa phương kh&aacute;c ở khu vực ĐBSCL như: Cần Thơ, Vĩnh Long, Đ&ocirc;ng Th&aacute;p, An Giang.. cũng chịu cảnh thua lỗ, treo ao. Những hộ nu&ocirc;i c&aacute; tra trước đ&acirc;u bị thua lỗ khi chuyển sang nu&ocirc;i <strong>c&aacute; r&ocirc; đầu vu&ocirc;ng </strong>lại tiếp tục thua lỗ. &Ocirc;ng &Uacute;t H&ograve;a ở x&atilde; Kh&aacute;nh H&ograve;a (huyện Ch&acirc;u Ph&uacute;, tỉnh An Giang) nu&ocirc;i c&aacute; tra li&ecirc;n tục thua lỗ n&ecirc;n năm 2011, &ocirc;ng tiếp tục đầu tư để nu&ocirc;i CRĐV. Tuy nhi&ecirc;n vụ thu hoạch ngay thời điểm rớt gi&aacute; n&ecirc;n &ocirc;ng tiếp tục lỗ cả trăm triệu đ&ocirc;ng. Hiện &ocirc;ng H&ograve;a phải treo ao để t&igrave;m lo&agrave;i thủy sản th&iacute;ch hơp để thả nu&ocirc;i.</p>\r\n', 'báo Nông Thôn', 0, 789, 'ca ro dau vuong, treo ao vi ca ro dau vuong, phong trao nuoi ca ro', 0, '0', 0, '', 0),
(92, 19, 0, 0, 'Update', 1329650485, 0, 'Tìm thấy chú cá “nóng” sống ở Nam cực', 'Các nhà khoa học vừa phát hiện loài cá “nóng” sống ở Nam cực hàng triệu năm. Đó là loài cá có khả năng tự sản xuất chất chống đông trong máu một cách tự nhiên để tồn tại hàng triệu năm.', 'Notothenioid1.jpg', '', 0, '', '<p>\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Lo&agrave;i c&aacute; c&oacute; t&ecirc;n gọi l&agrave; c&aacute; Notothenioid, ch&uacute;ng c&oacute; thể sống s&oacute;t sau hiện tượng đ&oacute;ng băng to&agrave;n cầu g&acirc;y tuyệt chủng h&agrave;ng loạt c&aacute;c lo&agrave;i c&aacute;ch đ&acirc;y 42 triệu năm nhờ chất chống đ&ocirc;ng glycolprotein, sản xuất tự nhi&ecirc;n trong m&aacute;u.</span></p>\r\n<p>\r\n	<span name="advenueINTEXT"><img alt="" src="/upload/images/news/Notothenioid.jpg" style="width: 468px; height: 286px;" /></span></p>\r\n<p>\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Hiện nay, theo gi&aacute;o sư Thomas Near, thuộc Trường đại học Yale, cảnh b&aacute;o: &ldquo;<em>Lo&agrave;i Notothenioid đang bị đe doạ tuyệt chủng khi Tr&aacute;i đất ng&agrave;y c&agrave;ng n&oacute;ng l&ecirc;n. B&ecirc;n cạnh đ&oacute; n&oacute; đang trở th&agrave;nh nguồn thức ăn ch&iacute;nh của chim c&aacute;nh cụt v&agrave; hải cẩu&rdquo;.</em></span></p>\r\n<p>\r\n	<span id="advenueINTEXT" name="advenueINTEXT">C&aacute;c nh&agrave; khoa học cho rằng lo&agrave;i c&aacute; <strong>&ldquo;icefish&rdquo; (c&aacute; băng) </strong>- t&ecirc;n gọi kh&aacute;c của lo&agrave;i Notothenioid - l&agrave; lo&agrave;i th&uacute; vị nhất trong lịch sử tiến h&oacute;a bởi qu&aacute; tr&igrave;nh tiến h&oacute;a của n&oacute; rất đặc biệt v&agrave; duy nhất.</span></p>\r\n', 'Tuoi Tre', 0, 545, 'Thomas Near, Notothenioid, ca nong, nam cua', 0, '0', 0, '', 0),
(94, 19, 0, 0, 'THIÊN NHIÊN', 1329794234, 0, '3 khu bảo tồn mới cho 2 loài cá heo nước ngọt', 'Chính phủ Bangladesh vừa tuyên bố thành lập 3 khu bảo tồn mới thuộc khu bảo tồn rừng ngập mặn lớn nhất thế giới Sundarbans để bảo vệ 2 loài cá heo nước ngọt cuối cùng còn lại ở châu Á. Tạp chí khoa học Mỹ Live Science đưa tin', 'ca_heo_nuo_ngot.jpg', '', 0, '', '<p class="pBody">\r\n	<img alt="" src="/upload/images/ca_heo_nuoc_ngot_song_hang.jpg" style="width: 500px; height: 280px;" /></p>\r\n<p class="pBody">\r\n	C&aacute; heo s&ocirc;ng Hằng (<em>ảnh tr&ecirc;n</em>) v&agrave; c&aacute; heo Irrawaddy (<em>ảnh dưới</em>)&nbsp;giờ đ&atilde; được bảo vệ trong v&ugrave;ng rừng đước Sundarbans - Ảnh: WCS/Zahangor Alom</p>\r\n<p class="pBody">\r\n	Đ&oacute; l&agrave; c&aacute; heo c&aacute; heo s&ocirc;ng Hằng (<em>Platanista gangetica gangetica</em>) v&agrave; c&aacute; heo Irrawaddy (<em>Orcaella brevirostris</em>).</p>\r\n<p class="pBody">\r\n	Mục đ&iacute;ch việc th&agrave;nh lập c&aacute;c khu bảo tồn c&aacute; heo n&agrave;y do c&aacute;c nh&agrave; khoa học kh&ocirc;ng muốn ch&uacute;ng đi theo con đường tuyệt chủng gần đ&acirc;y của lo&agrave;i c&aacute; heo s&ocirc;ng Dương Tử của Trung Quốc.</p>\r\n<p class="pBody">\r\n	Chi cục Kiểm l&acirc;m Bangladesh (BFD) đ&atilde; phối hợp c&aacute;c nh&agrave; bảo tồn Hiệp hội Bảo tồn động vật hoang d&atilde; (WCS) x&aacute;c định c&aacute;c vị tr&iacute; c&aacute;c khu bảo tồn cho 2 lo&agrave;i c&aacute; heo qu&yacute; hiếm tr&ecirc;n v&agrave; x&aacute;c định ba khu bảo tồn trong v&ugrave;ng rừng đước Sundarbans với tổng diện t&iacute;ch khu vực bảo vệ c&aacute; heo khoảng 10,6 km<sup>2</sup>.</p>\r\n<p class="pBody">\r\n	Theo WCS, c&aacute;c lo&agrave;i c&aacute; heo nước ngọt hiện bị đe dọa nghi&ecirc;m trọng do vướng v&agrave;o lưới đ&aacute;nh c&aacute; của ngư d&acirc;n v&agrave; chết, sự suy giảm đ&aacute;ng kể của con mồi khi bị con người đ&aacute;nh bắt qu&aacute; mức v&agrave; mực nước biển d&acirc;ng x&acirc;m nhập l&agrave;m độ mặn của nước trong v&ugrave;ng ng&agrave;y c&agrave;ng tăng.</p>\r\n<p class="pBody">\r\n	Bangladesh được xem l&agrave; một &ldquo;điểm n&oacute;ng&rdquo; đối với c&aacute; heo. Một cuộc điều tra của WCS năm 2009 đ&atilde; ph&aacute;t hiện một quần thể c&aacute; heo Irrawaddy khoảng 6.000 con ở nước n&agrave;y.</p>\r\n', 'TTO', 0, 1166, 'ca heo nuoc ngot, ca heo song hang, bangladesh,Orcaella brevirostris', 0, '0', 0, '', 0),
(95, 12, 0, 0, '', 1329819898, 0, 'Cá biển là con cháu của cá sông?', 'Nghiên cứu mới ở Mỹ đã chứng minh cá biển hiện nay đều tiến hóa từ các loài cá nước ngọt. Các nhà khoa học cho rằng đây chính là lý do đại dương chiếm đến 75% bề mặt trái đất nhưng lại chứa rất ít loài cá cũng như sinh vật, từ 15% đến 25% số loài ước tính toàn cầu.', 'seafish.jpg', '', 0, '', '<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Nghi&ecirc;n cứu được tiến h&agrave;nh bởi nh&oacute;m tiến sỹ John J. Wiens v&agrave; c&aacute;c sinh vi&ecirc;n Khoa Sinh th&aacute;i v&agrave; Tiến h&oacute;a, Đại Học Stony Brook, Mỹ; kết quả được đăng tr&ecirc;n tạp ch&iacute; <em>Proceedings of the Royal Society.</em></span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Wiens v&agrave; sinh vi&ecirc;n đ&atilde; xem x&eacute;t đa dạng sinh học của c&aacute; v&acirc;y tia, lo&agrave;i c&aacute; c&oacute; số lượng lo&agrave;i đ&ocirc;ng nhất trong tất cả những lo&agrave;i động vật c&oacute; xương sống trong m&ocirc;i trường nước mặn v&agrave; chiếm 96% tổng số c&aacute;c lo&agrave;i c&aacute; h&agrave;nh tinh.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Họ ph&acirc;n t&iacute;ch những c&acirc;y tiến h&oacute;a dựa tr&ecirc;n h&oacute;a thạch v&agrave; dữ liệu DNA (nguy&ecirc;n liệu di truyền của sinh vật). Họ cũng ph&acirc;n t&iacute;ch tr&ecirc;n một cơ sở dữ liệu lớn về m&ocirc;i trường sống của gần như mọi lo&agrave;i c&aacute; tồn tại hiện nay.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Ph&acirc;n t&iacute;ch chỉ ra sự kh&aacute;c biệt đ&aacute;ng ngạc nhi&ecirc;n về đa dạng sinh học giữa m&ocirc;i trường sống nước ngọt v&agrave; nước mặn.<em> &ldquo;C&oacute; nhiều lo&agrave;i c&aacute; trong m&ocirc;i trường nước ngọt hơn trong m&ocirc;i trường nước mặn, d&ugrave; m&ocirc;i trường nước ngọt chỉ chiếm khoảng 2% bề mặt tr&aacute;i đất. Đ&aacute;ng ngạc nhi&ecirc;n hơn nữa, hầu hết c&aacute;c lo&agrave;i c&aacute; nước mặn c&ograve;n sống hiện nay đều l&agrave; hậu duệ của c&aacute;c tổ ti&ecirc;n c&aacute; nước ngọt&rdquo;</em>, <em>Physorg</em> dẫn lời tiến sỹ Wiens.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">Kết quả n&agrave;y thoạt nghe c&oacute; vẻ m&acirc;u thuẫn với<em> &ldquo;định đề&rdquo;</em> rằng sự sống bắt nguồn từ biển. Song, giả thuyết được đưa ra l&agrave;, những cuộc đại tuyệt chủng trong m&ocirc;i trường nước mặn h&agrave;ng trăm triệu năm trước đ&atilde; ảnh hưởng đến h&agrave;nh tr&igrave;nh<em> &ldquo;di d&acirc;n&rdquo;</em> v&agrave; tiến h&oacute;a của c&aacute;.</span></p>\r\n<p style="text-align: justify;">\r\n	<span id="advenueINTEXT" name="advenueINTEXT">&Ocirc;ng giải th&iacute;ch cụ thể, đ&uacute;ng l&agrave; ban đầu c&aacute;c lo&agrave;i c&aacute; nước ngọt đ&atilde; đa dạng h&oacute;a từ c&aacute;c tổ ti&ecirc;n nước mặn, nhưng sau đ&oacute; những tổ ti&ecirc;n nước mặn đ&oacute; đ&atilde; bị tuyệt chủng ho&agrave;n to&agrave;n. Để rồi, c&aacute; nước ngọt lại quay lại đại dương, tiến h&oacute;a th&agrave;nh c&aacute;c lo&agrave;i c&aacute; nước mặn như ng&agrave;y nay (v&agrave; cũng v&igrave; thế c&aacute;c hậu duệ tr&ecirc;n c&oacute; &iacute;t thời gian hơn để ph&aacute;t triển đa dạng sinh học). Đ&acirc;y l&agrave; l&yacute; do v&igrave; sao c&aacute;c đại dương rất ngh&egrave;o c&aacute;c lo&agrave;i sinh vật, thậm ch&iacute; l&agrave; c&aacute;.</span></p>\r\n', 'Physorg', 0, 1060, 'ca bien, nguon goc ca bien, ca bien la con chau ca song, ca song', 0, '0', 0, '', 0),
(96, 7, 0, 0, 'Ths. Đặng Thụy Mai Thy', 1329840400, 0, 'Đặc điểm huyết học và biến đổi mô học ở cá tra bị vàng da', 'Cá tra bị vàng da là một trong những bệnh gây thiệt hại lớn cho người nuôi ở các tỉnh Đồng Bằng Sông Cửu Long và cho đến nay nguyên nhân gây bệnh vẫn đang được tiếp tục nghiên cứu. Cá bị bệnh vàng da xuất hiện quanh năm và nhiều nhất ở những tháng có thời tiết lạnh. Cá bị bệnh vàng da thường xuất hiện ở nhiều giai đoạn khác nhau nhưng có nhiều ở cá có trọng lượng khoảng 300-800g.', 'vang_da_ca_tra.jpg', '', 0, '', '<p>\r\n	Dấu hiệu bệnh ở c&aacute; tra bị v&agrave;ng da rất dễ nhận thấy, b&ecirc;n ngo&agrave;i cơ thể c&aacute; c&oacute; m&agrave;u v&agrave;ng nghệ, tập trung ở phần đầu, c&aacute;c v&acirc;y v&agrave; lườn bụng. C&aacute; bệnh bơi lờ đờ, phản ứng chậm với tiếng động, tập trung nơi thay nước của ao v&agrave;o buổi s&aacute;ng. B&ecirc;n trong xoang bụng cũng chuyển sang m&agrave;u v&agrave;ng c&oacute; chứa dịch, mỡ v&agrave;ng, gan thường sậm m&agrave;u từ v&agrave;ng n&acirc;u đến xanh, t&uacute;i mật trương to chứa dịch mật xanh đen, thận sưng to mềm nhũn, tỳ tạng sưng to sậm đen. Ngo&agrave;i ra, c&oacute; một số trường hợp ruột c&aacute; bệnh thường lồng đoạn ruột trước v&agrave;o đoạn ruột sau v&agrave; kh&ocirc;ng c&oacute; thức ăn.</p>\r\n<p style="text-align: justify;">\r\n	Quan s&aacute;t tế b&agrave;o hồng cầu trong m&aacute;u c&aacute; tra c&oacute; dạng h&igrave;nh oval hay elip v&agrave; nh&acirc;n h&igrave;nh tr&ograve;n ở giữa nhưng đa số hồng cầu ở c&aacute; bệnh c&oacute; hiện tượng tho&aacute;i ho&aacute; mất cấu tr&uacute;c kh&ocirc;ng c&ograve;n tế b&agrave;o chất chỉ thấy nh&acirc;n tế b&agrave;o. Số lượng hồng cầu ở c&aacute; bệnh v&agrave;ng da giảm c&ograve;n 10-20% so với c&aacute; khoẻ. Số lượng tổng bạch cầu ở c&aacute; bệnh kh&ocirc;ng kh&aacute;c ở c&aacute;c khoẻ tuy nhi&ecirc;n bạch cầu đơn nh&acirc;n v&agrave; bạch cầu trung t&iacute;nh ở c&aacute; bị v&agrave;ng da tăng l&ecirc;n.</p>\r\n<p style="text-align: justify;">\r\n	Nh&igrave;n chung, c&aacute;c biến đổi m&ocirc; học ở c&aacute; bị v&agrave;ng da kh&aacute; giống với một số loại bệnh kh&aacute;c như bệnh mủ gan, bệnh xuất huyết, bệnh ph&ugrave; mắt tr&ecirc;n c&aacute; tra. Cấu tr&uacute;c vi thể của gan c&aacute; gồm tế b&agrave;o h&igrave;nh đa gi&aacute;c c&oacute; nh&acirc;n h&igrave;nh cầu sắp xếp theo hướng lan toả từ tĩnh mạch trung t&acirc;m. Giữa những tế b&agrave;o gan c&oacute; xoang mao mạch chứa hồng cầu. Gan c&oacute; c&aacute;c tĩnh mạch, động mạch, ống dẫn mật, c&aacute;c trung t&acirc;m đại thực b&agrave;o sắc tố, c&aacute;c đảo tụy. C&aacute; bị v&agrave;ng da c&oacute; hiện tượng xung huyết ở c&aacute;c mạch m&aacute;u v&agrave; xuất huyết trong tổ chức m&ocirc;. Ngo&agrave;i ra, c&ograve;n t&igrave;m thấy nhiều v&ugrave;ng tế b&agrave;o bị mất cấu tr&uacute;c, hoại tử v&agrave; xuất hiện dịch vi&ecirc;m. C&aacute;c hiện tượng n&agrave;y l&agrave;m cho tổ chức gan bị sưng to.</p>\r\n<p style="text-align: justify;">\r\n	Thận l&agrave; một trong những cơ quan nhạy cảm với t&aacute;c nh&acirc;n g&acirc;y bệnh v&igrave; đ&acirc;y l&agrave; cơ quan tạo m&aacute;u v&agrave; l&agrave; cơ quan b&agrave;i tiết chủ yếu của c&aacute;. Thận sau của c&aacute; tra c&oacute; cấu tạo bởi tiểu cầu thận, c&aacute;c ống dẫn, ống thận, c&aacute;c mạch m&aacute;u. Cấu tr&uacute;c thận c&aacute; tra bị bệnh v&agrave;ng da dưới k&iacute;nh hiển vi cho thấy c&oacute; nhiều biến đổi tr&ecirc;n c&aacute;c ống thận, tiểu cầu thận v&agrave; m&ocirc; kẽ. Hiện tượng xung huyết trong hệ thống mạch m&aacute;u được t&igrave;m thấy rất thường xuy&ecirc;n. Ngo&agrave;i ra, hiện tượng xuất huyết ống thận v&agrave; c&aacute;c m&ocirc; kẽ cũng xảy ra phổ biến. C&aacute;c quản cầu thận cũng bị xung huyết l&agrave;m cho hệ thống ống mạch bị sưng v&agrave; tiểu cầu thận sưng to. Những biến đổi n&agrave;y l&agrave;m ảnh hưởng đến chức năng của thận, l&agrave;m thận suy giảm b&agrave;i tiết c&aacute;c chất thải của qu&aacute; tr&igrave;nh trao đổi chất g&acirc;y t&iacute;ch tụ c&aacute;c sản phẩm thải của qu&aacute; tr&igrave;nh biến dưỡng g&acirc;y độc cho cơ thể, m&ocirc; tạo m&aacute;u cũng bị hủy hoại l&agrave;m lượng m&aacute;u trong cơ thể giảm s&uacute;t do kh&ocirc;ng c&oacute; m&aacute;u thay thế.</p>\r\n<p style="text-align: justify;">\r\n	<img alt="mo ca tra bi vang da" src="/upload/images/news/ca_tra_vang_da_2.jpg" style="width: 600px; height: 806px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tỳ tạng được bao bọc bởi lớp tế b&agrave;o biểu m&ocirc; l&aacute;t đơn v&agrave; m&ocirc; li&ecirc;n kết. Th&agrave;nh phần ch&iacute;nh của tỳ tạng l&agrave; tủy trắng v&agrave; tủy đỏ. Tủy đỏ bắt m&agrave;u đậm hơn tủy trắng khi nhuộm hematoxyline v&agrave; eosin. Tủy đỏ tạo th&agrave;nh mạng lưới bao quanh tủy trắng. Ngo&agrave;i tủy đỏ v&agrave; tủy trắng, tỳ tạng c&oacute; sự hiện diện của trung t&acirc;m đại thực b&agrave;o sắc tố c&oacute; m&agrave;u v&agrave;ng n&acirc;u hoặc n&acirc;u đen. C&ugrave;ng với gan v&agrave; thận, tỳ tạng của c&aacute; tra bị bệnh v&agrave;ng da cũng xuất hiện nhiều biến đổi. Tr&ecirc;n tỳ tạng cũng t&igrave;m thấy xung huyết mạch m&aacute;u v&agrave; xuất huyết tổ chức m&ocirc;. Tế b&agrave;o trong m&ocirc; li&ecirc;n kết c&oacute; cấu tr&uacute;c lỏng lẻo v&agrave; rất kh&oacute; ph&acirc;n biệt tủy đỏ v&agrave; tủy trắng. Nguy&ecirc;n nh&acirc;n n&agrave;y c&oacute; thể do tế b&agrave;o m&aacute;u bị hoại tử kết hợp với hiện tượng xuất huyết tạo n&ecirc;n v&ugrave;ng bắt m&agrave;u hồng của Eosin. Một điểm đ&aacute;ng ch&uacute; &yacute; tr&ecirc;n tỳ tạng l&agrave; sự xuất hiện của c&aacute;c trung t&acirc;m đại thực b&agrave;o sắc tố nhiều hơn so với mức b&igrave;nh thường. Đ&acirc;y l&agrave; điểm ho&agrave;n to&agrave;n tr&aacute;i ngược với một số bệnh kh&aacute;c như l&agrave; bệnh ph&ugrave; mắt của c&aacute; tra v&agrave; bệnh do vi khuẩn <em>Aeromonas</em> v&agrave; <em>Vibrio</em> số lượng trung t&acirc;m đại thực b&agrave;o giảm thấp.</p>\r\n', 'UV Việt Nam', 0, 1477, 'ca tra, vang da, bien doi mo hoc, ky thuat nuoi ca tra, ca benh vang da', 0, '0', 0, '', 0),
(97, 7, 0, 0, 'PGs. Ts. Trương Quốc Phú', 1329844298, 0, 'Sử dụng vôi trong nuôi trồng thủy sản', 'Trong nuôi trồng thủy sản, pH của nước thường xuyên biến động theo chu kỳ ngày đêm, biên độ biến động tăng dần từ đầu cho đến cuối vụ nuôi. Ngoài ra, pH của nước có thể bị thấp do đất phèn, đặc biệt là hiện tượng pH giảm đột ngột sau những cơn mưa đầu mùa. Sự biến động pH quá lớn sẽ ảnh hưởng trực tiếp gây sinh trưởng chậm và tỉ lệ chết cao.', 'bon_voi_bot.jpg', '', 0, '', '<p>\r\n	pH qu&aacute; cao (v&agrave;o buổi trưa) hay qu&aacute; thấp (v&agrave;o s&aacute;ng sớm) c&ograve;n c&oacute; ảnh hưởng gi&aacute;n tiếp l&agrave;m tăng h&agrave;m lượng c&aacute;c kh&iacute; độc. Khi pH cao sẽ l&agrave;m tăng h&agrave;m lượng NH3, ngược lại pH thấp sẽ l&agrave;m tăng h&agrave;m lượng H2S g&acirc;y độc cho c&aacute;. Do đ&oacute;, người nu&ocirc;i c&aacute; thường sử dụng v&ocirc;i để giữ pH ổn định, gi&uacute;p t&ocirc;m c&aacute; sinh trưởng tốt, tỉ lệ sống v&agrave; năng suất cao. C&aacute;c trường hợp ao nu&ocirc;i cần được b&oacute;n v&ocirc;i gồm:</p>\r\n<div style="text-align: right">\r\n	<ul>\r\n		<li style="text-align: justify;">\r\n			<span style="font-size: 12px;"><span style="color: rgb(0, 0, 0);">Ao nu&ocirc;i bị mất c&acirc;n bằng dinh dưỡng với nhiều chất hữu cơ v&agrave; m&ugrave;n ở đ&aacute;y ao</span></span></li>\r\n		<li style="text-align: justify;">\r\n			<span style="font-size: 12px;"><span style="color: rgb(0, 0, 0);">Mất c&acirc;n bằng dinh dưỡng với nước bị nhiễm ph&egrave;n</span></span></li>\r\n		<li style="text-align: justify;">\r\n			<span style="font-size: 12px;"><span style="color: rgb(0, 0, 0);">Nước ao nu&ocirc;i bị mềm v&agrave; độ kiềm thấp</span></span></li>\r\n		<li style="text-align: justify;">\r\n			<span style="font-size: 12px;"><span style="color: rgb(0, 0, 0);">H&agrave;m lượng kh&iacute; CO<sub>2</sub> trong nước cao</span></span></li>\r\n	</ul>\r\n	<p style="text-align: justify;">\r\n		<span style="font-size: 12px"><span style="color: #000000"><strong>Loại v&ocirc;i v&agrave; hiệu quả trung h&ograve;a ax&iacute;t</strong></span></span></p>\r\n	<p style="text-align: left;">\r\n		C&aacute;c loại v&ocirc;i thường được sử dụng trong nu&ocirc;i trồng thủy sản gồm 4 loại: V&ocirc;i n&ocirc;ng nghiệp hay đ&aacute; v&ocirc;i (CaCO3), dolomite hay đ&aacute; v&ocirc;i đen (CaMg(CO3)2), v&ocirc;i t&ocirc;i (Ca(OH)2) v&agrave; v&ocirc;i sống (CaO). Hiệu quả trung h&ograve;a ax&iacute;t t&ugrave;y thộc v&agrave;o loại v&ocirc;i, với cỡ hạt nhỏ hơn 0,25 mm th&igrave; hiệu quả tương đối của c&aacute;c loại v&ocirc;i như sau:</p>\r\n	<table border="0px" cellpadding="2px" cellspacing="1px" class="bang">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					Loại v&ocirc;i</td>\r\n				<td>\r\n					CaCO<sub>3</sub></td>\r\n				<td>\r\n					CaMg(CO<sub>3</sub>)<sub>2 </sub></td>\r\n				<td>\r\n					Ca(OH)<sub>2</sub></td>\r\n				<td>\r\n					CaO</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					Hiệu quả tương đối (%)</td>\r\n				<td>\r\n					100</td>\r\n				<td>\r\n					109</td>\r\n				<td>\r\n					136</td>\r\n				<td>\r\n					179</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	Hiệu quả trung h&ograve;a ax&iacute;t c&ograve;n phụ thuộc v&agrave;o cỡ hạt v&agrave; tạp chất, cỡ hạt nhỏ hơn 0,25 mm th&igrave; hiệu quả trung h&ograve;a đạt 100%, cỡ hạt từ 0,25-0,85 mm th&igrave; hiệu quả trung h&ograve;a đạt 52%, cỡ hạt 0,85-1,7 mm th&igrave; hiệu quả trung h&ograve;a chỉ đạt 12,6% v&agrave; cỡ hạt lớn hơn 1,7 mm th&igrave; hiệu quả trung h&ograve;a chỉ c&ograve;n 3,6%. Lượng tạp chất c&agrave;ng nhiều th&igrave; hiệu quả trung h&ograve;a c&agrave;ng thấp. V&igrave; vậy, khi sử dụng v&ocirc;i n&ecirc;n ch&uacute; &yacute; lựa chọn loại v&ocirc;i mịn (cỡ hạt nhỏ hơn 0,25 mm) v&agrave; &iacute;t tạp chất đạt hiệu quả trung h&ograve;a cao nhất.</p>\r\n<p>\r\n	<span style="font-size: 12px"><span style="color: #000000"><strong>T&aacute;c dụng của v&ocirc;i</strong></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">pH của nước thấp thường do một trong 3 </span><span style="color: #000000"><span>nguy&ecirc;n nh&acirc;n sau:</span></span></span></p>\r\n<ul>\r\n	<li style="text-align: justify">\r\n		<span style="font-size: 12px"><span style="color: #000000">Qu&aacute; tr&igrave;nh oxy h&oacute;a đất ph&egrave;n tiềm t&agrave;ng (FeS<sub>2</sub>) tạo ra nhiều ax&iacute;t </span><span style="color: #000000">v&ocirc; cơ (H<sub>2</sub>SO<sub>4</sub>), đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n c&oacute; thể l&agrave;m giảm pH rất thấp. C&aacute;c phản ứng oxy h&oacute;a đất ph&egrave;n gồm:</span></span></li>\r\n</ul>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2FeS<sub>2</sub> + 7O<sub>2</sub> + 2H<sub>2</sub>O = 2FeSO<sub>4</sub> + <strong>2H<sub>2</sub>SO<sub>4</sub></strong></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2FeSO<sub>4</sub> + &frac12;O<sub>2</sub> + H<sub>2</sub>SO<sub>4</sub> = Fe<sub>2</sub>(SO<sub>4</sub>)<sub>3</sub> + H<sub>2</sub>O</span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; FeS<sub>2</sub> + 7Fe<sub>2</sub>(SO<sub>4</sub>)<sub>3</sub> + 8H<sub>2</sub>O = 15FeSO<sub>4</sub> + <strong>8H<sub>2</sub>SO<sub>4</sub></strong></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Fe<sub>2</sub>(SO<sub>4</sub>)<sub>3</sub> + 6H<sub>2</sub>O = 2Fe(OH)<sub>3</sub> + <strong>3H<sub>2</sub>SO<sub>4</sub> </strong>&nbsp;&nbsp;</span></span></p>\r\n<ul>\r\n	<li style="text-align: justify">\r\n		<span style="font-size: 12px"><span style="color: #000000">Qu&aacute; tr&igrave;nh ph&acirc;n hủy hữu cơ yếm kh&iacute; (l&ecirc;n men) sinh ra nhiều ax&iacute;t hữu cơ như: propionic (C<sub>3</sub>H<sub>6</sub>O<sub>2</sub>), butyric (C<sub>4</sub>H<sub>8</sub>O<sub>2</sub>), lactic (C<sub>3</sub>H<sub>6</sub>O<sub>3</sub>), succinic (C<sub>4</sub>H<sub>6</sub>O<sub>4</sub>), acetic (C<sub>2</sub>H<sub>4</sub>O<sub>2</sub>)&hellip; c&aacute;c ax&iacute;t n&agrave;y l&agrave;m giảm pH của nền đ&aacute;y v&agrave; nước ao.</span></span></li>\r\n</ul>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px">&nbsp;</span></p>\r\n<ul>\r\n	<li style="text-align: justify">\r\n		<span style="font-size: 12px"><span style="color: #000000">Qu&aacute; tr&igrave;nh ph&acirc;n hủy hữu cơ hiếu kh&iacute; v&agrave; h&ocirc; hấp của thủy sinh vật sinh ra nhiều CO<sub>2</sub>, trong nước CO<sub>2</sub> phản ứng với nước tạo ra H<sub>2</sub>CO<sub>3 </sub>cũng l&agrave;m cho pH giảm.</span></span></li>\r\n</ul>\r\n<p>\r\n	Khi b&oacute;n v&ocirc;i trong trường hợp nước nhiễm ph&egrave;n v&agrave; c&oacute; nhiều ax&iacute;t hữu cơ nhằm gi&uacute;p trung h&ograve;a c&aacute;c ax&iacute;t v&agrave; l&agrave;m tăng pH của nước ao. Phương tr&igrave;nh phản ứng trung h&ograve;a như sau:</p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaCO<sub>3</sub> + 2H+ = Ca<sup>2+</sup> +H<sub>2</sub>O + CO<sub>2</sub></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaMg(CO<sub>3</sub>)<sub>2</sub> + 4H<sup>+</sup> = Ca<sup>2+</sup> + Mg<sup>2+</sup> + 2H<sub>2</sub>O + 2CO<sub>2</sub></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ca(OH)<sub>2</sub> + 2H+ = Ca<sup>2+</sup> + 2H<sub>2</sub>O</span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaO + 2H<sup>+</sup> = Ca<sup>2+</sup> + H<sub>2</sub>O</span></span></p>\r\n<p>\r\n	Trong trường hợp h&agrave;m lượng kh&iacute; CO2 trong ao cao (&gt;10 mg/L), &aacute;p dụng biện ph&aacute;p b&oacute;n v&ocirc;i c&oacute; thể l&agrave;m giảm h&agrave;m lượng CO2, tăng hệ&nbsp; đệm v&agrave; tăng nguồn carbon cho qu&aacute; tr&igrave;nh quang hợp. Trong trường hợp n&agrave;y n&ecirc;n b&oacute;n v&ocirc;i v&agrave;o l&uacute;c 22:00-24:00 giờ v&igrave; l&uacute;c n&agrave;y h&agrave;m lượng CO2 bắt đầu tăng cao, b&oacute;n v&ocirc;i v&agrave;o ban đ&ecirc;m c&oacute; thể tr&aacute;nh t&igrave;nh trạng CO2 cao v&agrave;o l&uacute;c s&aacute;ng sớm v&agrave; c&oacute; thể l&agrave;m tăng ion hệ đệm HCO3-. Mỗi ph&acirc;n tử v&ocirc;i tham gia phản ứng với CO2 tạo ra 2 ion HCO3-, ion n&agrave;y c&oacute; vai tr&ograve; quan trọng trong việc chống lại sự thay đổi pH của nước. Phương tr&igrave;nh phản ứng khử CO2 gồm:&nbsp;</p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaCO<sub>3</sub> + CO<sub>2</sub> + H<sub>2</sub>O = Ca<sup>2+</sup> + 2HCO<sup>-</sup><sub>3</sub>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaMg(CO<sub>3</sub>)<sub>2</sub> + 2CO<sub>2</sub> + 2H<sub>2</sub>O = Ca<sup>2+</sup> + Mg<sup>2+</sup> + 4HCO<sub>3</sub><sup>-</sup></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ca(OH)<sub>2</sub> + 2CO<sub>2</sub> = Ca<sup>2+</sup> + 2HCO<sup>-</sup><sub>3</sub></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CaO + 2CO<sub>2</sub> + H<sub>2</sub>O = Ca<sup>2+</sup> + 2HCO<sup>-</sup><sub>3</sub></span></span></p>\r\n<p>\r\n	Ngo&agrave;i ra, b&oacute;n v&ocirc;i cho ao nu&ocirc;i t&ocirc;m c&aacute; c&oacute; thể l&agrave;m giảm độ đục do ph&ugrave; sa (hạt keo đất), c&aacute;c ion Ca2+ v&agrave; Mg2+ sẽ bị hấp thụ tr&ecirc;n bề mặt hạt keo đất l&agrave;m cho k&iacute;ch thước v&agrave; khối lượng hạt keo sẽ nặng hơn v&agrave; lắng nhanh hơn. Ion Ca2+ v&agrave; Mg2+ cũng c&oacute; vai tr&ograve; kết hợp với PO43- tạo th&agrave;nh Ca3(PO4)2 v&agrave; Mg3(PO4)2 g&acirc;y kết tủa l&acirc;n xuống đ&aacute;y ao, l&agrave; giảm sự ph&aacute;t triển của tảo trong ao.</p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12px"><span style="color: #000000"><strong>X&aacute;c định liều lượng v&ocirc;i</strong></span></span></p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000"><em>B&oacute;n v&ocirc;i khi cải tạo ao: </em></span></span></p>\r\n<p>\r\n	Để x&aacute;c định ch&iacute;nh x&aacute;c liều lượng v&ocirc;i cần b&oacute;n cho từng trường hợp của đ&aacute;y ao c&oacute; thể &aacute;p dụng phương ph&aacute;p thử với dung dịch đệm p-Nitrophenol pH=8 (h&ograve;a tan 10 g p-nitrophenol, 7,5 g H3BO3, 37 g KCl v&agrave; 5,25 g KOH trong nước cất rồi pha th&agrave;nh 1 l&iacute;t). Cho 20 g b&ugrave;n kh&ocirc; đ&atilde; được nghiền mịn v&agrave;o 40 mL dung dịch đệm p-nitrophenol, khuấy đều v&agrave;i lần trong một giờ, sau đ&oacute; đo pH của dung dịch (pHdd) v&agrave; x&aacute;c định lượng v&ocirc;i cần b&oacute;n theo c&ocirc;ng thức sau:</p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lượng v&ocirc;i cần b&oacute;n (kg CaCO<sub>3</sub>/ha) = (8,0 &ndash; pH<sub>dd</sub>) x 6000</span></span></p>\r\n<p>\r\n	Lượngv&ocirc;i cần b&oacute;n cho đ&aacute;y ao cũng c&oacute; thể được ước lượng dựa v&agrave;o cấu tr&uacute;c v&agrave; pH của đất đ&aacute;y ao, &aacute;p dụng bảng số sau đ&acirc;y để t&iacute;nh liều lượng v&ocirc;i cần b&oacute;n cho nền đ&aacute;y khi cải tạo ao nu&ocirc;i.</p>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px">&nbsp;</span></p>\r\n<table border="0" cellpadding="0" cellspacing="1" class="bang " style="width: 649px; height: 467px">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan="2" style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">pH của b&ugrave;n</span></span></p>\r\n			</td>\r\n			<td colspan="3" style="width: 479px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">Lượng v&ocirc;i cần b&oacute;n (kg CaCO<sub>3</sub>/ha) (Boyd, 1990)</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">Đất thịt hoặc đất s&eacute;t</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">Đất thịt pha c&aacute;t</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">Đất c&aacute;t</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">&lt; 4,0</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">14320</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">7160</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">4475</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">4,0-4,5</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">10740</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">5370</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">4475</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">4,6-5,0</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">8950</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">4475</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">3580</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">5,1-5,5</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">5370</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">3580</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">1790</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">5,6-6,0</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">3580</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">1790</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">895</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">6,1-6,5</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">1790</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">1790</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">0</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width: 159px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">&gt;6,5</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">0</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">0</span></span></p>\r\n			</td>\r\n			<td style="width: 160px">\r\n				<p>\r\n					<span style="font-size: 12px"><span style="color: #000000">0</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style="text-align: justify">\r\n	<span style="font-size: 12px"><span style="color: #000000"><em>B&oacute;n v&ocirc;i để tăng độ kiềm v&agrave; khử CO<sub>2</sub>:</em></span></span></p>\r\n<p>\r\n	Việc x&aacute;c định liều lượng v&ocirc;i cần b&oacute;n cho nước ao thường được dựa v&agrave;o tổng độ kiềm (total alkalinity) hoặc tổng độ cứng (total hardness). Tổng độ kiềm th&iacute;ch hợp l&agrave; lớn hơn 40 mg CaCO3/L cho ao nu&ocirc;i thủy sản nước ngọt v&agrave; lớn hơn 80 mg CaCO3/L cho ao nu&ocirc;i thủy sản nước mặn, lợ. Giả định rằng, ao nu&ocirc;i nước ngọt c&oacute; diện t&iacute;ch 1000 m2, s&acirc;u 1m v&agrave; c&oacute; độ kiềm l&agrave; 10 mg/L, để tăng độ kiềm l&ecirc;n 40 mg/L th&igrave; cần b&oacute;n 30 mg CaCO3/L hay 30 g CaCO3/m3, tổng lượng v&ocirc;i cần b&oacute;n cho ao l&agrave; 30 kg CaCO3. Tuy nhi&ecirc;n, theo c&aacute;ch t&iacute;nh liều lượng v&ocirc;i cần b&oacute;n như tr&ecirc;n th&igrave; độ kiềm của nước ao sau khi b&oacute;n v&ocirc;i c&oacute; thể kh&ocirc;ng đạt được 40 mg CaCO3/L như mong muốn, nguy&ecirc;n nh&acirc;n l&agrave; do một phần v&ocirc;i bị mất đi khi tham gia phản ứng trung h&ograve;a ax&iacute;t trong b&ugrave;n. Do đ&oacute;, sau khi b&oacute;n v&ocirc;i 2-3 tuần cần kiểm tra lại độ kiềm của nước, nếu độ kiềm chưa đạt 40 mg CaCO3/L th&igrave; cần b&oacute;n v&ocirc;i bổ sung với liều lượng được x&aacute;c định theo phương ph&aacute;p đ&atilde; n&ecirc;u tr&ecirc;n.</p>\r\n<p>\r\n	Trường hợp x&aacute;c định liều lượng v&ocirc;i để khử CO2 cần phải dựa v&agrave;o h&agrave;m lượng CO2 trong nước. H&agrave;m lượng CO2 th&iacute;ch hợp cho ao nu&ocirc;i thủy sản khoảng 1-10 mg/L, khi h&agrave;m lượng CO2 vượt qu&aacute; 10 mg/L c&oacute; thể l&agrave;m ảnh hưởng đến qu&aacute; tr&igrave;nh h&ocirc; hấp của c&aacute;, trường hợp n&agrave;y cần phải khử CO2. Theo l&yacute; thuyết, để khử 1 mg CO2/L, cần d&ugrave;ng 0,64 mg CaO/L, 0,84 mg Ca(OH)2/L, 2,1 mg CaMg(CO3)2/L hoặc 2,27 mg CaCO3/L. Giả định, ao nu&ocirc;i c&oacute; diện t&iacute;ch 1000 m2, s&acirc;u 1 m v&agrave; c&oacute; h&agrave;m lượng CO2 l&agrave; 15 mg/L, để l&agrave;m giảm CO2 xuống 5 mg/L cần d&ugrave;ng 22,7 mg CaCO3/L hay 22,7 g/m3 v&agrave; tổng lượng v&ocirc;i cần d&ugrave;ng cho cả ao l&agrave; 22,7 kg. Ch&uacute; &yacute;, khi sử dụng c&aacute;c loại v&ocirc;i để khử CO2 cần t&iacute;nh liều lượng ch&iacute;nh x&aacute;c, nếu sử dụng thừa v&ocirc;i c&oacute; thể l&agrave;m cho h&agrave;m lượng CO2 giảm xuống bằng 0, khi đ&oacute; pH sẽ tăng cao (&gt;8,34) g&acirc;y ảnh hưởng x&acirc;u cho t&ocirc;m c&aacute; nu&ocirc;i.</p>\r\n', 'UV Việt Nam', 0, 1469, 'bon voi ao nuoi, ky thuat bon voi, voi bot, lieu bon voi, cach dung voi', 0, '0', 0, '', 0),
(123, 23, 0, 0, 'Trung Hiếu', 1330358289, 0, 'Bún cá Châu Đốc - Món ngon không thể quên', 'Có dịp về Châu Đốc, du khách sẽ được thưởng thức nhiều món ăn đặc sản của vùng đất này. Dù được chiêu đãi nhiều món ăn mang đậm phong vị quê hương, nhưng có lẽ món bún nước lèo nấu với cá lóc mà người dân chọn làm nguyên liệu chính sẽ để lại trong lòng du khách nhiều ấn tượng nhất.', 'bunca.jpg', 'bun ca chau doc', 0, '', '<p>\r\n	Du khách đến Châu đốc vào khoảng tháng 9, tháng 10 âm lịch, khi những cơn mưa cuối mùa đã dứt hạt là thời điểm cá đã trưởng thành, lúc này nấu bún nước lèo ngon hơn các mùa khác. Bún nước lèo ăn cùng rau ghém sẽ tăng thêm độ hấp dẫn.</p>\r\n<p>\r\n	&nbsp;&nbsp; Bún nước lèo đã có mặt khắp nơi trong đời sống của người dân Nam bộ. Tuy cùng mang tên là bún nước lèo nhưng ở đây có gia vị rất đặc trưng là ngải bún và cách chế biến cũng khác với bún nước lèo ở Trà Vinh, Sóc Trăng…</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp; Ngoài ra du khách cũng có thể nhận ra sự khác nhau giữa bún cá Châu Đốc với bún cá của các vùng lân cận. Nếu như tô bún cá Long Xuyên có kèm thêm vài miếng chả cá thác lác chiên vừa dai vừa béo, hay tô bún cá Kiên Giang lại kèm thêm hải sản, nào là tôm và gạch tôm, thì tô bún cá Châu Đốc lại được ăn kèm thêm với một món đặc sản nữa đó là thịt heo quay.</p>\r\n<p>\r\n	&nbsp;&nbsp; Bún cá ngày càng phong phú và ở mỗi địa phương món bún cá lại mang một đặc điểm riêng nổi tiếng. Tuy nhiên ngon nhất và được biết đến nhiều nhất vẫn là bún cá Châu Đốc. Để có được một nồi bún cá ngon như vậy, người dân nơi đây phải trãi qua một công đoạn khá công phu là chuẩn bị về nguyên liệu cũng như về cách thực hiện như sau:<br />\r\n	<u><strong>* Nguyên liệu:</strong></u><br />\r\n	&nbsp;&nbsp;&nbsp; - Bún.<br />\r\n	&nbsp;&nbsp;&nbsp; - 1 con cá lóc (hơn 1kg).<br />\r\n	&nbsp;&nbsp;&nbsp; - Xương heo: 500g.<br />\r\n	&nbsp;&nbsp;&nbsp; - Nghệ tươi, ngải bún (2-3 củ), sả cây (6 cây).<br />\r\n	&nbsp;&nbsp;&nbsp; - Mắm ruốc: 1 muỗng canh.<br />\r\n	&nbsp;&nbsp;&nbsp; - Rau sống: rau thơm, giá, rau muống bào, bắp chuối…<br />\r\n	&nbsp;&nbsp;&nbsp; - Chanh, ớt, nước mắm.<br />\r\n	&nbsp;&nbsp;&nbsp; - Muối, đường, bột ngọt.<br />\r\n	&nbsp;&nbsp;&nbsp; - Tỏi và sả bâm nhuyễn<br />\r\n	<strong><u>Cách thực hiện</u></strong><br />\r\n	- Cá lóc luộc chín vớt ra để nguội rồi gỡ xương, lấy nạc cá.<br />\r\n	- Lấy nước luộc cá để hầm xương heo. Đập dập sả cho vào nồi nước hầm, sau đó lược lấy nước trong.<br />\r\n	- Dùng lá chuối gói mắm ruốc lại nướng sơ, sau đó cho vào 1/2 chén nước đánh tan rồi lược lại cho sạch.<br />\r\n	- Giã nhuyễn nghệ tươi, ngải bún cho vào 1 chén nước rồi đánh tan, lược lại lấy nước, bỏ xác.<br />\r\n	- Phần nạc cá ướp thêm một chút muối, đường, bột ngọt, bột nghệ rồi cho vào chảo xào sơ cho thấm gia vị.<br />\r\n	- Cho tất cả mắm ruốc, nghệ, ngải bún, tỏi và sả bâm nhuyễn cùng phần nạc cá đã xào vào nồi nước lèo để nấu sôi lại. Nêm gia vị vừa ăn.<br />\r\n	- Bún và rau sống cho vào tô, sau đó rưới nước lèo với cá lên trên sao cho phần nước xâm xấp tô. Dùng lúc còn nóng kèm với nước mắm trong, chanh, ớt.<br />\r\n	Nguyên liệu cá lóc đã làm tăng thêm hương vị ẩm thực cho món ăn bình dân miền sông nước đặc biệt là ở Châu Đốc: món bún nước lèo.<br />\r\n	&nbsp;</p>\r\n', 'Theo Việt Báo', 0, 5148, 'bún cá, châu đốc, an giang, bun', 0, '0', 0, '', 0),
(98, 4, 0, 0, 'TRÀ GIANG', 1329891162, 0, 'Trúng mùa tôm hùm giống', 'Tôm hùm giống đang xuất hiện nhiều tại khu vực biển xã Bình Thuận, huyện Bình Sơn, Quảng Ngãi. Gần 100 hộ ngư dân và bạn tàu đã trở thành triệu phú chỉ sau một đêm ra khơi đánh bắt.', 'luoi_quet_tom_hum.jpg', '', 0, '', '<p class="pBody">\r\n	14g, ngư d&acirc;n B&ugrave;i Thanh Vinh (50 tuổi) - th&ocirc;n Tuyết Di&ecirc;n 2, x&atilde; B&igrave;nh Thuận - dẫn t&ocirc;i bơi thuyền th&uacute;ng qua s&ocirc;ng Đầm nhằm hướng t&agrave;u QNg 55936 TS - nơi d&ugrave;ng l&agrave;m trạm trung chuyển - để săn t&ocirc;m h&ugrave;m giống.</p>\r\n<p class="pBody">\r\n	Chừng 80 chiếc ghe, t&agrave;u loại 45-90CV đ&atilde; neo sẵn theo h&igrave;nh c&aacute;nh cung, v&agrave;i chục chiếc thuyền th&uacute;ng chứa sẵn những tấm lưới (m&agrave;nh) m&agrave;u xanh được buộc quanh c&aacute;c t&agrave;u. Hai tấm lưới h&igrave;nh chữ V, m&agrave;u xanh nước biển d&agrave;i gần 80m được hai ngư d&acirc;n tr&ecirc;n hai chiếc thuyền th&uacute;ng đi song song thả xuống c&ugrave;ng l&uacute;c. Chờ trời tối hẳn, những ngọn đ&egrave;n pha loại 500W từ c&aacute;c t&agrave;u bật l&ecirc;n, cả vịnh biển lung linh như một TP thu nhỏ. &Ocirc;ng Vinh giải th&iacute;ch: &ldquo;C&aacute;c t&agrave;u đều d&ugrave;ng lưới m&agrave;u xanh v&igrave; phải tr&ugrave;ng với m&agrave;u của nước biển để t&ocirc;m kh&ocirc;ng &ldquo;lạ mắt&rdquo; m&agrave; bỏ đi, những b&oacute;ng đ&egrave;n c&ocirc;ng suất cực lớn để dụ t&ocirc;m v&agrave;o những gi&agrave;n m&agrave;nh, thời gian c&ograve;n lại l&agrave;... ngồi chơi th&ocirc;i&rdquo;.</p>\r\n<p class="pBody">\r\n	Khoảng 2g s&aacute;ng, những m&eacute;t lưới được gỡ, t&ocirc;m nh&iacute; được dồn về ph&iacute;a đu&ocirc;i m&agrave;nh cho v&agrave;o đục (một loại dụng cụ đựng gắn cuối đu&ocirc;i m&agrave;nh để dồn t&ocirc;m v&agrave;o). Chừng hơn hai giờ sau, lưới đ&atilde; được thu về gọn ghẽ, cũng l&agrave; l&uacute;c những con t&ocirc;m nh&iacute; l&oacute;ng l&aacute;nh nằm gọn v&agrave;o c&aacute;c th&ugrave;ng xốp.</p>\r\n<p class="pBody">\r\n	Sau một đ&ecirc;m, t&agrave;u của &ocirc;ng Vinh khai th&aacute;c 101 con t&ocirc;m nh&iacute;. &ldquo;Số t&ocirc;m n&agrave;y chờ c&aacute;c ngư d&acirc;n b&aacute;n cho người nu&ocirc;i t&ocirc;m của tỉnh Ph&uacute; Y&ecirc;n với gi&aacute; khoảng 140.000 đồng/con, thu về khoảng 14 triệu đồng. Chủ t&agrave;u v&agrave; bạn chia 50-50&rdquo; - &ocirc;ng Vinh cho biết. &Ocirc;ng n&oacute;i tiếp t&ocirc;m nh&iacute; xuất hiện tại v&ugrave;ng biển n&agrave;y từ tối 27 th&aacute;ng chạp n&ecirc;n người d&acirc;n tr&uacute;ng lắm. Sau mười đ&ecirc;m săn t&ocirc;m nh&iacute;, &ocirc;ng đ&atilde; thu về khoảng 250 triệu đồng, ph&acirc;n nửa chia cho bạn t&agrave;u, c&ograve;n &ocirc;ng l&agrave; chủ t&agrave;u n&ecirc;n thu về gần 130 triệu đồng.</p>\r\n<p class="pBody">\r\n	Nhưng số đ&oacute; vẫn chưa &ldquo;khủng&rdquo; bằng người đầu ti&ecirc;n săn t&ocirc;m nh&iacute; m&agrave; cả đội thuyền đ&aacute;nh bắt x&atilde; B&igrave;nh Thuận nể phục l&agrave; &ocirc;ng Ph&ugrave;ng Hữu Hạnh ở th&ocirc;n Tuyết Di&ecirc;m 2. &Ocirc;ng Hạnh v&agrave; s&aacute;u ngư d&acirc;n trong đ&ecirc;m 28 th&aacute;ng chạp đ&atilde; săn gần 1.500 con t&ocirc;m nh&iacute;, thu nhập gần 150 triệu đồng, trừ chi ph&iacute;, chia cho chủ t&agrave;u mỗi bạn ch&agrave;i c&ograve;n lại được tr&ecirc;n 10 triệu đồng/đ&ecirc;m.</p>\r\n<p class="pBody">\r\n	Nhớ lại những đ&ecirc;m &ldquo;ho&agrave;ng kim&rdquo; đ&oacute;, &ocirc;ng Hạnh vẫn c&ograve;n l&acirc;ng l&acirc;ng: &ldquo;Ri&ecirc;ng mồng 2 tết, t&agrave;u tui săn tiếp được mẻ t&ocirc;m 1.500 con, gi&aacute; t&ocirc;m tr&ecirc;n 250.000 đồng/con, thu về gần 400 triệu đồng&rdquo;. Qua hơn mười đ&ecirc;m khai th&aacute;c, t&agrave;u của &ocirc;ng thu được gần 1 tỉ đồng.</p>\r\n<p class="pBody">\r\n	&ldquo;Thật ra đ&aacute;nh bắt t&ocirc;m nh&iacute; chẳng thể dựa tr&ecirc;n kinh nghiệm, chỉ l&agrave; may mắn neo t&agrave;u ngay tại luồng của t&ocirc;m di chuyển, thả lưới rồi tr&uacute;ng th&ocirc;i&rdquo; - &ocirc;ng Hạnh bật m&iacute;.</p>\r\n', 'TTO', 0, 507, 'tom hum giong, tom hum nhi, mua tom hum, quang ngai', 0, '0', 0, '', 0);
INSERT INTO `tkb_news` (`news_id`, `cat_id`, `topic_id`, `group_id`, `author`, `add_time`, `edit_time`, `title`, `home_text`, `home_img_file`, `alt_img`, `op_img`, `note_img`, `body_text`, `source`, `allowed_comm`, `hit`, `keywords`, `focus`, `user_name`, `like`, `liked`, `count_comment`) VALUES
(99, 25, 0, 0, 'TẤN ĐỨC', 1329891573, 0, 'Ấp Nhà Lầu', 'Mười mấy năm trước, dù mang tên ấp Nhà Lầu nhưng ở xã Ninh Thạnh Lợi A (huyện Hồng Dân, Bạc Liêu) ngó quanh vẫn kiếm không nổi cái nhà lầu nào. Nhiều người đến đây vẫn cứ thắc mắc mãi về cái tên khá lạ này giữa “đồng chó ngáp”.', 'ap_nha_lau_ninh_thanh_loi.jpg', '', 0, '', '<p class="\\">\r\n	Cái tên gây thắc mắc này kéo dài từ 15 năm trước ngược về hồi lâu lắc, khoảng năm 1945. Đó là giữa vùng đồng không mông quạnh, dân độn trâu (giữ trâu thuê mùa nước nổi) nghe nói có xóm nhà lầu, xắn quần lội vô coi nhưng tìm hoài chỉ thấy toàn nhà tre vách lá.</p>\r\n<p class="\\">\r\n	Tức mình, có người hỏi mấy lão nông mới té ra: “Ngày xa xưa, trong xóm có một căn nhà gác gỗ của ông Cả Trí, dân xứ khác, tới khai hoang. Nghe đâu hồi Cách mạng Tháng Tám, ngôi nhà đã được tháo ra lấy cột cắm xuống lòng kênh Phó Sinh - Cạnh Đền chạy ngang qua làng để ngăn tàu Tây”.</p>\r\n<p class="\\">\r\n	Bởi vậy sau này không còn căn nhà lầu nào, nhưng địa danh ấp Nhà Lầu hữu danh vô thực vẫn tồn tại suốt mấy chục năm trong tiếc nuối của người dân địa phương và gây tò mò cho người xứ khác.</p>\r\n<p class="\\">\r\n	<strong>Kiếm cho ra cái nhà lầu</strong></p>\r\n<p class="\\">\r\n	Và cái tên ấp Nhà Lầu ấy chắc sẽ còn cắc cớ hoài, nếu không có một thanh niên địa phương tức mình tuyên bố: “Phải tính cách làm giàu, cất cho được cái nhà lầu, chớ không thì thiên hạ coi dân ấp Nhà Lầu này ra gì”. Để rồi không chỉ một, mà có tới cả trăm triệu phú, tỉ phú ào ạt xuất hiện trên vùng đất một thời được mệnh danh là “xứ độn trâu”, là “đồng chó ngáp”.</p>\r\n<p class="\\">\r\n	Người thanh niên đó bây giờ đã bước qua tuổi 56 - ông Lê Văn Nguyên (Năm Nguyên), ngụ ấp Nhà Lầu 1. Bên vuông tôm làm nên sản nghiệp, ông Năm Nguyên nhớ lại: “20 năm trước vùng này còn là cánh đồng hoang. Mấy tháng mùa lũ nước lêu bêu, dân sống bằng nghề giữ trâu thuê. Mùa khô nước mặn tràn vô, phèn dậy đỏ nước”. Những năm 1980, tuổi thanh niên của ông Năm trôi qua trên chiếc ghe thương hồ mua bán hàng tạp hóa.</p>\r\n<p class="\\">\r\n	“Một lần, đâu khoảng cuối năm 1985, khi chèo ngang Đại Ngãi (Sóc Trăng), tui nhìn thấy căn nhà gác gỗ của đôi vợ chồng cất cặp mé sông mà thèm nhỏ nước miếng. Nghĩ thấy tủi, mang tiếng ở ấp “nhà lầu” mà mãi tới gần ba mươi tuổi mới biết mặt mũi cái nhà lầu nó ra làm sao. Càng nghĩ tui càng quyết tâm phải tính cách làm ăn cho khấm khá, cất nhà lầu ngay tại xóm” - ông Năm kể.</p>\r\n<p class="\\">\r\n	Vậy là đi đâu, thấy người ta làm gì ông Năm cũng để ý học hỏi. Rồi ông mua mấy chục cây bạch đàn giống về trồng sau nhà, với ý định nếu trong mười năm tới không làm gì thành công thì đốn bạch đàn cất nhà lầu giống như nhà ông Cả Trí ngày xưa được cha ông kể.</p>\r\n<p class="\\">\r\n	Một dịp tình cờ, ông Năm nghe người ta nói dân vùng ven biển nuôi tôm sú có lãi lắm. Ông về bàn với hai người em là ông Bảy Tây và ông Tám Miên bán đồ đạc trong nhà, hùn tiền lại kéo nhau đến Cái Đôi Vàm (Cà Mau) nuôi tôm.</p>\r\n<p class="\\">\r\n	Sau mấy vụ đầu bết bát, không nản chí, anh em ông Năm đã biết cách dùng vôi bột “dằn phèn” cho ổn định, trồng cây năng tạo lớp mùn hữu cơ làm thức ăn và chỗ trú ngụ cho tôm. Tháng 9-1995, tròn mười năm sau ngày vạch mục tiêu “nhà lầu”, ông Năm đã cất xong căn nhà hai tầng bề ngang 6m, dài gần 20m, tốn hơn 40 lượng vàng.</p>\r\n<p class="\\">\r\n	Đây cũng là căn nhà lầu kiên cố đầu tiên của xã, sau 50 năm kể từ lúc căn nhà lầu bằng gỗ định danh cho ấp Nhà Lầu bị tháo dỡ.</p>\r\n<p class="\\">\r\n	Năm 1996, tới lượt ông Bảy Tây cất thêm một căn nhà lầu. Rồi lần lượt cả chục, cả trăm căn nhà kiên cố với chi phí lên tới cả tỉ đồng/căn xuất hiện nhờ nguồn thu từ việc nuôi tôm quảng canh, dọc đường liên ấp Nhà Lầu 1 - Nhà Lầu 2. Mọi người thở phào không còn lo ấp Nhà Lầu bị thu hồi “danh hiệu” nữa!</p>\r\n<p class="\\">\r\n	Giờ thì không chỉ ông Năm Nguyên mà trong xã có rất nhiều câu chuyện làm giàu, cất nhà lầu được người dân nhắc tới như niềm tự hào của ấp Nhà Lầu từ xa xưa. Câu chuyện về quá trình vươn lên của ông Nguyễn Thành Lập - 40 tuổi, cán bộ tuyên giáo, tổ chức của xã Ninh Thạnh Lợi A - được nhiều người dân trong xã nhắc tới như một hình mẫu.</p>\r\n<p class="\\">\r\n	Năm 1997, ông khởi nghiệp với 5.000m2 đất trồng khóm do cha mẹ vợ chia cho. Làm ban ngày chưa đủ, đêm ông đi soi cá dọc các con kênh ngang trong ấp. Vừa làm ông vừa tích cóp để có ít vốn thuê máy Kobe móc vuông, san liếp, xử lý phèn trên phần đất ít ỏi của mình nhằm chuyển qua nuôi tôm sú.</p>\r\n<p class="\\">\r\n	Qua hơn mười năm, vợ chồng ông đã cất được căn biệt thự cả tỉ đồng và mua thêm hơn 14ha đất. Với mô hình tôm cua cá kết hợp, năm nay gia đình ông Lập đã thu lãi hơn 700 triệu đồng.</p>\r\n<p class="\\">\r\n	Đặc biệt, dù nuôi trên diện tích khá lớn nhưng ông Lập không phải mất nhiều thời gian chăm sóc tôm, cá. Bởi nuôi quảng canh không cần phải cho ăn, tới thời điểm thu hoạch ông chỉ cần đặt vài cái lú (dụng cụ bắt tôm), lọp (bắt cua, cá) trong vuông nuôi.</p>\r\n<p class="\\">\r\n	Mỗi sáng sớm, ông Lập chỉ cần mất chưa đầy một giờ để thăm qua một lượt, bắt tôm cá lên cân cho lái tới mua tận nhà là xong. Thời gian còn lại trong ngày, ông dành hết cho việc đi gặp dân vận động chuyển đổi cây trồng vật nuôi phù hợp với điều kiện đất đai của từng người. “Thằng Lập làm rất giỏi nên nói gì bà con cũng nghe” - ông Phạm Văn Bảo (Năm Bảo, 82 tuổi), “vua” độn trâu một thời, cũng là nông dân sản xuất giỏi ở ấp Nhà Lầu 1, nhận xét.</p>\r\n<p class="\\">\r\n	Không chỉ có ông Nguyễn Thành Lập, hầu hết người dân, cán bộ của năm ấp trong toàn xã Ninh Thạnh Lợi A, rồi chủ tịch, phó chủ tịch hội nông dân và nhiều cán bộ chủ chốt của xã cũng là những người có kinh nghiệm cả chục năm vừa làm việc cơ quan, vừa tranh thủ thời gian tăng gia sản xuất tại nhà. Thậm chí có nhiều người còn là nông dân giỏi trước khi là cán bộ ấp, xã. Nhiều người trong số họ đã phấn đấu từ nghèo khó vươn lên khá giàu.</p>\r\n<p class="\\">\r\n	<strong>Nông dân đổi đời</strong></p>\r\n<p class="\\">\r\n	“Đó là một lợi thế vì có làm giỏi hơn dân thì nói dân mới nghe. Mặt khác, khi cán bộ đã có đời sống kinh tế ổn định sẽ an tâm, tập trung hết công sức cho việc cơ quan” - ông Nguyễn Hồng Thái, bí thư Đảng ủy xã Ninh Thạnh Lợi A, lý giải.</p>\r\n', 'TTO', 0, 682, 'ap nha lau, xa ninh thanh loi, nong dan, lam giau, nuoi tom, bac lieu, loc ninh', 0, '0', 0, '', 0),
(143, 22, 0, 0, '', 1330599171, 0, 'Mỹ: Chế biến cá da trơn tháng 1/2012 giảm 26 phần trăm', 'Chế biến cá da trơn nuôi của Mỹ trong tháng 1/2012 đạt 25,8 triệu tấn, giảm 26% so với tháng 1/2011. Giá trung bình cá da trơn trả cho các nhà sản xuất là 1,25 USD/pao, giảm 0,3 xen so với tháng 12/2011 và tăng 31,7 xen so với cùng kỳ năm ngoái.', 'ca_lang_ca_da_tron.jpg', 'ca_lang_ca_da_tron', 0, '', '<p>\r\n	Tiêu thụ cá da trơn chế biến đạt 13,4 triệu pao trong tháng 1/2012, giảm 26% so với tháng 1/2011; tiêu thụ cá da trơn tươi đạt 4,92 triệu pao, giảm 22% và chiếm 37% tổng lượng tiêu thụ cá da trơn; tiêu thụ cá da trơn đông lạnh đạt 8,5 triệu pao, giảm 28% và chiếm 63% tổng lượng tiêu thụ cá da trơn.</p>\r\n<p>\r\n	Tiêu thụ cá da trơn nguyên con chiếm 20%, philê chiếm 55%, cắt miếng, cắt quân cờ và các sản phẩm giá trị gia tăng khác chiếm 25%. Lượng cá trong kho giảm 2% so với tháng 12/2011 nhưng tăng 14% so với tháng 1/2011.<br />\r\n	So với tháng 1/2011, giá trung bình cá da trơn tươi và đông lạnh tháng 1/2012 là 3,43 USD/pao, tăng 59 xen; giá cá da trơn tươi là 3,45 USD/pao, tăng 76 xen; giá cá da trơn tươi nguyên con là 2,44USD/pao, tăng 54 xen; giá cá da trơn philê tươi là 4,73 USD/pao, tăng 1,26 USD.</p>\r\n<p>\r\n	Giá trung bình cá da trơn đông lạnh là 3,42 USD/pao, tăng 49 xen so với tháng 1/2011; giá cá da trơn đông lạnh nguyên con lột da là 3,04 USD/pao, tăng 63 xen; giá cá da trơn đông lạnh philê là 4,26 USD/pao, tăng 96 xen.<br />\r\n	NK cá nước ngọt Ictalurus spp., Pangasius spp., và cá da trơn khác thuộc bộ Siluriformes để tiêu thụ trong tháng 12/2011 đạt 22,6 triệu pao, tăng 34% so với tháng 12/2010 và từ các nguồn chính là Cămpuchia, Trung Quốc, Thái Lan và Việt Nam. Riêng cá Ictalurus spp., Mỹ NK 2,11 triệu pao từ Trung Quốc.</p>\r\n<p>\r\n	Tháng 12/2011, Mỹ XK 51,4 nghìn pao cá da trơn philê, trong đó 45,5 nghìn pao sang Canađa, còn lại sang St. Kitts-Nevis. Mỹ còn XK 3,6 nghìn pao cá da trơn philê đông lạnh sang Bahamat.</p>\r\n<p>\r\n	Năm 2011, Mỹ chế biến 334 triệu pao cá da trơn nuôi, giảm 29% so với 472 triệu pao năm 2010. Giá trung bình cá da trơn trả cho các nhà sản xuất là 1,17 USD/pao, giảm 37% so với 80,1 xen của năm trước.</p>\r\n<p>\r\n	Năm 2011, Mỹ tiêu thụ 167 triệu pao cá da trơn chế biến, giảm 28% so với 232 triệu pao năm 2010. Giá trung bình cá da trơn chế biến là 3,39 USD/pao, tăng 89 xen so với năm trước.</p>\r\n<p>\r\n	Lượng cá trong kho thời điểm cuối năm 2011 đạt 12,9 triệu pao, tăng 5% so với 12,3 triệu pao năm trước.</p>\r\n<table border="1" cellpadding="0" cellspacing="0" style="line-height:19px;width:660px;" width="661">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="10" style="width:660px;height:10px;">\r\n				<p align="center">\r\n					<strong>Khối lượng và giá cá da trơn nuôi tại Mỹ, 2011 - 2012</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan="3" style="width:90px;height:20px;">\r\n				<p align="center">\r\n					&nbsp;</p>\r\n				<p align="center">\r\n					&nbsp;</p>\r\n				<p align="center">\r\n					Tháng</p>\r\n			</td>\r\n			<td colspan="3" style="width:192px;height:20px;">\r\n				<p align="center">\r\n					Hàng tháng</p>\r\n			</td>\r\n			<td colspan="3" style="width:206px;height:20px;">\r\n				<p align="center">\r\n					Lũy kế từ đầu năm</p>\r\n			</td>\r\n			<td colspan="3" style="width:171px;height:20px;">\r\n				<p align="center">\r\n					Giá</p>\r\n				<p align="center">\r\n					(USD/pao)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="width:110px;height:5px;">\r\n				<p align="center">\r\n					KL (nghìn pao)</p>\r\n			</td>\r\n			<td rowspan="2" style="width:81px;height:5px;">\r\n				<p align="center">\r\n					2012/2011 (%)</p>\r\n			</td>\r\n			<td colspan="2" style="width:120px;height:5px;">\r\n				<p align="center">\r\n					KL (nghìn pao)</p>\r\n			</td>\r\n			<td rowspan="2" style="width:85px;height:5px;">\r\n				<p align="center">\r\n					2012/2011 (%)</p>\r\n			</td>\r\n			<td rowspan="2" style="width:54px;height:5px;">\r\n				<p align="cent