INSERT INTO `tkb_qa_question` (`id`, `cat`, `topic`, `title`, `content`, `user_id`, `add_time`, `ip`, `like`, `liked`, `hit`, `active`, `report`, `reported`) VALUES
(1, 1, 11, 'Cá có ngủ không?', '<p>\r\n	Khi quan sát ở bể cá, tôi thấy đôi khi chúng đứng yên một chỗ, vây chỉ cử động nhẹ. Có phải khi đó là cá đang ngủ không?</p>\r\n', 39, 0, '', 3, '39|39|11661|', 384, 1, 0, ''),
(2, 2, 2, 'pH nuôi tôm sú?', '<p>\r\n	Tôi nuôi tôm sú quảng canh cải tiến ( 8 con/m<sup>2</sup>), không có ao chứa, sau khi nuôi 30 ngày màu nước bình thường pH dao động từ 7.1-7.5 (không quá 0.5 ngày đêm). Như vậy có bình thường không? Có cách gì cải thiện tốt hơn không?</p>\r\n', 39, 0, '', 3, '39|39|', 269, 0, 0, ''),
(3, 1, 11, 'Thuốc diệt ốc?', '<p>\r\n	Trong quá trình nuôi thấy xuất hiện ốc, cá và sứa trong ao, cạnh tranh thức ăn và tiết chất nhày. Có thuốc gì điều trị?</p>\r\n', 39, 0, '', 5, '39|39|', 302, 1, 0, ''),
(5, 1, 2, 'Yếu tố ảnh hưởng tôm lột xác?', '<p>\r\n	Tôm sú lột xác theo chu kỳ nào? các yếu tố ảnh hưởng đến lột xác của tôm sú?</p>\r\n', 39, 1363715051, '', 2, '39|39|', 255, 0, 0, ''),
(6, 5, 4, 'Món chế biến cá lóc?', '<p>\r\n	Những món cá lóc nào ngon nhất?<br />\r\n	Cách chế biến?</p>\r\n', 39, 1363716940, '', 2, '39|39|', 361, 1, 0, ''),
(7, 1, 9, 'Sinh sản cá rô phi?', '<p>\r\n	Chúng tôi nghe nói cá rô phi sinh sản rất nhanh, nhưng địa phương tôi có nhiều người nuôi loài cá này được 2-3 năm (cỡ 3-5 lạng) mà vẫn không thu được cá con. Vui lòng giải thích tại sao? Và hướng dẫn cách nhân giống loài cá này?</p>\r\n', 39, 1363717094, '', 4, '39|39|1|486|', 377, 1, 0, ''),
(8, 4, 4, 'Vốn nuôi cá lóc?', '<p>\r\n	Nuôi cá lóc trong bể xi mang thì tốt hơn nuôi trong ao đất hay không?<br />\r\n	Tôi cần có bao nhiêu vốn để nuôi loài cá này?</p>\r\n', 39, 1363717699, '', 3, '39|39|1|', 366, 1, 0, ''),
(9, 2, 4, 'Bệnh cá lóc và cách phòng?', '<p>\r\n	Tôi nuôi cá lóc trong bể bạt. Nhưng cá có dấu hiệu lở loét từng mảng trên thân. Xin hỏi đây là bệnh gì? Và cách phòng trị?</p>\r\n', 39, 1363718134, '', 4, '39|39|1|748|', 356, 1, 0, ''),
(10, 4, 0, 'Nuôi cá lóc bể bạt?', '<p>\r\n	Tôi đang chuẩn bị thiết kế bể bạt để nuôi cá bống tượng thương phẩm. Rất mong được hướng dẫn thêm kỹ thuật thiết kế bể nuôi phủ bạt nilon cho có hiệu quả. Thành thật cảm ơn.</p>\r\n', 39, 1363890186, '', 3, '39|39|1|', 215, 1, 0, ''),
(12, 2, 2, 'Cách trị tôm bị dính chân?', 'Ao tôm của tôi được xây dựng trên nền đất giàu sulffate acid (phèn tiềm tàng). Khoảng 2-3 tuần sau khi thả, khá nhiều tôm bị cong chân (dính chân) không thể rút khỏi vỏ khi lột, đặc biệt là sau mỗi trận mưa lớn. Có cách nào điều trị ?', 17, 1368548828, '', 1, '7603|', 518, 1, 0, ''),
(13, 3, 0, 'Lấy não thùy cá', '<p>\r\n	Có bác nào bít cách lấy não thùy thể cá chép không? chỉ em cách với.</p>\r\n', 6317, 1369185014, '', 0, '', 713, 1, 0, ''),
(14, 4, 0, 'Kỹ thuật nuôi cá Tầm', '<p>\r\n	Cho em hỏi cá tầm nuôi ở nhiệt độ nào là thích hợp nhất? Cách xây dựng công trình ra sao để phù hợp với con cá tầm? Em hiện đang ở Lục Nam - Bắc Giang thì có thể mua giống cá tầm ở đâu?</p>\r\n', 6317, 1369877711, '', 0, '', 196, 1, 0, ''),
(15, 6, 0, 'Sản xuất tảo khô', 'Hiện nay một số loại tảo như Spilurina hay Chlorella...có giá trị dinh dưỡng cao được &nbsp;nuôi và &nbsp;chế biến dưới dạng khô,ép viên..Vậy qui trình nuôi cấy và chế biến như thế nào. Ai biết cho em xin một các tài liệu về qui trình chế biến tảo khô. Cần những thiết bị gì? Có các công đoạn như thế nào?Chân thành cảm ơn', 8091, 1371988121, '', 1, '8091|', 273, 1, 0, ''),
(16, 2, 3, 'Sử dụng Nano bạc phòng chữa bệnh cho Thủy sản', '<p>\r\n	Công ty chúng tôi chuyên bán những chế phẩm&nbsp;N200&nbsp;phục vụ cho sát trùng chuồng trại, diệt trừ bệnh trồng trọt, Thủy hải sản -&nbsp;HIỆU QUẢ CAO, THÂN THIỆN VỚI MÔI TRƯỜNG và AN TOÀN CHO NGƯỜI SỬ DỤNG.<br />\r\n	<br />\r\n	- &nbsp;Dưới tác dụng của các hạt&nbsp;Nano bạc, tế bào của hơn&nbsp;650 loại vi khuẩn bị phá hủy và tiêu diệt, ngoài ra Nano bạc còn có tính năng ngăn mùi hôi.<br />\r\n	Nano bạc&nbsp;có đặc tính kháng khuẩn và&nbsp;ngăn ngừa vi khuẩn phát sinh tới 99,99%,. Khi cho tiếp xúc với Nano bạc trong vòng 5 phút thì hầu hết những vi khuẩn sống đều bị tiêu diệt.<br />\r\n	- &nbsp;Nano bạc sở hữu khả năng tuyệt vời để&nbsp;tiêu diệt vi khuẩn gấp 200 lần&nbsp;so với bạc kim loại và giúp chữa lành các vết thương thông qua công nghệ nano.&nbsp;<br />\r\n	<br />\r\n	Đối với thủy sản nano bạc có công dụng như thế nào?</p>\r\n<ul>\r\n	<li>\r\n		Phòng và diệt nguồn bệnh trong ao nuôi, ổn định màu nước, khử mùi hôi tanh của nước</li>\r\n	<li>\r\n		Hạn chế cá ăn nổi và các bệnh như đốm đỏ, đốm trắng, bệnh nấm thủy my, nấm mang, nấm bào tử… đối với những ao có chất thải hữu cơ từ phân&nbsp;gia súc&nbsp;và gia cầm</li>\r\n	<li>\r\n		Đặc biệt N200 không phụ thuộc vào môi trường nước mặn hay ngọt, không bị phân hủy ở nhiệt độ cao, là một chất trơ sử dụng&nbsp;thích hợp trong mọi môi trường.</li>\r\n</ul>\r\n<p>\r\n	Phòng bệnh:<br />\r\n	Pha 100ml vào 15 lít nước, té đều cho 150m3 ao nuôi thủy sản<br />\r\n	Chu kì: 4 tuần/lần<br />\r\n	Khi có hiện tượng nước bị ô nhiễm:<br />\r\n	Trong giai đoạn cá ăn nổi ( mùa mưa, tháng 3-4, tháng 8-9) pha 150ml vào 15 lít nước, té đều cho 150m3 ao nuôi.<br />\r\n	Trong khi cho ăn bằng thức ăn công nghiệp, thấy nước đục và có mùi hôi thối, cá ăn nổi nhiều: pha 200ml vào 15 lít nước, té đều cho 150m3 ao nuôi<br />\r\n	Chu kì: 1-2 lần/ tuần<br />\r\n	Giai đoạn phát dịch:<br />\r\n	Pha 150ml vào 15 lít nước, té đều cho 150 m3 ao nuôi<br />\r\n	Chu kì: 1-2 tuần/lần</p>\r\n<p>\r\n	Sản phẩm được Viện Vệ sinh dịch tễ Trung Ương&nbsp; - Bộ Y tế chứng nhận:</p>\r\n<ul>\r\n	<li>\r\n		Khả năng diệt khuẩn</li>\r\n	<li>\r\n		An toàn cho người và vật nuôi</li>\r\n	<li>\r\n		Thân thiện với môi trường</li>\r\n</ul>\r\n<p>\r\n	Giá :150.000đ/1 chai 450ml</p>\r\n<p>\r\n	MỌI THẮC MẮC VÀ HỖ TRỢ TƯ VẤN VUI LÒNG GỌI:</p>\r\n<p>\r\n	Phòng tư vấn kỹ thuật Nông nghiệp:&nbsp;<br />\r\n	Ms. NGA - 0975.204.283</p>\r\n', 8151, 1372302128, '', 0, '', 224, 1, 0, ''),
(17, 1, 0, 'Vì sao động vật có trí não mà chu kỳ sống ngắn hơn thực vật?', '<p>\r\n	Theo quy luật sinh tồn thì rõ ràng loài nào có trí não, tức là có nhận thức thì sẽ thích nghi hơn với môi trường.&nbsp;Vậy mà sao thực vật đứng ỳ một chỗ và không có tư duy thì có nhiều loài sống lâu hơn động vật. Ở động vật, loài được cho là sống lâu nhất là con rùa cũng chỉ sống vài trăm năm.&nbsp;<br />\r\n	Liệu rằng theo quy luật sinh tồn thì tương lai động vật có sống lâu hơn không, giống như con người ngày càng sống thọ hơn vậy?</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 748, 1372412339, '', 1, '748|', 185, 1, 0, ''),
(18, 2, 0, 'Bệnh vi khuẩn Aeromonas di động', '<p>\r\n	Có cách nào trị bệnh vi khuẩn aeromonas tốt nhất để giảm thiệt hại không các bác?</p>\r\n', 6317, 1372412846, '', 0, '', 197, 1, 0, ''),
(19, 4, 0, 'Nuôi cá Chình', '<p>\r\n	Cá Chình sống trong môi trường nước ngọt hay nước mặn? Nuôi cá Chình có dễ không?</p>\r\n', 6317, 1372413233, '', 0, '', 201, 1, 0, ''),
(20, 6, 0, 'Chỉ tiêu đánh giá nguồn nước mặt', 'Trong bảng kết quả phân tích các chỉ tiêu của nguồn nước mặt (như sông, suối, ao hồ) thường bao gồm có các chỉ tiêu như N_NO2-, N_NO3-, N_NH3.<br />Theo tôi biết cả ba chỉ tiêu trên đều chứa thành phần nitơ. Vậy ba chỉ tiêu trên khác nhau chỗ nào? Tại sao lại phân tích ba chỉ tiêu đó? Mỗi chỉ tiêu thì đánh giá vấn đề gì của nguồn nước mặt?', 748, 1372593283, '', 0, '', 217, 1, 0, ''),
(21, 6, 0, 'Tại sao luôn hình thành xoáy nước khi xả khỏi bồn?', 'Khi ta xả nước từ bồn nước bằng lỗ thoát đáy thì luôn tạo thành các dòng xoáy, cho dù ta có làm mọi cách để phá các xoáy nước đó đi, sau đó chúng lại xuất hiện trở lại. Vì sao thế?', 748, 1372593576, '', 0, '', 283, 1, 0, ''),
(22, 6, 0, 'Vi sinh cắt tảo', '<p>\r\n	Sử dụng vi sinh để giảm bớt tảo nên sử dụng vào lúc nào để đạt hiệu quả cao nhất?.</p>\r\n', 748, 1372604401, '', 0, '', 327, 1, 0, ''),
(23, 1, 3, 'Lột xác ở tôm thẻ', '<p>\r\n	Một số nguyên nhân làm tôm thẻ khó lột vỏ và cách khắc phục?</p>\r\n', 748, 1372604553, '', 0, '', 335, 1, 0, ''),
(24, 2, 0, 'Bệnh ở ếch', 'Ếch nuôi thường mắc phải những bệnh nào?', 748, 1372604915, '', 0, '', 222, 1, 0, ''),
(25, 4, 2, 'Nuôi tôm kết hợp cá rô phi', 'Mô hình nuôi tôm kết hợp cá rô phi đem lại lợi ích gì?', 748, 1372605252, '', 0, '', 261, 1, 0, ''),
(26, 2, 0, 'Bệnh long đầu trên tôm hùm', '<p>\r\n	Nguyên nhân gây ra bệnh long đầu và cách chữa trị bệnh này ở tôm hùm?</p>\r\n', 748, 1372605462, '', 0, '', 203, 1, 0, ''),
(27, 1, 0, 'Cá không có bong bóng thì bơi được không?', '<p>\r\n	Tại sao có con cá có bong bóng, có con cá không có bong bóng, nhưng tất cả chúng đều bơi lại được dưới nước?</p>\r\n', 748, 1373691643, '', 0, '', 373, 1, 0, ''),
(28, 2, 0, 'Cách xử lý môi trường nước ôi nhiễm nấm tảo, vi khuẩn, vi sinh vật có hại cho tôm cá. chữa bênh cho tôm cá', '<p>\r\n	hiện nay do sự phát truyển ồ ạt của các ao hồ nuôi thủy sản mà chưa có một quy hạch nhất đinh cùng với việc cho thủy sản ăn nhiều loại bột thức ăn khác nhau dễ dẫn điến phát sinh vi khuẩn nấm tảo, virut các loại bênh cho tôm cá gây ảnh hưởng rất lớn đến thu năng xuất thu nhập của bà con. năm bắt được những khó khăn của bà con chúng tôi nghiên cứu và chế xuất ra dịch dịch bạc, hay còn gọi là nano bạc cho xử lý các vấn đề hiệu quả cho bà còn</p>\r\n<p>\r\n	Vậy Nano bạc khả năng gì ?</p>\r\n<p>\r\n	Dưới tác dụng của các Ion bạc, tế bào của hơn 650 loại vi khuẩn bị phá hủy và tiêu diệt, ngoài ra&nbsp;NANO Bạc&nbsp;còn có tính năng ngăn mùi hôi.&nbsp;NANO Bạc&nbsp;có đặc tính kháng khuẩn và ngăn ngừa vi khuẩn phát sinh tới 99,99%,. Khi cho tiếp xúc với&nbsp;NANO bạc&nbsp;trong vòng 5 phút thì hầu hết những vi khuẩn sống đều bị tiêu diệt.&nbsp;</p>\r\n<p>\r\n	Cơ chế diệt khuẩn của NANO bạc:&nbsp;Nano bạc với kích cỡ phân tử từ 2~5 nano mét, tác dụng trực tiếp với vi khuẩn gây hại. Nó bao lấy trực tiếp tế bào của vi khuẩn, và phá vỡ cấu trúc tế bào, vô hiệu hóa sự phát triển và sinh trưởng của chúng.</p>\r\n<p>\r\n	sản phẩm nano chúng tôi dùng trong các lĩnh vực của nông nghiệp</p>\r\n<p>\r\n	Đối với thủy sản</p>\r\n<p>\r\n	Phòng và diệt nguồn bệnh trong ao nuôi, ổn định màu nước, khử mùi hôi tanh của nước</p>\r\n<p>\r\n	Hạn chế cá ăn nổi và các bệnh như đốm đỏ, đốm trắng, bệnh nấm thủy my, nấm mang, nấm bào tử… đối với những ao có chất thải hữu cơ từ phân gia súc và gia cầm</p>\r\n<p>\r\n	Đặc biệt không phụ thuộc vào môi trường nước mặn hay ngọt, không bị phân hủy ở nhiệt độ cao, là một chất trơ sử dụng thích hợp trong mọi môi trường.</p>\r\n<p>\r\n	đặc biệt sử dụng nano bạc sẽ dúp tôm cá sinh trưởng mau hơn nuôi được nhiều hơn. tăng năng suất hiệu quả cao, tôm cá đảm bảo vệ sinh oan toàn.</p>\r\n<p>\r\n	Đối với chăn nuôi</p>\r\n<p>\r\n	Sát khuẩn chuồng trại, khử mùi hôi chuồng trại, tiêu diệt mầm bệnh, các loại vi khuẩn, vi rút có hại cho vật nuôi, cực kỳ hiệu quả trong việc phòng dịch và dập dịch</p>\r\n<p>\r\n	Hoàn toàn không độc hại cho người và vật nuôi.</p>\r\n<p>\r\n	Bà Con Lưu ý khi mua sản phẩm nano bạc là bà con chú ý đến nồng độ của dung dịch nano bạc là bao nhiêu có rất nhiều công ty nồng độ ppm và kích thước các hạt nano. kích thước càng nhỏ khả năng diệt khuẩn càng cao.</p>\r\n<p>\r\n	chúng tôi chuyên cung cấp dung dịch nano bạc với số lượng lớn tốt hơn những những dụng dịch cùng loại nhập khẩu với nồng độ có thể lên tới 1000ppm kích thước các hạt nano la &lt;10nm siêu nhỏ</p>\r\n<p>\r\n	chúng tôi cung cấp với số lượng lớn nhanh chóng. Và hỗ trợ thị kỹ thuật pha chế sử dụng.</p>\r\n', 8219, 1373771037, '', 2, '8219|8219|', 448, 1, 0, ''),
(29, 1, 0, 'Vận chuyển cá Tầm bằng phương pháp ngủ đông', 'Cho biết Phương pháp vận chuyển cá tầm bằng phương pháp ngủ đông./.', 486, 1374725890, '', 0, '', 436, 1, 0, ''),
(30, 7, 0, 'anh/chị cho em hỏi về Rong Nho', 'Hiện nay em đang làm đồ án nghiên cứu về Rong nho, nhưng lại khá xa so với vùng nguyên liệu nên không thể cung cấp liên tục và kịp thời được. Em ở tpHCM. Anh/chị nào biết chỗ cung cấp uy tín 1 xíu có thể chỉ cho em được không? em cám ơn', 8410, 1374757323, '', 1, '748|', 331, 1, 0, ''),
(31, 6, 0, 'Cải thiện độ kiềm', '<p>\r\n	Các anh chị cho hỏi cách nào để cải thiện độ kiềm cao? Xin cám ơn.</p>\r\n', 5194, 1375273366, '', 3, '5194|748|5194|', 310, 1, 0, ''),
(260, 4, 1, 'Hướng đi nào cho ngành nuôi ngao ở Nam Định?', '<p>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;<em>Sản phẩm&nbsp;ngao Giao Thủy&nbsp;hiện đã có mặt không chỉ ở thị trường trong nước, tại các hệ thống siêu thị lớn, chuỗi cửa hàng thực phẩm sạch, nhà hàng, khách sạn... mà còn vươn ra các thị trường nước ngoài như thị trường Trung Quốc, thị trường EU thông qua các nhà máy chế biến xuất khẩu phía nam.</em></p><p><i>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</i>Nghề nuôi ngao<i>&nbsp;ở Giao Thủy - Nam Định&nbsp;bắt đầu hình thành từ năm 1992 và phát triển trải qua nhiều giai đoạn thăng trầm. Đến năm 2004 có bước phát triển mạnh, thu nhập tăng nhanh, trung bình mỗi năm đạt sản lượng 23- 25 ngàn tấn/ha, riêng năm 2015 sản lượng đạt trên 28 ngàn tấn và có nhiều hộ đạt trên 40 tấn/ha như hộ ông Nguyễn Văn Cửu ở Giao Xuân, ông Thực, ông Hưng. Sản phẩm&nbsp;</i>ngao Giao Thủy<i>&nbsp;hiện đã có mặt không chỉ ở thị trường trong nước, tại các hệ thống siêu thị lớn, chuỗi cửa hàng thực phẩm sạch, nhà hàng, khách sạn... mà còn vươn ra các thị trường nước ngoài như thị trường Trung Quốc, thị trường EU thông qua các nhà máy chế biến xuất khẩu phía nam.</i></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Để nghề nuôi ngao phát triển bền vững, mở rộng thêm nhiều thị trường tiêu thụ trong và ngoài nước, nâng cao thu nhập cho người nuôi trồng, cải thiện đời sống của hàng ngàn người dân trong vùng,&nbsp;tại Hội thảo tìm hiểu nhu cầu thị trường&nbsp;thúc đẩy liên kết chuỗi giá trị và sản xuất bền vững nghề nuôi ngao tỉnh Nam Định diễn ra vừa qua, các đại biểu đã đóng góp ý kiến, thảo luận các vấn đề nhằm tìm ra các giải pháp khắc phục những tồn tại, hạn chế để&nbsp;nghề nuôi ngao&nbsp;tiếp tục duy trì và phát triển bền vững.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hội thảo do Trung tâm Bảo tồn sinh vật biển và Phát triển cộng đồng kết hợp với Chi cục Thủy sản và Hội nhuyễn thể huyện Giao Thủy tổ chức tại Quất Lâm - Giao Thủy - Nam Định. Đến tham dự hội thảo có đồng chí Hoàng Mạnh Hà - Chi cục trưởng Chi cục thủy sản, Sở NN&amp;PTNT Nam Định, bà Nguyễn Thị Thu Huệ - Giám đốc Trung tâm bảo tồn sinh vật và phát triển cộng đồng (MCD).</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nam Định là tỉnh ven biển có diệc tích&nbsp;nuôi ngao&nbsp;lớn nhất Miền Bắc Việt Nam với diện tích trên 1600 ha, chủ yếu tập trung ở hai huyện Giao Thủy và Nghĩa Hưng, trong đó diện tích nuôi ngao chủ yếu ở huyện Giao Thủy, cụ thể là tập trung ở 5 xã vùng đệm Vườn Quốc gia Xuân Thủy. Từ năm 1992, nghề nuôi ngao bắt đầu hình thành và phát triển ở đây, đến năm 2004 đã có bước phát triển mạnh, sản lượng lớn, thu nhập của người nuôi tăng nhanh, trung bình mỗi năm đạt sản lượng 23- 25 ngàn tấn/ha, riêng năm 2015 sản lượng đạt trên 28 ngàn tấn và có nhiều hộ đạt trên 40 tấn/ha như hộ ông Cửu, ông Hưng, ông Thực ở Giao Xuân - Đây là những hộ đã chú trọng chọn bãi, cải tạo nền bãi nuôi, khơi thông dòng chảy tạo môi trường thuận lợi cho ngao phát triển và chọn thời điểm thả giống, quản lý sản phẩm nên năng suất tăng lên qua từng năm. Trong khi đó, toàn tỉnh có nhiều Doanh nghiệp sản xuất theo quy trình khép kín từ sản xuất giống, ương nuôi thương phẩm và tổ chức bao tiêu sản phẩm tới người tiêu dùng như&nbsp;Doanh nghiệp tư nhân Cửu Dung. Do đó, con ngao của tỉnh đã tìm được thị trường tiêu thụ trong và ngoài nước như thị trường Trung Quốc, thị trường EU thông qua các nhà máy chế biến xuất khẩu phía nam và tiêu thụ tại các hệ thống siêu thị trong nước.</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tuy nhiên, nguồn lợi thủy sản, trong đó có con ngao đang ngày một cạn kiệt, nguyên nhân chính là do môi trường sinh thái vùng nuôi đang bị suy thoái cùng với mật nuôi thả dày dẫn đến thiếu nguồn thức ăn cho ngao; các khâu liên kết giữa phát triển nguồn giống, nuôi thương phẩm và tổ chức tiêu thụ sản phẩm chưa chặt chẽ nên sản phẩm bị ép giá, thị trường tiêu thụ bị thu hẹp. Trong khi đó nhận thức của nhân dân trong bảo vệ môi trường còn hạn chế, việc xả rác thải trên bãi biển và các khu vực nuôi đã gây ô nhiễm môi trường là những nguyên nhân mà trước đây EU công nhận sản phẩm&nbsp;ngao&nbsp;của tỉnh ở loại B, nay đã giảm xuống còn loại C cũng đồng nghĩa với con ngao có khả năng không còn được tiêu thụ tại thị trường Châu Âu.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Để nghề nuôi ngao phát triển bền vững, tìm và hướng tới nhiều thị trường tiêu thụ trong và ngoài nước, nâng cao thu nhập cho người nuôi trồng thủy sản, tại Hội thảo, các đại biểu đã thảo luận tìm các giải pháp cho nghề nuôi ngao, trong đó cùng với cần có sự tham gia vào cuộc của cấp ủy, chính quyền và các ngành địa phương trong việc xây dựng thương hiệu, cần quy hoạch vùng nuôi phù hợp, áp dụng khoa học kỹ thuật, cải tạo mặt bãi, nâng dần diện tích nuôi bán thâm canh, thâm canh thương phẩm, đầu tư xây dựng hệ thống thủy lợi cho vùng nuôi ngao tập trung, kiểm soát chặt chẽ chất lượng giống nhập từ tỉnh ngoài vào địa bàn, đồng thời có chính sách khuyến khích nhằm hạn chế việc xuất giống đi nơi khác, vận động các hộ nuôi ngao thành lập các tổ nhóm sản xuất để xây dựng mô hình sản xuất mới như góp cổ phần thuê đất từng vùng nuôi theo nhóm, giảm thiểu vây chắn và chi phí để nâng cao hiệu quả sản xuất; với&nbsp;ngao thương phẩm&nbsp;cần tuyên truyền vận động nhân dân bảo vệ môi trường nhằm củng cố, bảo vệ và mở rộng tầm ảnh hưởng của thương hiệu&nbsp;ngao Nam Định&nbsp;nói riêng, "Ngao Giao Thủy" nói riêng, xác định tiêu chuẩn chất lượng để khẳng định sản phẩm trên thị trường, từng bước đưa sản phẩm ngao của tỉnh xuất bán rộng rãi không chỉ trong nước mà trên thị trường thế giới.</p><p>Thực trạng ngành nuôi&nbsp;ngao thương phẩm&nbsp;ở Nam Định đang gặp không ít khó khăn đang đe dọa đời sống của hơn hàng chục nghìn&nbsp;ngư dân ven biển. Cần lắm những ý kiến đóng góp từ các tổ chức, cá nhân để tìm ra hướng đi cho địa phương.</p>', 15200, 1467101151, '', 2, '39|17|', 744, 1, 0, ''),
(33, 8, 0, 'Nhu cầu dinh dưỡng', 'đối với con cá chép thì nhu cầu vitamin, nhu cầu protein, nhu cầu năng lượng, nhu cầu acidamin, acid béo cần ra sao?<br />', 6317, 1376357274, '', 0, '', 743, 1, 0, ''),
(34, 6, 0, 'Thông tin các sự kiện quốc tế ngành Thủy sản', 'Ban quản trị tepbac.com xin gửi đến bạn đọc những thông tin về các khóa đào tạo ngắn hạn, hội thảo, báo cáo chuyên đề... của quốc tế tại:&nbsp;http://www.aquatt.ie/2011-11-07-09-02-49/aquatt-newsletter/training-news-archives/archive/listid-2-aquatt-training-newsletter-list\r\nChúc các bạn thành công\r\nTrân trọng,\r\nBQT\r\n', 486, 1376537817, '', 3, '748|748|486|', 293, 1, 0, ''),
