(280, 3, 2, 'Cần giúp đỡ', 'Xin tài liệu nuôi tôm', 17, 1561100191, '', 0, '', 1, 1, 0, ''),
(281, 5, 2, 'Cách nấu ăn', 'Cách nấu các món ăn về tôm', 17, 1561100291, '', 1, '17|', 1, 1, 0, ''),
