INSERT INTO `tkb_sys_text` (`id`, `name`, `text`) VALUES
(0, 'alert', 'TEPBAC gửi lời xin lỗi chân thành tới bạn đọc và tất cả thành viên tepbac.com, mấy ngày qua hệ thống xảy ra sự cố sao lưu dữ liệu nên một số thông tin trên TEPBAC gần đây bị mất. Đặc biệt thành viên nào trước đây đã đăng ký nhưng giờ không đăng nhập được thì phải đăng ký lại. Rất tiếc vì sự cố này.');
