(390, 14606, 6, 'Trần Thị Thủy Lam', 'Nhân viên phòng Lab, Nhân viên Văn Phòng, Sale admin', 'Miền Trung và Miền Nam', '<p>\r\n	Nhân viên phụ trách bán hàng tại đại lý thuốc&nbsp;Long An: kiểm tra nước, phân tích trong phòng thí nghiệm, tư vấn cho khách hàng...&nbsp;</p>\r\n<p>\r\n	Nhân viên chăm sóc khách hàng Viettel</p>\r\n', 'Kỹ Sư Thủy sản', '<p>\r\n	Giao tiếp và tạo lập quan hệ</p>\r\n<p>\r\n	Lắng nghe, Tổ chức công việc hiệu quả,&nbsp;Giải quyết vấn đề</p>\r\n<p>\r\n	Làm việc nhóm</p>\r\n<p>\r\n	Tư duy sáng tạo, Đàm phán thuyết phục</p>\r\n<p>\r\n	Quản lý bản thân,&nbsp;Phát triển cá nhân sự nghiệp,&nbsp;</p>\r\n<p>\r\n	Lãnh đạo</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Được làm việc trong môi trường thân thiện và đúng chuyên ngành đã học</p>\r\n<p>\r\n	Mục tiêu nghề nghiệp</p>\r\n<p>\r\n	Học hỏi kinh nghiệm, nâng cao trình độ chuyên môn.</p>\r\n<p>\r\n	Công việc ổn định, gắn bó lâu dài với công ty</p>\r\n', '0938899277', 'thuylam125@gmail.com', 1453443464, 0, 1, 1, 789),
(391, 8830, 3, 'Nguyễn Bảo Toàn', 'Kỹ thuật cá', 'ĐBSCL', '<p>\r\n	Từ 6/2015 - nay.</p>\r\n', 'Đại học', '<p>\r\n	Tự thu thập tự và phân tích để điều trị bệnh trên cá.</p>\r\n', '<p>\r\n	Thu nhập ổn định.</p>\r\n', '0987534241', 'baotoan8998@gmail.com', 1454421758, 0, 1, 0, 431),
(392, 14762, 2, 'Phạm Thị Trâm', 'Kỹ thuật vùng nuôi, phòng lab', 'Miền Tây', '<p>\r\n	1 năm làm việc tại vùng nuôi tôm thẻ và cá&nbsp;</p>\r\n', 'Cao đẳng', '<p>\r\n	Làm việc trực tiếp tại trại/ao nuôi</p>\r\n<p>\r\n	Giao tiếp và tạo lập quan hệ</p>\r\n<p>\r\n	Lắng nghe, Tổ chức công việc hiệu quả,&nbsp;Giải quyết vấn đề</p>\r\n<p>\r\n	Làm việc nhóm</p>\r\n<p>\r\n	Tư duy sáng tạo, Đàm phán thuyết phục</p>\r\n<p>\r\n	Lãnh đạo</p>\r\n', '<p>\r\n	Được làm việc trong môi trường thân thiện và đúng chuyên ngành đã học</p>\r\n<p>\r\n	Mục tiêu nghề nghiệp</p>\r\n<p>\r\n	Học hỏi kinh nghiệm, nâng cao trình độ chuyên môn.</p>\r\n<p>\r\n	Công việc ổn định, gắn bó lâu dài với công ty</p>\r\n', '01226578462', 'pttram93@gmail.com', 1455861541, 0, 1, 0, 452),
