(394, 14883, 2, 'Lê Việt Tân', 'Nhân viên kỹ thuật hoặc kinh doanh thuốc thủy sản', 'Long An', '<p>\r\n	Nắm Vững kỹ thuật nuôi tôm thương phẩm, quản lý môi trường ao nuôi,...</p>\r\n', 'Đại Học', '<p>\r\n	Năng động kiên trì trong công việc,..</p>\r\n', '<p>\r\n	Công việc ổn đinh lâu dài nâng cao kiến thức chuyên ngành,được đào tạo kỹ năng mềm,...</p>\r\n', '01677194711', 'leviettan92@gmail.com', 1456884079, 0, 1, 0, 405),
(395, 9771, 2, 'Phan Văn Tuấn', 'Nhân Viên', 'Tiền Giang, Bến Tre', '<p>\r\n	1 Năm kinh nghiệm nuôi tôm Thương Phẩm</p>\r\n', 'Nuôi trồng Thủy Sản', '<p>\r\n	Làm việc nhóm</p>\r\n', '<p>\r\n	Công việc phù hợp&nbsp;</p>\r\n', '0907100775', 'tuan141191@gmail.com', 1457338142, 0, 1, 0, 367),
(396, 10709, 1, 'Nguyễn Chí Toại', 'Nhân viên kinh doanh', 'Cà mau', '<p>\r\n	3 năm kĩ thuật tôm cua 1 năm nhân viên kinh doanh tôm giống.</p>\r\n<p>\r\n	2 năm nhân viên kinh doanh thức ăn và men thuốc tôm cua giống.</p>\r\n', '10/12', '<p>\r\n	Có kinh nghiệm đi thị trường</p>\r\n', '<p>\r\n	Lương ổn định</p>\r\n', '0888909690', 'Chitoai2905@gmail.com', 1457710227, 0, 1, 0, 274),
(397, 15017, 1, 'Trần Thanh Tuấn', 'Nhân Viên Kinh Doanh', 'Miền Tây', '<p>\r\n	1 năm kinh nghiệp trong farm nuôi</p>\r\n<p>\r\n	1 năm làm thị trường Long An cho công ty Uni president Việt Nam.</p>\r\n', 'Nuôi Trồng Thủy Sản', '<p>\r\n	Linh hoạt trong công việc, khả năng tiếp xúc khách hàng tốt.</p>\r\n<p>\r\n	Có khả năng làm việc độc lập, theo nhóm.</p>\r\n', '<p>\r\n	Mức lương thỏa thuận.&nbsp;</p>\r\n', '0947978001', 'trantuan978001@gmail.com', 1457843624, 0, 1, 0, 370),
(398, 15069, 7, 'Phan Hữu Phước', 'Kiểm Hàng Thủy Sản', 'Đồng Bằng Sông Cửu Long', '<p>\r\n	- Kinh nghiệm làm việc 7 năm.</p>\r\n<p>\r\n	​- Kinh nghiệm kiểm hàng: 2 năm.</p>\r\n', 'TC Chế Biến Thủy Sản', '<p>\r\n	- Giao tiếp tốt, nhanh nhẹn.</p>\r\n<p>\r\n	​- Làm việc nhóm hoặc độc lập.</p>\r\n<p>\r\n	​- Quyết định công việc.</p>\r\n<p>\r\n	​- Lập kế hoạch.</p>\r\n', '<p>\r\n	Được trãi nghiệm; được thể hiện ; được thỏa mãn công việc.​</p>\r\n', '0944288616', 'huuphuocagg@gmail.com', 1458227139, 0, 1, 1, 286),
(399, 15175, 2, 'Huỳnh Thanh Long', 'Nhân viên kỹ thuật', 'miền nam', '<p>\r\n	10 năm&nbsp;</p>\r\n', 'Đại Học', '<p>\r\n	khả năng tạo lập mối quan hệ tốt với khách hàng, nhạy bén trong công việc.</p>\r\n', '<p>\r\n	lương căn bản</p>\r\n', '01632605388', 'huynhthanhlong3010@gmail.com', 1459314434, 0, 1, 1, 338),
(400, 13612, 3, 'Lê Hoàng Thanh', 'Kỹ sư', 'Đồng Tháp', '<p>\r\n	Cao đẳng</p>\r\n', 'Cao đẳng', '<p>\r\n	Bằng A anh văn</p>\r\n', '<p>\r\n	Hưởng theo chế độ nhà nước</p>\r\n', '0964148949', 'Lehoangthanh11061996@gmail.com', 1459755246, 0, 1, 0, 184),
(401, 14876, 6, 'Vương Thanh Duy', 'Nhân viên', 'Ổn định lâu dài', '<p>\r\n	chưa có kinh nghiệm</p>\r\n', 'Cao đẳng', '<p>\r\n	ham học hỏi năng động</p>\r\n', '<p>\r\n	công việc ổn định</p>\r\n', '01648816079', 'thanhduy1995cdnttsk12@gmail.com', 1459861031, 0, 1, 1, 286),
(402, 14468, 6, 'Phạm Hoàng Nho', 'Nhân viên nuôi trồng thủy sản', 'Miền nam', '<p>\r\n	Sinh viên mới ra trường</p>\r\n', 'Cao đẳng', '<p>\r\n	Chiu khó, hoà đồng, thích nghi với môi trường làm việc</p>\r\n', '<p>\r\n	Công việc ổn định</p>\r\n', '0924509965', 'nholop05@gmail.com', 1459869817, 0, 1, 1, 298),
(403, 15335, 2, 'Đoàn Việt Thống', 'Nhân viên kỹ thuật', 'Cà Mau', '<p>\r\n	3 năm làm tổ trưởng tại công ty cổ phần thủy sãn Trung Sơn</p>\r\n', 'Đại học', '<p>\r\n	Kỹ thuật nuôi tốt</p>\r\n', '<p>\r\n	Công việc ổn định</p>\r\n', '0946455922', 'thongca1088@yahoo.com', 1460630555, 0, 1, 1, 326),
(404, 15364, 6, 'Phạm Văn Nam', 'Nghiên cứu- kỹ thuật viên', 'Toàn quốc (đặc biệt miền trung)', '<p>\r\n	-Tôi đã có thời gian thực tập tại:</p>\r\n<p>\r\n	Trung tâm nghiên cứu thực hành thí nghiệm thủy sản - Phú thuận, Huế trong 2 tháng.</p>\r\n<p>\r\n	Công Ty TNHH Đầu Tư Thủy Sản Huy Thuận- Bến Tre trong 4 tháng.</p>\r\n<p>\r\n	-Tôi đã hoàn thành một số đề tài nghiên cứu khoa học cấp khoa, bao gồm:</p>\r\n<p>\r\n	Đánh giá tồn dư kháng sinh ở tôm thẻ chân trắng nuôi tại huyện Phong Điền- tỉnh Thừa Thiên Huế (Chủ nhiệm đề tài)</p>\r\n<p>\r\n	Ảnh hưởng của tỷ lệ nuôi xen ghép cá Dìa- tôm Sú tới tốc độ tăng trưởng Của cá Dìa trong mô hình (Phó chủ nhiệm đề tài)</p>\r\n<p>\r\n	Hoàn thành kỹ thuật giải phẫu can thiệp vào các dị tật trên cá chép Koi (Phó chủ nhiệm đề tài)</p>\r\n', 'Đại học', '<p>\r\n	Tốt nghiệp đại học Nông Lâm Huế, ngành Nuôi Trồng Thủy sản (Chuyên ngành bệnh học thủy sản), Các tín chỉ anh văn loại B, tin học loại A.</p>\r\n<p>\r\n	Có khả năng thực hiện thao tác phòng thí nghiệm thuần thục, nắm được quy trình nuôi tôm thẻ chân trắng,&nbsp;tương tác tốt&nbsp;với cộng đồng.&nbsp;</p>\r\n<p>\r\n	Sử dụng thành thạo các kĩ năng tin học văn phòng&nbsp;(Word, Exel, Power point…),&nbsp;có khả năng đánh máy tốt.</p>\r\n<p>\r\n	Có khả năng sử dụng tiếng anh nâng cao.</p>\r\n<p>\r\n	Có khả năng làm việc dưới áp lực cao.</p>\r\n<p>\r\n	Là người có khả năng giao tiếp tốt với mọi người, thích ứng nhanh với môi trường mới và công việc.</p>\r\n<p>\r\n	Nhiệt tình, có trách nhiệm đối với công việc.</p>\r\n<p>\r\n	Có khả năng tham gia các hoạt động thể dục thể thao, văn hóa văn nghệ (guitar, bóng đá....).</p>\r\n', '<p>\r\n	Là một sinh viên mới ra trường, tôi mong muốn được làm việc phù hợp&nbsp;để có thể vận dụng kiến thức và kĩ năng đã được học, được cống hiến cho quý công ty và phát triển, hoàn thiện bản thân.</p>\r\n', '01657705231', 'vannampham94@gmail.com', 1460923120, 0, 1, 1, 384);
INSERT INTO `tkb_workcv` (`id`, `user_id`, `cat_id`, `name`, `position`, `place`, `experience`, `degree`, `skill`, `request`, `phone`, `email`, `add_time`, `counter`, `allow`, `public`, `hit`) VALUES
(405, 15502, 1, 'Dương Thị Kim Ngân', 'Nhân viên phòng kinh doanh - xuất nhập khẩu', 'TP Đà Nẵng', '<p>\r\n	10/2011- Nay&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>\r\n	Tham gia câu lạc bộ KẾT NỐI THÀNH CÔNG Đại học Nông Lâm Tp Hồ Chí Minh</p>\r\n<p>\r\n	12/2015 – 2/2016:</p>\r\n<p>\r\n	Nhân viên tư vấn – bán hàng Công Ty TNHH Tâm Duy Green – TP Hồ Chí Minh</p>\r\n<p>\r\n	08/2015 – 10/2015</p>\r\n<p>\r\n	Thực tập tại phòng xuất khẩu Công ty Cổ Phần Xuất Nhập Khẩu Thủy Sản Miền Trung – Seaprodex Danang&nbsp;</p>\r\n<p>\r\n	06 /2015 – 08/2015&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>\r\n	Thực tập tại phòng sản xuất kinh doanh Công Ty TNHH Sản Xuất Giống Thủy Sản V.A.L</p>\r\n<p>\r\n	01/2015 – 05/2015</p>\r\n<p>\r\n	Nhân viên Phát Triển Kinh Doanh tại Trung Nguyên No.1 – TP Hồ Chí Minh</p>\r\n<p>\r\n	03/2014 đến 11/2014:</p>\r\n<p>\r\n	Nhân viên thu ngân Phòng khám Đa Khoa Thanh Tâm – TP Hồ Chí Minh</p>\r\n<p>\r\n	03/2013 đến 01/2014 :</p>\r\n<p>\r\n	Nhân viên bán hàng tại cửa hàng Yến Sào Khánh Hòa</p>\r\n<p>\r\n	5/2012 đến 8/2015:</p>\r\n<p>\r\n	Nhân viên quảng cáo ( PG ) các công ty, nhãn hàng : Samsung, Cocacola, Nước hoa IZZI, Trà Ô long Tea + ...</p>\r\n', 'Đại Học', '<p>\r\n	Hoạt động văn nghệ ( từng tham gia các hoạt động văn nghệ ca hát ở Trường Đại Học và hoạt động từ thiện )</p>\r\n<p>\r\n	Sử dụng thành thạo các công cụ tin học văn phòng như: Word, Excel, Powerpoint…</p>\r\n<p>\r\n	Kỹ năng bán hàng</p>\r\n<p>\r\n	Kỹ năng giao tiếp và đàm phán tốt.</p>\r\n<p>\r\n	Kỹ năng làm việc nhóm và linh hoạt trong việc xử lý các tình huống.</p>\r\n<p>\r\n	Khả năng tiếp thu và thích ứng nhanh với môi trường / công việc mới.</p>\r\n<p>\r\n	Có thể làm việc trong môi trường áp lực cao.</p>\r\n<p>\r\n	Có kinh nghiệm trong việc bán hàng, tư vấn sản phẩm và chăm sóc khách hàng</p>\r\n', '<p>\r\n	Tìm được công việc trong một môi trường Kinh doanh Xuất Nhập khẩu Thủy Sản. Một công&nbsp;việc mà tôi có thể vận dụng được tất cả những kiến thức, khả năng chuyên môn và kinh nghiệm&nbsp;làm việc của bản thân.</p>\r\n<p>\r\n	Đặc biệt, tôi muốn được góp phần sức lực của bản thân trong việc tìm hướng đi mới cho thủysản Việt Nam giống như sứ mệnh mà những người trẻ như tôi đã và đang cố gắng hết sức để&nbsp;thực hiện.<br />\r\n	&nbsp;</p>\r\n', '0905421388', 'kimnganduong2010@gmail.com', 1462348324, 0, 1, 1, 323),
(406, 15535, 3, 'Huỳnh Dủ Hão', 'Nhân viên kĩ thuật cá', 'Bạc Liêu', '<p>\r\n	Mới ra trường chưa có kinh nghiệm nhiều, nhưng có thể nuôi thức ăn tự nhiên cho cá giống.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Đại học', '<p>\r\n	Chịu khó, ham học hỏi và thích nghiên cứu.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Làm việc trong môi trường năng động, chuyên nghiệp.</p>\r\n', '01676747973', 'haob1209589@student.ctu.edu.vn', 1462702806, 0, 1, 0, 188),
(407, 5737, 6, 'Võ Thị Minh Tiến', 'Nnhân viên văn phòng, nhân viên phòng Lap, nhân viên chăm sóc khách hàng', 'Sóc Trăng, Quảng Nam', '<p>\r\n	1 năm làm nhân viên chăm sóc khách hàng, hổ sợ kỹ thuật trại giống tôm thẻ, lên kế hoạch xuất tôm tại công ty TNHH MTV GTS Nam Mỹ VN -Nha Trang</p>\r\n<p>\r\n	1,5 năm làm nhân viên Nghiên cứu thủy sản tại công ty TNHH Uni- President Việt Nam tại Tiền Giang.</p>\r\n', 'Kỹ sư nuôi trồng thủy sản', '<p>\r\n	Thành thạo vi tính văn phòng.</p>\r\n<p>\r\n	Có kỹ năng chăm sóc khách hàng và bán hàng qua điện thoại.</p>\r\n<p>\r\n	Có trách nhiệm hoàn thành công việc được giao trong thời gian sớm nhất.</p>\r\n<p>\r\n	Khả năng làm việc nhóm cao.</p>\r\n<p>\r\n	Có khả năng quản lý.</p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n', '<p>\r\n	Nơi làm việc phù hợp, công việc phù hợp, mức lương hợp lý.</p>\r\n<p>\r\n	Nơi làm việc có điều kiện để phát triển lợi thế của bản thân.</p>\r\n', '0905145615', 'minhtientshuaf@gmail.com', 1464333985, 0, 1, 0, 212),
(408, 14883, 2, 'Lê Việt Tân', 'Nhân Viên Kỹ Thuật.', 'Long An,Tiền Giang', '<p>\r\n	1 năm làm kỹ thuật sản xuất tôm giống. Kỹ năng quản lý chất lương môi trường ao nuôi.</p>\r\n', 'Kỹ sư Nuôi Trồng Thủy Sản', '<p>\r\n	Vui vẻ, hòa đồng,đam mê kỹ thuật thủy sản.</p>\r\n', '<p>\r\n	Công việc ổn định lâu dài.</p>\r\n', '0987976172', 'leviettan92@gmail.com', 1464948695, 0, 1, 1, 273),
(409, 12578, 1, 'Trương Quốc Cường', 'Nhân viên kinh doanh', 'LA, TG, BT, ĐT', '<p>\r\n	Kinh nghiệm: 8 năm bán hàng (thuốc Thú y-thức ăn gia súc) tự do.</p>\r\n', 'Đại Học Thú Y-NLTP.HCM', '<p>\r\n	Nhanh nhẹn, năng động, có khả năng tìm kiếm khách hàng mới&nbsp;ở khắp mọi nơi và chăm sóc khách hàng cũ.</p>\r\n', '<p>\r\n	Cần tìm sales các mặt hàng về Nông Nghiệp (thuốc Thú Y, Thuốc Thủy Sản, Tôm post giống).<br />\r\n	-Yêu cầu: Sản phẩm có chất lượng, có đăng ký với Cục Thú Y, nhãn mác rõ ràng, công ty phải có uy tính trách nhiệm, thương hiệu trên Thị Trường.<br />\r\n	-Khu vực: Long An, Tiền Giang, Bến Tre, Đồng Tháp.<br />\r\n	-Ưu thế: có khả năng tìm khách hàng mới, quảng bá sản phẩm mới.<br />\r\n	-Có thể phỏng vấn tại Thị Trường làm việc, Strainning trực tiếp qua điện thoại và Email.<br />\r\n	-Sẽ bổ sung hồ sơ hoàn chỉnh sau quá trình thử việc.<br />\r\n	-Làm việc như là nhân viên chính thức, sẽ có báo cáo thông tin Thị Trường sản phẩm, tâm tư và nguyện vọng khách hàng và các sản phẩm đang cạnh tranh.<br />\r\n	-Lương: hoa hồng trực tiếp trên đơn hàng.<br />\r\n	-Nhà tuyển dụng lưu ý: Chỉ xem bằng cấp, đối chiếu rồi trả lại, không giam giữ bằng gốc (bằng chuyên môn) của ứng viên (thanks).<br />\r\n	-Thời gian: nhận việc ngay theo thỏa thuận.</p>\r\n', '0919845212', 'quoccuongbsty@yahoo.com.vn', 1465289230, 0, 1, 1, 285),
